DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Spider Eggs', (SELECT id FROM sources WHERE name ILIKE 'WDMM' LIMIT 1), 47) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'If the party is defeated, stabilized characters awaken after {@dice 1d4} hours to find themselves cocooned in sticky webs and suspended 10 feet off the floor, with spider eggs implanted in their bodies. A cocooned character is {@condition restrained} and can use its action to try to escape by making a successful {@dc 15} Strength check.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"If the party is defeated, stabilized characters awaken after {@dice 1d4} hours to find themselves cocooned in sticky webs and suspended 10 feet off the floor, with spider eggs implanted in their bodies. A cocooned character is {@condition restrained} and can use its action to try to escape by making a successful {@dc 15} Strength check."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, 'Spider eggs implanted in a host hatch in {@dice 2d12} hours, killing the host as the baby spiders chew their way out. This infestation functions like a disease. A {@spell lesser restoration} spell or similar magic cast upon the host kills the spider eggs, ending the threat.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Spider eggs implanted in a host hatch in {@dice 2d12} hours, killing the host as the baby spiders chew their way out. This infestation functions like a disease. A {@spell lesser restoration} spell or similar magic cast upon the host kills the spider eggs, ending the threat."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Super-Tetanus', (SELECT id FROM sources WHERE name ILIKE 'TftYP' LIMIT 1), 104) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'A creature that contracts the disease of super-tetanus is wracked with pain as its heart races and its muscles spasm hard enough to break its bones. The creature takes 11 ({@damage 2d10}) damage at the start of each of its turns. If a victim is not cured by other means, it can repeat the saving throw at the end of every minute after becoming exposed, ending the effect on itself with a successful save.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"A creature that contracts the disease of super-tetanus is wracked with pain as its heart races and its muscles spasm hard enough to break its bones. The creature takes 11 ({@damage 2d10}) damage at the start of each of its turns. If a victim is not cured by other means, it can repeat the saving throw at the end of every minute after becoming exposed, ending the effect on itself with a successful save."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('The Gnawing Plague', (SELECT id FROM sources WHERE name ILIKE 'VRGR' LIMIT 1), 153) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'The Gnawing Plague, also known as "the Gnaws," is known in every corner of Richemulot.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"The Gnawing Plague, also known as \"the Gnaws,\" is known in every corner of Richemulot."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, 'entries', 'Transmission', 'The Gnaws is spread when a creature is bitten by a {@creature rat}, {@creature giant rat}, {@creature swarm of rats}, or {@creature wererat} that carries the disease, or by coming into physical contact with an infected creature.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '{"type":"entries","name":"Transmission","page":153,"entries":["The Gnaws is spread when a creature is bitten by a {@creature rat}, {@creature giant rat}, {@creature swarm of rats}, or {@creature wererat} that carries the disease, or by coming into physical contact with an infected creature."]}'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 2, 'entries', 'Infection', 'Creatures exposed to the disease must succeed on a {@dc 10} Constitution saving throw or become infected. The DC of this saving throw can increase depending on the severity of the plague''s spread (see "{@book Cycle of the Plague|VRGR|3|Cycle of the Plague}" below).', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '{"type":"entries","name":"Infection","page":153,"entries":["Creatures exposed to the disease must succeed on a {@dc 10} Constitution saving throw or become infected. The DC of this saving throw can increase depending on the severity of the plague''s spread (see \"{@book Cycle of the Plague|VRGR|3|Cycle of the Plague}\" below)."]}'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 3, 'entries', 'Symptoms', 'It takes {@dice 1d2} days for the Gnawing Plague''s symptoms to manifest in an infected creature. The infected creature then gains 1 level of {@condition exhaustion}, regains only half the normal number of hit points from spending Hit Dice, and regains no hit points from finishing a long rest.
The plague''s symptoms include buboes, fatigue, splotchy rashes, sweats, and shaking, particularly facial tremors. Locals liken these twitches to the sniffing of rats. Sufferers often have scraps of leather placed in their mouths to prevent their teeth from clattering, though they inevitably gnaw through these scraps.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '{"type":"entries","name":"Symptoms","page":153,"entries":["It takes {@dice 1d2} days for the Gnawing Plague''s symptoms to manifest in an infected creature. The infected creature then gains 1 level of {@condition exhaustion}, regains only half the normal number of hit points from spending Hit Dice, and regains no hit points from finishing a long rest.","The plague''s symptoms include buboes, fatigue, splotchy rashes, sweats, and shaking, particularly facial tremors. Locals liken these twitches to the sniffing of rats. Sufferers often have scraps of leather placed in their mouths to prevent their teeth from clattering, though they inevitably gnaw through these scraps."]}'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 4, 'entries', 'Recovery', 'At the end of each long rest, an infected creature must make a {@dc 10} Constitution saving throw. On a failed save, the creature gains 1 level of {@condition exhaustion}. On a successful save, the creature''s {@condition exhaustion} level decreases by 1. If a successful saving throw reduces the infected creature''s level of {@condition exhaustion} below 1, the creature recovers from the disease.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '{"type":"entries","name":"Recovery","page":154,"entries":["At the end of each long rest, an infected creature must make a {@dc 10} Constitution saving throw. On a failed save, the creature gains 1 level of {@condition exhaustion}. On a successful save, the creature''s {@condition exhaustion} level decreases by 1. If a successful saving throw reduces the infected creature''s level of {@condition exhaustion} below 1, the creature recovers from the disease."]}'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 5, 'entries', 'Immunity', 'All forms of rats and wererats are immune to the Gnawing Plague.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '{"type":"entries","name":"Immunity","page":154,"entries":["All forms of rats and wererats are immune to the Gnawing Plague."]}'::jsonb);
END $DISEASE_INSERT_BLOCK$;

DO $DISEASE_INSERT_BLOCK$
DECLARE
    disease_id_var INTEGER;
BEGIN
    INSERT INTO diseases (name, source_id, page) VALUES ('Throat Leeches', (SELECT id FROM sources WHERE name ILIKE 'ToA' LIMIT 1), 40) RETURNING id INTO disease_id_var;
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 0, NULL, NULL, 'Minuscule parasites known as throat leeches infect the water in Chult''s forests, swamps, and rivers. Any giant or humanoid that swallows tainted water must succeed on a {@dc 12} Constitution saving throw or be infested with throat leeches. Immediate symptoms include throat inflammation and shortness of breath. After {@dice 1d6} hours, the infected character gains 1 level of {@condition exhaustion} that can''t be removed (except as described below) until the disease is cured. At the end of each long rest, the infected creature must repeat the saving throw. On a failed save, the creature''s {@condition exhaustion} increases by 1 level; on a successful save, the creature''s {@condition exhaustion} decreases by 1 level. If a successful saving throw reduces the infected creature''s level of {@condition exhaustion} below 1, the creature recovers from the disease.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Minuscule parasites known as throat leeches infect the water in Chult''s forests, swamps, and rivers. Any giant or humanoid that swallows tainted water must succeed on a {@dc 12} Constitution saving throw or be infested with throat leeches. Immediate symptoms include throat inflammation and shortness of breath. After {@dice 1d6} hours, the infected character gains 1 level of {@condition exhaustion} that can''t be removed (except as described below) until the disease is cured. At the end of each long rest, the infected creature must repeat the saving throw. On a failed save, the creature''s {@condition exhaustion} increases by 1 level; on a successful save, the creature''s {@condition exhaustion} decreases by 1 level. If a successful saving throw reduces the infected creature''s level of {@condition exhaustion} below 1, the creature recovers from the disease."'::jsonb);
    INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, raw_json) VALUES (disease_id_var, 1, NULL, NULL, 'Explorers can avoid contracting throat leeches by drinking only rainwater or water that''s been boiled or magically purified.', NULL::jsonb, NULL, NULL::jsonb, NULL::jsonb, '"Explorers can avoid contracting throat leeches by drinking only rainwater or water that''s been boiled or magically purified."'::jsonb);
END $DISEASE_INSERT_BLOCK$;

