INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (1, 0, 'list', NULL, NULL, '["A blinded creature can''t see and automatically fails any ability check that requires sight.","Attack rolls against the creature have advantage, and the creature''s attack rolls have disadvantage."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (2, 0, 'list', NULL, NULL, '["A charmed creature can''t attack the charmer or target the charmer with harmful abilities or magical effects.","The charmer has advantage on any ability check to interact socially with the creature."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (3, 0, 'list', NULL, NULL, '["A deafened creature can''t hear and automatically fails any ability check that requires hearing."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (4, 0, 'string', NULL, 'Some special abilities and environmental hazards, such as starvation and the long-term effects of freezing or scorching temperatures, can lead to a special condition called exhaustion. Exhaustion is measured in six levels. An effect can give a creature one or more levels of exhaustion, as specified in the effect''s description.', NULL, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (4, 1, 'table', NULL, NULL, NULL, 'undefined', '["Level","Effect"]'::jsonb, '[["1","Disadvantage on ability checks"],["2","Speed halved"],["3","Disadvantage on attack rolls and saving throws"],["4","Hit point maximum halved"],["5","Speed reduced to 0"],["6","Death"]]'::jsonb);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (4, 2, 'string', NULL, 'If an already exhausted creature suffers another effect that causes exhaustion, its current level of exhaustion increases by the amount specified in the effect''s description.', NULL, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (4, 3, 'string', NULL, 'A creature suffers the effect of its current level of exhaustion as well as all lower levels. For example, a creature suffering level 2 exhaustion has its speed halved and has disadvantage on ability checks.', NULL, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (4, 4, 'string', NULL, 'An effect that removes exhaustion reduces its level as specified in the effect''s description, with all exhaustion effects ending if a creature''s exhaustion level is reduced below 1.', NULL, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (4, 5, 'string', NULL, 'Finishing a long rest reduces a creature''s exhaustion level by 1, provided that the creature has also ingested some food and drink. Also, being raised from the dead reduces a creature''s exhaustion level by 1.', NULL, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (5, 0, 'list', NULL, NULL, '["A frightened creature has disadvantage on ability checks and attack rolls while the source of its fear is within line of sight.","The creature can''t willingly move closer to the source of its fear."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (6, 0, 'list', NULL, NULL, '["A grappled creature''s speed becomes 0, and it can''t benefit from any bonus to its speed.","The condition ends if the grappler is {@condition incapacitated}.","The condition also ends if an effect removes the grappled creature from the reach of the grappler or grappling effect, such as when a creature is hurled away by the {@spell thunderwave} spell."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (7, 0, 'list', NULL, NULL, '["An incapacitated creature can''t take actions or reactions."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (8, 0, 'list', NULL, NULL, '["An invisible creature is impossible to see without the aid of magic or a special sense. For the purpose of hiding, the creature is heavily obscured. The creature''s location can be detected by any noise it makes or any tracks it leaves.","Attack rolls against the creature have disadvantage, and the creature''s attack rolls have advantage."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (9, 0, 'list', NULL, NULL, '["A paralyzed creature is {@condition incapacitated} and can''t move or speak.","The creature automatically fails Strength and Dexterity saving throws.","Attack rolls against the creature have advantage.","Any attack that hits the creature is a critical hit if the attacker is within 5 feet of the creature."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (10, 0, 'list', NULL, NULL, '["A petrified creature is transformed, along with any nonmagical object it is wearing or carrying, into a solid inanimate substance (usually stone). Its weight increases by a factor of ten, and it ceases aging.","The creature is {@condition incapacitated}, can''t move or speak, and is unaware of its surroundings.","Attack rolls against the creature have advantage.","The creature automatically fails Strength and Dexterity saving throws.","The creature has resistance to all damage.","The creature is immune to poison and disease, although a poison or disease already in its system is suspended, not neutralized."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (11, 0, 'list', NULL, NULL, '["A poisoned creature has disadvantage on attack rolls and ability checks."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (12, 0, 'list', NULL, NULL, '["A prone creature''s only movement option is to crawl, unless it stands up and thereby ends the condition.","The creature has disadvantage on attack rolls.","An attack roll against the creature has advantage if the attacker is within 5 feet of the creature. Otherwise, the attack roll has disadvantage."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (13, 0, 'list', NULL, NULL, '["A restrained creature''s speed becomes 0, and it can''t benefit from any bonus to its speed.","Attack rolls against the creature have advantage, and the creature''s attack rolls have disadvantage.","The creature has disadvantage on Dexterity saving throws."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (14, 0, 'list', NULL, NULL, '["A stunned creature is {@condition incapacitated}, can''t move, and can speak only falteringly.","The creature automatically fails Strength and Dexterity saving throws.","Attack rolls against the creature have advantage."]'::jsonb, NULL, NULL, NULL);
INSERT INTO condition_entries (condition_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (15, 0, 'list', NULL, NULL, '["An unconscious creature is {@condition incapacitated}, can''t move or speak, and is unaware of its surroundings.","The creature drops whatever it''s holding and falls {@condition prone}.","The creature automatically fails Strength and Dexterity saving throws.","Attack rolls against the creature have advantage.","Any attack that hits the creature is a critical hit if the attacker is within 5 feet of the creature."]'::jsonb, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (1, 0, 'string', NULL, 'Any humanoid that spends 12 hours in the necropolis must succeed on a 1 Constitution saving throw or contract an arcane blight. This magical disease transforms the humanoid into a n, but only after the victim experiences hallucinations and feelings of isolation and paranoia. Other symptoms include clammy skin, hair loss, and myopia (nearsightedness).', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (1, 1, 'string', NULL, 'A player character infected with the arcane blight gains the following flaw: "I don''t trust anyone." This flaw, which supersedes any conflicting flaw, is fed by delusions that are difficult for the character to distinguish from reality. Common delusions include the belief that that allies are conspiring to steal the victim''s riches or otherwise turn against the victim.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (1, 2, 'string', NULL, 'Whenever it finishes a long rest, an infected humanoid must repeat the saving throw. On a successful save, the DC for future saves against the arcane blight drops by 1. If the saving throw DC drops to 0, the creature overcomes the arcane blight and becomes immune to the effect of further exposure. A creature that fails three of these saving throws transforms into a n under the DM''s control. Only a w spell or divine intervention can undo this transformation.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (1, 3, 'string', NULL, 'A g spell or similar magic ends the infection on the target, removing the flaw and all other symptoms, but this magic doesn''t protect the target against further exposure.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (2, 0, 'string', NULL, 'Pain grips the creature''s mind, and its eyes turn milky white. The creature has disadvantage on Wisdom checks and Wisdom saving throws and is b.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (2, 1, 'string', NULL, 'T spell.}', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (3, 0, 'string', NULL, 'A magical mist creeps through the jungles of Chult. Contact with this thin, blue, odorless mist can infect giants and humanoids with blue mist fever. A d spell destroys the mist in a 20-foot square starting at a point chosen by the caster within the spell''s range. An encounter with this mist typically covers 1 such areas (400-2,400 square feet).', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (3, 1, 'string', NULL, 'A giant or humanoid that comes into contact with the mist must succeed on a 1 Constitution saving throw or become infected with blue mist fever. An infected creature begins seeing vivid hallucinations of blue monkeys 1 hours after failing the save, and the hallucinations last until the disease ends on the creature. A creature can repeat the saving throw every 24 hours, ending the effect on itself on a success.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (4, 0, 'string', NULL, 'This disease targets humanoids. While afflicted with bluerot, a victim grows grotesque blue boils on their face and back. This disease is carried by undead (including the drowned ones in Tammeraut''s Fate), and victims most often acquire it through wounds caused by infected creatures. The disease''s boils manifest in 1 hours, causing the victim''s Constitution and Charisma scores to decrease by 1 each, to a minimum of 3. This is quickly followed by a fever and tingling in the extremities. An infected creature is vulnerable to radiant damage and gains the ability to breathe underwater.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (4, 1, 'string', NULL, 'At the end of each long rest, an infected creature makes a 1 Constitution saving throw. On a success, the victim regains 1 point of Constitution and 1 point of Charisma lost to the disease. If the infected creature regains all the points lost to the disease, it is cured. Other effects that raise the victim''s ability scores do not cure the disease. On a failed saving throw, the victim takes 18 (4) necrotic damage as the boils burst and spread. A creature reduced to 0 hit points by this damage cannot regain hit points until the disease is cured, though it can be stabilized as normal.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (5, 0, 'string', NULL, 'This disease targets humanoids, although gnomes are strangely immune. While in the grips of this disease, victims frequently succumb to fits of mad laughter, giving the disease its common name and its morbid nickname: "the shrieks."', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (5, 1, 'string', NULL, 'Symptoms manifest 1 hours after infection and include fever and disorientation. The infected creature gains one level of e that can''t be removed until the disease is cured.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (5, 2, 'string', NULL, 'Any event that causes the infected creature great stress—including entering combat, taking damage, experiencing fear, or having a nightmare—forces the creature to make a 1 Constitution saving throw.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (5, 3, 'string', NULL, 'On a failed save, the creature takes 5 (1) psychic damage and becomes i with mad laughter for 1 minute. The creature can repeat the saving throw at the end of each of its turns, ending the mad laughter and the i condition on a success. Any humanoid creature that starts its turn within 10 feet of an infected creature in the throes of mad laughter must succeed on a 1 Constitution saving throw or also become infected with the disease. Once a creature succeeds on this save, it is immune to the mad laughter of that particular infected creature for 24 hours.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (5, 4, 'string', NULL, 'At the end of each long rest, an infected creature can make a 1 Constitution saving throw. On a successful save, the DC for this save and for the save to avoid an attack of mad laughter drops by 1. When the saving throw DC drops to 0, the creature recovers from the disease. A creature that fails three of these saving throws gains a randomly determined form of M.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (6, 0, 'string', NULL, 'A raging fever sweeps through the creature''s body. The creature has disadvantage on Strength checks, Strength saving throws, and attack rolls that use Strength.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (6, 1, 'string', NULL, 'T spell.}', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (7, 0, 'string', NULL, 'The creature''s flesh decays. The creature has disadvantage on Charisma checks and vulnerability to all damage.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (7, 1, 'string', NULL, 'T spell.}', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (8, 0, 'string', NULL, 'Frigid woe is a special disease developed by Aeor''s mages that cannot be cured by conventional treatment or magic. The only way a creature infected with the disease can be cured is by finding and drinking the manufactured antidote, a milky liquid stored in gold vials found in Eiselcross''s ruins. This disease was created to slow down the forces of the gods and get around the healing power of their clerics and angels.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (8, 1, 'string', NULL, 'The disease is transmitted by breathing in blue spores that Aeor''s mages created long ago. When a creature comes into contact with these spores, it must succeed on a 1 Constitution saving throw or become infected with frigid woe. It takes 1 days for the symptoms to manifest in an infected creature. These symptoms include fatigue, chills, and visible blue veins that appear on the creature''s body. The infected creature''s speed is reduced by 5 feet as long as it remains infected. Every 10 days after symptoms appear, an infected creature must succeed on a 1 Constitution saving throw, or its speed is reduced by another 5 feet. If a creature''s speed is reduced to 0 as a result of this disease, the creature dies and its body turns into a statue made of ice.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (8, 2, 'string', NULL, 'A creature can drink the antidote as an action, ending all symptoms and effects of the disease instantly.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (9, 0, 'string', NULL, 'Water polluted with ghoul parts carries ghoul gut. A creature drinking this tainted water must succeed on a 1 Constitution saving throw or contract the disease. Symptoms strike when a victim starts a long rest or next becomes excited, as during combat. A victim suffers severe cramps, and their bowels make sounds like two ghouls fighting. When the symptoms first strike, the victim takes 5 (2) necrotic damage. In addition, a diseased victim regains no hit points or Hit Dice from nonmagical sources, including resting or spending Hit Dice. A victim who finishes a long rest can attempt a 1 Constitution saving throw. On a successful save, the victim shakes off the disease. Failure deals the character another 5 (2) necrotic damage.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (9, 1, 'string', NULL, 'A character who succeeds on a 1 Wisdom (M) check can also remove the disease from another creature. Doing so requires the character making the check to care for the sick individual during a long rest. The caregiver gains no benefit from that rest.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (10, 0, 'string', NULL, 'The constant smog in Gracklstugh causes grackle-lung in living, breathing creatures, resulting in persistent, wracking coughs and the spewing of thick, black phlegm. Whenever a living, breathing creature finishes a long rest in Gracklstugh, it must make a 1 Constitution saving throw. On each failed save, the creature gains one level of e as its airways become increasingly clogged. A creature that reaches level 6 e dies, as normal.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (10, 1, 'string', NULL, 'A creature with one or more levels of e brought on by grackle-lung must succeed on a Constitution check to take the D action. If the check fails, the action cannot be attempted. If the creature attempts to cast a spell with a verbal component, it must succeed on a Constitution check or be unable to complete the spell, causing the spell to fail with no effect. The DC for each check is 10 + the creature''s current e level.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (10, 2, 'string', NULL, 'If a creature''s e level drops below 1, it no longer suffers the effects of grackle-lung and becomes immune to it for the next week. Duergar and derro are inured to grackle-lung, making Constitution checks against it with advantage. Any spell or effect that cures disease also cures grackle-lung, effectively removing all levels of e brought on by the affliction.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (11, 0, 'string', NULL, 'The creature''s mind becomes feverish. The creature has disadvantage on Intelligence checks and Intelligence saving throws, and the creature behaves as if under the effects of the c spell during combat.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (11, 1, 'string', NULL, 'T spell.}', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (12, 0, 'string', NULL, 'The effects of redface are identical to those of s (see "D" in chapter 8 of the D), but it''s caused by pollutants in the air rather than contaminated drinking water, making it difficult to avoid.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (13, 0, 'string', NULL, 'Xanthoria''s research into lichdom and her creation of a living phylactery led to the emergence of the plague spreading across the Sword Coast. Any beast or humanoid that comes within 10 feet of a creature infected by the saprophytic plague must succeed on a 2 Constitution saving throw or become infected as well. On a successful save, a creature is immune to the plague for 24 hours, and any creature that is immune to disease succeeds on the save automatically.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (13, 1, 'string', NULL, 'After a failed save, a creature experiences the first symptoms—body aches, nausea, slurred speech, and uncontrollable drooling—1 hours later and gains 1 level of e. Every 24 hours thereafter, the creature automatically gains another level of e. Any creature killed by the saprophytic plague transforms into a lifeless magenta ooze.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (13, 2, 'string', NULL, 'A creature infected by the saprophytic plague for at least 24 hours behaves like a zombie and seems barely aware of its surroundings, as fungal growths sprout from its head, body, and limbs. It feels an overriding urge to be around groups of uninfected creatures so that it can spread the contagion. This is the time when infected humanoids begin speaking the word "Xanthoria" over and over, with no understanding of what the word means. This behavior is the result of a mycelial network of spores that forms a tenuous connection between Xanthoria and humanoids that become infected by the plague.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (13, 3, 'string', NULL, 'Foodstuffs that are exposed to the plague''s spores decay or go sour within 2 hours, leaving behind a lurid magenta mass of oozing fungi. Eating infected food requires a saving throw as above.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (13, 4, 'string', NULL, 'Any magic that cures a disease can rid a creature of the saprophytic plague. Casting p destroys the infection in foodstuffs.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (14, 0, 'string', NULL, 'The creature is overcome with shaking. The creature has disadvantage on Dexterity checks, Dexterity saving throws, and attack rolls that use Dexterity.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (14, 1, 'string', NULL, 'T spell.}', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (15, 0, 'string', NULL, 'Sewer plague is a generic term for a broad category of illnesses that incubate in sewers, refuse heaps, and stagnant swamps, and which are sometimes transmitted by creatures that dwell in those areas, such as rats and otyughs.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (15, 1, 'string', NULL, 'When a humanoid creature is bitten by a creature that carries the disease, or when it comes into contact with filth or offal contaminated by the disease, the creature must succeed on a 1 Constitution saving throw or become infected.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (15, 2, 'string', NULL, 'It takes 1 days for sewer plague''s symptoms to manifest in an infected creature. Symptoms include fatigue and cramps. The infected creature suffers one level of e, and it regains only half the normal number of hit points from spending Hit Dice and no hit points from finishing a long rest.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (15, 3, 'string', NULL, 'At the end of each long rest, an infected creature must make a 1 Constitution saving throw. On a failed save, the character gains one level of e.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (15, 4, 'string', NULL, 'On a successful save, the character''s e level decreases by one level. If a successful saving throw reduces the infected creature''s level of e below 1, the creature recovers from the disease.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (16, 0, 'string', NULL, 'Insects native to the jungles and marshes of Chult carry this disease, shivering sickness. The easiest protection against it is a coating of insect-repelling salve on all exposed skin (for the cost of insect repellent, see ".', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (16, 1, 'string', NULL, 'A giant or humanoid that takes damage from S or from g, g, or g is exposed to the disease at the end of the encounter. Those who haven''t applied insect repellent since their previous long rest are exposed to the disease when they finish a long rest.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (16, 2, 'string', NULL, 'A giant or humanoid exposed to the disease must succeed on a 1 Constitution saving throw or become infected. A creature with natural armor has advantage on the saving throw. It takes 2 hours for symptoms to manifest in an infected creature. Symptoms include blurred vision, disorientation, and a sudden drop in body temperature that causes uncontrollable shivering and chattering of the teeth.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (16, 3, 'string', NULL, 'Once symptoms begin, the infected creature regains only half the normal number of hit points from spending Hit Dice and no hit points from a long rest. The infected creature also has disadvantage on ability checks and attack rolls. At the end of a long rest, an infected creature repeats the saving throw, shaking off the disease on a successful save.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (17, 0, 'string', NULL, 'This painful infection causes bleeding from the eyes and eventually blinds the victim.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (17, 1, 'string', NULL, 'A beast or humanoid that drinks water tainted by sight rot must succeed on a 1 Constitution saving throw or become infected. One day after infection, the creature''s vision starts to become blurry. The creature takes a -1 penalty to attack rolls and ability checks that rely on sight. At the end of each long rest after the symptoms appear, the penalty worsens by 1. When it reaches -5, the victim is b until its sight is restored by magic such as l or h.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (17, 2, 'string', NULL, 'Sight rot can be cured using a rare flower called Eyebright, which grows in some swamps. Given an hour, a character who has proficiency with an h can turn the flower into one dose of ointment. Applied to the eyes before a long rest, one dose of it prevents the disease from worsening after that rest. After three doses, the ointment cures the disease entirely.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (18, 0, 'string', NULL, 'The creature begins to bleed uncontrollably. The creature has disadvantage on Constitution checks and Constitution saving throws. In addition, whenever the creature takes damage, it is s until the end of its next turn.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (18, 1, 'string', NULL, 'T spell.}', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (19, 0, 'string', NULL, 'If the party is defeated, stabilized characters awaken after 1 hours to find themselves cocooned in sticky webs and suspended 10 feet off the floor, with spider eggs implanted in their bodies. A cocooned character is r and can use its action to try to escape by making a successful 1 Strength check.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (19, 1, 'string', NULL, 'Spider eggs implanted in a host hatch in 2 hours, killing the host as the baby spiders chew their way out. This infestation functions like a disease. A l spell or similar magic cast upon the host kills the spider eggs, ending the threat.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (20, 0, 'string', NULL, 'A creature that contracts the disease of super-tetanus is wracked with pain as its heart races and its muscles spasm hard enough to break its bones. The creature takes 11 (2) damage at the start of each of its turns. If a victim is not cured by other means, it can repeat the saving throw at the end of every minute after becoming exposed, ending the effect on itself with a successful save.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (21, 0, 'string', NULL, 'The Gnawing Plague, also known as "the Gnaws," is known in every corner of Richemulot.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (21, 1, 'entries', 'Transmission', NULL, NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (21, 2, 'entries', 'Infection', NULL, NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (21, 3, 'entries', 'Symptoms', NULL, NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (21, 4, 'entries', 'Recovery', NULL, NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (21, 5, 'entries', 'Immunity', NULL, NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (22, 0, 'string', NULL, 'Minuscule parasites known as throat leeches infect the water in Chult''s forests, swamps, and rivers. Any giant or humanoid that swallows tainted water must succeed on a 1 Constitution saving throw or be infested with throat leeches. Immediate symptoms include throat inflammation and shortness of breath. After 1 hours, the infected character gains 1 level of e that can''t be removed (except as described below) until the disease is cured. At the end of each long rest, the infected creature must repeat the saving throw. On a failed save, the creature''s e increases by 1 level; on a successful save, the creature''s e decreases by 1 level. If a successful saving throw reduces the infected creature''s level of e below 1, the creature recovers from the disease.', NULL, NULL, NULL, NULL);
INSERT INTO disease_entries (disease_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows) VALUES (22, 1, 'string', NULL, 'Explorers can avoid contracting throat leeches by drinking only rainwater or water that''s been boiled or magically purified.', NULL, NULL, NULL, NULL);
