INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (1, 0, 'string', NULL, 'A ballista is a massive crossbow that fires heavy bolts. Before it can be fired, it must be loaded and aimed. It takes one action to load the weapon, one action to aim it, and one action to fire it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (2, 0, 'string', NULL, 'This wagon-sized device is designed to look roughly like a dragon. Before it can be used, the boilerdrak must be lit and aimed. It takes 1 action to light the weapon, 1 action to aim it, and 1 action to fire it. When you use your action to fire it, roll a d. If you roll a 2 or higher, use the Flames action. If you roll a 1, use the Explosion action.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (3, 0, 'string', NULL, 'A cannon uses gunpowder to propel heavy balls of cast iron through the air at destructive speeds. In a campaign without gunpowder, a cannon might be an arcane device built by clever gnomes or wizardly engineers.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (3, 1, 'string', NULL, 'A cannon is usually supported in a wooden frame with wheels. Before it can be fired, the cannon must be loaded and aimed. It takes one action to load the weapon, one action to aim it, and one action to fire it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (5, 0, 'string', NULL, 'C subclass feature.}', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
