INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (7, 0, 'string', NULL, 'C subclass feature.}', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (8, 0, 'string', NULL, 'When characters need to saw through ropes, shatter a window, or smash a vampire''s coffin, the only hard and fast rule is this: given enough time and the right tools, characters can destroy any destructible object. Use common sense when determining a character''s success at damaging an object. Can a fighter cut through a section of a stone wall with a sword? No, the sword is likely to break before the wall does.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (8, 1, 'string', NULL, 'For the purpose of these rules, an object is a discrete, inanimate item like a window, door, sword, book, table, chair, or stone, not a building or a vehicle that is composed of many other objects.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (8, 2, 'entries', 'Statistics for Objects', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["When time is a factor, you can assign an Armor Class and hit points to a destructible object. You can also give it immunities, resistances, and vulnerabilities to specific types of damage.",{"type":"entries","name":"Armor Class","entries":[" An object''s Armor Class is a measure of how difficult it is to deal damage to the object when striking it (because the object has no chance of dodging out of the way). The Object Armor Class table provides suggested AC values for various substances.",{"type":"table","caption":"Object Armor Class","colLabels":["Substance","AC"],"colStyles":["col-6","col-6"],"rows":[["Cloth, paper, rope","11"],["Crystal, glass, ice","13"],["Wood, bone","15"],["Stone","17"],["Iron, steel","19"],["Mithral","21"],["Adamantine","23"]]}]},{"type":"entries","name":"Hit Points","entries":[" An object''s hit points measure how much damage it can take before losing its structural integrity. Resilient objects have more hit points than fragile ones. Large objects also tend to have more hit points than small ones, unless breaking a small part of the object is just as effective as breaking the whole thing. The Object Hit Points table provides suggested hit points for fragile and resilient objects that are Large or smaller.",{"type":"table","caption":"Object Hit Points","colLabels":["Size","Fragile","Resilient"],"colStyles":["col-6","col-6"],"rows":[["Tiny (bottle, lock)","2 (1d4)","5 (2d4)"],["Small (chest, lute)","3 (1d6)","10 (3d6)"],["Medium (barrel, chandelier)","4 (1d8)","18 (4d8)"],["Large (cart, 10-ft.-by-10-ft. window)","5 (1d10)","27 (5d10)"]]}]},{"type":"entries","name":"Huge and Gargantuan Objects","entries":["Normal weapons are of little use against many Huge and Gargantuan objects, such as a colossal statue, towering column of stone, or massive boulder. That said, one torch can burn a Huge tapestry, and an earthquake spell can reduce a colossus to rubble. You can track a Huge or Gargantuan object''s hit points if you like, or you can simply decide how long the object can withstand whatever weapon or force is acting against it. If you track hit points for the object, divide it into Large or smaller sections, and track each section''s hit points separately. Destroying one of those sections could ruin the entire object. For example, a Gargantuan statue of a human might topple over when one of its Large legs is reduced to 0 hit points."]},{"type":"entries","name":"Objects and Damage Types","entries":["Objects are immune to poison and psychic damage. You might decide that some damage types are more effective against a particular object or substance than others. For example, bludgeoning damage works well for smashing things but not for cutting through rope or leather. Paper or cloth objects might be vulnerable to fire and lightning damage. A pick can chip away stone but can''t effectively cut down a tree. As always, use your best judgment."]},{"type":"entries","name":"Damage Threshold","entries":["Big objects such as castle walls often have extra resilience represented by a damage threshold. An object with a damage threshold has immunity to all damage unless it takes an amount of damage from a single attack or effect equal to or greater than its damage threshold, in which case it takes damage as normal. Any damage that fails to meet or exceed the object''s damage threshold is considered superficial and doesn''t reduce the object''s hit points."]}]'::jsonb);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (9, 0, 'string', NULL, 'The gnomeflinger is a catapult designed to hurl creatures, instead of projectiles, in a high arc. The gnomeflinger was designed in conjunction with the n (see the " section) to hasten transportation. The device can hold one Medium or smaller creature. Before it can be used, the gnomeflinger must be loaded and aimed. It takes 1 action to load the gnomeflinger, 1 action to aim it, and 1 action to fire it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (9, 1, 'string', NULL, 'While taking an action to aim the gnomeflinger, you set the maximum horizontal distance of the Fling Personnel action to 60 feet, 150 feet, or 300 feet.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (10, 0, 'entries', 'Fruit', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["Two magical fruits (evocation school) ripen on the tree each year. Each fruit resembles an apple, but it slowly extrudes from the tree like a cyst.","Up to a week before the summer solstice, a ruby red fruit grows. Someone other than a construct who eats a quarter or more of it regains 70 hit points. In addition, any {@condition blinded||blindness}, {@condition deafened||deafness}, and diseases affecting the eater end.","Up to a week before the winter solstice, a sickly white fruit grows. Someone who eats a quarter or more of it takes 70 necrotic damage. Each fruit has {@dice 1d4} seeds that, if planted, grow leafless woody shrubs that animate as twig blights after one year."]'::jsonb);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (10, 1, 'entries', 'Tree Thralls', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["If a humanoid is bound to the bole of the Gulthias Tree, over the next 24 hours the victim is sucked completely into the tree. Once the victim is completely absorbed into the tree, it becomes the tree''s thrall, and is expelled over the course of 1 hour. The Gulthias Tree can have only four thralls at any one time. A victim''s skin is rough, gray, and bark-like. A thrall is totally corrupted, becoming neutral evil. Such a creature exists only to serve the Gulthias Tree and those who tend it (such as Belak). It possesses all of its former abilities and gains the following traits:",{"type":"entries","name":"Barkskin","entries":["The thrall''s AC can''t be lower than 16."]},{"type":"entries","name":"Tree Thrall","entries":["If the Gulthias Tree dies, the thrall dies 24 hours later."]}]'::jsonb);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (11, 0, 'entries', 'Heart Effects', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["An ice troll''s heart remains cold even after the troll''s destruction. As long as the troll can''t regenerate, its heart can be safely removed from its remains, handled, and kept. An ice troll''s heart, once removed, becomes a harmless Tiny object. For the next 24 hours, the heart has the following magical properties:",{"type":"list","items":["A creature that eats the heart gains the ability to regenerate for the next 24 hours, regaining 5 hit points at the start of each of its turns. If the creature takes acid or fire damage, this trait doesn''t function at the start of its next turn. The creature dies only if it starts its turn with 0 hit points and doesn''t regenerate.","If buried in the ground under a foot or more of earth, the heart melts away and summons a blizzard like that created by the {@spell control weather} spell. It takes 10 minutes for the heart to melt and the blizzard to form. The blizzard lasts for 8 hours.","A hag or similar creature can perform a ritual that turns the heart into a magical talisman that acts like a patch of brown mold (see \"{@hazard Brown Mold}\" in the {@book Dungeon Master''s Guide|DMG}). This effect lasts until the heart is destroyed.","A creature proficient with alchemist''s supplies can squeeze enough residual fluid out of the heart to mix with other alchemical ingredients, creating one {@item Potion of Cold Resistance||potion of resistance (cold)}. It takes 1 hour to create this potion. "]}]'::jsonb);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (12, 0, 'string', NULL, 'A mangonel is a type of catapult that hurls heavy projectiles in a high arc. This payload can hit targets behind cover. Before the mangonel can be fired, it must be loaded and aimed. It takes two actions to load the weapon, two actions to aim it, and one action to fire it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (12, 1, 'string', NULL, 'A mangonel typically hurls a heavy stone, although it can hurl other kinds of projectiles, with different effects.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (13, 0, 'string', NULL, 'A ram consists of a movable gallery equipped with a heavy log suspended from two roof beams by chains.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (13, 1, 'string', NULL, 'The log is shod in iron and used to batter through doors and barricades.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (13, 2, 'string', NULL, 'It takes a minimum of four Medium creatures to operate a ram. Because of the gallery roof, these operators have C against attacks from above.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (14, 0, 'string', NULL, 'A siege tower is a mobile wooden structure with a beam frame and slats in its walls. Large wooden wheels or rollers allow the tower to be pushed or pulled by soldiers or beasts of burden. Medium or smaller creatures can use the siege tower to reach the top of walls up to 40 feet high. A creature in the tower has C from attacks outside the tower.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (15, 0, 'string', NULL, 'If destroyed, the blister bursts with a chorus of distant sighs. A moment later, a.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (16, 0, 'string', NULL, 'A cauldron is an iron pot suspended so that it can be tipped easily, spilling its contents. Once emptied, a cauldron must be refilled-and its contents must usually be reheated-before it can be used again. It takes three actions to fill a cauldron and one action to tip it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (16, 1, 'string', NULL, 'Cauldrons can be filled with other liquids, such as acid or g, with different effects.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (18, 0, 'string', NULL, 'A trebuchet is a powerful catapult that throws its payload in a high arc, so it can hit targets behind cover.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (18, 1, 'string', NULL, 'Before the trebuchet can be fired, it must be loaded and aimed. It takes two actions to load the weapon, two actions to aim it, and one action to fire it.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (18, 2, 'string', NULL, 'A trebuchet typically hurls a heavy stone. However, it can launch other kinds of projectiles, such as barrels of oil or sewage, with different effects.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_entries (object_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json) VALUES (19, 0, 'string', NULL, 'The Unborn Palanquin is a wormlike leviathan of screeching gears made up of hundreds of tireless m. It is a unique vehicle which serves as both M''s primary conveyance and the Fiend''s lair.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (1, 0, 'actions', 'Bolt', '[{"type":"attack","attackType":"RW","attackEntries":["{@hit +6} to hit, range 120/480 ft., one target."],"hitEntries":["16 ({@damage 3d10}) piercing damage."]}]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (2, 0, 'entries', 'Flames', '["The boilerdrak expels flames in a 60-foot cone. Each creature in that area must make a {@dc 15} Dexterity saving throw, taking 27 ({@damage 5d10}) fire damage on a failed save, or half as much damage on a successful one."]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (2, 1, 'entries', 'Explosion', '["The boilerdrak explodes in a 30-foot-radius sphere and is destroyed. Each creature in that area must make a {@dc 15} Dexterity saving throw, taking 27 ({@damage 5d10}) fire damage on a failed save, or half as much damage on a successful one."]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (3, 0, 'actions', 'Cannon Ball', '[{"type":"attack","attackType":"RW","attackEntries":["{@hit +6} to hit, range 600/2,400 ft., one target."],"hitEntries":["44 ({@damage 8d10}) bludgeoning damage."]}]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (4, 0, 'actions', 'Cannon', '[{"type":"attack","attackType":"RW","attackEntries":["{@hit +5} to hit, range 30 ft., one target."],"hitEntries":["3 ({@damage 1d6}) bludgeoning damage."]}]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (5, 0, 'entries', 'Flamethrower', '["The cannon exhales fire in an adjacent 15-foot cone that you designate. Each creature in that area must make a Dexterity saving throw against your spell save DC, taking {@damage 2d8} fire damage on a failed save or half as much damage on a successful one. The fire ignites any flammable objects in the area that aren''t being worn or carried."]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (6, 0, 'entries', 'Force Ballista', '["Make a ranged spell attack, originating from the cannon, at one creature or object within 120 feet of it. On a hit, the target takes {@damage 2d8} force damage, and if the target is a creature, it is pushed up to 5 feet away from the cannon."]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (7, 0, 'entries', 'Protector', '["The cannon emits a burst of positive energy that grants itself and each creature of your choice within 10 feet of it a number of temporary hit points equal to {@damage 1d8} + your Intelligence modifier (minimum of +1)."]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (9, 0, 'entries', 'Fling Personnel', '["The gnomeflinger catapults a Medium or smaller creature at least 60 feet away horizontally, with a maximum horizontal distance of 60 feet, 150 feet, or 300 feet, depending on the option chosen when the catapult was aimed. During this trajectory, the creature always reaches a height of 60 feet vertically.","A creature that collides with another creature or an object during this trajectory takes 3 ({@damage 1d6}) bludgeoning damage for every 10 feet away from the gnomeflinger it is."]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (12, 0, 'actions', 'Mangonel Stone', '[{"type":"attack","attackType":"RW","attackEntries":["{@hit +5} to hit, range 200/800 ft. (can''t hit targets within 60 feet of it), one target."],"hitEntries":["27 ({@damage 5d10}) bludgeoning damage."]}]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (13, 0, 'actions', 'Ram', '[{"type":"attack","attackType":"MW","attackEntries":["{@hit +8} to hit, reach 5 ft., one object."],"hitEntries":["16 ({@damage 3d10}) bludgeoning damage."]}]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (16, 0, 'entries', 'Boiling Oil', '["The cauldron pours boiling oil onto a 10-foot-square area directly below it. Any creature in the area must make a {@dc 15} Dexterity saving throw, taking 10 ({@damage 3d6}) fire damage on a failed save, or half as much damage on a successful one."]'::jsonb);
INSERT INTO object_action_entries (object_id, entry_order, type, name, entries_json) VALUES (18, 0, 'actions', 'Trebuchet Stone', '[{"type":"attack","attackType":"RW","attackEntries":["{@hit +5} to hit, range 300/1,200 ft. (can''t hit targets within 60 feet of it), one target."],"hitEntries":["44 ({@damage 8d10}) bludgeoning damage."]}]'::jsonb);
