INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (4, 1, 'string', NULL, 'You''ve gained the ability to imbue mundane items with certain magical infusions, turning those objects into magic items.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
