INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (49, 2, 'entries', 'Spell Slots', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["The Bard table shows how many spell slots you have to cast your {@filter bard spells|spells|class=bard} of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","For example, if you know the 1st-level spell {@spell cure wounds} and have a 1st-level and a 2nd-level spell slot available, you can cast {@spell cure wounds} using either slot."]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
