INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (101, 2, 'entries', 'Preparing and Casting Spells', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["The Druid table shows how many spell slots you have to cast your {@filter druid spells|spells|class=druid} of 1st level and higher. To cast one of these druid spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","You prepare the list of druid spells that are available for you to cast, choosing from the druid spell list. When you do so, choose a number of druid spells equal to your Wisdom modifier + your druid level (minimum of one spell). The spells must be of a level for which you have spell slots.","For example, if you are a 3rd-level druid, you have four 1st-level and two 2nd-level spell slots. With a Wisdom of 16, your list of prepared spells can include six spells of 1st or 2nd level, in any combination. If you prepare the 1st-level spell {@spell cure wounds}, you can cast it using a 1st-level or 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.","You can also change your list of prepared spells when you finish a long rest. Preparing a new list of druid spells requires time spent in prayer and meditation: at least 1 minute per spell level for each spell on your list."]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
