INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (255, 2, 'entries', 'Spell Slots', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["The Sorcerer table shows how many spell slots you have to cast your {@filter sorcerer spells|spells|class=sorcerer} of 1st level and higher. To cast one of these sorcerer spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","For example, if you know the 1st-level spell {@spell burning hands} and have a 1st-level and a 2nd-level spell slot available, you can cast {@spell burning hands} using either slot."]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (255, 3, 'entries', 'Spells Known of 1st Level and Higher', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["You know two 1st-level spells of your choice from the sorcerer spell list.","You learn an additional sorcerer spell of your choice at each level except 12th, 14th, 16th, 18th, 19th, and 20th. Each of these spells must be of a level for which you have spell slots. For instance, when you reach 3rd level in this class, you can learn one new spell of 1st or 2nd level.","Additionally, when you gain a level in this class, you can choose one of the sorcerer spells you know and replace it with another spell from the sorcerer spell list, which also must be of a level for which you have spell slots."]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
