INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (291, 0, 'string', NULL, 'At 20th level, you can draw on your inner reserve of mystical power while entreating your patron to regain expended spell slots. You can spend 1 minute entreating your patron for aid to regain all your expended spell slots from your Pact Magic feature. Once you regain spell slots with this feature, you must finish a long rest before you can do so again.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (292, 0, 'string', NULL, 'You have learned to regain some of your magical energy by studying your spellbook. Once per day when you finish a short rest, you can choose expended spell slots to recover. The spell slots can have a combined level that is equal to or less than half your wizard level (rounded up), and none of the slots can be 6th level or higher.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (292, 1, 'string', NULL, 'For example, if you''re a 4th-level wizard, you can recover up to two levels worth of spell slots. You can recover either a 2nd-level spell slot or two 1st-level spell slots.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (293, 0, 'string', NULL, 'As a student of arcane magic, you have a spellbook containing spells that show the first glimmerings of your true power. See c for the general rules of spellcasting and c for the w.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (293, 1, 'entries', 'Cantrips', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["At 1st level, you know three cantrips of your choice from the wizard spell list. You learn additional wizard cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Wizard table."]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (293, 2, 'entries', 'Spellbook', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["At 1st level, you have a spellbook containing six 1st-level {@filter wizard spells|spells|class=wizard} of your choice. Your spellbook is the repository of the wizard spells you know, except your cantrips, which are fixed in your mind."]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (293, 3, 'entries', 'Preparing and Casting Spells', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["The Wizard table shows how many spell slots you have to cast your wizard spells of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.","You prepare the list of wizard spells that are available for you to cast. To do so, choose a number of wizard spells from your spellbook equal to your Intelligence modifier + your wizard level (minimum of one spell). The spells must be of a level for which you have spell slots.","For example, if you''re a 3rd-level wizard, you have four 1st-level and two 2nd-level spell slots. With an Intelligence of 16, your list of prepared spells can include six spells of 1st or 2nd level, in any combination, chosen from your spellbook. If you prepare the 1st-level spell {@spell magic missile}, you can cast it using a 1st-level or a 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.","You can change your list of prepared spells when you finish a long rest. Preparing a new list of wizard spells requires time spent studying your spellbook and memorizing the incantations and gestures you must make to cast the spell: at least 1 minute per spell level for each spell on your list."]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (293, 4, 'entries', 'Spellcasting Ability', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["Intelligence is your spellcasting ability for your wizard spells, since you learn your wizard spells through dedicated study and memorization. You use your Intelligence whenever a spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for a wizard spell you cast and when making an attack roll with one.",{"type":"abilityDc","name":"Spell","attributes":["int"]},{"type":"abilityAttackMod","name":"Spell","attributes":["int"]}]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (293, 5, 'entries', 'Ritual Casting', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["You can cast a wizard spell as a ritual if that spell has the ritual tag and you have the spell in your spellbook. You don''t need to have the spell prepared."]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (293, 6, 'entries', 'Spellcasting Focus', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["You can use an {@item arcane focus|phb} as a spellcasting focus for your wizard spells."]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (293, 7, 'entries', 'Learning Spells of 1st Level and Higher', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["Each time you gain a wizard level, you can add two wizard spells of your choice to your spellbook. Each of these spells must be of a level for which you have spell slots, as shown on the Wizard table. On your adventures, you might find other spells that you can add to your spellbook (see \"Your Spellbook\")."]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO feature_entries (feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_rows, image_href_type, image_href_path, image_title, image_width, image_height, raw_json, ref_feature_name, ref_feature_class, ref_feature_source, ref_feature_level, ref_subclass_feature_name, ref_subclass_feature_class, ref_subclass_feature_class_source, ref_subclass_feature_subclass_short_name, ref_subclass_feature_subclass_source, ref_subclass_feature_level, ability_dc_attributes, ability_attack_mod_attributes) VALUES (293, 8, 'inset', 'Your Spellbook', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '["The spells that you add to your spellbook as you gain levels reflect the arcane research you conduct on your own, as well as intellectual breakthroughs you have had about the nature of the multiverse. You might find other spells during your adventures. You could discover a spell recorded on a scroll in an evil wizard''s chest, for example, or in a dusty tome in an ancient library.","A spellbook doesn''t contain cantrips.",{"type":"entries","entries":[{"type":"entries","name":"Copying a Spell into the Book","entries":["When you find a wizard spell of 1st level or higher, you can add it to your spellbook if it is of a spell level you can prepare and if you can spare the time to decipher and copy it.","Copying a spell into your spellbook involves reproducing the basic form of the spell, then deciphering the unique system of notation used by the wizard who wrote it. You must practice the spell until you understand the sounds or gestures required, then transcribe it into your spellbook using your own notation.","For each level of the spell, the process takes 2 hours and costs 50 gp. The cost represents material components you expend as you experiment with the spell to master it, as well as the fine inks you need to record it. Once you have spent this time and money, you can prepare the spell just like your other spells.",{"type":"entries","name":"Copying from a Spell Scroll","source":"DMG","page":139,"entries":["A wizard spell on a spell scroll can be copied just as spells in spellbooks can be copied. When you copy a spell from a spell scroll, you must succeed on an Intelligence ({@skill Arcana}) check with a DC equal to 10 + the spell''s level. If the check succeeds, the spell is successfully copied. Whether the check succeeds or fails, the spell scroll is destroyed."]}]}]},{"type":"entries","entries":[{"type":"entries","name":"Replacing the Book","entries":["You can copy a spell from your own spellbook into another bookfor example, if you want to make a backup copy of your spellbook. This is just like copying a new spell into your spellbook, but faster and easier, since you understand your own notation and already know how to cast the spell. You need spend only 1 hour and 10 gp for each level of the copied spell.","If you lose your spellbook, you can use the same procedure to transcribe the spells that you have prepared into a new spellbook. Filling out the remainder of your spellbook requires you to find new spells to do so, as normal. For this reason, many wizards keep backup spellbooks in a safe place."]}]},{"type":"entries","entries":[{"type":"entries","name":"The Book''s Appearance","entries":["Your spellbook is a unique compilation of spells, with its own decorative flourishes and margin notes. It might be a plain, functional leather volume that you received as a gift from your master, a finely bound gilt-edged tome you found in an ancient library, or even a loose collection of notes scrounged together after you lost your previous spellbook in a mishap."]}]}]', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
