INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1, 0, 'string', 'This simple screwdriver can transform into a variety of tools; as an action, you can touch the item and transform it into any type of artisan''s tool of your choice (see the "Equipment" chapter in the Player''s Handbook for a list of artisan''s tools). Whatever form the tool takes, you are proficient with it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1, 1, 'string', 'While holding this tool, you gain a +1 bonus to the spell attack rolls and the saving throw DCs of your artificer spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1, 2, 'string', 'As an action, you can focus on the tool to channel your creative forces. Choose a cantrip that you don''t know from any class list. For 8 hours, you can cast that cantrip, and it counts as an artificer cantrip for you. Once this property is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (2, 0, 'string', 'This amulet bears the symbol of a deity inlaid with precious stones or metals. While you wear the holy symbol, you gain a +1 bonus to spell attack rolls and the saving throw DCs of your spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (2, 1, 'string', 'While you wear this amulet, you can use your Channel Divinity feature without expending one of the feature''s uses. Once this property is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (3, 0, 'string', 'While you are holding this leather-bound book, you can use it as a spellcasting focus for your wizard spells, and you gain a +1 bonus to spell attack rolls and to the saving throw DCs of your wizard spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (3, 1, 'string', 'You can use this book as a spellbook. In addition, when you use your Arcane Recovery feature, you can increase the number of spell slot levels you regain by 1.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (4, 0, 'string', 'To attune to this vial, you must place a few drops of your blood into it. The vial can''t be opened while your attunement to it lasts. If your attunement to the vial ends, the contained blood turns to ash. You can use the vial as a spellcasting focus for your spells while wearing or holding it, and you gain a +1 bonus to spell attack rolls and to the saving throw DCs of your sorcerer spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (4, 1, 'string', 'In addition, when you roll any Hit Dice to recover hit points while you are carrying the vial, you can regain 5 sorcery points. This property of the vial can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (5, 0, 'string', 'This finely detailed belt is made of dragonhide. While wearing it, you gain a +1 bonus to the saving throw DCs of your ki features. In addition, you can use an action to regain ki points equal to a roll of your Martial Arts die. You can''t use this action again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (6, 0, 'string', 'The backs of these cards are inscribed with glyphs representing the Inner Planes, the Outer Planes, or the holy symbols of various deities. While holding this deck, you can use it as a spellcasting focus, and you gain a +1 bonus to spell attack rolls and to your spell save DC.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (6, 1, 'string', 'In addition, while you''re holding the deck, you can draw a card as an action to expend and roll one of your Hit Dice and add the deck''s bonus to the number rolled. One creature you can see within 30 feet of you either takes radiant damage or regains hit points (your choice) equal to the total.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (7, 0, 'string', 'This silver-bladed sickle glimmers softly with moonlight. While holding this magic weapon, you gain a +1 bonus to attack and damage rolls made with it, and you gain a +1 bonus to spell attack rolls and the saving throw DCs of your druid and ranger spells. In addition, you can use the sickle as a spellcasting focus for your druid and ranger spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (7, 1, 'string', 'When you cast a spell that restores hit points, you can roll a d4 and add the number rolled to the amount of hit points restored, provided you are holding the sickle.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (8, 0, 'string', 'While holding this drum, you gain a +1 bonus to spell attack rolls and to the saving throw DCs of your bard spells.', NULL);
