INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (60, 0, 'string', 'A transparent dark green gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (61, 0, 'string', 'A transparent watery gold to rich gold gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (62, 0, 'string', 'This piece of polished amber is engraved with many tiny runes. As a bonus action, you can mentally command the stone to shed bright light in a 15-foot radius and dim light for an additional 15 feet. The light is sunlight. While the light persists, you can also use a bonus action to expand or reduce its radius of bright and dim light by 5 feet each, to a maximum of 30 feet each or a minimum of 10 feet each.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (63, 0, 'string', 'A transparent deep purple gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (64, 0, 'string', 'This fist-sized chunk of amethyst is infused with an amethyst dragon''s ability to bend gravitational forces. While you are carrying the lodestone, you have advantage on Strength saving throws.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (64, 1, 'string', 'The lodestone has 6 charges for the following properties, which you can use while you are holding the stone. The stone regains 1d6 expended charges daily at dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (64, 2, 'entries', NULL, '{"type":"entries","name":"Flight","entries":["As a bonus action, you can expend 1 charge to gain the power of flight for 10 minutes. For the duration, you gain a flying speed equal to your walking speed, and you can hover."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (64, 3, 'entries', NULL, '{"type":"entries","name":"Gravitational Thrust","entries":["As an action, you can expend 1 charge to focus gravity around a creature you can see within 60 feet of you. The target must succeed on a {@dc 18} Strength saving throw or be pushed up to 20 feet in a direction of your choice."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (64, 4, 'entries', NULL, '{"type":"entries","name":"Reverse Gravity","entries":["As an action, you can expend 3 charges to cast {@spell reverse gravity} from the stone (save {@dc 18})."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (66, 0, 'string', 'Your armor, weapons, and other equipment always shine as if just polished. Even if you''re wounded, your wounds do not appear to others. You''re immune to the frightened and poisoned conditions, as these would otherwise ruin your elegant appearance. However, whenever you would otherwise have been affected by one of these conditions, a random non-evil Humanoid on the Material Plane whom you have previously met gets the condition instead. You know this.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (67, 0, 'string', 'You can use a bonus action to move one of the following conditions from yourself to an ally within 60 feet of you: blinded, deafened, frightened, poisoned, stunned, exhaustion. When transferring exhaustion, move all your exhaustion levels. If the chosen ally is immune to a transferred condition, the transfer fails.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (69, 0, 'string', 'When you die, you''re transported to an extradimensional space where you''re stabilized at 0 hit points and kept in that state. The amulet creates a perfect copy of your corpse and places it where you were just before you died, but without any of your worn or carried items. After 24 hours, you regain 1 hit point and are returned to the location of your near-death. Everything about you is the same, except that your facial features are entirely different from before. Only a Wish spell can restore your true identity.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (69, 1, 'string', 'Until then, you have disadvantage on any Charisma (Persuasion) checks to attempt to reveal who you really are.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (69, 2, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["An {@spell Identify} spell or similar reveals only that the amulet can prevent death. Once you attune to the amulet, you can''t describe its ability to any other creature, and if unattuned, you immediately forget what the amulet does. A {@spell Remove Curse} spell reveals the details of the curse but does not remove it from the amulet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (70, 0, 'string', 'The amulet is a golden disk about 4 inches across. There are symbols etched into its surface and there are six bezels that hold the Gems of Power.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (70, 1, 'string', 'The Amulet of Harmony has the following properties:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (70, 2, 'list', NULL, '{"type":"list","items":["While attuned to this amulet, you feel a pull in the direction of the Prismatic Well.","If the Prismatic Well is dormant, you can restore its power by expending your own energy. You must be within 10-feet of the well to restore its power and you must say the words, \"I invoke the power of the Prismatic Well and restore the colors of Harmony for all.\" If you do so, roll a {@dice d6}. On a 2-6, the power of the well is restored. On a 1, the well is restored but you fall into a deep, magical slumber for 1 year and cannot be roused except by a remove curse spell or similar magic.","You can turn off the power of the Prismatic Well, plunging all of Harmony into a polarized world where everything is horribly black and white. To do this, you must be within 10-feet of the well and you must say the words, \"I revoke the power of the Prismatic Well and withhold the colors of Harmony to all.\" If you do so, roll a {@dice d6}. On a 3-6, the well is turned off. On 1-2, the power of the well remains and you turn into a statue of black stone."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (70, 3, 'string', 'Only a wish spell can undo this effect.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (71, 0, 'string', 'Your Constitution score is 19 while you wear this amulet. It has no effect on you if your Constitution score is already 19 or higher without it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (72, 0, 'string', 'While wearing this amulet, you are hidden from divination magic. You can''t be targeted by such magic or perceived through magical scrying sensors.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (73, 0, 'string', 'While you wear this amulet of silver and turquoise, you have advantage on saving throws against effects that turn undead.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (73, 1, 'string', 'If you fail a saving throw against such an effect, you can choose to succeed instead. You can do so three times, and expended uses recharge daily at dawn. Each time an effect that turns undead is used against you, the amulet glows with silvery blue light for a few seconds.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (74, 0, 'string', 'This amulet is carved from obsidian and shaped like a screaming humanoid skull, with ruby eyes and emeralds for teeth. It hangs from an iron chain necklace.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (74, 1, 'string', 'The amulet has 6 charges and regains 1d6 charges daily at dawn. While wearing the amulet, you can use an action to expend 1 of its charges to transport yourself and anything you are wearing or carrying to a location within 100 feet of you. The destination you choose doesn''t need to be in your line of sight, but it must be familiar to you (in other words, a place you have seen or visited), and it must be on the same plane of existence as you. This effect isn''t subject to the magic restrictions placed on the Tomb of the Nine Gods; thus, the amulet can be used to enter and exit the tomb.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (74, 2, 'string', 'If you aren''t undead, you must make a 16 Constitution saving throw each time you use the amulet to teleport. On a failed saving throw, the black skull cackles as you are transformed in transit. The transformation takes effect as soon as you arrive at the destination, and is determined randomly by rolling percentile dice and consulting the Black Skull Transformation table.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (74, 3, 'table', NULL, '{"type":"table","caption":"Black Skull Transformation","colLabels":["{@dice d100}","Transformation"],"colStyles":["col-1 text-center","col-11"],"rows":[["01-20","The symbol of {@creature Acererak|ToA} is burned into your flesh, a curse that can only be removed with a {@spell remove curse} spell or similar magic. Until the curse ends, your hit points can''t be restored by magic."],["21-35","You grow larger as if affected by an {@spell enlarge/reduce} spell, except the effect lasts for 1 hour."],["36-50","You grow smaller as if affected by an {@spell enlarge/reduce} spell, except the effect lasts for 1 hour."],["51-70","You arrive at the destination wearing nothing but the amulet of the black skull. Everything else that you were wearing or carrying appears in a random unoccupied space within 100 feet of you."],["71-95","You are {@condition paralyzed} for 1 minute or until this effect is ended with a {@spell lesser restoration} spell or similar magic."],["96-00","You become {@condition petrified}. This effect can be ended only with a {@spell greater restoration} spell or similar magic."]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (75, 0, 'string', 'This amulet smells of old, ale-stained wood. While wearing it, you can regain 4d4 + 4 hit points when you drink a pint of beer, ale, mead, or wine. Once the amulet has restored hit points, it can''t do so again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (76, 0, 'string', 'When Fierna was born, petitioners and devils from all over came to celebrate her birth. Many brought gifts and treasures, including a powerful infernal warlock. Recognizing the strength and inner fire of Fierna, the warlock gifted her with the Amulet of the Inferno, and it has been around her neck ever since.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (76, 1, 'string', 'The Amulet of the Inferno is a magic necklace that grants a +3 bonus to spell save DC and spell attack bonus.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (76, 2, 'entries', NULL, '{"type":"entries","name":"Random Properties","entries":["The Amulet of the Inferno has the following random properties:",{"type":"list","items":["3 {@table Artifact Properties; Major Beneficial Properties|dmg|major beneficial} properties","2 {@table Artifact Properties; Major Detrimental Properties|dmg|major detrimental} properties"]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (76, 3, 'entries', NULL, '{"type":"entries","name":"Infernal Fire","entries":["While worn, the amulet augments any magical flames created by you, transforming normal fire into Hellfire. Hellfire ignores resistances and immunities to fire damage and deals double damage to creatures vulnerable to either fire or necrotic damage. It is also capable of melting stone, as well as igniting flammable objects."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (76, 4, 'entries', NULL, '{"type":"entries","name":"Raging Inferno","entries":["While attuned to the Amulet of the Inferno, you may use a bonus action to cast {@spell Fire Bolt} or {@spell Produce Flame}. The cantrips scale based on your total character level and use Intelligence as a spellcasting modifier if you''re normally incapable of casting spells."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (76, 5, 'entries', NULL, '{"type":"entries","name":"Everburning","entries":["The Amulet of the Inferno grants resistance to cold damage and the ability to breathe underwater. Any magical flames you conjure are similarly augmented: they can be used underwater, can''t be extinguished through any means, and can exude any temperature. While the casting of a fire spell can still be interrupted with {@spell Counterspell}, once the spell has been cast, any lingering flames can''t be removed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (76, 6, 'entries', NULL, '{"type":"entries","name":"Destroying the Amulet","entries":["The only creature capable of destroying the amulet is the warlock that created it. Nobody living knows the warlock''s identity, making destroying the amulet almost impossible."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (77, 0, 'string', 'While wearing this amulet, you can use an action to name a location that you are familiar with on another plane of existence. Then make a 15 Intelligence check. On a successful check, you cast the plane shift spell. On a failure, you and each creature and object within 15 feet of you travel to a random destination. Roll a d100. On a 1-60, you travel to a random location on the plane you named. On a 61-100, you travel to a randomly determined plane of existence.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (78, 0, 'string', 'While holding this shield, you can speak its command word as a bonus action to cause it to animate. The shield leaps into the air and hovers in your space to protect you as if you were wielding it, leaving your hands free. The shield remains animated for 1 minute, until you use a bonus action to end this effect, or until you are incapacitated or die, at which point the shield falls to the ground or into your hand if you have one free.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (79, 0, 'string', 'Metal tubes ring the lower edge of this wide belt. It is powered by an energy cell stored in a metal case near the buckle. Placing a full energy cell in the belt gives the belt 10 charges.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (79, 1, 'entries', NULL, '{"name":"Activating the Belt","type":"entries","entries":["As a bonus action, you can expend any number of the belt''s charges to activate it; the belt remains active for 1 minute per charge expended. You can use a bonus action to deactivate the belt early, but doing so doesn''t recover any expended charges.","When you activate the belt, and as a bonus action while it remains active, you can rise or descend vertically up to 20 feet. You remain floating in place while the property is active, and you can move yourself horizontally by being pushed or towed or by scooting yourself along a surface, such as a wall or ceiling, at half your walking speed. If you are still levitating when the belt deactivates, you fall."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (79, 2, 'entries', NULL, '{"name":"Replacing the Energy Cell","type":"entries","entries":["While the belt has charges remaining, its {@item energy cell} can''t be removed. Once the belt has 0 charges, you can replace the {@item energy cell} with a new cell by using an action or a bonus action."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (80, 0, 'string', 'A creature that drinks this vial of liquid gains advantage on saving throws against poison for 1 hour. It confers no benefit to undead or constructs.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (81, 0, 'string', 'This item first appears to be a Large sealed iron barrel weighing 500 pounds. The barrel has a hidden catch, which can be found with a successful 20 Intelligence (Investigation) check. Releasing the catch unlocks a hatch at one end of the barrel, allowing two Medium or smaller creatures to crawl inside. Ten levers are set in a row at the far end, each in a neutral position, able to move either up or down. When certain levers are used, the apparatus transforms to resemble a giant lobster.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (81, 1, 'string', 'The apparatus of Kwalish is a Large object with the following statistics:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (81, 2, 'list', NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Armor Class:","entry":"20"},{"type":"item","name":"Hit Points:","entry":"200"},{"type":"item","name":"Speed:","entry":"30 ft., swim 30 ft. (or 0 ft. for both if the legs and tail aren''t extended)"},{"type":"item","name":"Damage Immunities:","entry":"poison, psychic"}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (81, 3, 'string', 'To be used as a vehicle, the apparatus requires one pilot. While the apparatus''s hatch is closed, the compartment is airtight and watertight. The compartment holds enough air for 10 hours of breathing, divided by the number of breathing creatures inside.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (81, 4, 'string', 'The apparatus floats on water. It can also go underwater to a depth of 900 feet. Below that, the vehicle takes 2d6 bludgeoning damage per minute from pressure.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (81, 5, 'string', 'A creature in the compartment can use an action to move as many as two of the apparatus''s levers up or down. After each use, a lever goes back to its neutral position. Each lever, from left to right, functions as shown in the Apparatus of Kwalish Levers table.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (81, 6, 'table', NULL, '{"type":"table","caption":"Apparatus of Kwalish Levers:","colLabels":["Lever","Up","Down"],"colStyles":["col-1 text-center","col-5-5","col-5-5"],"rows":[["1","Legs and tail extend, allowing the apparatus to walk and swim.","Legs and tail retract, reducing the apparatus''s speed to 0 and making it unable to benefit from bonuses to speed."],["2","Forward window shutter opens.","Forward window shutter closes."],["3","Side window shutters open (two per side).","Side window shutters close (two per side)."],["4","Two claws extend from the front sides of the apparatus.","The claws retract."],["5","Each extended claw makes the following melee weapon attack: {@hit 8} to hit, reach 5 ft., one target. {@h} 7 ({@damage 2d6}) bludgeoning damage.","Each extended claw makes the following melee weapon attack: {@hit 8} to hit, reach 5 ft., one target. {@h} The target is {@condition grappled} (escape {@dc 15})."],["6","The apparatus walks or swims forward.","The apparatus walks or swims backward."],["7","The apparatus turns 90 degrees left.","The apparatus turns 90 degrees right."],["8","Eyelike fixtures emit bright light in a 30-foot radius and dim light for an additional 30 feet.","The light turns off."],["9","The apparatus sinks as much as 20 feet in liquid.","The apparatus rises up to 20 feet in liquid."],["10","The rear hatch unseals and opens.","The rear hatch closes and seals."]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (82, 0, 'string', 'A transparent pale blue-green gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (83, 0, 'string', 'This music box is made of dark wood with gold filigree. Each of its five sides is sculpted with the image of a horned woman playing a different musical instrument: a dulcimer, a flute, a harp, a lyre, and a viol. While touching the box, you can use an action to make it play music featuring one of the instruments shown, which can be heard up to 60 feet away. You can also use an action to stop the music.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (84, 0, 'string', 'This Large cannon is imbued with magic. It requires no ammunition and doesn''t need to be loaded. It takes one action to aim the cannon and one action to fire it. After the cannon has fired, it must recharge for 5 minutes before it can be fired again.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (84, 1, 'string', 'The creature firing the cannon chooses the effect from the following options:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (84, 2, 'entries', NULL, '{"type":"entries","name":"Acid Jet","entries":["The cannon discharges acid in a line 300 feet long and 5 feet wide. Each creature in that line must make a {@dc 15} Dexterity saving throw, taking 22 ({@damage 4d10}) acid damage on a failed save, or half as much damage on a successful one. In addition, a creature that fails its saving throw takes 11 ({@damage 2d10}) acid damage at the start of each of its turns; a creature can end this damage by using its action to wash off the acid with a pint or more of water."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (84, 3, 'entries', NULL, '{"type":"entries","name":"Fire Jet","entries":["The cannon discharges fire in a line 300 feet long and 5 feet wide. Each creature in the area must make a {@dc 15} Dexterity saving throw, taking 33 ({@damage 6d10}) fire damage on a failed save, or half as much damage on a successful one. The fire ignites any flammable objects in the area that aren''t being worn or carried."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (84, 4, 'entries', NULL, '{"type":"entries","name":"Frost Shot","entries":["The cannon shoots a ball of frost to a point you can see within 1,200 feet of the cannon. The ball then expands to form a 30-foot-radius sphere centered on that point. Each creature in that area must make a {@dc 15} Constitution saving throw. On a failed save, a creature takes 22 ({@damage 4d10}) cold damage, and its speed is reduced by 10 feet for 1 minute. On a successful save, the creature takes half as much damage, and its speed isn''t reduced. A creature whose speed is reduced by this effect can repeat the save at the end of each of its turns, ending the effect on itself on a success."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (84, 5, 'entries', NULL, '{"type":"entries","name":"Lightning Shot","entries":["The cannon shoots a ball of lightning to a point you can see within 1,200 feet of the cannon. The lightning then expands to form a 20-foot-radius sphere centered on that point. Each creature in that area must make a {@dc 15} Dexterity saving throw, taking 33 ({@damage 6d10}) lightning damage on a failed save, or half as much damage on a successful one. Creatures wearing metal armor have disadvantage on the save."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (84, 6, 'entries', NULL, '{"type":"entries","name":"Poison Spray","entries":["The cannon expels poison gas in a 60-foot cone. Each creature in that area must make a {@dc 15} Constitution saving throw. On a failed save, the creature takes 22 ({@damage 4d10}) poison damage and is {@condition poisoned} for 1 minute. On a successful save, the creature takes half as much damage and isn''t {@condition poisoned}. A creature {@condition poisoned} in this way can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (85, 0, 'string', 'This prosthetic appendage was developed by artificers of House Cannith. To attune to this item, you must attach it to your arm at the wrist, elbow, or shoulder, at which point the prosthetic magically forms a copy of the appendage it''s replacing.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (85, 1, 'string', 'While attached, the prosthetic provides these benefits:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (85, 2, 'list', NULL, '{"type":"list","items":["The prosthetic is a fully capable part of your body.","You can take an action to remove the prosthetic, and it removes itself if your attunement to it ends. It can''t be removed against your will.","The prosthetic is a magic melee weapon with which you''re proficient. It deals {@damage 1d8} force damage on a hit and has the thrown property, with a normal range of 20 feet and a long range of 60 feet. When thrown, the prosthetic detaches and flies at the target of the attack, then immediately returns to you and reattaches."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (86, 0, 'table', NULL, '{"caption":"Trinkets from Argonnessen","colLabels":["d10","Trinket"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","A pierced dragon scale on a cord"],["2","A statuette of a dragon carved from black bone"],["3","A dragon''s tooth, engraved with an unknown sigil"],["4","A child''s doll of a dragon, woven from leather cords"],["5","A dagger carved from a dragon''s talon"],["6","A brass disk bearing the silhouette of a black dragon"],["7","A small egg-shaped piece of polished bone"],["8","A bone fragment with brass inlaid runes"],["9","A leather pouch filled with tiny draconic teeth"],["10","A single large seed that''s warm to the touch"]],"type":"table"}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (87, 0, 'string', 'You have resistance to nonmagical damage while you wear this armor. Additionally, you can use an action to make yourself immune to nonmagical damage for 10 minutes or until you are no longer wearing the armor. Once this special action is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (89, 0, 'string', 'This antique armor forged from jade and black dragon scales was once worn by J''mon Sa Ord of Ank''Harel.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (89, 1, 'entries', NULL, '{"type":"entries","name":"Dormant","entries":["While the Armor of the Valiant Soul is in a dormant state, you gain a +1 bonus to AC while wearing it. You can also cast the {@spell command} spell (save {@dc 14}) while attuned to this armor. Once this property is used, it can''t be used again until you finish a {@quickref resting|PHB|2|0|long rest}."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (91, 0, 'string', 'While wearing this armor, you have resistance to bludgeoning damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (91, 1, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["This armor is cursed, a fact that is revealed only when an {@spell identify} spell is cast on the armor or you attune to it. Attuning to the armor curses you until you are targeted by the {@spell remove curse} spell or similar magic; removing the armor fails to end the curse. While cursed you have vulnerability to piercing and slashing damage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (92, 0, 'string', 'While wearing this armor, you have resistance to piercing damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (92, 1, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["This armor is cursed, a fact that is revealed only when an {@spell identify} spell is cast on the armor or you attune to it. Attuning to the armor curses you until you are targeted by the {@spell remove curse} spell or similar magic; removing the armor fails to end the curse. While cursed you have vulnerability to bludgeoning and slashing damage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (93, 0, 'string', 'While wearing this armor, you have resistance to slashing damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (93, 1, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["This armor is cursed, a fact that is revealed only when an {@spell identify} spell is cast on the armor or you attune to it. Attuning to the armor curses you until you are targeted by the {@spell remove curse} spell or similar magic; removing the armor fails to end the curse. While cursed you have vulnerability to bludgeoning and piercing damage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (94, 0, 'string', 'You gain a +2 bonus to AC against ranged attacks while you wield this shield. This bonus is in addition to the shield''s normal bonus to AC. In addition, whenever an attacker makes a ranged attack against a target within 5 feet of you, you can use your reaction to become the target of the attack instead.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (95, 0, 'string', 'This vessel can be a potion bottle, drinking horn, or other container meant to hold a liquid.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (95, 1, 'string', 'As a bonus action, if the vessel is empty, you can speak the command word to fill the vessel with one of the following (your choice): ale, olive oil, mead, wine, whiskey, a potion of healing, a potion of greater healing, a potion of superior healing, a potion of supreme healing, a potion of climbing, a potion of fire breath, a potion of flying, or a potion of dragon''s majesty. Once this property is used, it can''t be used until the next dawn. A potion you create in this way loses its magical properties if it isn''t imbibed within 24 hours.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (96, 0, 'string', 'This wondrous item can be a scepter, an orb, an amulet, a crystal, or another finely crafted object. It typically incorporates imagery of dragons'' wings, claws, teeth, or scales.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (96, 1, 'string', 'You have advantage on initiative rolls. While you are holding the focus, it can function as a spellcasting focus for all your spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (96, 2, 'string', 'The focus gains an additional property determined by the family of the dragon in whose hoard it became Stirring:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (96, 3, 'list', NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Chromatic","entries":["Whenever you use a spell slot to cast a spell that deals acid, cold, fire, lightning, or poison damage, roll a {@dice d6}, and you gain a bonus equal to the number rolled to one of the spell''s damage rolls."]},{"type":"item","name":"Gem","entries":["Whenever you use a spell slot to cast a spell, you can immediately teleport to an unoccupied space you can see within 15 feet of you."]},{"type":"item","name":"Metallic","entries":["When a creature you can see within 30 feet of you makes a saving throw, you can use your reaction to give that creature advantage on the saving throw."]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (96, 4, 'string', 'While you are holding a Wakened focus, you can use it to cast certain spells. Once the item is used to cast a given spell, it can''t be used to cast that spell again until the next dawn. The spells are determined by the family of the dragon in whose hoard it became Wakened.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (96, 5, 'list', NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Chromatic","entries":["{@spell Hold monster}, {@spell Rime''s binding ice|FTD}"]},{"type":"item","name":"Gem","entries":["{@spell Rary''s telepathic bond}, {@spell Raulothim''s psychic lance|FTD}"]},{"type":"item","name":"Metallic","entries":["{@spell Fizban''s platinum shield|FTD}, {@spell legend lore}"]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (96, 6, 'string', 'When you cast a spell of 1st level or higher while holding this focus, you can treat the spell as if it were cast using a 9th-level spell slot. Once this property is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (97, 0, 'string', 'This ornament can be jewelry, a cloak, or another wearable accessory. It appears to be fashioned from a dragon''s scale, tooth, or claw, or it incorporates images in those shapes.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (97, 1, 'string', 'You gain a +1 bonus to AC, and you can''t be charmed or frightened. Moreover, each creature of your choice within 30 feet of you has advantage on saving throws it makes to avoid being charmed or frightened or to end those conditions on itself.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (97, 2, 'string', 'When you would take damage of the type dealt by the breath of the dragon in whose hoard the ornament became Wakened, you can use your reaction to take no damage instead, and you regain hit points equal to the damage you would have taken. Once this property is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (97, 3, 'string', 'While you are wearing the ornament, you gain a flying speed equal to your walking speed and can hover. While you are flying using this speed, spectral dragon wings appear on your back.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (98, 0, 'string', 'A creature subjected to this poison must make a 10 Constitution saving throw. On a failed save, it takes 6 (1d12) poison damage and is poisoned for 24 hours. On a successful save, the creature takes half damage and isn''t poisoned.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (99, 0, 'string', 'This crystal is a solidified shard of the Astral Plane, swirling with silver mist. As an action, you can attach the shard to a Tiny object (such as a weapon or a piece of jewelry) or detach it. It falls off if your attunement to it ends. You can use the shard as a spellcasting focus while you hold or wear it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (99, 1, 'string', 'When you use a Metamagic option on a spell while you are holding or wearing the shard, immediately after casting the spell you can teleport to an unoccupied space you can see within 30 feet of you.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (100, 0, 'string', 'This brass disc of articulated, concentric rings unfolds into an armillary sphere. As a bonus action, you can unfold it into the sphere or back into a disc. When found, it contains the following spells, which are wizard spells for you while you are attuned to it: augury, divination, find the path, foresight, locate creature, and locate object. It functions as a spellbook for you, with spells encoded on the rings.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (100, 1, 'string', 'While you are holding the archive, you can use it as a spellcasting focus for your wizard spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (100, 2, 'string', 'The archive has 3 charges, and it regains 1d3 expended charges daily at dawn. You can use the charges in the following ways while holding it:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (100, 3, 'list', NULL, '{"type":"list","items":["If you spend 1 minute studying the archive, you can expend 1 charge to replace one of your prepared wizard spells with a different spell in the archive. The new spell must be of the divination school.","When a creature you can see within 30 feet of you makes an attack roll, an ability check, or a saving throw, you can use your reaction to expend 1 charge and force the creature to roll a {@dice d4} and apply the number rolled as a bonus or penalty (your choice) to the original roll. You can do this after you see the roll but before its effects are applied."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (101, 0, 'string', 'This thick book is bound in dark leather, crisscrossed with inlaid silver lines suggesting a map or chart. When found, the book contains the following spells, which are wizard spells for you while you are attuned to the book: arcane gate, dimension door, gate, misty step, plane shift, teleportation circle, and word of recall. It functions as a spellbook for you.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (101, 1, 'string', 'While you are holding the book, you can use it as a spellcasting focus for your wizard spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (101, 2, 'string', 'The book has 3 charges, and it regains 1d3 expended charges daily at dawn. You can use the charges in the following ways while holding it:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (101, 3, 'list', NULL, '{"type":"list","items":["If you spend 1 minute studying the book, you can expend 1 charge to replace one of your prepared wizard spells with a different spell in the book. The new spell must be of the conjuration school.","When you are hit by an attack, you can use your reaction to expend 1 charge to teleport up to 10 feet to an unoccupied space you can see. If your new position is out of range of the attack, it misses you."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (102, 0, 'string', 'An axe beak''s splayed toes allow it to run across snow, and it can carry as much weight as a mule. A domesticated axe beak can be purchased in Ten-Towns for 50 gp.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 0, 'string', 'Seeing the peril his people faced, a young dwarf prince came to believe that his people needed something to unite them. Thus, he set out to forge a weapon that would be such a symbol.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 1, 'string', 'Venturing deep under the mountains, deeper than any dwarf had ever delved, the young prince came to the blazing heart of a great volcano. With the aid of Moradin, the dwarven god of creation, he first crafted four great tools: the Brutal Pick, the Earthheart Forge, the Anvil of Songs, and the Shaping Hammer. With them, he forged the Axe of the Dwarvish Lords.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 2, 'string', 'Armed with the artifact, the prince returned to the dwarf clans and brought peace. His axe ended grudges and answered slights. The clans became allies, and they threw back their enemies and enjoyed an era of prosperity. This young dwarf is remembered as the First King. When he became old, he passed the weapon, which had become his badge of office, to his heir. The rightful inheritors passed the axe on for many generations.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 3, 'string', 'Later, in a dark era marked by treachery and wickedness, the axe was lost in a bloody civil war fomented by greed for its power and the status it bestowed. Centuries later, the dwarves still search for the axe, and many adventurers have made careers of chasing after rumors and plundering old vaults to find it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 4, 'entries', NULL, '{"name":"Magic Weapon","type":"entries","entries":["The {@italic Axe of the Dwarvish Lords} is a magic weapon that grants a +3 bonus to attack and damage rolls made with it. The axe also functions as a {@item belt of dwarvenkind}, a {@item dwarven thrower}, and a {@item sword of sharpness}."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 5, 'entries', NULL, '{"type":"entries","name":"Random Properties","entries":["The axe has the following randomly determined properties:",{"type":"list","items":["2 {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial properties}","1 {@table Artifact Properties; Major Beneficial Properties|dmg|major beneficial property}","2 {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental properties}"]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 6, 'entries', NULL, '{"type":"entries","name":"Blessings of Moradin","entries":["If you are a dwarf attuned to the axe, you gain the following benefits:",{"type":"list","items":["You have immunity to poison damage.","The range of your {@sense darkvision} increases by 60 feet.","You gain proficiency with {@item artisan''s tools|PHB} related to blacksmithing, brewing, and stonemasonry."]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 7, 'entries', NULL, '{"type":"entries","name":"Conjure Earth Elemental","entries":["If you are holding the axe, you can use your action to cast the {@spell conjure elemental} spell from it, summoning an {@creature earth elemental}. You can''t use this property again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 8, 'entries', NULL, '{"type":"entries","name":"Travel the Depths","entries":["You can use an action to touch the axe to a fixed piece of dwarven stonework and cast the {@spell teleport} spell from the axe. If your intended destination is underground, there is no chance of a mishap or arriving somewhere unexpected. You can''t use this property again until 3 days have passed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 9, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["The axe bears a curse that affects any non-dwarf that becomes attuned to it. Even if the attunement ends, the curse remains. With each passing day, the creature''s physical appearance and stature become more dwarflike. After seven days, the creature looks like a typical dwarf, but the creature neither loses its racial traits nor gains the racial traits of a dwarf. The physical changes wrought by the axe aren''t considered magical in nature (and therefore can''t be dispelled), but they can be undone by any effect that removes a curse, such as a {@spell greater restoration} or {@spell remove curse} spell."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (103, 10, 'entries', NULL, '{"type":"entries","name":"Destroying the Axe","entries":["The only way to destroy the axe is to melt it down in the {@italic Earthheart Forge}, where it was created. It must remain in the burning forge for fifty years before it finally succumbs to the fire and is consumed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (104, 0, 'string', 'This ring, adorned with the symbol of Azorius, allows you to cast ensnaring strike. A guild signet is sometimes awarded to a guild member whose renown score in that guild is 5 or higher, as a reward for performing special services for the guild. Aside from its magical properties, the ring is also an indicator of Azorius''s recognition and favor.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (104, 1, 'string', 'A signet has 3 charges, and it regains 1d3 expended charges daily at dawn. While wearing it, you can expend 1 charge to cast the associated spell (save 13).', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (105, 0, 'string', 'This keyrune is carved from white marble and lapis lazuli to resemble a noble bird of prey. It can become a giant eagle for up to 1 hour. While the transformed eagle is within 1 mile of you, you can communicate with it telepathically. As an action, you can see through the eagle''s eyes and hear what it hears until the start of your next turn, and you gain the benefit of its keen sight. During this time, you are deaf and blind with regard to your own senses.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (105, 1, 'string', 'When you use an action to speak the item''s command word and place the keyrune on the ground in an unoccupied space within 5 feet of you, the keyrune transforms into a giant eagle. If there isn''t enough space for the eagle, the keyrune doesn''t transform.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (105, 2, 'string', 'The creature is friendly to you, your companions, and other members of your guild (unless those guild members are hostile to you). It understands your languages and obeys your spoken commands. If you issue no commands, the eagle takes the Dodge action and moves to avoid danger.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (105, 3, 'string', 'At the end of the duration, the creature reverts to its keyrune form. It reverts early if it drops to 0 hit points or if you use an action to speak the command word again while touching it. When the creature reverts to its keyrune form, it can''t transform again until 36 hours have passed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (106, 0, 'string', 'Forged by the archwizard Ahghairon, this intelligent battleaxe was crafted to defend Waterdeep. Its current wielder is a former member of Force Grey named Meloon Wardragon, but the weapon is searching for a new owner.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (106, 1, 'string', 'Azuredge has a solid steel handle etched with tiny runes, wrapped in blue dragon hide with a star sapphire set into the pommel. The axe head is forged from silver, electrum, and steel alloys whose edges constantly shimmer with a deep blue luminescence.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (106, 2, 'string', 'You gain a +3 bonus to attack and damage rolls made with this magic weapon. The shield spell provides no defense against the axe, which passes through that spell''s barrier of magical force.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (106, 3, 'string', 'When you hit a fiend or an undead with the axe, cold blue flames erupt from its blade and deal an extra 2d6 radiant damage to the target.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (106, 4, 'entries', NULL, '{"type":"entries","name":"Hurling","entries":["The battleaxe has 3 charges. You can expend 1 charge and make a ranged attack with the axe, hurling it as if it had the thrown property with a normal range of 60 feet and a long range of 180 feet. Whether it hits or misses, the axe flies back to you at the end of the current turn, landing in your open hand or at your feet in your space (as you choose). The axe regains all expended charges daily at dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (106, 5, 'entries', NULL, '{"type":"entries","name":"Illumination","entries":["While holding the axe, you can use an action to cause the axe to glow blue or to quench the glow. This glow sheds bright light in a 30-foot radius and dim light for an additional 30 feet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (106, 6, 'entries', NULL, '{"type":"entries","name":"Sentience","entries":["Azuredge is a sentient lawful neutral weapon with an Intelligence of 12, a Wisdom of 15, and a Charisma of 15. It has hearing and {@sense darkvision} out to a range of 120 feet.","The weapon communicates telepathically with its wielder and can speak, read, and understand Common. It has a calm, delicate voice. The weapon can sense the presence of non-lawful creatures within 120 feet of it."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (106, 7, 'entries', NULL, '{"type":"entries","name":"Personality","entries":["Azuredge is sworn to protect Waterdeep, and it desires to be wielded by a law-abiding person willing to dedicate everything to the city''s defense. The weapon is patient and takes its time finding its ideal wielder.","If someone tries to use Azuredge against its will, the axe can become ten times heavier than normal, and can magically adhere to any Medium or larger object or surface it comes into contact with. Once it does so, the axe can''t be wielded. Nothing short of a {@spell wish} spell can separate the axe from the item or surface to which it is adhered without destroying one or the other, though the axe can choose to end the effect at any time."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (107, 0, 'string', 'The ruins of this sprawling city are now known as the Crystalfen Caverns. These caves are home to scattered veins of azuremite, a gorgeous blue crystal that formed from millennia of psychic energies existing near element deposits. Curious explorers who discovered the veins found that, when mined and refined into a fine dust, the azuremite powder is a strong mind-altering agent and induces temporary visions and other psychic phenomena.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (107, 1, 'string', 'While a number of small, isolated entrances to this labyrinth of tunnels have been discovered and adventuring parties have attempted to chart the mines, the caverns are so vast and deep that either they gave up for fear of becoming lost, or they were assailed by the terrible denizens of the caverns, never to return. Most who now brave the caverns are foolish treasure hunters or criminals seeking to mine more azuremite to sell on the black market.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (108, 0, 'string', 'An opaque mottled deep blue gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (109, 0, 'string', 'The creations of the immortal hag Baba Yaga defy the laws of mortal magic. Among the notorious implements that cement her legend on countless worlds are the artifacts that propel her through the planes: Baba Yaga''s Mortar and Pestle. These signature tools of Baba Yaga are a single artifact for purposes of attunement. Should the two objects become separated, the pestle appears next to the mortar at the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (109, 1, 'entries', NULL, '{"type":"entries","name":"Random Properties","entries":["This artifact has the following random properties, which you can determine by rolling on the tables in the \"Artifacts\" section of the {@book Dungeon Master''s Guide|DMG}:",{"type":"list","items":["2 {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} properties","1 {@table Artifact Properties; Major Beneficial Properties|dmg|major beneficial} property","1 {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental} property"]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (109, 2, 'entries', NULL, '{"type":"entries","name":"Properties of the Mortar","entries":["The mortar is a Tiny wooden bowl. However, the mortar increases in size to accommodate anything you place inside, expanding-if there''s enough space-up to Large size, meaning it can hold even a Large creature."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (109, 3, 'entries', NULL, '{"type":"entries","name":"Properties of the Pestle","entries":["The pestle is a 6-inch-long, worn wooden tool. Once during your turn while you are holding the pestle, you can extend it into a quarterstaff or shrink it back into a pestle (no action required). As a quarterstaff, the pestle is a magic weapon that grants a +3 bonus to attack and damage rolls made with it.","The pestle has 12 charges. When you hit with a melee attack using the pestle, you can expend up to 3 of its charges to deal an extra {@damage 1d8} force damage for each charge expended. The pestle regains all expended charges daily at dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (109, 4, 'entries', NULL, '{"type":"entries","name":"Perfect Tools","entries":["While holding the mortar and pestle, you can use your action to say the name of any nonmagical plant, mineral, or fluid and an amount of the material worth 10 gp or less. The mortar instantly fills with the desired amount of that material. Once you use this action, you can''t do so again until you finish a short or long rest.","You can also use the artifact as {@item alchemist''s supplies|PHB}, {@item brewer''s supplies|PHB}, {@item cook''s utensils|PHB}, an {@item herbalism kit|PHB}, and a {@item poisoner''s kit|PHB}. You have advantage on any check you make using the artifact as one of these tools."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (109, 5, 'entries', NULL, '{"type":"entries","name":"Primal Parts","entries":["As an action while the pestle and the mortar is within 5 feet of you, you can command the pestle to grind. For the next minute, or until you use your action to verbally command it to stop, the pestle moves on its own, grinding the contents of the mortar into a mush or fine powder that''s equally useful for cooking or alchemy. At the start of each of your turns, whatever is in the mortar takes {@damage 4d10} force damage. If this reduces the target''s hit points to 0, the target is reduced to powder, pulp, or paste, as appropriate. Only magic items are unaffected. If you wish, when the pestle stops, you can have the mortar separate its contents-like powdered bone, crushed herbs, pulped organs-into separate piles."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (109, 6, 'entries', NULL, '{"type":"entries","name":"Traverse the Night","entries":["If you are holding the pestle while you are inside the mortar, you can use your action to verbally command the mortar to travel to a specific place or creature.","You don''t need to know where your destination is, but it must be a specific destination-not just the nearest river or a red dragon''s lair. If the stated destination is within 1,000 miles of you, the mortar lifts into the air and vanishes. You and any creatures in the mortar travel through a dreamlike sky, with hazy reflections of the world passing by below. Creatures might see images of you streaking through the sky between your point of origin and the destination. You arrive at the location 1 hour later or, if it is night, 1 minute later."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (109, 7, 'entries', NULL, '{"type":"entries","name":"Bones Know Their Home","entries":["When you command the mortar to travel, you can instead throw out the dust or paste of something ground by the mortar and name a location on a different plane of existence or a different world on the Material Plane. If that material came from a creature native to the named plane or world, the mortar travels through an empty night sky to an unoccupied space at that destination, arriving in 1 minute."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (109, 8, 'entries', NULL, '{"type":"entries","name":"Destroying the Mortar and Pestle","entries":["The mortar and pestle are destroyed if they are crushed underfoot by the {@i Dancing Hut of Baba Yaga} or by Baba Yaga herself."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (110, 0, 'string', 'The pestle is a 6-inch-long, worn wooden tool. Once during your turn while you are holding the pestle, you can extend it into a quarterstaff or shrink it back into a pestle (no action required). As a quarterstaff, the pestle is a magic weapon that grants a +3 bonus to attack and damage rolls made with it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (110, 1, 'string', 'The pestle has 12 charges. When you hit with a melee attack using the pestle, you can expend up to 3 of its charges to deal an extra 1d8 force damage for each charge expended. The pestle regains all expended charges daily at dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (111, 0, 'string', 'A backpack can hold one cubic foot or 30 pounds of gear. You can also strap items, such as a bedroll or a coil of rope, to the outside of a backpack.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (112, 0, 'string', 'A humanoid wearing this piece of gear can deploy the parachute as a reaction while falling, or as an action otherwise. The parachute requires at least a 10-foot cube of unoccupied space in which to deploy, and it doesn''t open fast enough to slow a fall of less than 60 feet. If it has sufficient time and space to deploy properly, the parachute allows its wearer to land without taking falling damage. Once it has been used, the parachute takes 10 minutes to repack.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (113, 0, 'string', 'A badge of the Watch is given only to those who have earned the trust of the Open Lord of Waterdeep. The badge, signifying the rank of captain in Waterdeep''s City Watch, bears the emblem of Waterdeep and is meant to be worn or carried.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (113, 1, 'string', 'While wearing the badge, you gain a +2 bonus to AC if you aren''t using a shield.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (113, 2, 'string', 'If the badge is more than 5 feet away from you for more than 1 minute, it vanishes and harmlessly reappears on a surface within 5 feet of the Open Lord. While holding the badge, the Open Lord knows your location, provided the two of you are on the same plane of existence and your attunement to the badge hasn''t ended.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (113, 3, 'string', 'As an action, the Open Lord can touch the badge and end your attunement to it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (114, 0, 'string', 'Inside this heavy cloth bag are 3d4 dry beans. The bag weighs  pound plus  pound for each bean it contains.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (114, 1, 'string', 'If you dump the bag''s contents out on the ground, they explode in a 10-foot radius, extending from the beans. Each creature in the area, including you, must make a 15 Dexterity saving throw, taking 5d4 fire damage on a failed save, or half as much damage on a successful one. The fire ignites flammable objects in the area that aren''t being worn or carried.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (114, 2, 'string', 'If you remove a bean from the bag, plant it in dirt or sand, and then water it, the bean produces an effect 1 minute later from the ground where it was planted. The DM can choose an effect from the following table, determine it randomly, or create an effect.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (114, 3, 'table', NULL, '{"type":"table","colLabels":["{@dice d100}","Effect"],"colStyles":["col-1 text-center","col-11"],"rows":[["01","{@dice 5d4} toadstools sprout. If a creature eats a toadstool, roll any die. On an odd roll, the eater must succeed on a {@dc 15} Constitution saving throw or take {@damage 5d6} poison damage and become {@condition poisoned} for 1 hour. On an even roll, the eater gains {@dice 5d6} temporary hit points for 1 hour."],["02-10","A geyser erupts and spouts water, beer, berry juice, tea, vinegar, wine, or oil (DM''s choice) 30 feet into the air for {@dice 1d12} rounds."],["11-20","A {@creature treant} sprouts. There''s a {@chance 50} chance that the treant is chaotic evil and attacks."],["21-30","An animate, immobile stone statue in your likeness rises. It makes verbal threats against you. If you leave it and others come near, it describes you as the most heinous of villains and directs the newcomers to find and attack you. If you are on the same plane of existence as the statue, it knows where you are. The statue becomes inanimate after 24 hours."],["31-40","A campfire with blue flames springs forth and burns for 24 hours (or until it is extinguished)."],["41-50","{@dice 1d6 + 6} {@creature shrieker||shriekers} sprout."],["51-60","{@dice 1d4 + 8} bright pink toads crawl forth. Whenever a toad is touched, it transforms into a Large or smaller monster of the DM''s choice. The monster remains for 1 minute, then disappears in a puff of bright pink smoke."],["61-70","A hungry {@creature bulette} burrows up and attacks."],["71-80","A fruit tree grows. It has {@dice 1d10 + 20} fruit, {@dice 1d8} of which act as randomly determined magic potions, while one acts as an ingested poison of the DM''s choice. The tree vanishes after 1 hour. Picked fruit remains, retaining any magic for 30 days."],["81-90","A nest of {@dice 1d4 + 3} eggs springs up. Any creature that eats an egg must make a {@dc 20} Constitution saving throw. On a successful save, a creature permanently increases its lowest ability score by 1, randomly choosing among equally low scores. On a failed save, the creature takes {@damage 10d6} force damage from an internal magical explosion."],["91-99","A pyramid with a 60-foot-square base bursts upward. Inside is a sarcophagus containing a {@creature mummy lord}. The pyramid is treated as the {@creature mummy lord||mummy lord''s} lair, and its sarcophagus contains treasure of the DM''s choice."],["00","A giant beanstalk sprouts, growing to a height of the DM''s choice. The top leads where the DM chooses, such as to a great view, a {@creature cloud giant||cloud giant''s} castle, or a different plane of existence."]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (115, 0, 'string', 'This bag superficially resembles a bag of holding but is a feeding orifice for a gigantic extradimensional creature. Turning the bag inside out closes the orifice.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (115, 1, 'string', 'The extradimensional creature attached to the bag can sense whatever is placed inside the bag. Animal or vegetable matter placed wholly in the bag is devoured and lost forever. When part of a living creature is placed in the bag, as happens when someone reaches inside it, there is a 50 chance that the creature is pulled inside the bag. A creature inside the bag can use its action to try to escape with a successful 15 Strength check. Another creature can use its action to reach into the bag to pull a creature out, doing so with a successful 20 Strength check (provided it isn''t pulled inside the bag first). Any creature that starts its turn inside the bag is devoured, its body destroyed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (115, 2, 'string', 'Inanimate objects can be stored in the bag, which can hold a cubic foot of such material. However, once each day, the bag swallows any objects inside it and spits them out into another plane of existence. The DM determines the time and plane.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (115, 3, 'string', 'If the bag is pierced or torn, it is destroyed, and anything contained within it is transported to a random location on the Astral Plane.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (116, 0, 'string', 'This bag has an interior space considerably larger than its outside dimensions, roughly 2 feet in diameter at the mouth and 4 feet deep. The bag can hold up to 500 pounds, not exceeding a volume of 64 cubic feet. The bag weighs 15 pounds, regardless of its contents. Retrieving an item from the bag requires an action.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (116, 1, 'string', 'If the bag is overloaded, pierced, or torn, it ruptures and is destroyed, and its contents are scattered in the Astral Plane. If the bag is turned inside out, its contents spill forth, unharmed, but the bag must be put right before it can be used again. Breathing creatures inside the bag can survive up to a number of minutes equal to 10 divided by the number of creatures (minimum 1 minute), after which time they begin to suffocate.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (116, 2, 'string', 'Placing a bag of holding inside an extradimensional space created by a Heward''s handy haversack, portable hole, or similar item instantly destroys both items and opens a gate to the Astral Plane. The gate originates where the one item was placed inside the other. Any creature within 10 feet of the gate is sucked through it to a random location on the Astral Plane. The gate then closes. The gate is one-way only and can''t be reopened.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (117, 0, 'string', 'This ordinary bag, made from gray cloth, appears empty. Reaching inside the bag, however, reveals the presence of a small, fuzzy object.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (117, 1, 'string', 'You can use an action to pull the fuzzy object from the bag and throw it up to 20 feet. When the object lands, it transforms into a creature you determine by rolling a d8 and consulting the table. The creature vanishes at the next dawn or when it is reduced to 0 hit points.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (117, 2, 'string', 'The creature is friendly to you and your companions, and it acts on your turn. You can use a bonus action to command how the creature moves and what action it takes on its next turn, or to give it general orders, such as to attack your enemies. In the absence of such orders, the creature acts in a fashion appropriate to its nature.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (117, 3, 'string', 'Once three fuzzy objects have been pulled from the bag, the bag can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (117, 4, 'table', NULL, '{"type":"table","colLabels":["{@dice d8}","Creature"],"colStyles":["col-1 text-center","col-11"],"rows":[["1","{@creature Weasel}"],["2","{@creature Giant rat}"],["3","{@creature Badger}"],["4","{@creature Boar}"],["5","{@creature Panther}"],["6","{@creature Giant badger}"],["7","{@creature Dire wolf}"],["8","{@creature Giant elk}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (118, 0, 'string', 'This ordinary bag, made from rust cloth, appears empty. Reaching inside the bag, however, reveals the presence of a small, fuzzy object.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (118, 1, 'string', 'You can use an action to pull the fuzzy object from the bag and throw it up to 20 feet. When the object lands, it transforms into a creature you determine by rolling a d8 and consulting the table. The creature vanishes at the next dawn or when it is reduced to 0 hit points.', NULL);
