INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 7, 'entries', NULL, '{"type":"entries","name":"Aberration","entries":["You gain telepathy within a range of 90 feet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 8, 'entries', NULL, '{"type":"entries","name":"Beast","entries":["You immediately transform into a {@filter random Beast with a CR of 5 or lower|bestiary|source=|Challenge Rating=[&0;&5]|type=beast}. Your game statisticsincluding your ability scores, hit points, and possible actionsare replaced by the Beast''s game statistics, and any nonmagical equipment you''re wearing or carrying melds into your new form and can''t be used. Any magic items you''re carrying drop in an unoccupied space within 5 feet of your new form.","You remain transformed in this way for {@dice 2d12} days; nothing can alter your form while you''re under the effects of this card, but the {@spell Wish} spell can end the transformation early. When you revert to your normal form, you return to the same state you were in when you initially transformed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 9, 'entries', NULL, '{"type":"entries","name":"Book","entries":["You gain the ability to speak, read, and write {@dice 1d6 + 2} languages of your choice."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 10, 'entries', NULL, '{"type":"entries","name":"Bridge","entries":["You gain the ability to cast the {@spell Time Stop} spell {@dice 1d3} times. Use your Intelligence, Wisdom, or Charisma as the spellcasting ability (your choice)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 11, 'entries', NULL, '{"type":"entries","name":"Campfire","entries":["You immediately gain the benefits of finishing a long rest."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 12, 'entries', NULL, '{"type":"entries","name":"Cavern","entries":["You gain a climbing speed equal to your walking speed. You also gain the ability to move up, down, across vertical surfaces, and along ceilings, while leaving your hands free."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 13, 'entries', NULL, '{"type":"entries","name":"Celestial","entries":["You sprout a pair of softly luminescent, feathered wings from your back and gain a flying speed of 30 feet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 14, 'entries', NULL, '{"type":"entries","name":"Construct","entries":["A {@creature homunculus} appears in an unoccupied space within 5 feet of you. The appearance of the homunculus is determined by the DM, and the homunculus treats you as its creator."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 15, 'entries', NULL, '{"type":"entries","name":"Corpse","entries":["You immediately drop to 0 hit points, have the {@condition unconscious} condition, and must begin making death saving throws. Spells and other magical effects that restore hit points have no effect on you until you are stabilized. If you fail three death saving throws, you die and can be resurrected only by the {@spell Wish} spell."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 16, 'entries', NULL, '{"type":"entries","name":"Crossroads","entries":["Roll a {@dice d20}. If the roll is even, you age {@dice 1d10} years. If the roll is odd, you become younger by {@dice 1d10} years, to a minimum of 1 year. This effect can be undone only by the {@spell Wish} spell, divine intervention, or similar magic."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 17, 'entries', NULL, '{"type":"entries","name":"Door","entries":["You gain the ability to cast the {@spell Gate} spell {@dice 1d4} times, requiring no material components. Use your Intelligence, Wisdom, or Charisma as the spellcasting ability (your choice)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 18, 'entries', NULL, '{"type":"entries","name":"Dragon","entries":["A dragon egg appears at your feet and immediately hatches into a dragon wyrmling. The type of dragon is chosen by the DM. The wyrmling views you as its parent and is staunchly loyal to you and your allies."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 19, 'entries', NULL, '{"type":"entries","name":"Elemental","entries":["You become immune to one of the following damage types (choose immediately upon drawing this card): acid, cold, fire, lightning, or thunder."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 20, 'entries', NULL, '{"type":"entries","name":"Expert","entries":["Your Dexterity score increases by 2, to a maximum of 22."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 21, 'entries', NULL, '{"type":"entries","name":"Fey","entries":["A fey crossing opens into the Feywild, and you''re immediately pulled through it, disappearing in a flash of rainbow-colored light. You draw no more cards.","The fey crossing appears as a shimmering fractal of light above the deck, and it remains open for 1 minute after the card is drawn. The precise location in the Feywild to which the fey crossing leads is determined by the DM."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 22, 'entries', NULL, '{"type":"entries","name":"Fiend","entries":["A powerful Fiend appears in a nearby unoccupied space and offers you a deal. The precise nature of this deal is up to the DM, but usually the Fiend offers some material reward in exchange for you and your allies completing a task for the Fiend. The Fiend is indifferent to you and can be bargained with; it keeps its side of any bargain it makes, though it might twist the wording of any agreement to suit its purposes. If attacked, or if negotiations fail and you refuse the Fiend''s offer, it returns to its home plane."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 23, 'entries', NULL, '{"type":"entries","name":"Giant","entries":["You immediately grow {@dice 2d10} inches in height, and your hit point maximum and current hit points both increase by 20."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 24, 'entries', NULL, '{"type":"entries","name":"Humanoid","entries":["You can immediately choose to stop drawing from the deck, regardless of how many cards you initially declared."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 25, 'entries', NULL, '{"type":"entries","name":"Lance","entries":["All your ability scores increase by 1, to a maximum of 20."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 26, 'entries', NULL, '{"type":"entries","name":"Mage","entries":["Your Intelligence score increases by 2, to a maximum of 22."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 27, 'entries', NULL, '{"type":"entries","name":"Map","entries":["At any time you choose within 1 year of drawing this card, you can mentally name or describe an object or individual that is familiar to you. You immediately know the location of the object or individual, as well as the distance between you and the object or individual, even if the object or individual is on a different plane of existence. If you named an individual, you know if they are alive and any conditions they have. If you named an object, you know if it is broken or not. If you named a magic item that has charges, you know how many charges it has remaining."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 28, 'entries', NULL, '{"type":"entries","name":"Maze","entries":["You gain {@dice 1d3} levels of {@condition exhaustion}."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 29, 'entries', NULL, '{"type":"entries","name":"Mine","entries":["A pile of {@dice 2d6} gems (each worth 5,000 gp) and {@dice 1d10} chunks of precious ore (each worth 2,500 gp) appears at your feet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 30, 'entries', NULL, '{"type":"entries","name":"Monstrosity","entries":["A {@filter Large or larger Monstrosity with a challenge rating of 10 or less|bestiary|source=|Challenge Rating=[&0;&10]|type=monstrosity|size=l;h;g} (chosen by the DM) appears in an unoccupied space within 15 feet of you. The creature is hostile toward you and attacks immediately. The creature disappears when it is killed or when you are reduced to 0 hit points. If there isn''t enough space for a Large or larger creature to appear, this card has no effect."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 31, 'entries', NULL, '{"type":"entries","name":"Ooze","entries":["A {@creature gelatinous cube} immediately appears in your space and engulfs you. The gelatinous cube is hostile and remains until it is destroyed. If there isn''t enough space for the gelatinous cube to appear, this card has no effect."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 32, 'entries', NULL, '{"type":"entries","name":"Path","entries":["Your walking speed increases by 10 feet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 33, 'entries', NULL, '{"type":"entries","name":"Pit","entries":["A pit opens beneath you. You plummet {@damage 3d6  10} feet, take damage from the fall, and have the {@condition prone} condition."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 34, 'entries', NULL, '{"type":"entries","name":"Plant","entries":["You gain the ability to cast {@spell Speak with Plants} without using a spell slot; you must finish a long rest before you can cast it this way again. If you have spell slots of 3rd level or higher, you can cast this spell using them. Use your Intelligence, Wisdom, or Charisma as the spellcasting ability (your choice)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 35, 'entries', NULL, '{"type":"entries","name":"Priest","entries":["Your Wisdom score increases by 2, to a maximum of 22."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 36, 'entries', NULL, '{"type":"entries","name":"Prisoner","entries":["Glowing chains made of magical force appear and wrap around you. You have the {@condition restrained} condition until the chains are destroyed or you are freed. While you have this condition, you can''t cast spells, and any magic items you''re wearing or carrying have their properties suppressed. You draw no more cards. The chains are immune to damage and can''t be dispelled using the {@spell Dispel Magic} spell or similar magic. However, a {@spell Disintegrate} spell destroys the chains instantly, freeing you. Another creature can also free you by succeeding on a {@dc 30} Dexterity check using {@item thieves'' tools|PHB}."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 37, 'entries', NULL, '{"type":"entries","name":"Ring","entries":["A {@filter rare or rarer magic ring|items|source=|type=ring|rarity=rare;very rare;legendary;artifact|category=} appears on your finger. If you have the attunement slots available, you''re automatically attuned to the ring when it appears. The DM chooses the ring."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 38, 'entries', NULL, '{"type":"entries","name":"Shield","entries":["A {@filter rare or rarer suit of magic armor|items|source=|type=light armor;heavy armor;medium armor|rarity=rare;very rare;legendary;artifact|category=} that you are proficient with appears in your hands. The DM chooses the armor. If you lack proficiency with any armor, your base AC instead now equals 12 + your Dexterity modifier while you aren''t wearing armor."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 39, 'entries', NULL, '{"type":"entries","name":"Ship","entries":["You gain proficiency in three skills chosen by the DM."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 40, 'entries', NULL, '{"type":"entries","name":"Staff","entries":["A {@filter rare or rarer magic rod, staff, or wand|items|source=|type=rod;wand;staff|rarity=rare;very rare;legendary;artifact|category=} appears in your hands. The DM chooses the item."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 41, 'entries', NULL, '{"type":"entries","name":"Stairway","entries":["You can choose to either decrease your number of declared draws by two or receive a {@filter rare or rarer wondrous item|items|source=|type=wondrous item|rarity=rare;very rare;legendary;artifact|category=}, which appears in your hands. The DM chooses the item."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 42, 'entries', NULL, '{"type":"entries","name":"Statue","entries":["You immediately have the {@condition petrified} condition as your body is transformed into marble. The petrification lasts until you are freed with the {@spell Greater Restoration} spell or similar magic."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 43, 'entries', NULL, '{"type":"entries","name":"Tavern","entries":["Your Charisma score increases by 2 to a maximum of 22."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 44, 'entries', NULL, '{"type":"entries","name":"Temple","entries":["A deity or entity of similar power becomes bound to aid you. At any point in time between drawing the card and when you die, you can use your action to call on this entity for divine intervention, and the entity is bound to answer. The parameters and nature of this intervention are chosen by the DM. If you die without having used this intervention, the deity fulfills its obligation by casting the {@spell Resurrection} spell on you. Once the entity has answered your call for divine intervention or resurrected you, the entity is no longer bound to aid you."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 45, 'entries', NULL, '{"type":"entries","name":"Tomb","entries":["At any time you choose within 1 year of drawing this card, you can cast the {@spell True Resurrection} spell once without expending a spell slot or requiring material components. Use your Intelligence, Wisdom, or Charisma as the spellcasting ability (your choice)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 46, 'entries', NULL, '{"type":"entries","name":"Tower","entries":["Draw two additional cards beyond your declared number of draws. The magic of these cards doesn''t immediately take effect; instead, choose one of the two additional cards to keep, returning the other to the deck. The magic of the card you keep takes effect immediately thereafter."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 47, 'entries', NULL, '{"type":"entries","name":"Tree","entries":["Your skin immediately becomes rough, like tree bark. Your base AC now equals 15 + your Dexterity modifier while you aren''t wearing armor, but you have vulnerability to fire damage. This transformation can be undone only by the {@spell Wish} spell, divine intervention, or similar magic."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 48, 'entries', NULL, '{"type":"entries","name":"Undead","entries":["Somewhere on the Material Plane, a {@creature revenant} rises. This revenant blames you for its existence and relentlessly hunts you to exact its revenge. The revenant exists until either 1 year passes, the revenant kills you, or you use a {@spell Wish} spell to banish it permanently to the afterlife."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 49, 'entries', NULL, '{"type":"entries","name":"Warrior","entries":["Your Strength score increases by 2 to a maximum of 22."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (400, 50, 'entries', NULL, '{"type":"entries","name":"Well","entries":["You learn three cantrips of your choice from any spell list."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (401, 0, 'string', 'A Deck of Many Things typically appears not as individual cards, but as a collection of cards characters can draw from. But this doesn''t have to be true; you can give each card a property as a standalone item, so characters who find only one card can still enjoy this famed item and use the card on adventures.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (401, 1, 'string', 'This approach is particularly useful if you make a Deck of Many Things the object of a quest; as the characters explore, each card they find grants them a magical ability they can use in subsequent adventures to find the cards that remain. Eventually the heroes assemble the entire deck, which they can then use in the traditional manner, drawing transformative cards from it in a fitting climax to the campaign.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (401, 2, 'string', 'This deck item comprises the following individual cards:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (401, 3, 'list', NULL, '{"type":"list","columns":3,"items":["{@item Balance Card|BMT}","{@item Comet Card|BMT}","{@item Donjon Card|BMT}","{@item Euryale Card|BMT}","{@item Fates Card|BMT}","{@item Flames Card|BMT}","{@item Fool Card|BMT}","{@item Gem Card|BMT}","{@item Jester Card|BMT}","{@item Key Card|BMT}","{@item Knight Card|BMT}","{@item Moon Card|BMT}","{@item Puzzle Card|BMT}","{@item Rogue Card|BMT}","{@item Ruin Card|BMT}","{@item Sage Card|BMT}","{@item Skull Card|BMT}","{@item Star Card|BMT}","{@item Sun Card|BMT}","{@item Talons Card|BMT}","{@item Throne Card|BMT}","{@item Void Card|BMT}"]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 0, 'string', 'Usually found in a box or pouch, this deck contains a number of cards made of ivory or vellum. Most (75) of these decks have only thirteen cards, but the rest have twenty-two.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 1, 'string', 'Before you draw a card, you must declare how many cards you intend to draw and then draw them randomly (you can use an altered deck of playing cards to simulate the deck). Any cards drawn in excess of this number have no effect. Otherwise, as soon as you draw a card from the deck, its magic takes effect. You must draw each card no more than 1 hour after the previous draw. If you fail to draw the chosen number, the remaining number of cards fly from the deck on their own and take effect all at once.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 2, 'string', 'Once a card is drawn, it fades from existence. Unless the card is the Fool or the Jester, the card reappears in the deck, making it possible to draw the same card twice.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 3, 'entries', NULL, '{"type":"entries","name":"A Question of Enmity","entries":["Two of the cards in a deck of many things can earn a character the enmity of another being. With the Flames card, the enmity is overt. The character should experience the devil''s malevolent efforts on multiple occasions. Seeking out the fiend shouldn''t be a simple task, and the adventurer should clash with the devil''s allies and followers a few times before being able to confront the devil itself.","In the case of the {@card Rogue|Deck of Many Things} card, the enmity is secret and should come from someone thought to be a friend or an ally. As Dungeon Master, you should wait for a dramatically appropriate moment to reveal this enmity, leaving the adventurer guessing who is likely to become a betrayer."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 4, 'table', NULL, '{"type":"table","colLabels":["{@dice 1d22|Playing Card}","Card"],"colStyles":["col-6 text-center","col-6 text-center"],"rows":[[{"type":"cell","roll":{"exact":1},"entry":"Ace of diamonds"},"Vizier*"],[{"type":"cell","roll":{"exact":2},"entry":"King of diamonds"},"Sun"],[{"type":"cell","roll":{"exact":3},"entry":"Queen of diamonds"},"Moon"],[{"type":"cell","roll":{"exact":4},"entry":"Jack of diamonds"},"Star"],[{"type":"cell","roll":{"exact":5},"entry":"Two of diamonds"},"Comet*"],[{"type":"cell","roll":{"exact":6},"entry":"Ace of hearts"},"The Fates*"],[{"type":"cell","roll":{"exact":7},"entry":"King of hearts"},"Throne"],[{"type":"cell","roll":{"exact":8},"entry":"Queen of hearts"},"Key"],[{"type":"cell","roll":{"exact":9},"entry":"Jack of hearts"},"Knight"],[{"type":"cell","roll":{"exact":10},"entry":"Two of hearts"},"Gem*"],[{"type":"cell","roll":{"exact":11},"entry":"Ace of clubs"},"Talons*"],[{"type":"cell","roll":{"exact":12},"entry":"King of clubs"},"The Void"],[{"type":"cell","roll":{"exact":13},"entry":"Queen of clubs"},"Flames"],[{"type":"cell","roll":{"exact":14},"entry":"Jack of clubs"},"Skull"],[{"type":"cell","roll":{"exact":15},"entry":"Two of clubs"},"Idiot*"],[{"type":"cell","roll":{"exact":16},"entry":"Ace of spades"},"Donjon*"],[{"type":"cell","roll":{"exact":17},"entry":"King of spades"},"Ruin"],[{"type":"cell","roll":{"exact":18},"entry":"Queen of spades"},"Euryale"],[{"type":"cell","roll":{"exact":19},"entry":"Jack of spades"},"Rogue"],[{"type":"cell","roll":{"exact":20},"entry":"Two of spades"},"Balance*"],[{"type":"cell","roll":{"exact":21},"entry":"Joker (with TM)"},"Fool*"],[{"type":"cell","roll":{"exact":22},"entry":"Joker (without TM)"},"Jester"]],"footnotes":["* Found only in a deck with twenty-two cards"]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 5, 'entries', NULL, '{"type":"entries","name":"Vizier","entries":["At any time you choose within one year of drawing this card, you can ask a question in meditation and mentally receive a truthful answer to that question. Besides information, the answer helps you solve a puzzling problem or other dilemma. In other words, the knowledge comes with wisdom on how to apply it."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 6, 'entries', NULL, '{"type":"entries","name":"Sun","entries":["You gain 50,000 XP, and a wondrous item (which the DM determines randomly) appears in your hands."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 7, 'entries', NULL, '{"type":"entries","name":"Moon","entries":["You are granted the ability to cast the {@spell wish} spell {@dice 1d3} times."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 8, 'entries', NULL, '{"type":"entries","name":"Star","entries":["Increase one of your ability scores by 2. The score can exceed 20 but can''t exceed 24."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 9, 'entries', NULL, '{"type":"entries","name":"Comet","entries":["If you single-handedly defeat the next hostile monster or group of monsters you encounter, you gain experience points enough to gain one level. Otherwise, this card has no effect."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 10, 'entries', NULL, '{"type":"entries","name":"The Fates","entries":["Reality''s fabric unravels and spins anew, allowing you to avoid or erase one event as if it never happened. You can use the card''s magic as soon as you draw the card or at any other time before you die."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 11, 'entries', NULL, '{"type":"entries","name":"Throne","entries":["You gain proficiency in the {@skill Persuasion} skill, and you double your proficiency bonus on checks made with that skill. In addition, you gain rightful ownership of a small keep somewhere in the world. However, the keep is currently in the hands of monsters, which you must clear out before you can claim the keep as yours."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 12, 'entries', NULL, '{"type":"entries","name":"Key","entries":["A rare or rarer magic weapon with which you are proficient appears in your hands. The DM chooses the weapon."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 13, 'entries', NULL, '{"type":"entries","name":"Knight","entries":["You gain the service of a 4th-level fighter who appears in a space you choose within 30 feet of you. The fighter is of the same race as you and serves you loyally until death, believing the fates have drawn him or her to you. You control this character."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 14, 'entries', NULL, '{"type":"entries","name":"Gem","entries":["Twenty-five pieces of jewelry worth 2,000 gp each or fifty gems worth 1,000 gp each appear at your feet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 15, 'entries', NULL, '{"type":"entries","name":"Talons","entries":["Every magic item you wear or carry disintegrates. Artifacts in your possession aren''t destroyed but do vanish."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 16, 'entries', NULL, '{"type":"entries","name":"The Void","entries":["This black card spells disaster. Your soul is drawn from your body and contained in an object in a place of the DM''s choice. One or more powerful beings guard the place. While your soul is trapped in this way, your body is {@condition incapacitated}. A {@spell wish} spell can''t restore your soul, but the spell reveals the location of the object that holds it. You draw no more cards."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 17, 'entries', NULL, '{"type":"entries","name":"Flames","entries":["A powerful devil becomes your enemy. The devil seeks your ruin and plagues your life, savoring your suffering before attempting to slay you. This enmity lasts until either you or the devil dies."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 18, 'entries', NULL, '{"type":"entries","name":"Skull","entries":["You summon an {@creature avatar of death|DMG}a ghostly humanoid skeleton clad in a tattered black robe and carrying a spectral scythe. It appears in a space of the DM''s choice within 10 feet of you and attacks you, warning all others that you must win the battle alone. The avatar fights until you die or it drops to 0 hit points, whereupon it disappears. If anyone tries to help you, the helper summons its own {@creature avatar of death|DMG}. A creature slain by an {@creature avatar of death|DMG} can''t be restored to life."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 19, 'entries', NULL, '{"type":"entries","name":"Idiot","entries":["Permanently reduce your Intelligence by {@dice 1d4 + 1} (to a minimum score of 1). You can draw one additional card beyond your declared draws."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 20, 'entries', NULL, '{"type":"entries","name":"Donjon","entries":["You disappear and become entombed in a state of suspended animation in an extradimensional sphere. Everything you were wearing and carrying stays behind in the space you occupied when you disappeared. You remain imprisoned until you are found and removed from the sphere. You can''t be located by any divination magic, but a {@spell wish} spell can reveal the location of your prison. You draw no more cards."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 21, 'entries', NULL, '{"type":"entries","name":"Ruin","entries":["All forms of wealth that you carry or own, other than magic items, are lost to you. Portable property vanishes. Businesses, buildings, and land you own are lost in a way that alters reality the least. Any documentation that proves you should own something lost to this card also disappears."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 22, 'entries', NULL, '{"type":"entries","name":"Euryale","entries":["The card''s {@creature medusa}-like visage curses you. You take a 2 penalty on saving throws while cursed in this way. Only a god or the magic of {@card The Fates|Deck of Many Things} card can end this curse."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 23, 'entries', NULL, '{"type":"entries","name":"Rogue","entries":["A nonplayer character of the DM''s choice becomes hostile toward you. The identity of your new enemy isn''t known until the NPC or someone else reveals it. Nothing less than a {@spell wish} spell or divine intervention can end the NPC''s hostility toward you."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 24, 'entries', NULL, '{"type":"entries","name":"Balance","entries":["Your mind suffers a wrenching alteration, causing your alignment to change. Lawful becomes chaotic, good becomes evil, and vice versa. If you are true neutral or unaligned, this card has no effect on you."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 25, 'entries', NULL, '{"type":"entries","name":"Fool","entries":["You lose 10,000 XP, discard this card, and draw from the deck again, counting both draws as one of your declared draws. If losing that much XP would cause you to lose a level, you instead lose an amount that leaves you with just enough XP to keep your level."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (402, 26, 'entries', NULL, '{"type":"entries","name":"Jester","entries":["You gain 10,000 XP, or you can draw two additional cards beyond your declared draws."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (403, 0, 'string', 'This wooden box contains a set of thirty-two parchment cards.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (403, 1, 'table', NULL, '{"type":"table","caption":"Deck of Miscellany","colStyles":["col-2 text-center","col-10"],"colLabels":["{@dice 1d32|Card}","Item"],"rows":[[{"type":"cell","roll":{"exact":1},"entry":"3 {@color |#ff0000}"},"Wooden {@item abacus|PHB}"],[{"type":"cell","roll":{"exact":2},"entry":"4 {@color |#ff0000}"},"Four {@item Perfume (vial)|PHB|vials of perfume}"],[{"type":"cell","roll":{"exact":3},"entry":"5 {@color |#ff0000}"},"5 days'' worth of {@item Rations (1 day)|PHB|rations}"],[{"type":"cell","roll":{"exact":4},"entry":"6 {@color |#ff0000}"},"{@item Iron pot|PHB}"],[{"type":"cell","roll":{"exact":5},"entry":"7 {@color |#ff0000}"},"{@item Disguise kit|PHB}"],[{"type":"cell","roll":{"exact":6},"entry":"8 {@color |#ff0000}"},"Window (up to 5 feet wide and 5 feet high), which you can place on a vertical surface up to 5 feet thick and which allows you to look through the surface"],[{"type":"cell","roll":{"exact":7},"entry":"9 {@color |#ff0000}"},"{@item Manacles|PHB}"],[{"type":"cell","roll":{"exact":8},"entry":"10 {@color |#ff0000}"},"Ten {@item Parchment (one sheet)|PHB|sheets of parchment}"],[{"type":"cell","roll":{"exact":9},"entry":"3 {@color |#ff0000}"},"Three {@item dagger|PHB|daggers}"],[{"type":"cell","roll":{"exact":10},"entry":"4 {@color |#ff0000}"},"Four {@item Oil (flask)|PHB|flasks of oil}"],[{"type":"cell","roll":{"exact":11},"entry":"5 {@color |#ff0000}"},"Five silk {@item robes|PHB}"],[{"type":"cell","roll":{"exact":12},"entry":"6 {@color |#ff0000}"},"{@item Forgery kit|PHB}"],[{"type":"cell","roll":{"exact":13},"entry":"7 {@color |#ff0000}"},"{@item Quarterstaff|PHB}"],[{"type":"cell","roll":{"exact":14},"entry":"8 {@color |#ff0000}"},"{@item Fishing tackle|PHB}"],[{"type":"cell","roll":{"exact":15},"entry":"9 {@color |#ff0000}"},"Leather {@item pouch|PHB} containing 18 gp"],[{"type":"cell","roll":{"exact":16},"entry":"10 {@color |#ff0000}"},"10 {@item crossbow bolt|PHB|crossbow bolts}"],[{"type":"cell","roll":{"exact":17},"entry":"3 "},"Three {@item book|PHB|books}, written in {@language Common}, about random historical events"],[{"type":"cell","roll":{"exact":18},"entry":"4 "},"Canvas {@item Two-Person Tent|PHB|tent}"],[{"type":"cell","roll":{"exact":19},"entry":"5 "},"{@item Silk Rope (50 feet)|PHB|50 feet of coiled silk rope}"],[{"type":"cell","roll":{"exact":20},"entry":"6 "},"Two {@item crowbar|PHB|crowbars}"],[{"type":"cell","roll":{"exact":21},"entry":"7 "},"{@item Healer''s kit|PHB}"],[{"type":"cell","roll":{"exact":22},"entry":"8 "},"Eight gems worth 5 gp each"],[{"type":"cell","roll":{"exact":23},"entry":"9 "},"{@item Lamp|PHB}"],[{"type":"cell","roll":{"exact":24},"entry":"10 "},"{@item Chain (10 feet)|PHB|10 feet of iron chain}"],[{"type":"cell","roll":{"exact":25},"entry":"3 "},"Three {@item spear|PHB|spears}"],[{"type":"cell","roll":{"exact":26},"entry":"4 "},"{@item Steel mirror|PHB}"],[{"type":"cell","roll":{"exact":27},"entry":"5 "},"15-foot wooden pole"],[{"type":"cell","roll":{"exact":28},"entry":"6 "},"Burlap {@item sack|PHB}"],[{"type":"cell","roll":{"exact":29},"entry":"7 "},"Two sets of {@item fine clothes|PHB}"],[{"type":"cell","roll":{"exact":30},"entry":"8 "},"{@item Shovel|PHB}"],[{"type":"cell","roll":{"exact":31},"entry":"9 "},"{@item Light hammer|PHB}"],[{"type":"cell","roll":{"exact":32},"entry":"10 "},"Ten {@item arrow|PHB|arrows}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (403, 2, 'string', 'The face of each card bears an illustration of a different item or set of items. As an action, you can draw a card of your choice from the deck and throw it to the ground in an unoccupied space within 5 feet of yourself. When the card hits the ground, the card permanently transforms into the item or set of items depicted on its face. An altered deck of real-world playing cards can simulate the deck, as shown on the Deck of Miscellany table.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (404, 0, 'string', 'The illustrations on this deck of oracle cards move or change subtly when viewed indirectly. When you finish a long rest, you can spend 10 minutes consulting the cards for an omen of the coming day. Roll a d20 and record the number rolled. Once in the next 8 hours, immediately after a creature within 60 feet of you makes an ability check, an attack roll, or a saving throw, you can use your reaction to discard the d20 roll; the creature must use the number you rolled in place of its roll.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (404, 1, 'string', 'Additionally, while holding the cards, you can cast Divination from them. Once this property is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 0, 'string', 'Stored in a leather pouch, this unique deck contains twenty-two colored cards made of some strong but unknown metal, each of which features a design printed as a mosaic of raised dots. Before you draw a card, you must declare how many cards you intend to draw and then draw them randomly (you can use an altered deck of playing cards to simulate the deck). Any cards drawn in excess of this number have no effect. Otherwise, as soon as you draw a card from the deck, its magic takes effect. You must draw each card no more than 1 hour after the previous draw. If you fail to draw the chosen number, the remaining number of cards fly from the deck on their own and take effect all at once.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 1, 'string', 'Once a card is drawn, it fades from existence. Unless the card is the Fool or the Jester, the card reappears in the deck, making it possible to draw the same card twice.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 2, 'table', NULL, '{"type":"table","colLabels":["{@dice 1d22|Playing Card}","Card"],"colStyles":["col-6 text-center","col-6 text-center"],"rows":[[{"type":"cell","roll":{"exact":1},"entry":"Ace of diamonds"},"{@card Vizier|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":2},"entry":"King of diamonds"},"{@card Sun|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":3},"entry":"Queen of diamonds"},"{@card Moon|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":4},"entry":"Jack of diamonds"},"{@card Star|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":5},"entry":"Two of diamonds"},"{@card Comet|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":6},"entry":"Ace of hearts"},"{@card The Fates|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":7},"entry":"King of hearts"},"{@card Throne|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":8},"entry":"Queen of hearts"},"{@card Key|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":9},"entry":"Jack of hearts"},"{@card Knight|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":10},"entry":"Two of hearts"},"{@card Gem|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":11},"entry":"Ace of clubs"},"{@card Talons|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":12},"entry":"King of clubs"},"{@card The Void|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":13},"entry":"Queen of clubs"},"{@card Flames|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":14},"entry":"Jack of clubs"},"{@card Skull|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":15},"entry":"Two of clubs"},"{@card Idiot|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":16},"entry":"Ace of spades"},"{@card Donjon|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":17},"entry":"King of spades"},"{@card Ruin|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":18},"entry":"Queen of spades"},"{@card Euryale|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":19},"entry":"Jack of spades"},"{@card Rogue|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":20},"entry":"Two of spades"},"{@card Balance|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":21},"entry":"Joker (with TM)"},"{@card Fool|Deck of Several Things|LLK}"],[{"type":"cell","roll":{"exact":22},"entry":"Joker (without TM)"},"{@card Jester|Deck of Several Things|LLK}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 3, 'entries', NULL, '{"type":"entries","name":"Balance","entries":["Your mind suffers a wrenching alteration, causing your alignment to change for the duration of the adventure. Lawful becomes chaotic, good becomes evil, and vice versa. If you are true neutral or unaligned, this card has no effect on you."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 4, 'entries', NULL, '{"type":"entries","name":"Comet","entries":["If you single-handedly defeat the next hostile monster or group of monsters you encounter, you have advantage on ability checks made using one skill of your choice for the duration of the adventure. Otherwise, this card has no effect."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 5, 'entries', NULL, '{"type":"entries","name":"Donjon","entries":["You are instantly teleported to and confined within the prison of the Monastery of the Distressed Body (area M6). Everything you were wearing and carrying stays behind in the space you occupied when you disappeared. You draw no more cards."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 6, 'entries', NULL, '{"type":"entries","name":"Euryale","entries":["The card''s medusa-like visage curses you. You take a 1 penalty on saving throws for the duration of the adventure."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 7, 'entries', NULL, '{"type":"entries","name":"The Fates","entries":["Reality''s fabric unravels and spins anew, allowing you to avoid or erase one event as if it never happened. You can use the card''s magic as soon as you draw the card or at any other point during the adventure."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 8, 'entries', NULL, '{"type":"entries","name":"Flames","entries":["The Grand Master of the Monastery of the Distressed Body becomes your enemy. The bone devil seeks your ruin, savoring your suffering before attempting to slay you. If the Grand Master has already been defeated, you gain the enmity of {@creature Garret Levistusson|LLK|Garret Levistusson''s} patrona similarly powerful devil."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 9, 'entries', NULL, '{"type":"entries","name":"Fool","entries":["For the duration of the adventure, you lose proficiency with one skill or gain disadvantage on all checks made with one skill (with the skill and the penalty determined by the DM). Discard this card and draw from the deck again, counting both draws as one of your declared draws."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 10, 'entries', NULL, '{"type":"entries","name":"Gem","entries":["The 1,000 gp hoard of the leprechaun from the Wilderness Encounters table (see appendix A) appears at your feet. If that treasure has already been claimed, you gain an equivalent hoard."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 11, 'entries', NULL, '{"type":"entries","name":"Idiot","entries":["Reduce your Intelligence by {@dice 1d4 + 1} (to a minimum score of 1) for the duration of the adventure. You can draw one additional card beyond your declared draws."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 12, 'entries', NULL, '{"type":"entries","name":"Jester","entries":["You gain proficiency in a skill of your choice for the duration of the adventure, or you can draw two additional cards beyond your declared draws."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 13, 'entries', NULL, '{"type":"entries","name":"Key","entries":["A common or uncommon magic weapon with which you are proficient, or a {@item spell scroll} featuring a spell of a level you can cast, appears in your hands. The DM chooses the weapon or spell, which you possess for the duration of this adventure."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 14, 'entries', NULL, '{"type":"entries","name":"Knight","entries":["You gain the service of any of the NPCs in the \"{@book Hirelings|phb|5|services}\" section not currently with the party, who appears in a space you choose within 30 feet of you. The NPC serves you loyally for the duration of the adventure, believing that the fates have drawn them to you. You control this character."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 15, 'entries', NULL, '{"type":"entries","name":"Moon","entries":["You are granted the ability to cast any spell of 5th level or lower, and can use that ability {@dice 1d3} times for the duration of the adventure."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 16, 'entries', NULL, '{"type":"entries","name":"Rogue","entries":["An NPC of the DM''s choice becomes secretly hostile toward you. The identity of your new enemy isn''t known until the NPC or someone else reveals it. Any enchantment spell cast on the NPC at 6th level or higher can end the NPC''s hostility toward you."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 17, 'entries', NULL, '{"type":"entries","name":"Ruin","entries":["All forms of wealth that you carry or own, other than magic items, are lost to you. This wealth can be recovered either in the treasury of the Monastery of the Distressed Body (area M10) or Kwalish''s lab in Daoine Gloine (area O7), whichever comes later in the adventure."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 18, 'entries', NULL, '{"type":"entries","name":"Skull","entries":["You summon an avatar of deatha mechanical skeleton (use bone naga statistics) clad in a tattered black robe. It appears in a space of the DM''s choice within 10 feet of you and attacks you, warning all others that you must win the battle alone. The avatar fights until you die or it drops to 0 hit points, whereupon it disappears. If anyone tries to help you, the helper summons its own avatar of death. A creature slain by an avatar of death can''t be restored to life."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 19, 'entries', NULL, '{"type":"entries","name":"Star","entries":["Increase one of your ability scores by 1 for the duration of the adventure. The score can exceed 20 but can''t exceed 24."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 20, 'entries', NULL, '{"type":"entries","name":"Sun","entries":["You gain proficiency in the skill of your choice for the duration of the adventure. In addition, a common or uncommon wondrous item appears in your hands. The DM chooses the item, which you possess for the duration of this adventure."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 21, 'entries', NULL, '{"type":"entries","name":"Talons","entries":["Every magic item you wear or carry is lost to you. These items can be recovered either in the treasury of the Monastery of the Distressed Body (area M10) or Kwalish''s lab in Daoine Gloine (area O7), whichever comes later in the adventure."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 22, 'entries', NULL, '{"type":"entries","name":"Throne","entries":["You gain proficiency in the {@skill Persuasion} skill and you double your proficiency bonus on checks made with that skill for the duration of the adventure. In addition, the Monastery of the Distressed Body''s brains in jars regard you thereafter as the monastery''s rightful master. You must defeat or otherwise clear out the Grand Master and its servants before you can claim the monastery as yours."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 23, 'entries', NULL, '{"type":"entries","name":"Vizier","entries":["At any one time you choose within the duration of the adventure, you can ask a question in meditation and mentally receive a truthful answer to that question. Besides information, the answer helps you solve a puzzling problem or other dilemma. In other words, the knowledge comes with wisdom on how to apply it."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 24, 'entries', NULL, '{"type":"entries","name":"The Void","entries":["This black card spells disaster. Your soul is drawn from your body and held within machinery in either the control room of the Monastery of the Distressed Body (area M8) or Kwalish''s lab in Daoine Gloine (area O7), whichever comes later in the adventure. While your soul is trapped in this way, your body is {@condition incapacitated}. {@spell Divination}, {@spell contact other plane}, or a similar spell of 4th level or higher reveals the location of the machinery that holds your soul. You draw no more cards."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (405, 25, 'inset', NULL, '{"type":"inset","name":"Using the Deck of Several Things","entries":["The special nature of this deck (carried by {@creature Mary Greymalkin|LLK} if she is used as an NPC) means that many of its effects operate only within and with respect to this specific adventure. This typically covers any time the characters spend between setting out for the Barrier Peaks and the resolution of whatever events transpire in Kwalish''s lab (area O7).","For cards that effectively remove a character from play for a period of time ({@card Donjon|Deck of Several Things|LLK} and {@card the Void|Deck of Several Things|LLK}), you can allow a player to take on the role of one of the party''s NPC hirelings, or introduce a temporary character as an NPC met during the party''s journey. Alternatively, you can decide that only some aspect of the character''s will disappears and is imprisoned, leaving the character to operate in a robotic state until freed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (406, 0, 'string', 'This deck of heavy vellum cards hums with the magic of the Elemental Chaos.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (406, 1, 'string', 'The magic of the deck functions only if cards are drawn at random (a deck of real-world playing cards can simulate the deck). As an action, you can draw a random card from this deck and throw it to make a ranged spell attack, using Dexterity for the attack roll. The card has a range of 30 feet. On a hit, it deals 1d4 slashing damage and imposes a magical effect determined by its suit, as detailed in the Deck of Wild Cards table. The card immediately returns to the deck after it hits or misses a target.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (406, 2, 'table', NULL, '{"type":"table","caption":"Deck of Wild Cards","colStyles":["col-2 text-center","col-2 text-center","col-8"],"colLabels":["d4","Suit","Effect"],"rows":[["1"," (Rods)","The card explodes in a burst of fire. The target and each creature within 5 feet of it must succeed on a {@dc 15} Dexterity saving throw or take {@damage 2d8} fire damage."],["2","{@color |#ff0000} (Coins)","The card shoots streaks of lightning. The target must make a {@dc 15} Dexterity saving throw. On a failed save, the target has the blinded condition until the end of your next turn, and each creature within 10 feet of the target takes {@damage 1d6} lightning damage."],["3","{@color |#ff0000} (Cups)","The card covers the target in a thick layer of ice. The target takes {@damage 1d10} cold damage and must succeed on a {@dc 15} Constitution saving throw or have the restrained condition until the end of your next turn."],["4"," (Swords)","The card unleashes a sharp, concussive blast. The target and each creature within 5 feet of it take {@damage 1d6} force damage and must succeed on a {@dc 15} Strength saving throw or have the prone condition."]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 0, 'string', 'Created in the image of the Deck of Many Things, this deck of ivory or vellum cards bestows an assortment of minor benefits and penalties on those who draw from it. Most (75 percent) of these decks have only thirteen cards, but the rest have twenty-two.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 1, 'string', 'Before you draw a card, you must declare how many cards you intend to draw, then draw them randomly. Any additional cards drawn have no effect. Unless a card states otherwise, as soon as you draw a card from the deck, its magic takes effect. You must draw each card you declared no more than 1 hour after the previous draw. If you fail to draw the chosen number, the remaining number of cards fly from the deck and take effect simultaneously.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 2, 'string', 'Unless it is the Mystery card, a drawn card immediately takes effect, fades from existence, and reappears in the deck, making it possible to draw the same card multiple times.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 3, 'string', 'You can use an altered deck of playing cards to simulate the deck, as shown in the Deck of Wonder table.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 4, 'table', NULL, '{"type":"table","caption":"Deck of Wonder","colStyles":["col-4 text-center","col-8"],"colLabels":["{@dice 1d22|Card}","Card Title"],"rows":[[{"type":"cell","roll":{"exact":1},"entry":"A {@color |#ff0000}"},"{@card Chancellor|Deck of Wonder|BMT}*"],[{"type":"cell","roll":{"exact":2},"entry":"K {@color |#ff0000}"},"{@card Day|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":3},"entry":"Q {@color |#ff0000}"},"{@card Night|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":4},"entry":"J {@color |#ff0000}"},"{@card Dawn|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":5},"entry":"2 {@color |#ff0000}"},"{@card Dusk|Deck of Wonder|BMT}*"],[{"type":"cell","roll":{"exact":6},"entry":"A {@color |#ff0000}"},"{@card Destiny|Deck of Wonder|BMT}*"],[{"type":"cell","roll":{"exact":7},"entry":"K {@color |#ff0000}"},"{@card Crown|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":8},"entry":"Q {@color |#ff0000}"},"{@card Lock|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":9},"entry":"J {@color |#ff0000}"},"{@card Champion|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":10},"entry":"2 {@color |#ff0000}"},"{@card Coin|Deck of Wonder|BMT}*"],[{"type":"cell","roll":{"exact":11},"entry":"A "},"{@card Vulture|Deck of Wonder|BMT}*"],[{"type":"cell","roll":{"exact":12},"entry":"K "},"{@card Chaos|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":13},"entry":"Q "},"{@card Order|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":14},"entry":"J "},"{@card Beginning|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":15},"entry":"2 "},"{@card Mystery|Deck of Wonder|BMT}*"],[{"type":"cell","roll":{"exact":16},"entry":"A "},"{@card Isolation|Deck of Wonder|BMT}*"],[{"type":"cell","roll":{"exact":17},"entry":"K "},"{@card End|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":18},"entry":"Q "},"{@card Monster|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":19},"entry":"J "},"{@card Knife|Deck of Wonder|BMT}"],[{"type":"cell","roll":{"exact":20},"entry":"2 "},"{@card Justice|Deck of Wonder|BMT}*"],[{"type":"cell","roll":{"exact":21},"entry":"Joker (with )"},"{@card Student|Deck of Wonder|BMT}*"],[{"type":"cell","roll":{"exact":22},"entry":"Joker (without )"},"{@card Mischief|Deck of Wonder|BMT}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 5, 'entries', NULL, '{"type":"entries","name":"Beginning","entries":["Your hit point maximum and current hit points increase by {@dice 2d10}. Your hit point maximum remains increased in this way for the next 8 hours."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 6, 'entries', NULL, '{"type":"entries","name":"Champion","entries":["You gain a +1 bonus to weapon attack and damage rolls. This bonus lasts for 8 hours."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 7, 'entries', NULL, '{"type":"entries","name":"Chancellor","entries":["Within 8 hours of drawing this card, you can cast {@spell Augury} once as an action, requiring no material components. Use your Intelligence, Wisdom, or Charisma as the spellcasting ability (your choice)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 8, 'entries', NULL, '{"type":"entries","name":"Chaos","entries":["You gain resistance to one of the following damage types (chosen by the DM): acid, cold, fire, lightning, or thunder. This resistance lasts for {@dice 1d12} days."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 9, 'entries', NULL, '{"type":"entries","name":"Coin","entries":["Five pieces of jewelry, each worth 100 gp, or ten gemstones, each worth 50 gp, appear at your feet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 10, 'entries', NULL, '{"type":"entries","name":"Crown","entries":["You learn the {@spell Friends} cantrip. Use your Intelligence, Wisdom, or Charisma as the spellcasting ability (your choice). If you already know this cantrip, the card has no effect."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 11, 'entries', NULL, '{"type":"entries","name":"Dawn","entries":["This card invigorates you. For the next 8 hours, you can add your proficiency bonus to your initiative rolls."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 12, 'entries', NULL, '{"type":"entries","name":"Day","entries":["You gain a +1 bonus to saving throws. This benefit lasts until you finish a long rest."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 13, 'entries', NULL, '{"type":"entries","name":"Destiny","entries":["This card protects you against an untimely demise. The first time after drawing this card that you would drop to 0 hit points from taking damage, you instead drop to 1 hit point."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 14, 'entries', NULL, '{"type":"entries","name":"Dusk","entries":["This card supernaturally saps your energy. You have disadvantage on initiative rolls. This effect lasts until you finish a long rest, but it can be ended early by a {@spell Remove Curse} spell or similar magic."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 15, 'entries', NULL, '{"type":"entries","name":"End","entries":["This card is an omen of death. You take {@damage 2d10} necrotic damage, and your hit point maximum is reduced by an amount equal to the damage taken. This effect can''t reduce your hit point maximum below 10 hit points. This reduction lasts until you finish a long rest, but it can be ended early by a {@spell Remove Curse} spell or similar magic."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 16, 'entries', NULL, '{"type":"entries","name":"Isolation","entries":["You disappear, along with anything you are wearing or carrying, and become trapped in a harmless extradimensional space for {@dice 1d4} minutes. You draw no more cards. You then reappear in the space you left or the nearest unoccupied space. When you reappear, you must succeed on a {@dc 11} Constitution saving throw or have the poisoned condition for 1 hour as your body reels from the extradimensional travel."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 17, 'entries', NULL, '{"type":"entries","name":"Justice","entries":["You momentarily gain the ability to balance the scales of fate. For the next 8 hours, whenever you or a creature within 60 feet of you is about to roll a {@dice d20} with advantage or disadvantage, you can use your reaction to prevent the roll from being affected by advantage or disadvantage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 18, 'entries', NULL, '{"type":"entries","name":"Knife","entries":["An uncommon magic weapon you''re proficient with appears in your hands. The DM chooses the weapon."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 19, 'entries', NULL, '{"type":"entries","name":"Lock","entries":["You gain the ability to cast {@spell Knock} {@dice 1d3} times. Use your Intelligence, Wisdom, or Charisma as the spellcasting ability (your choice)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 20, 'entries', NULL, '{"type":"entries","name":"Mischief","entries":["You receive an uncommon wondrous item (chosen by the DM), or you can draw two additional cards beyond your declared draws."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 21, 'entries', NULL, '{"type":"entries","name":"Monster","entries":["This card''s monstrous visage curses you. While cursed in this way, whenever you make a saving throw, you must roll {@dice 1d4} and subtract the number rolled from the total. The curse lasts until you finish a long rest, but it can be ended early with a {@spell Remove Curse} spell or similar magic."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 22, 'entries', NULL, '{"type":"entries","name":"Mystery","entries":["You have disadvantage on Intelligence saving throws for 1 hour. Discard this card and draw from the deck again; together, the two draws count as one of your declared draws."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 23, 'entries', NULL, '{"type":"entries","name":"Night","entries":["You gain {@sense darkvision} within a range of 300 feet. This {@sense darkvision} lasts for 8 hours."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 24, 'entries', NULL, '{"type":"entries","name":"Order","entries":["You gain resistance to one of the following damage types (chosen by the DM): force, necrotic, poison, psychic, or radiant. This resistance lasts for {@dice 1d12} days."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 25, 'entries', NULL, '{"type":"entries","name":"Student","entries":["You gain proficiency in Wisdom saving throws. If you already have this proficiency, you instead gain proficiency in Intelligence or Charisma saving throws (your choice)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (407, 26, 'entries', NULL, '{"type":"entries","name":"Vulture","entries":["One nonmagical item or piece of equipment in your possession (chosen by the DM) disappears. The item remains nearby but concealed for a short time, so it can be found with a successful {@dc 15} Wisdom ({@skill Perception}) check. If the item isn''t recovered within 1 hour, it disappears forever."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (408, 0, 'string', 'Thassa wields Dekella, a two-pronged weapon gifted to her by Purphoros. When the god of the sea bestows her weapon on a mortal, it''s often so they might work her will far from the ocean, right some wrong affecting those she''d prefer not to harm, or give a mortal a glimpse of her terrible responsibilities.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (408, 1, 'entries', NULL, '{"type":"entries","name":"Bident of the Deep","entries":["Thassa''s signature weapon thrums with the icy currents of the deep ocean. This weapon functions as a trident that grants a +3 bonus to attack and damage rolls made with it. When you hit with an attack using the bident, the target takes an extra {@damage 2d10} cold damage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (408, 2, 'entries', NULL, '{"type":"entries","name":"Blessing of the Deep","entries":["If you are a worshiper of Thassa, you gain all the following benefits for which you have the required piety:",{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Piety 10+","entry":"You can breathe underwater, and you gain a swimming speed of 60 feet."},{"type":"item","name":"Piety 25+","entry":"The bident has 1 randomly determined {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} property."},{"type":"item","name":"Piety 50+","entry":"The bident has 1 randomly determined {@table Artifact Properties; Major Beneficial Properties|dmg|major beneficial} property."}]},"If you aren''t a worshiper of Thassa, the bident has 1 {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental} property and 1 {@table Artifact Properties; Major Detrimental Properties|dmg|major detrimental} property, both randomly determined.","See \"Artifacts\" in chapter 7 of the Dungeon Master''s Guide for details on randomly determined properties."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (408, 3, 'entries', NULL, '{"type":"entries","name":"Command the Deep","entries":["The bident holds the power to command the waves and its creatures. As an action, you can change the condition of the sea within 1 mile of you, creating strong winds and heavy rain that cause violent waves (see \"Weather at Sea\" in chapter 5 of the Dungeon Master''s Guide) or calming a storm. In either case, the unnatural weather lasts for 1 hour before returning to normal. Once used, this property of the bident can''t be used again until the next dusk.","Additionally, you can cast the {@spell dominate monster} spell (save {@dc 18}) from the bident, but only on beasts and monstrosities that have an innate swimming speed. Once used, this property of the bident can''t be used again until the next dusk."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (408, 4, 'entries', NULL, '{"type":"entries","name":"Aquatic Metamorphosis","entries":["You can cast the {@spell true polymorph} spell (save {@dc 18}) from the bident, but you must cast it on a creature to turn it into a kind of creature that has an innate swimming speed. Once used, this property of the bident can''t be used again until the next dusk."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (408, 5, 'entries', NULL, '{"type":"entries","name":"Destroying the Bident","entries":["To destroy the bident, it must be heated by the breath of an {@creature ancient red dragon} and then, while still hot, immersed in the Tartyx River."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (409, 0, 'string', 'The back of this weatherworn leather glove is adorned with three large metal hooks shaped like a mole''s claws. Stitched into the glove''s palm is the mountain rune.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (409, 1, 'string', 'The glove is considered a simple melee weapon with the finesse and light properties, and it deals 1d4 slashing damage on a hit. While attuned to the glove, you gain a burrowing speed equal to your walking speed and blindsight to 15 feet.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (409, 2, 'entries', NULL, '{"type":"entries","name":"Invoking the Rune","entries":["As an action, you can invoke the glove''s rune to bolster yourself with the sturdiness of the earth. Spend and roll a number of your unspent Hit Dice up to a maximum equal to your proficiency bonus. You then regain a number of hit points equal to the total roll plus your Constitution modifier.","Once the rune has been invoked, it can''t be invoked again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (410, 0, 'string', 'While wearing this armor, you gain a +1 bonus to AC, and you can understand and speak Abyssal. In addition, the armor''s clawed gauntlets turn unarmed strikes with your hands into magic weapons that deal slashing damage, with a +1 bonus to attack and damage rolls and a damage die of 1d8.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (410, 1, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["Once you don this cursed armor, you can''t doff it unless you are targeted by the {@spell remove curse} spell or similar magic. While wearing the armor, you have disadvantage on attack rolls against demons and on saving throws against their spells and special abilities."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (411, 0, 'string', 'An expansive treatise documenting the Abyss''s infinite layers and inhabitants, the Demonomicon of Iggwilv is the most thorough and blasphemous tome of demonology in the multiverse. The tome recounts both the oldest and most current profanities of the Abyss and demons. Demons have attempted to censor the text, and while sections have been ripped from the book''s spine, the general chapters remain, ever revealing demonic secrets. And the book holds more than blasphemies. Caged behind lines of script roils a secret piece of the Abyss itself, which keeps the book up-to-date, no matter how many pages are removed, and it longs to be more than mere reference material.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (411, 1, 'entries', NULL, '{"type":"entries","name":"Random Properties","entries":["The artifact has the following random properties, which you can determine by rolling on the tables in the \"Artifacts\" section of the {@book Dungeon Master''s Guide|DMG}:",{"type":"list","items":["2 {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} properties","1 {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental} property","1 {@table Artifact Properties; Major Detrimental Properties|dmg|major detrimental} property"]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (411, 2, 'entries', NULL, '{"type":"entries","name":"Spells","entries":["The book has 8 charges. It regains {@dice 1d8} expended charges daily at dawn. While holding it, you can use an action to cast {@spell Tasha''s hideous laughter} from it or to expend 1 or more of its charges to cast one of the following spells (save {@dc 20}) from it: {@spell magic circle} (1 charge), {@spell magic jar} (3 charges), {@spell planar ally} (3 charges), {@spell planar binding} (2 charges), {@spell plane shift} (to layers of the Abyss only; 3 charges), {@spell summon fiend|TCE} (3 charges)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (411, 3, 'entries', NULL, '{"type":"entries","name":"Abyssal Reference","entries":["You can reference the {@i Demonomicon} whenever you make an Intelligence check to discern information about demons or a Wisdom ({@skill Survival}) check related to the Abyss. When you do so, you can add double your proficiency bonus to the check."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (411, 4, 'entries', NULL, '{"type":"entries","name":"Fiendish Scourging","entries":["Your magic causes pain to fiends. While carrying the book, when you make a damage roll for a spell you cast against a fiend, you use the maximum possible result instead of rolling."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (411, 5, 'entries', NULL, '{"type":"entries","name":"Ensnarement","entries":["While carrying the book, whenever you cast the {@spell magic circle} spell naming only fiends, or the {@spell planar binding} spell targeting a fiend, the spell is cast at 9th level, regardless of what level spell slot you used, if any. Additionally, the fiend has disadvantage on its saving throw against the spell."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (411, 6, 'entries', NULL, '{"type":"entries","name":"Containment","entries":["The first 10 pages of the {@i Demonomicon} are blank. As an action while holding the book, you can target a fiend that you can see that is trapped within a {@spell magic circle}. The fiend must succeed on a {@dc 20} Charisma saving throw with disadvantage or become trapped within one of the {@i Demonomicon''s} empty blank pages, which fills with writing detailing the trapped creature''s widely known name and depravities. Once used, this action can''t be used again until the next dawn.","When you finish a long rest, if you and the {@i Demonomicon} are on the same plane of existence, the trapped creature of the highest challenge rating within the book can attempt to possess you. You must make a {@dc 20} Charisma saving throw. On a failure, you are possessed by the creature, which controls you like a puppet. The possessing creature can release you as an action, appearing in the closest unoccupied space. On a successful save, the fiend can''t try to possess you again for 7 days.","When the tome is discovered, it has {@dice 1d4} fiends occupying its pages, typically an assortment of demons."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (411, 7, 'entries', NULL, '{"type":"entries","name":"Destroying the Demonomicon","entries":["To destroy the book, six different demon lords must each tear out a sixth of the book''s pages. If this occurs, the pages reappear after 24 hours. Before all those hours pass, anyone who opens the book''s remaining binding is transported to a nascent layer of the Abyss that lies hidden within the book. At the heart of this deadly, semi-sentient domain lies a long-lost artifact, {@i Fraz-Urb''luu''s Staff}. If the staff is dragged from the pocket plane, the tome is reduced to a mundane and quite out-of-date copy of the {@i Tome of Zyx}, the work that served as the foundation for the {@i Demonomicon}. Once the staff emerges, the demon lord Fraz-Urb''luu instantly knows."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (412, 0, 'string', 'A devastation orb is an elemental bomb that can be created at the site of an elemental node by performing a ritual with an elemental weapon. The type of orb created depends on the node used. For example, an air node creates a devastation orb of air. The ritual takes 1 hour to complete and requires 2,000 gp worth of special components, which are consumed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (412, 1, 'string', 'A devastation orb measures 12 inches in diameter, weighs 10 pounds, and has a solid outer shell. The orb detonates 1d100 hours after its creation, releasing the elemental energy it contains. The orb gives no outward sign of how much time remains before it will detonate. Spells such as identify and divination can be used to ascertain when the orb will explode. An orb has AC 10, 15 hit points, and immunity to poison and psychic damage. Reducing it to 0 hit points causes it to explode instantly.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (412, 2, 'string', 'A special container can be crafted to contain a devastation orb and prevent it from detonating. The container must be inscribed with symbols of the orb''s opposing element. For example, a case inscribed with earth symbols can be used to contain a devastation orb of air and keep it from detonating. While in the container, the orb thrums. If it is removed from the container after the time when it was supposed to detonate, it explodes 1d6 rounds later, unless it is returned to the container.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (412, 3, 'string', 'Regardless of the type of orb, its effect is contained within a sphere with a 1 mile radius. The orb is the sphere''s point of origin. The orb is destroyed after one use.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (412, 4, 'entries', NULL, '{"name":"Air Orb","type":"entries","entries":["When this orb detonates, it creates a powerful windstorm that lasts for 1 hour. Whenever a creature ends its turn exposed to the wind, the creature must succeed on a {@dc 18} Constitution saving throw or take {@damage 1d4} bludgeoning damage, as the wind and debris batter it. The wind is strong enough to uproot weak trees and destroy light structures after at least 10 minutes of exposure. Otherwise, the rules for {@hazard strong wind} apply, as detailed in chapter 5 of the Dungeon Master''s Guide."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (413, 0, 'string', 'A devastation orb is an elemental bomb that can be created at the site of an elemental node by performing a ritual with an elemental weapon. The type of orb created depends on the node used. For example, an air node creates a devastation orb of air. The ritual takes 1 hour to complete and requires 2,000 gp worth of special components, which are consumed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (413, 1, 'string', 'A devastation orb measures 12 inches in diameter, weighs 10 pounds, and has a solid outer shell. The orb detonates 1d100 hours after its creation, releasing the elemental energy it contains. The orb gives no outward sign of how much time remains before it will detonate. Spells such as identify and divination can be used to ascertain when the orb will explode. An orb has AC 10, 15 hit points, and immunity to poison and psychic damage. Reducing it to 0 hit points causes it to explode instantly.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (413, 2, 'string', 'A special container can be crafted to contain a devastation orb and prevent it from detonating. The container must be inscribed with symbols of the orb''s opposing element. For example, a case inscribed with earth symbols can be used to contain a devastation orb of air and keep it from detonating. While in the container, the orb thrums. If it is removed from the container after the time when it was supposed to detonate, it explodes 1d6 rounds later, unless it is returned to the container.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (413, 3, 'string', 'Regardless of the type of orb, its effect is contained within a sphere with a 1 mile radius. The orb is the sphere''s point of origin. The orb is destroyed after one use.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (413, 4, 'entries', NULL, '{"name":"Earth Orb","type":"entries","entries":["When this orb detonates, it subjects the area to the effects of the {@spell earthquake} spell for 1 minute (spell save {@dc 18}). For the purpose of the spell''s effects, the spell is cast on the turn that the orb explodes."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (414, 0, 'string', 'A devastation orb is an elemental bomb that can be created at the site of an elemental node by performing a ritual with an elemental weapon. The type of orb created depends on the node used. For example, an air node creates a devastation orb of air. The ritual takes 1 hour to complete and requires 2,000 gp worth of special components, which are consumed.', NULL);
