INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (547, 3, 'entries', NULL, '{"type":"entries","name":"{@creature mastiff||Onyx Dog}","entries":["This onyx statuette of a dog can become a {@creature mastiff} for up to 6 hours. The {@creature mastiff} has an Intelligence of 8 and can speak Common. It also has {@sense darkvision} out to a range of 60 feet and can see {@condition invisible} creatures and objects within that range. Once it has been used, it can''t be used again until 7 days have passed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (548, 0, 'string', 'A figurine of wondrous power is a statuette of a beast small enough to fit in a pocket. If you use an action to speak the command word and throw the figurine to a point on the ground within 60 feet of you, the figurine becomes a living creature. If the space where the creature would appear is occupied by other creatures or objects, or if there isn''t enough space for the creature, the figurine doesn''t become a creature.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (548, 1, 'string', 'The creature is friendly to you and your companions. It understands your languages and obeys your spoken commands. If you issue no commands, the creature defends itself but takes no other actions.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (548, 2, 'string', 'The creature exists for a duration specific to each figurine. At the end of the duration, the creature reverts to its figurine form. It reverts to a figurine early if it drops to 0 hit points or if you use an action to speak the command word again while touching it. When the creature becomes a figurine again, its property can''t be used again until a certain amount of time has passed, as specified in the figurine''s description.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (548, 3, 'entries', NULL, '{"type":"entries","name":"{@creature giant owl||Serpentine Owl}","entries":["This serpentine statuette of an owl can become a {@creature giant owl} for up to 8 hours. Once it has been used, it can''t be used again until 2 days have passed. The owl can telepathically communicate with you at any range if you and it are on the same plane of existence."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (549, 0, 'string', 'A figurine of wondrous power is a statuette of a beast small enough to fit in a pocket. If you use an action to speak the command word and throw the figurine to a point on the ground within 60 feet of you, the figurine becomes a living creature. If the space where the creature would appear is occupied by other creatures or objects, or if there isn''t enough space for the creature, the figurine doesn''t become a creature.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (549, 1, 'string', 'The creature is friendly to you and your companions. It understands your languages and obeys your spoken commands. If you issue no commands, the creature defends itself but takes no other actions.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (549, 2, 'string', 'The creature exists for a duration specific to each figurine. At the end of the duration, the creature reverts to its figurine form. It reverts to a figurine early if it drops to 0 hit points or if you use an action to speak the command word again while touching it. When the creature becomes a figurine again, its property can''t be used again until a certain amount of time has passed, as specified in the figurine''s description.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (549, 3, 'entries', NULL, '{"type":"entries","name":"{@creature raven||Silver Raven}","entries":["This silver statuette of a raven can become a raven for up to 12 hours. Once it has been used, it can''t be used again until 2 days have passed. While in raven form, the figurine allows you to cast the {@spell animal messenger} spell on it at will."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (550, 0, 'string', 'The lenses of these garish goggles are carved from Siberys dragonshards. While wearing these lenses, you gain the following benefits:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (550, 1, 'list', NULL, '{"type":"list","items":["When you make a Wisdom ({@skill Insight}) check, you can roll a {@dice d4} and add the number rolled to the check.","As an action, you can use the goggles to examine an object to identify the aura of the last creature that touched it. Make a Wisdom ({@skill Insight}) check against a DC of 13 + the number of days since the last contact occurred. On a success, you learn the creature''s type and can immediately use the goggles to cast {@spell locate creature} to find that creature. This property can''t be used again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (558, 0, 'string', '{#itemEntry Absorbing Tattoo|TCE}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (559, 0, 'string', 'A translucent fiery red gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (560, 0, 'string', 'This bulky suit, which fully encases your head and body, takes 1 minute to don or doff. While worn, it enables you to breathe in an airless environment and renders you immune to the harmful effects of any gas that surrounds you. The suit also grants you a swimming speed equal to your walking speed while underwater, or a flying speed equal to your walking speed in an environment with no gravity.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (561, 0, 'string', 'This kit includes a wooden rod, silken line, corkwood bobbers, steel hooks, lead sinkers, velvet lures, and narrow netting.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (562, 0, 'string', 'This magic flail is made in the image of Tiamat, with five jagged heads shaped like the heads of five different chromatic dragons. You gain a +3 bonus to attack and damage rolls made with this flail. When you hit with an attack roll using it, the target takes an extra 5d4 damage of your choice of one of the following damage types: acid, cold, fire, lightning, or poison.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (562, 1, 'string', 'While holding the flail, you can use an action and speak a command word to cause the heads to breathe multicolored flames in a 90-foot cone. Each creature in that area must make a 18 Dexterity saving throw. On a failed save, it takes 14d6 damage of one of the following damage types (your choice): acid, cold, fire, lightning, or poison. On a successful save, it takes half as much damage. Once this action is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (563, 0, 'string', 'You can use a bonus action to speak this magic sword''s command word, causing flames to erupt from the blade. These flames shed bright light in a 40-foot radius and dim light for an additional 40 feet. While the sword is ablaze, it deals an extra 2d6 fire damage to any target it hits. The flames last until you use a bonus action to speak the command word again or until you drop or sheathe the sword.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (563, 1, 'string', 'While you are attuned to this sword you can use an action to mentally command it to detect gems and jewels. You learn the kind and number of such objects within 60 feet of the sword.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (564, 0, 'string', 'This wrought-iron lantern holds a small fire elemental spirit. To attune to this item, you must also succeed on a 10 Charisma check to bond with the spirit inside the lantern.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (564, 1, 'entries', NULL, '{"type":"entries","name":"Light and Flame","entries":["While attuned to the lantern, you can verbally ask the elemental to glow (no action required), causing the lantern to cast bright light in a radius of your choice up to 60 feet and dim light for the same distance beyond that. You can also cast the {@spell produce flame} cantrip from the lantern at will. Wisdom is your spellcasting ability for this spell."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (564, 2, 'entries', NULL, '{"type":"entries","name":"Elemental Essence","entries":["The fire elemental spirit in the lantern can grant you a small fragment of its power. As an action, you gain one of the following benefits of your choice for 1 hour:",{"type":"list","items":["You have resistance to fire damage.","Whenever a creature hits you with a melee attack, that creature takes fire damage equal to your proficiency bonus.","You can cast the {@spell burning hands} spell (spell save {@dc 15}) from the lantern at 3rd level. Once you cast the spell, you can''t do so again until the next time you use this property of the lantern."]},"Once the elemental has shared its power with you in this way, it can''t do so again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (564, 3, 'entries', NULL, '{"type":"entries","name":"Willful Attunement","entries":["Your {@quickref attunement|DMG|1|0} to the lantern lasts until you choose to end it, or until you anger the elemental (by dousing the lantern in water, attacking another nonhostile elemental being, and so forth). You can attempt to attune to the lantern again and repeat the check to bond with the elemental during a {@quickref resting|PHB|2|0|short or long rest}, but you make this check with {@quickref Advantage and Disadvantage|PHB|2|0|disadvantage} until you have made amends to the spirit, as the GM determines."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (565, 0, 'string', 'You can use this card to summon a devil. The card is attuned to a specific devil with a challenge rating of 8 or lower. As an action, you can hold this card aloft and summon that devil, which appears in an unoccupied space you can see within 30 feet of you. The devil is initially unfriendly toward you and your companions. Roll initiative for the devil, which has its own turns. The DM has the creature''s stat block. On each of your turns, you can issue a verbal command to the devil (no action required by you), and as long as you are holding this card, the devil obeys your commands. Otherwise, it is under the DM''s control and acts according to its natureit might attack you if it thinks it can prevail or try to tempt you to undertake an evil act in exchange for further service. After 1 minute or when the devil''s hit points drop to 0, the devil returns to the Lower Planes. Once this property is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (566, 0, 'string', 'A flask holds 1 pint of liquid.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (567, 0, 'string', 'Carvings of decapitated mind flayers adorn this greataxe''s metal blade. You gain a +1 bonus to attack and damage rolls made with this greataxe.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (567, 1, 'string', 'An Aberration hit with this greataxe takes an extra 1d12 slashing damage. If the Aberration is currently grappling a creature, the Aberration must succeed on a 15 Strength saving throw or release each creature it is grappling.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (568, 0, 'string', 'Flensing claws take the form of articulated digits that extend into long metal blades. The claws are knitted into the flesh and bones of a creature''s arms and can''t be removed without surgical amputation.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (568, 1, 'string', 'Each set of flensing claws is designed for a specific creature and can''t be used by anyone else. A creature equipped with flensing claws can use its action to make one melee weapon attack with the claws. The creature adds its proficiency bonus and Strength modifier to any attack roll made with the claws, and its Strength modifier to its damage roll when it hits a target with the claws.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (569, 0, 'string', 'Flensing claws take the form of articulated digits that extend into long metal blades. The claws are knitted into the flesh and bones of a creature''s arms and can''t be removed without surgical amputation.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (569, 1, 'string', 'Each set of flensing claws is designed for a specific creature and can''t be used by anyone else. A creature equipped with flensing claws can use its action to make one melee weapon attack with the claws. The creature adds its proficiency bonus and Strength modifier to any attack roll made with the claws, and its Strength modifier to its damage roll when it hits a target with the claws.', NULL);
