INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (720, 0, 'string', 'Before you finish making a character, roll once on the Horror Trinkets table for a unique object your character has with them at the start of their adventuring career. These trinkets hint toward various horrific tales and might lead to dreadful revelations or be nothing more than grim keepsakes. At the DM''s discretion any of these trinkets might also be a Mist talisman (detailed in chapter 3), a focal item that can lead the bearer through the Mists to a particular Domain of Dread.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (720, 1, 'table', NULL, '{"type":"table","caption":"Horror Trinkets","colLabels":["d100","Trinket"],"colStyles":["col-2 text-center","col-10"],"rows":[["01","A picture you drew as a child of your imaginary friend"],["02","A lock that opens when blood is dripped in its keyhole"],["03","Clothes stolen from a scarecrow"],["04","A spinning top carved with four faces: happy, sad, wrathful, and dead"],["05","The necklace of a sibling who died on the day you were born"],["06","A wig from someone executed by beheading"],["07","The unopened letter to you from your dying father"],["08","A pocket watch that runs backward for an hour every midnight"],["09","A winter coat stolen from a dying soldier"],["10","A bottle of invisible ink that can only be read at sunset"],["11","A wineskin that refills when interred with a dead person for a night"],["12","A set of silverware used by a king for his last meal"],["13","A spyglass that always shows the world suffering a terrible storm"],["14","A cameo with the profile''s face scratched away"],["15","A lantern with a black candle that never runs out and that burns with green flame"],["16","A teacup from a child''s tea set, stained with blood"],["17","A little black book that records your dreams, and yours alone, when you sleep"],["18","A necklace formed of the interlinked holy symbols of a dozen deities"],["19","A noose that feels heavier than it should"],["20","A birdcage into which small birds fly but once inside never eat or leave"],["21","A lepidopterist''s box filled with dead moths with skull-like patterns on their wings"],["22","A jar of pickled ghouls'' tongues"],["23","The wooden hand of a notorious pirate"],["24","A urn with the ashes of a dead relative"],["25","A hand mirror backed with a bronze depiction of a medusa"],["26","Pallid leather gloves crafted with ivory fingernails"],["27","Dice made from the knuckles of a notorious charlatan"],["28","A rin