INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (757, 4, 'string', 'In addition, the Ollamh harp can be used to cast confusion, control weather, and fire storm.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (758, 0, 'string', 'An Ioun stone is named after Ioun, a god of knowledge and prophecy revered on some worlds. Many types of Ioun stone exist, each type a distinct combination of shape and color.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (758, 1, 'string', 'When you use an action to toss one of these stones into the air, the stone orbits your head at a distance of 1d3 feet and confers a benefit to you. Thereafter, another creature must use an action to grasp or net the stone to separate it from you, either by making a successful attack roll against AC 24 or a successful 24 Dexterity (Acrobatics) check. You can use an action to seize and stow the stone, ending its effect.', NULL);
