INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (781, 0, 'string', 'This rusty iron sphere measures 3 inches in diameter and weighs 1 pound. You can use an action to speak the command word and throw the sphere at a Huge or smaller creature you can see within 60 feet of you. As the sphere moves through the air, it opens into a tangle of metal bands.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (781, 1, 'string', 'Make a ranged attack roll with an attack bonus equal to your Dexterity modifier plus your proficiency bonus. On a hit, the target is restrained until you take a bonus action to speak the command word again to release it. Doing so, or missing with the attack, causes the bands to contract and become a sphere once more.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (781, 2, 'string', 'A creature, including the one restrained, can use an action to make a 20 Strength check to break the iron bands. On a success, the item is destroyed, and the restrained creature is freed. If the check fails, any further attempts made by that creature automatically fail until 24 hours have elapsed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (781, 3, 'string', 'Once the bands are used, they can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (782, 0, 'string', 'This iron bottle has a brass stopper. You can use an action to speak the flask''s command word, targeting a creature that you can see within 60 feet of you. If the target is native to a plane of existence other than the one you''re on, the target must succeed on a 17 Wisdom saving throw or be trapped in the flask. If the target has been trapped by the flask before, it has advantage on the saving throw. Once trapped, a creature remains in the flask until released. The flask can hold only one creature at a time. A creature trapped in the flask doesn''t need to breathe, eat, or drink and doesn''t age.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (782, 1, 'string', 'You can use an action to remove the flask''s stopper and release the creature the flask contains. The creature is friendly to you and your companions for 1 hour and obeys your commands for that duration. If you give no commands or give it a command that is likely to result in its death, it defends itself but otherwise takes no actions. At the end of the duration, the creature acts in accordance with its normal disposition and alignment.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (782, 2, 'string', 'An identify spell reveals that a creature is inside the flask, but the only way to determine the type of creature is to open the flask. A newly discovered bottle might already contain a creature chosen by the DM or determined randomly.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (782, 3, 'table', NULL, '{"type":"table","colLabels":["{@dice d100}","Contents"],"colStyles":["col-1 text-center","col-11"],"rows":[["01-50","Empty"],["51","{@creature Arcanaloth}"],["52","{@creature Cambion}"],["53-54","{@creature Dao}"],["55-57","Demon (type 1): {@creature barlgura}, {@creature shadow demon}, or {@creature vrock}"],["58-60","Demon (type 2): {@creature chasme} or {@creature hezrou}"],["61-62","Demon (type 3): {@creature glabrezu} or {@creature yochlol}"],["63-64","Demon (type 4): {@creature nalfeshnee}"],["65","Demon (type 5): {@creature marilith}"],["66","Demon (type 6): {@creature balor} or {@creature goristro}"],["67","{@creature Deva}"],["68-69","Devil (greater): {@creature horned devil}, {@creature erinyes}, {@creature ice devil}, or {@creature pit fiend}"],["70-72","Devil (lesser): {@creature imp}, {@creature spined devil}, {@creature bearded devil}, {@creature barbed devil}, {@creature chain devil}, or {@creature bone devil}"],["73-74","{@creature Djinni}"],["75-76","{@creature Efreeti}"],["77-78","Elemental (any)"],["79","{@creature Githyanki knight}"],["80","{@creature Githzerai zerth}"],["81-82","{@creature Invisible stalker}"],["83-84","{@creature Marid}"],["85-86","{@creature Mezzoloth}"],["87-88","{@creature Night hag}"],["89-90","{@creature Nycaloth}"],["91","{@creature Planetar}"],["92-93","{@creature Salamander}"],["94-95","Slaad (any)"],["96","{@creature Solar}"],["97-98","{@creature succubus||Succubus/Incubus}"],["99","{@creature Ultroloth}"],["00","{@creature Xorn}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (783, 0, 'string', 'An iron pot holds 1 gallon of liquid.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (786, 0, 'string', 'A war pick forged from a single piece of iron, Ironfang has a fang-like head inscribed with ancient runes. The pick is heavy in the hand, but when the wielder swings the pick in anger, the weapon seems almost weightless. This weapon is immune to any form of rust, acid, or corrosionnothing seems to mark it. Ironfang contains a spark of Ogrmoch, the Prince of Evil Earth.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (786, 1, 'string', 'You gain a +2 bonus to attack and damage rolls made with this magic weapon. When you hit with it, the target takes an extra 1d8 thunder damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (786, 2, 'entries', NULL, '{"type":"entries","name":"Earth Mastery","entries":["You gain the following benefits while you hold Ironfang:",{"type":"list","items":["You can speak Terran fluently.","You have resistance to acid damage.","You have {@sense tremorsense|MM} out to a range of 60 feet.","You can sense the presence of precious metals and stones within 60 feet of you, but not their exact location.","You can cast {@spell dominate monster} (save {@dc 17}) on an {@creature earth elemental}. Once you have done so, Ironfang can''t be used this way again until the next dawn."]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (786, 3, 'entries', NULL, '{"type":"entries","name":"Shatter","entries":["Ironfang has 3 charges. You can use your action to expend 1 charge and cast the 2nd-level version of {@spell shatter} ({@dc 17}). Ironfang regains {@dice 1d3} expended charges daily at dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (786, 4, 'entries', NULL, '{"type":"entries","name":"The Rumbling","entries":["While inside an earth node, you can perform a ritual called the Rumbling, using Ironfang to create a {@item devastation orb of earth|PotA}. Once you perform the ritual, Ironfang can''t be used to perform the ritual again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (786, 5, 'entries', NULL, '{"type":"entries","name":"Flaw","entries":["Ironfang heightens its wielder''s destructive nature. While attuned to the weapon, you gain the following flaw: \"I like to break things and cause ruin.\""]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (787, 0, 'string', 'This poison bears a distinct scent and chemical message from Ivana Boritsi. A creature subjected to this poison must succeed on a 18 Constitution saving throw or experience the effects of a dream spell created by Ivana the next time they sleep. This poison is nonmagical, and Ivana doesn''t directly communicate with those affected during the dream. Rather, she creates the illusion of speaking with her intended target by alchemically crafting her message, predicting her target''s reactions, and chemically encoding in her responses. She wears this poison as a perfume or hides it within gift bouquets, allowing it to convey her message later.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (788, 0, 'string', 'This ring, adorned with the symbol of Izzet, allows you to cast chaos bolt. A guild signet is sometimes awarded to a guild member whose renown score in that guild is 5 or higher, as a reward for performing special services for the guild. Aside from its magical properties, the ring is also an indicator of Izzet''s recognition and favor.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (788, 1, 'string', 'A signet has 3 charges, and it regains 1d3 expended charges daily at dawn. While wearing it, you can expend 1 charge to cast the associated spell (save 13).', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (789, 0, 'string', 'Formed of carved and polished red and blue stone, the keyrune includes bits of cable and wire. One end resembles a humanlike head, suggesting the jagged elemental form of the galvanice weird that it can become for a duration of 3 hours. In this form, it will serve you as a bodyguard, lift and carry things for you, act as a test subject for your experiments, or aid you in any other way that its capabilities allow.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (789, 1, 'string', 'When you use an action to speak the item''s command word and place the keyrune on the ground in an unoccupied space within 5 feet of you, the keyrune transforms into a galvanice weird. If there isn''t enough space for the creature, the keyrune doesn''t transform.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (789, 2, 'string', 'The creature is friendly to you, your companions, and other members of your guild (unless those guild members are hostile to you). It understands your languages and obeys your spoken commands. If you issue no commands, the creature takes the Dodge action and moves to avoid danger.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (789, 3, 'string', 'At the end of the duration, the creature reverts to its keyrune form. It reverts early if it drops to 0 hit points or if you use an action to speak the command word again while touching it. When the creature reverts to its keyrune form, it can''t transform again until 36 hours have passed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (790, 0, 'string', 'A transparent fiery orange gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (791, 0, 'string', 'A translucent light green, deep green, or white gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (796, 0, 'string', 'This staff is broken into five pieces, each worth 100 gp for the jade alone. The entire staff can be restored with mending cantrips; each casting of the spell repairs one break in the staff. If the staff is made whole, it transforms from an object into an animated jade serpent that has the statistics of a giant poisonous snake, with these changes:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (796, 1, 'list', NULL, '{"type":"list","items":["The serpent is a construct that understands and obeys whoever was holding the staff when it transformed.","It has immunity to poison damage and the {@condition poisoned} condition. It doesn''t require air, food, drink, or sleep.","When it drops to 0 hit points, roll a {@dice d6}. On a roll of 1, the snake turns to dust and is destroyed. On any other roll, it changes back into a staff and breaks into {@dice 1d4 + 1} pieces that must be magically mended before the staff can be used again."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (800, 0, 'string', 'An opaque blue, black, or brown gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (801, 0, 'string', 'You gain a +2 bonus to attack and damage rolls made with this magic weapon. When you throw it, its normal and long ranges both increase by 30 feet, and it deals one extra die of damage on a hit. After you throw it and it hits or misses, it flies back to your hand immediately.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (801, 1, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["This weapon is cursed, and becoming attuned to it extends the curse to you. Until the curse is broken with {@spell remove curse} or similar magic, you are unwilling to part with the weapon, keeping it within reach at all times. In addition, you have disadvantage on attack rolls made with weapons other than this one."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (801, 2, 'string', 'Whenever you roll a 1 on an attack roll using this weapon, the weapon bends or flies to hit you in the back. Make a new attack roll with advantage against your own AC. If the result is a hit, you take damage as if you had attacked yourself with the javelin.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (802, 0, 'string', 'This javelin is a magic weapon. When you hurl it and speak its command word, it transforms into a bolt of lightning, forming a line 5 feet wide that extends out from you to a target within 120 feet. Each creature in the line excluding you and the target must make a 13 Dexterity saving throw, taking 4d6 lightning damage on a failed save, and half as much damage on a successful one. The lightning bolt turns back into a javelin when it reaches the target. Make a ranged weapon attack against the target. On a hit, the target takes damage from the javelin plus 4d6 lightning damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (802, 1, 'string', 'The javelin''s property can''t be used again until the next dawn. In the meantime, the javelin can still be used as a magic weapon.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (803, 0, 'string', 'As an action, you can brandish this card and use it to cast the Otto''s Irresistible Dance spell (save 17), and you have advantage on Constitution saving throws to maintain concentration on it. Once this property is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (804, 0, 'string', 'This colorful, harlequin domino mask is edged with pearls. While wearing this mask, you gain the following benefits:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (804, 1, 'list', NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Charismatic Focus","entries":["You can use the mask as a spellcasting focus. You gain a +3 bonus to any spell attack rolls and spell saving throw DCs that use Charisma as the spellcasting ability."]},{"type":"item","name":"Marvelous Escape","entries":["When a creature hits you with an attack roll, you can use your reaction to disappear in a puff of smoke and colorful sparkles. You take no damage and instead teleport, along with anything you are wearing or carrying, to an unoccupied space you can see within 30 feet of yourself. Once this reaction is used, it can''t be used again until the next dawn."]},{"type":"item","name":"Topsy-Turvy","entries":["When you roll a 1 on a {@dice d20}, you can treat the roll as if you rolled a 20 instead. Once this property is used, it can''t be used again until the next dawn."]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (805, 0, 'string', 'An opaque deep black gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (807, 0, 'string', 'The Jewel of Three Prayers is a Vestige of Divergence (see the "Vestiges of Divergence" sidebar). In ancient times, Alyxian the Apotheon bore this amulet as a symbol of his covenant with three Prime Deities: Sehanine the Moon Weaver, Avandra the Change Bringer, and Corellon the Arch Heart. When the jewel is found, only Sehanine''s power thrums within its dormant heart. The power of the other two deities waits to be reawakened by a heroor heroeswho can follow in Alyxian''s footsteps.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (807, 1, 'entries', NULL, '{"type":"entries","name":"Dormant State","entries":["In this state, the Jewel of Three Prayers is a glittering golden disk attached to a fine golden chain. The chain magically resizes to function as a necklace for the creature that wears it.","In its Dormant State, the jewel has the following properties:",{"type":"list","items":["You gain a +1 bonus to AC while wearing the jewel.","While wearing or holding the jewel, you can use an action to cause it to shed bright light in a 15-foot radius and dim light for an additional 15 feet. The light lasts until you extinguish it (no action required).","The jewel has 3 charges and regains all its expended charges daily at dawn. While holding the jewel, you can expend 1 charge from it to cast the {@spell invisibility} spell."]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (814, 0, 'string', 'A jug holds 1 gallon of liquid.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (815, 0, 'string', 'You have a +1 bonus to attack and damage rolls made with this dagger, which looks like junk. When it is used, its grip frays, its blade chips, and it flakes rust. If you get a natural 1 on an attack roll while wielding this weapon, it breaks and becomes nonmagical.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (816, 0, 'string', 'This cloak appears to be made of autumnal leaves woven together. While you wear this cloak, you have advantage on Dexterity (Stealth) checks, and you can use a bonus action to magically teleport, along with any equipment you are wearing or carrying, to an unoccupied space you can see within 30 feet of yourself. You then have advantage on the next attack roll you make before the end of the turn. Once this bonus action is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (817, 0, 'string', 'Keelboats and rowboats are used on lakes and rivers. If going downstream, add the speed of the current (typically 3 miles per hour) to the speed of the vehicle. These vehicles can''t be rowed against any significant current, but they can be pulled upstream by draft animals on the shores.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (818, 0, 'string', 'A keg of alchemist''s fire lobbed at a creature or an object deals 21 (6d6) fire damage on a hit. Further, the target is set ablaze and takes the damage again every round (in the case of a creature, at the start of each of its turns) until the fire is put out. A creature within reach of the blaze can take an action to smother the flames using a blanket or carpet, reducing the fire damage by 2d6. Three such actions are needed to fully put out the fire.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (819, 0, 'string', 'This glass jar, 3 inches in diameter, contains 1d4 + 1 doses of a thick mixture that smells faintly of aloe. The jar and its contents weigh  pound.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (819, 1, 'string', 'As an action, one dose of the ointment can be swallowed or applied to the skin. The creature that receives it regains 2d8 + 2 hit points, ceases to be poisoned, and is cured of any disease.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (820, 0, 'string', 'As an action, you can brandish this card and use it to cast the Knock spell, with no sound made by the spell. This property can be used five times, and it regains all expended uses at dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (821, 0, 'string', 'This small stylized key plays a vital role in the work of House Kundarak. If you cast the alarm, arcane lock, or glyph of warding spell, you can tie the effect to the keycharm so that whoever holds it receives the notification from the alarm spell, bypasses the lock of the arcane lock spell, or avoids triggering the glyph placed by the glyph of warding spell. In addition, the holder (who needn''t be attuned to the item) can take an action to end any one spell tied to it, provided the holder knows the command word you set for ending the tied spells. The keycharm can have up to three tied spells at one time.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (822, 0, 'string', 'A keystone of creation is a piece of the Stone of Creation, usually between 6 to 10 inches on a side, and can be rough-hewn or cleanly cut and polished by a mason attuned to the Stone of Creation.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (822, 1, 'entries', NULL, '{"type":"entries","name":"Random Properties","entries":["A keystone of creation has the following random properties:",{"type":"list","items":["1 {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} property","1 {@table Beneficial Manifestations|AitFR-AVT|beneficial} manifestation","1 {@table Detrimental Manifestations|AitFR-AVT|detrimental} manifestation"]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (822, 2, 'entries', NULL, '{"type":"entries","name":"Extradimensional Places","entries":["You can use the keystone to conjure an extradimensional space such as {@spell Mordenkainen''s magnificent mansion}. The easiest way is to simply cast the spell using the artifact''s power, which requires no spell slots or material components. Each time you cast a spell with the artifact, the subsequent casting replaces the prior one. For more detailed control of a conjured demiplane or extradimensional space, see \"Construction Details.\""]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (822, 3, 'entries', NULL, '{"type":"entries","name":"Raising Structures","entries":["You can use the keystone to conjure a physical structure on the Material Plane. The easiest way is to simply cast the {@spell mighty fortress|XGE} spell using the artifact''s power, which requires no spell slots or material components. Each time you cast a spell with the artifact, the subsequent casting replaces the prior one. For more detailed control of a conjured demiplane or extradimensional space, see \"Construction Details.\" Locations conjured by keystones cannot become permanent through repeated spell castings."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (822, 4, 'entries', NULL, '{"type":"entries","name":"Connected Spaces","entries":["You can use multiple keystones to create larger spaces, whether adjacent to each other or even interlinked. You can even combine structures on the Material Plane and extradimensional spaces this way. If you are attuned to one keystone when you attune to a second keystone, the keystones combine to become a single artifact (utilizing a single attunement slot), combining their features and their fates forevermore. You cannot attune to, and thus combine, more than three keystones at once."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (822, 5, 'entries', NULL, '{"type":"entries","name":"Destroying a Keystone","entries":["A keystone is an artifact in its own right and impervious to most damage, but a {@spell disintegrate} spell is sufficient to destroy an unattuned keystone.","After it has been attuned at least once, a given keystone requires attunement to maintain its magical power. Otherwise, its creations on the Material Plane gradually rot and decay over {@dice 1d12 + 1} days, and a demiplane relying on the keystone collapses over {@dice 1d20 + 10} minutes. Additional destructive forces like fire may hasten the destruction. A demiplane created or upheld by a keystone ejects all creatures within it to their home planes when it collapses.","When a keystone''s creation is fully destroyed, the keystone becomes an inert, powerless rock. If the {@item Stone of Creation|AitFR-AVT} is destroyed, all its keystones break attunement, lose all magical properties, and their creations decay or collapse to nothing.","If it reaches the Elemental Chaos or is struck by a forked, metal rod attuned to the Elemental Chaos (as per the {@spell plane shift} spell), a keystone is destroyed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (822, 6, 'section', NULL, '{"type":"section","entries":[{"type":"entries","name":"Construction Details","entries":["You can customize a keystone''s creation with cosmetic details you can imagine. You can also rearrange the floorplans and structural details of your creations as follows.",{"type":"entries","entries":[{"type":"entries","name":"Demiplane Design","entries":["With a single keystone, you can conjure an extradimensional space in the shape of a dome or pyramid, up to 1 mile on each side, with whatever terrain you wish. The edges of this space are solid and impassable but may be {@condition invisible}, so that your space can appear to go on forever if you wish. With additional keystones, you can add an additional square mile to your creation, adding or combining terrain types.","Each square mile can include a single structure up to the size of {@spell Mordenkainen''s magnificent mansion}, with all the attendant servants and feasts, or any number of smaller structures that together equal a magnificent mansion in total area.","You cannot create flora or fauna that deal damage or otherwise harm creatures. If you create cliffs, waters, or other natural features, however, creatures in the space might fall, drown, or otherwise be harmed."]},{"type":"entries","name":"Physical Structure Design","entries":["With a single keystone, you can create a structure akin to a mighty fortress with the added flexibility to customize that fortress not only for cosmetic details but for substantive effects. With a single keystone, you can rearrange the walls, open spaces, floors, doors, and other features of the mighty fortress provided you do not exceed 150 10-foot-wide cubes of interior space. Your fortress can reach across a creek, river, or other waters. It can take reckless or even dangerous shapes to include deep pits, flooded chambers, and so on. Your design, once conjured, is not guaranteed to be capable of supporting its own weight or surviving the stresses of attacks, weather, or time.","You may post any number of your structure''s servants as guards, if you wish. They have statistics identical to an unseen servant with the addition of a passive {@skill Perception} score equal to your own, though you cannot perceive through their senses. The servants can ring bells or otherwise manipulate the environment to raise an alarm."]},{"type":"entries","name":"Manifestations","entries":["Each place or structure conjured by a keystone of creation has special, unforeseen manifestations that affect its makeup and appearance. Some manifestations are beneficial, some are detrimental. These manifestations change each time a keystone is used to conjure a structure or place. The DM may roll or choose from the following tables, or devise unique manifestations based on the specific creations being conjured."]}]}]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (823, 0, 'string', 'Heliod wields Khrusor, a spear blessed with the power of the sun. In his hands, it can be pitched from Nyx to any point on Theros. When Heliod lends his weapon to a mortal, it''s typically so they can perform some great work in his name or as a test of their worthiness.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (823, 1, 'entries', NULL, '{"type":"entries","name":"Spear of the Sun","entries":["This spear grants a +3 bonus to attack and damage rolls made with it. When you hit with an attack using this spear, the target takes an extra {@damage 2d8} radiant damage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (823, 2, 'entries', NULL, '{"type":"entries","name":"Blessing of the Sun","entries":["If you are a worshiper of Heliod, you gain all the following benefits for which you have the required piety:",{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Piety 3+","entry":"You gain 15 temporary hit points each dawn."},{"type":"item","name":"Piety 10+","entry":"The spear has 1 randomly determined {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} property."},{"type":"item","name":"Piety 25+","entry":"The spear has 1 additional randomly determined {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} property."},{"type":"item","name":"Piety 50+","entry":"The spear has 1 randomly determined {@table Artifact Properties; Major Beneficial Properties|dmg|major beneficial} property."}]},"If you aren''t a worshiper of Heliod, the spear has 2 randomly determined {@table Artifact Properties; Major Detrimental Properties|dmg|major detrimental} properties.","See \"Artifacts\" in chapter 7 of the Dungeon Master''s Guide for details on randomly determined properties."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (823, 3, 'entries', NULL, '{"type":"entries","name":"Luminous","entries":["The spear sheds bright light in a 30-foot radius and dim light for an additional 30 feet. The light is sunlight."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (823, 4, 'entries', NULL, '{"type":"entries","name":"Sun''s Retaliation","entries":["When you take damage from a creature within 5 feet of you, you can use your reaction to make a melee attack with the spear against that creature. On a hit, the spear deals damage as normal, and the creature is {@condition blinded} until the start of its next turn. This property of the spear can''t be used again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (823, 5, 'entries', NULL, '{"type":"entries","name":"Spells","entries":["The spear has 10 charges. While holding it, you can use an action to expend 1 or more of its charges to cast one of the following spells (save {@dc 18}) from it: {@spell guiding bolt} (1 charge), {@spell daylight} (3 charges, targeting the tip of the spear only), {@spell sunbeam} (6 charges). The spear regains {@dice 1d6 + 4} expended charges daily at dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (823, 6, 'entries', NULL, '{"type":"entries","name":"Destroying the Spear","entries":["If taken to Erebos''s palace in Tizerus, and used to sacrifice a champion of Heliod to Erebos, Khrusor is either destroyed or fundamentally twisted to Erebos''s service."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (824, 0, 'table', NULL, '{"caption":"Trinkets from Khyber","colLabels":["d10","Trinket"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","A pressed flower with vivid green petals; when you smell it, you hear eerie music"],["2","A tiny ball of putty; if you set it down, it begins to slowly crawl around"],["3","A perpetually warm disk of dark iron"],["4","A small journal with leathery pages; any words you write in it slowly disappear"],["5","A four-sided die carved with strange markings"],["6","A cameo with the silhouette of an unknown species"],["7","A preserved finger with purple flesh and four joints"],["8","A perfectly preserved eye; if you set it down, it rotates to follow your movement"],["9","A small box; when opened, you alone hear screaming"],["10","A preserved insect; you''ve never seen another like it"]],"type":"table"}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (826, 0, 'string', 'A beautiful, glittering emerald is set within this silver chain, humming with power and possibility. It is said that this amulet was stolen from the neck of The Changebringer herself by the The Lord of the Hells, and locked within the vaults of Bazzoxan in Wildemount. It was later claimed there by a group of Aurora Watch soldiers during an expedition into that grim fortress''s abandoned temples.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (826, 1, 'entries', NULL, '{"type":"entries","name":"Dormant","entries":["While the Kiss of the Changebringer is in a dormant state, you have a +1 bonus to {@quickref saving throws|PHB|2|1} and are immune to the {@condition grappled} condition while wearing it."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (828, 0, 'string', 'While wearing this eye patch, you gain these benefits:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (828, 1, 'list', NULL, '{"type":"list","items":["You have advantage on Wisdom ({@skill Perception}) checks that rely on sight.","If you have the Sunlight Sensitivity trait, you are unaffected by the trait.","You are immune to magic that allows other creatures to read your thoughts or determine whether you are lying. Creatures can communicate telepathically with you only if you allow it."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (829, 0, 'string', 'Using an action, you carve a single infernal rune into the flesh of an unconscious Beast, Celestial, Dragon, Fey, or Giant with this knife. Over the next 10 minutes the creature dies an agonizing death that can''t be prevented short of the Wish spell. If the creature has any resistances or immunities, you gain those resistances and immunities until the creature dies or a Wish spell is used to save the creature. The knife''s power can''t be used again until you finish a long rest.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (830, 0, 'string', 'As an action, you can throw this card to an unoccupied space on the ground within 60 feet of yourself while speaking a command word, whereupon the card magically transforms into a deck defender (see chapter 9 for its stat block) for up to 1 minute or until you use an action to speak the command word again, whereupon it transforms back into a card. The deck defender obeys your spoken commands. If you issue no commands, it takes the Dodge action and moves to avoid danger. The deck defender reverts to a card early if it drops to 0 hit points. Once this property is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (831, 0, 'string', 'The Korolnor Scepter is one of ten Ruling Scepters of Shanatar, forged by the dwarven gods and given to the ruling houses of the ancient dwarven empire. The Korolnor Scepter''s location was unknown for the longest time until a storm giant queen, Neri, found it in a barnacle-covered shipwreck at the bottom of the Trackless Sea. The Ruling Scepters are all roughly the same size and shape, but their materials and properties vary.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (831, 1, 'string', 'The Korolnor Scepter is a tapered mithral rod as thick and long as a dwarf''s forearm, with a small platinum knob at the bottom and a rounded disk adorned with a ring of seven tiny blue gems at the top.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (831, 2, 'string', 'You gain a +3 bonus to attack and damage rolls made with this scepter, which can be wielded as a magic club.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (831, 3, 'string', 'You can use the properties of the Wyrmskull Throne, as well as the properties of the scepter itself. The scepter has 10 charges, and it regains 1d6 + 4 expanded charges at dawn. Its properties are as follows:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (831, 4, 'list', NULL, '{"type":"list","items":["If you are underground or underwater, you can use an action to expend 1 charge to determine the distance to the surface.","As an action: you can expend 2 charges to cast the {@spell sending} spell from the scepter.","As an action: you can expend 3 charges to cast the {@spell teleport} spell from the scepter. If the destination is within 60 feet of the {@item Wyrmskull Throne|SKT}, there is no chance of a teleport error or mishap occurring."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (832, 0, 'string', 'This opalescent, symbiotic goo comes sealed in a jar and slowly shifts and moves, as if endlessly exploring the jar''s interior. To attune to this item, you must first drink the contents of the jar, unlocking the following properties.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (832, 1, 'entries', NULL, '{"type":"entries","name":"Resistant","entries":["While attuned to Kyrzin''s ooze, you have resistance to poison and acid damage, and you''re immune to the {@condition poisoned} condition."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (832, 2, 'entries', NULL, '{"type":"entries","name":"Amorphous","entries":["As an action, you can speak a command word and cause your body to assume the amorphous qualities of an ooze. For the next minute, you (along with any equipment you''re wearing or carrying) can move through a space as narrow as 1 inch wide without squeezing. Once you use this property, it can''t be used again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (832, 3, 'entries', NULL, '{"type":"entries","name":"Acid Breath","entries":["As an action, you can exhale acid in a 30-foot line that is 5 feet wide. Each creature in that line must make a {@dc 15} Dexterity saving throw, taking 36 ({@damage 8d8}) acid damage on a failed save, or half as much damage on a successful one. Once you use this property, it can''t be used again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (832, 4, 'entries', NULL, '{"type":"entries","name":"Symbiotic Nature","entries":["The ooze can''t be removed from you while you''re attuned to it, and you can''t voluntarily end your attunement to it. If you''re targeted by a spell that ends a curse, your attunement to the ooze ends, as it seeps out of you.","If you die while the ooze is inside you, it bursts out and engulfs you, turning your corpse into a {@creature black pudding} allied with the daelkyr."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (833, 0, 'string', 'Ladder (10-foot)', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (834, 0, 'string', 'A lamp casts bright light in a 15-foot radius and dim light for an additional 30 feet. Once lit, it burns for 6 hours on a flask (1 pint) of oil.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (835, 0, 'string', 'Lamprey ships are antiquated, being among the oldest spelljamming ships still in use. Psurlons (see Boo''s Astral Menagerie) are particularly fond of them.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (835, 1, 'string', 'Using the metal grappling jaws built into its bow, a lamprey ship can attach itself to another ship, which is a critical feature during boarding actions. Other standard weapons include four ballistae on the main deck.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (835, 2, 'string', 'A lamprey can float on water, though it can''t land safely on the ground. Lamprey ships that land on the ground have the distressing habit of rolling over, as more than a few crews have discovered to their dismay.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (836, 0, 'string', 'While lit, this hooded lantern burns for 6 hours on 1 pint of oil, shedding bright light in a 30-foot radius and dim light for an additional 30 feet. Invisible creatures and objects are visible as long as they are in the lantern''s bright light. You can use an action to lower the hood, reducing the light to dim light in a 5-foot radius.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (837, 0, 'string', 'This hooded lantern burns for 6 hours on 1 pint of oil, shedding bright light in a 30-foot radius and dim light for an additional 30 feet.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (837, 1, 'string', 'Each lantern of tracking is designed to track down a certain type of creature, which is determined by rolling on the Lantern of Tracking table. Once determined, this creature type can''t be changed. While the lantern is within 300 feet of any creature of that type, its flame turns bright green. The lantern doesn''t pinpoint the creature''s exact location, however.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (837, 2, 'table', NULL, '{"type":"table","caption":"Lantern of Tracking","colLabels":["1d10","Creature Type"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","Aberrations"],["2","Celestials"],["3","Constructs"],["4","Dragons"],["5","Elementals"],["6","Fey"],["7","Fiends"],["8","Giants"],["9","Monstrosities"],["10","Undead"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (838, 0, 'string', 'An opaque light and dark blue with yellow flecks gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (844, 0, 'string', 'The handle of this dark leather whip bears the fire rune, and embers dance around the whip''s tail.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (844, 1, 'string', 'You gain a +1 bonus to attack and damage rolls made with this weapon, and on a hit, the whip deals an extra 1d6 fire damage. When you score a critical hit with an attack using this whip, the target also has the restrained condition until the start of your next turn, as fiery bands lash around the target.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (844, 2, 'entries', NULL, '{"type":"entries","name":"Invoking the Rune","entries":["When you make an attack with the whip and hit, you can use your reaction to invoke the whip''s rune. Doing so increases the extra fire damage dealt by the whip to {@dice 2d6}.","Once the rune has been invoked, it can''t be invoked again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (846, 0, 'string', 'This snakeskin whip is touched by the power of Zehir and ends in five animated serpent heads.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (846, 1, 'entries', NULL, '{"type":"entries","name":"Sentience","entries":["The Lash of Shadows is a sentient chaotic evil weapon with an Intelligence of 18, a Wisdom of 16, and a Charisma of 20. It has hearing and {@sense darkvision} out to a range of 120 feet.","The weapon communicates telepathically with its wielder and can speak, read, and understand Abyssal, Common, and Draconic."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (846, 2, 'entries', NULL, '{"type":"entries","name":"Personality","entries":["A mad {@creature marilith} named Sizlifeth lives within the Lash of Shadows. The weapon has a short temper and experiences only extreme emotions. It is most happy when dealing harm."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (846, 3, 'entries', NULL, '{"type":"entries","name":"Dormant","entries":["The whip grants the following benefits in its dormant state:",{"type":"list","items":["You can speak, read, and write Abyssal and Draconic.","You gain a +1 bonus to attack and damage rolls made with this magic weapon.","You can attempt to poison any creature that is hit by the whip. The creature must succeed on a {@dc 13} Constitution saving throw or suffer the effect of one of the following poisons of your choice; once a poison other than Serpent Venom has been used, that poison can''t be used again until the next dawn:",{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Dead Eyes","entry":"A creature that fails the saving throw against this poison is {@condition poisoned} for 1 hour. While {@condition poisoned} in this way, the creature is {@condition blinded}."},{"type":"item","name":"Serpent Venom","entry":"A creature that fails the saving throw against this poison takes {@damage 3d6} poison damage, or half as much damage on a successful save."}]}]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (846, 4, 'inset', NULL, '{"type":"inset","name":"Betrayer Artifact Properties","entries":["The Arms of the Betrayers advance in power in the same manner as the Vestiges of Divergence. In its dormant state, each of these artifacts has one {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} property and one {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental} property. When the artifact attains an awakened state, it gains an additional {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} property and an additional {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental} property. When the item reaches its exalted state, it gains a {@table Artifact Properties; Major Beneficial Properties|dmg|major beneficial} property. See \"Artifact Properties\" in chapter 7 of the Dungeon Master''s Guide for more information."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (848, 0, 'string', 'Strange rituals have repurposed the body of a flesh golem into this partially sentient suit of leather armor. While wearing this armor, you gain a +1 bonus to AC and to saving throws against spells and other magical effects. In addition, you gain the following properties:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (848, 1, 'list', NULL, '{"type":"list","items":[{"type":"entries","name":"Immutable Form","entries":["You are immune to any spell or effect that would alter your form."]},{"type":"entries","name":"Lightning Absorption","entries":["You gain resistance to lightning damage. Whenever you take lightning damage, you gain 5 temporary hit points."]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (848, 2, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["This armor is cursed, and becoming attuned to it extends the curse to you. Until the curse is broken with a {@spell remove curse} spell or similar magic, you are unwilling to part with the armor. In addition, while you wear the cursed armor, you gain the following properties:",{"type":"list","items":[{"type":"entries","name":"Aversion of Fire","entries":["If you take fire damage, you have disadvantage on attack rolls and ability checks until the end of your next turn."]},{"type":"entries","name":"Berserk","entries":["Whenever a critical hit is made against you, roll a {@dice d6}. On a 6, the armor causes you to go berserk. On each of your turns while berserk, you attack the nearest creature you can see. If no creature is near enough to move to and attack, you attack an object, with preference for an object smaller than yourself. Once the armor causes you to go berserk, it cannot be removed. You continue to attack until you are {@condition incapacitated} or until another creature is able to calm you with appropriate magic (such as a {@spell calm emotions} spell) or a successful {@dc 15} Charisma ({@skill Persuasion}) check."]}]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (850, 0, 'string', 'The Hammock of Worlds is a colorful hammock woven with traditional Ataguan designs.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (850, 1, 'string', 'You can use an action to unfold and place the lesser version of the Hammock of Worlds on a solid surface, whereupon it creates a two-way portal to another world or plane of existence. Each time the item opens a portal, the DM decides where it leads or the user can cause it to always reliably connect to the Ghost Orchid Tepui. You can use an action to close an open portal by taking hold of the edges of the cloth and folding it up. Once the lesser version of the Hammock of Worlds has opened a portal, it can''t do so again for 1d8 hours.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (851, 0, 'string', 'With covers made of skin and fittings of bone, this tome is cold to the touch, and it whispers faintly. When found, the book contains the following spells, which are wizard spells for you while you are attuned to the book: animate dead, circle of death, false life, finger of death, speak with dead, summon undead, and vampiric touch. It functions as a spellbook for you.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (851, 1, 'string', 'While you are holding the book, you can use it as a spellcasting focus for your wizard spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (851, 2, 'string', 'The book has 3 charges, and it regains 1d3 expended charges daily at dawn. You can use the charges in the following ways while holding it:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (851, 3, 'list', NULL, '{"type":"list","items":["If you spend 1 minute studying the book, you can expend 1 charge to replace one of your prepared wizard spells with a different spell in the book. The new spell must be of the necromancy school.","As an action, you can expend 1 charge to take on a semblance of undeath for 10 minutes. For the duration, you take on a deathly appearance, and undead creatures are indifferent to you, unless you have damaged them. You also appear undead to all outward inspection and to spells used to determine the target''s status. The effect ends if you deal damage or force a creature to make a saving throw."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (852, 0, 'string', 'Produced by a special needle, this magic tattoo features symbols of life and rebirth.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (852, 1, 'entries', NULL, '{"type":"entries","name":"Tattoo Attunement","entries":["To attune to this item, you hold the needle to your skin where you want the tattoo to appear, pressing the needle there throughout the attunement process. When the attunement is complete, the needle turns into the ink that becomes the tattoo, which appears on the skin.","If your attunement to the tattoo ends, the tattoo vanishes, and the needle reappears in your space."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (852, 2, 'entries', NULL, '{"type":"entries","name":"Necrotic Resistance","entries":["You have resistance to necrotic damage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (852, 3, 'entries', NULL, '{"type":"entries","name":"Life Ward","entries":["When you would be reduced to 0 hit points, you drop to 1 hit point instead. Once used, this property can''t be used again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (853, 0, 'string', 'This +1 mace was made for a cleric of Lathander, the god of dawn. The head of the mace is shaped like a sunburst and made of solid brass. Named Lightbringer, this weapon glows as bright as a torch when its wielder commands. While glowing, the mace deals an extra 1d6 radiant damage to undead creatures.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (854, 0, 'string', 'You have a +1 bonus to attack and damage rolls made with this magic weapon.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (854, 1, 'string', 'This weapon, known as Lightbringer, was made for a cleric of Lathander, the god of dawn. The head of the mace is shaped like a sunburst and is made of solid brass. The mace glows as bright as a torch when its wielder commands. While glowing, the mace deals an extra 1d6 radiant damage to Undead creatures.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (855, 0, 'string', '{#itemEntry Absorbing Tattoo|TCE}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (857, 0, 'string', 'These symbiotic glovesmade of thin chitin and sinewpulse with a life of their own. To attune to them, you must wear them for the entire attunement period, during which the gloves bond with your skin.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (857, 1, 'string', 'While attuned to these gloves, you gain one of the following proficiencies (your choice when you attune to the gloves):', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (857, 2, 'list', NULL, '{"type":"list","items":["{@skill Sleight of Hand}","{@item Thieves'' tools|PHB}","One kind of {@item artisan''s tools|PHB} of your choice","One kind of {@item musical instrument|PHB} of your choice"]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (857, 3, 'string', 'When you make an ability check using the chosen proficiency, you add double your proficiency bonus to the check, instead of your normal proficiency bonus.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (857, 4, 'entries', NULL, '{"type":"entries","name":"Symbiotic Nature","entries":["The gloves can''t be removed from you while you''re attuned to them, and you can''t voluntarily end your attunement to them. If you''re targeted by a spell that ends a curse, your attunement to the gloves ends, and they can be removed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (858, 0, 'string', 'As a rank 2 hoardsperson, you are granted the use of a living loot satchel, which is an uncommon magic item. It functions as a bag of holding and is available in a variety of colors and styles.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (858, 1, 'table', NULL, '{"type":"table","caption":"Class-Based Living Loot Satchel","colStyles":["col-2","col-10"],"colLabels":["Class","Satchel"],"rows":[["Barbarian","Broad belt with a dozen hanging pockets"],["Bard","Lute case"],["Cleric","Hollowed-out holy tome"],["Druid","Made from natural, organic, locally sourced woven fibers"],["Fighter","A thick, battle-scarred iron lockbox"],["Paladin","A metal case with fine engraving and scrollwork"],["Ranger","A fur-lined bindle"],["Rogue","A nondescript coin pouch"],["Sorcerer","A battered leather satchel, prone to spitting out multicolored sparks at the seams"],["Warlock","A patchwork monster-leather satchel with a \"purely decorative\" fanged mouth."],["Wizard","A pocket dimension hidden up your sleeve or inside your hat"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (858, 2, 'string', 'The living loot satchel is a kind of magical being that safeguards the franchise''s funds and valuables. Its innards are connected to a secure coffer within Head Office''s vault in Waterdeep, to which the satchel periodically transfers the franchise''s wealth. As an action, you can transfer any amount of your franchise funds back to your satchel with a successful 15 Dexterity (Sleight of Hand) check.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (858, 3, 'entries', NULL, '{"type":"entries","name":"Secret Satchel","entries":["As a rank 3 hoardsperson, your living loot satchel gets an upgrade to function as the replica chest used for the {@spell Leomund''s secret chest} spell, becoming a rare magic item. You can open the secret chest through your living loot satchel to deposit or withdraw itemseven items that wouldn''t normally fit in your satchel, but which fit within the chest. Thanks to Head Office striking deals you don''t want to know about with extraplanar creatures you really don''t want to know about, there is no chance for the spell to end."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (858, 4, 'entries', NULL, '{"type":"entries","name":"That Thing You Need","entries":["At rank 3, you can use a bonus action to reach into your living loot satchel and make a {@dc 15} Dexterity ({@skill Sleight of Hand}) check. On a success, you draw forth an item of your choice on the Adventuring Gear table in chapter 5 of the Player''s Handbook. The item must be of a size that can fit into your secret chest and be worth no more than 15 gp. Once you attempt to draw five items from your satchel, you cannot draw forth any more items until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (858, 5, 'entries', NULL, '{"type":"entries","name":"Portable Hole Satchel","entries":["At rank 4, your living loot satchel receives another upgrade, becoming a very rare magic item. The secret chest accessed by your satchel now has the storage capacity of a {@item portable hole}6 feet in diameter and 10 feet deep. As before, you can place any appropriately sized object into the portable-hole-sized chest, even if it wouldn''t normally fit into your satchel."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (858, 6, 'entries', NULL, '{"type":"entries","name":"That Expensive Thing You Need","entries":["Also at rank 4, when you use your That Thing You Need feature, you can requisition any item of up to 250 gp in value, as long as it would fit into the confines of your satchel''s portable hole."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (859, 0, 'string', 'Druids, rangers, and clerics devoted to gods of nature are often found aboard living ships, as are Wildspace explorers who don''t want to have to worry about their ship''s air envelope becoming fouled during a long voyage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (859, 1, 'string', 'This ship''s main distinctive feature is the fully grown treant on the aft deck. The treant has a speed of 0 because its roots are woven into the deck; it and the ship are inseparable. If the ship is reduced to 0 hit points, the treant dies of shock. The ship can function if its treant dies, but the treant can never be replaced with another.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (859, 2, 'string', 'When the treant finishes a long rest, it repairs the ship''s hull, enabling the ship to regain 4d12 hit points, and refreshes the ship''s air envelope (turning deadly air into foul air, or foul air into fresh air).', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (859, 3, 'string', 'A living ship can float on water and sail across it, but it can''t land safely on the ground (its keel would cause it to roll on its side). Its standard weaponry is an aft-mounted ballista.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (860, 0, 'string', 'This stone is a large gem worth 150 gp.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (860, 1, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["The stone is cursed, but its magical nature is hidden; {@spell detect magic} doesn''t detect it. An {@spell identify} spell reveals the stone''s true nature. If you use the {@action Dash} or {@action Disengage} action while the stone is on your person, its curse activates. Until the curse is broken with {@spell remove curse} or similar magic, your speed is reduced by 5 feet, and your maximum load and maximum lift capacities are halved. You also become unwilling to part with the stone."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (862, 0, 'string', 'A key is provided with the lock. Without the key, a creature proficient with thieves'' tools can pick this lock with a successful 15 Dexterity check. Your DM may decide that better locks are available for higher prices.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (863, 0, 'string', 'This lock appears to be an ordinary lock (of the type described in chapter 5 of the Player''s Handbook) and comes with a single key. The tumblers in this lock magically adjust to thwart burglars. Dexterity checks made to pick the lock have disadvantage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (864, 0, 'string', 'This ivory longbow is inscribed with a prayer to the god Hiatea, the runes of which are entwined with gilded engravings of wheat stalks and deer antlers.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (864, 1, 'string', 'You gain a +3 bonus to attack and damage rolls made with this weapon. If you load no ammunition in the weapon, it produces its own, automatically creating one magic arrow when you pull back the string. The arrow created by the bow vanishes the instant after it hits or misses a target.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (864, 2, 'string', 'The bow has 8 charges for the following properties, which you can use while wielding the bow. The bow regains 1d4 + 1 charges daily at dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (864, 3, 'entries', NULL, '{"type":"entries","name":"Curative Arrow","entries":["When you take the {@action Attack} action using the bow, you can expend 1 charge to replace one of your attacks with a blazing arrow of curative magic, which automatically hits one creature you can see within 150 feet of you. The target can then immediately spend and roll one of its unspent Hit Dice and regain a number of hit points equal to the roll plus your Wisdom modifier (minimum of +1).","If the target has no unspent Hit Dice remaining, nothing happens. You can use a curative arrow only once per turn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (864, 4, 'entries', NULL, '{"type":"entries","name":"Spellcasting","entries":["While holding the bow, you can use an action to expend 1 or more of its charges to cast one of the following spells from it (save {@dc 18}): {@spell create food and water} (1 charge), {@spell warding bond} (2 charges), {@spell guardian of faith} (3 charges)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (866, 0, 'string', 'The Masked Lords of Waterdeep don this ensemble when meeting with one another. This raiment renders each lord indistinguishable from the others. The ensemble consists of three piecesa helm, an amulet, and a robethat function as a single magic item when worn together, but only within the city of Waterdeep and its sewers. You become attuned to the ensemble as a single item.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (866, 1, 'entries', NULL, '{"type":"entries","name":"Lord''s Helm","entries":["This bucket helm covers your head and conceals your face. Screens over the eyes help to shroud your identity without blinding you. While you wear the helm, your voice is magically altered to sound genderless, and you are immune to magic that allows other creatures to read your thoughts, to determine whether you are lying, to know your alignment, or to know your creature type. Creatures can communicate telepathically with you only if you allow it."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (866, 2, 'entries', NULL, '{"type":"entries","name":"Lord''s Amulet","entries":["This amulet bears the crest of Waterdeep. It functions as an {@item amulet of proof against detection and location}."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (866, 3, 'entries', NULL, '{"type":"entries","name":"Lord''s Robe","entries":["This elegant robe functions as a {@item ring of free action}, and it creates the illusion that you have a nondescript, androgynous humanoid build and stand 6 feet tall."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (867, 0, 'string', 'The Lorehold Primer is a magic textbook created at Strixhaven''s Lorehold College. The primer has 3 charges, and it regains 1d3 expended charges daily at dawn. If you make an Intelligence (History) or Intelligence (Religion) check while holding the primer, you can expend 1 charge to give yourself 1d4 bonus to the check, immediately after you roll the d20.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (867, 1, 'string', 'In addition, if you study the primer at the end of a long rest, you can choose one 1st-level spell from the cleric or wizard spell list. Before you finish your next long rest, you can cast the chosen spell once without a spell slot if you are holding the primer. Your spellcasting ability for this spell is your choice of Intelligence, Wisdom, or Charisma.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (868, 0, 'string', 'When you make your character, you may roll once on the Lorehold Trinkets table, instead of on the trinket in the Player''s Handbook, for your starting trinket.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (868, 1, 'table', NULL, '{"caption":"Lorehold Trinkets","colLabels":["d6","Trinket"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","A map made of concentric circles that can be rotated around the page"],["2","A puzzle box bedecked with amber"],["3","A dented brass compass with a red needle"],["4","The head of a broken statue that houses the consciousness of a snarky sage"],["5","A sheet of parchment embossed with tactile lettering, glowing red"],["6","A broken dagger with a wavy blade and a serpentine hilt"]],"type":"table"}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (869, 0, 'string', 'This dwarven battle-helm consists of a sturdy open-faced steel helmet, decorated with a golden circlet above the brow from which seven small gold spikes project upward. You gain the following benefits while wearing the crown:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (869, 1, 'list', NULL, '{"type":"list","items":["You have resistance to psychic damage.","You have advantage on saving throws against effects that would charm you.","You can use a bonus action to inspire one creature you can see that is within 60 feet of you and that can see or hear you. Once before the end of your next turn, the inspired creature can roll a {@dice d6} and add the number rolled to one ability check, attack roll, or saving throw it makes. This uses 1 charge from the crown. It has 3 charges, and it regains {@dice 1d3} expended charges daily at dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (870, 0, 'string', 'Not all lingering spirits are tragic souls, lost on their way to the hereafter. Some languish as prisoners, souls so wicked mortals dare not free them upon an unsuspecting afterlife.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (870, 1, 'string', 'Created by a figure of Vistani legend, Luba''s Tarokka of Souls shaped the destiny of countless heroes. The prophecies of this deck of cards also revealed great evils and guided its creator into the path of nefarious forces. Untold times the deck''s creator, Mother Luba, narrowly escaped doom, spared only by her keen insights. But even for her, not all wickedness could be escaped. In the most dire cases, Mother Luba managed to ensnare beings of pure evil amid the strands of fate, imprisoning them within her tarokka deck. There these foul spirits dwell still, trapped within a nether-realm hidden amid shuffling cards, waiting for fate to turn foulas it inevitably will.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (870, 2, 'string', 'Like all Tarokka Deck, the Tarokka of Souls is a lavishly illustrated collection of fifty-four cards, comprising the fourteen cards of the high deck and forty other cards divided into four suits: coins, glyphs, stars, and swords.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (870, 3, 'entries', NULL, '{"type":"entries","name":"Random Properties","entries":["The artifact has the following random properties, which you can determine by rolling on the tables in the \"Artifacts\" section of the {@book Dungeon Master''s Guide|DMG}:",{"type":"list","items":["2 {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental} properties","2 {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} properties"]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (870, 4, 'entries', NULL, '{"type":"entries","name":"Spells","entries":["While holding the deck, you can use an action to cast one of the following spells (save {@dc 18}) from it: {@spell comprehend languages}, {@spell detect evil and good}, {@spell detect magic}, {@spell detect poison and disease}, {@spell locate object}, or {@spell scrying}. Once you use the deck to cast a spell, you can''t cast that spell again from it until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (870, 5, 'entries', NULL, '{"type":"entries","name":"Enduring Vision","entries":["While holding the deck, you automatically succeed on Constitution saving throws made to maintain your {@status concentration} on divination spells."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (870, 6, 'entries', NULL, '{"type":"entries","name":"Twist of Fate","entries":["As an action, you can draw a card from the deck and twist the fortune of another creature you can see within 15 feet of you. Choose one of the following effects:",{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Weal","entry":"The creature has advantage on attack rolls, ability checks, and saving throws for the next hour."},{"type":"item","name":"Woe","entry":"The creature has disadvantage on attack rolls, ability checks, and saving throws for the next hour."}]},"The deck can be used in this way twice, and you regain all expended uses at the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (870, 7, 'entries', NULL, '{"type":"entries","name":"Prisoners of Fate","entries":["Whenever you use the Twist of Fate property, there is a chance that one of the souls trapped in the deck escapes. Roll {@dice d100} and consult the Souls of the Tarokka table. If you roll one of the high cards, the soul associated with it escapes. You can find its statistics in the {@book Monster Manual|MM}. If you roll a soul that has already escaped, roll again.",{"type":"table","caption":"Souls of the Tarokka","colLabels":["d100","Card","Soul"],"colStyles":["col-2 text-center","col-5 text-center","col-5 text-center"],"rows":[["1","Artifact","{@creature Flameskull}"],["2","Beast","{@creature Wraith}"],["3","Broken","{@creature Banshee}"],["4","Darklord","{@creature Vampire}"],["5","Donjon","{@creature Mummy}"],["6","Executioner","{@creature Death knight}"],["7","Ghost","{@creature Ghost}"],["8","Horseman","{@creature Mummy lord}"],["9","Innocent","{@creature Ghost}"],["10","Marionette","{@creature Mummy}"],["11","Mists","{@creature Wraith}"],["12","Raven","{@creature Vampire spawn}"],["13","Seer","{@creature Vampire}"],["14","Tempter","{@creature Vampire spawn}"],["15-100","",""]]},"The released soul appears at a random location within {@dice 10d10} miles of you and terrorizes the living. Until the released soul is destroyed, it gains the benefit of a weal from the deck''s Twist of Fate property, and both you and the original target of Twist of Fate suffer the effect of woe."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (870, 8, 'entries', NULL, '{"type":"entries","name":"Shuffling Fate","entries":["If you go 7 days without using the Twist of Fate property, your attunement to Luba''s Tarokka of Souls ends, and you can''t attune to it again until after another creature uses Twist of Fate on you."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (870, 9, 'entries', NULL, '{"type":"entries","name":"Destroying the Deck","entries":["Luba''s Tarokka of Souls can be destroyed only if all fourteen souls within are released and destroyed. This reveals a fifteenth soul, a {@creature lich}, that inhabits the Nether card, which appears only when the fourteen souls are defeated. If this ancient entity is destroyed, the Nether card vanishes and the deck becomes a normal {@deck tarokka deck|CoS}, with no special properties, but it includes a new card of the DM''s design.",{"type":"entries","name":"Mother Luba and the Vistani","entries":["The creator of the Tarokka of Souls, Mother Luba was one of the most influential leaders of the Vistani. For untold generations, the Vistani have wandered the Shadowfell, which includes terrifying demiplanes like the vampire-haunted realm of Barovia. These travelers have learned many secrets of these domains and encountered countless others wandering amid the Shadowfell''s horrors. Most Vistani bands accept well-intentioned wayfarers from diverse walks and of disparate origins, embracing any who seek to find a home amid the endless roads and vistas hidden amid the mists.","A halfling Vistani, Mother Luba led one of the largest groups of Vistani in the Shadowfell. She hailed from the same world that Count Strahd von Zarovich and Madam Eva came from, and she created a community of kindness and resilience-ever rare to find in the Plane of Shadow. She led her people in welcoming strangers, feeding the hungry, and defying the cruel. She and Madam Eva were once friends, until Madam Eva began bargaining with the creatures of the night. \"We may wander amid the shadows,\" Mother Luba said. \"But we must ever serve as a light to our fellow travelers.\"","Some years ago, Mother Luba disappeared into the mists, leaving behind only the Tarokka of Souls. It is said that if you draw the Mists card from it, you can hear the whispers of her kind voice."]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (871, 0, 'string', 'This magic weapon is a triple-barreled bronze musket. You gain a +1 bonus to attack and damage rolls made with it. It requires no ammunition, its damage is radiant instead of piercing, and it doesn''t have the loading property. The base of the weapon is emblazoned with the light rune.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (871, 1, 'string', 'Additionally, while attuned to the weapon, you can cast dancing lights from the musket at will.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (871, 2, 'entries', NULL, '{"type":"entries","name":"Invoking the Rune","entries":["As an action, you can invoke the weapon''s rune to cast the {@spell sunbeam} spell (save {@dc 17}) with it. Once the rune has been invoked, it can''t be invoked again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (872, 0, 'string', 'The haft of this war pick is inlaid with crushed pearlescent stones that imbue the weapon with a faint luminescence. You gain a +1 bonus to attack and damage rolls made with this war pick.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (872, 1, 'string', 'While wielding the war pick, you can use a bonus action to cast the daylight spell, choosing a point on the war pick. Once you use this bonus action, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (874, 0, 'string', 'This dodecahedron of faintly glowing crystal is heavier than it appears. A set of handles are affixed to its sides, and it pulsates and thrums when touched.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (874, 1, 'entries', NULL, '{"type":"entries","name":"Fragment of Possibility","entries":["A creature that touches the beacon and concentrates for 1 minute receives a Fragment of Possibility, which looks like a Tiny, grayish bead of energy that follows the creature around, staying within 1 foot of it at all times. The fragment lasts for 8 hours or until used. Once the beacon grants a Fragment of Possibility, it can''t grant another until the next dawn. A creature with a Fragment of Possibility from a Luxon Beacon can''t gain another Fragment of Possibility from any source.","When a creature with a Fragment of Possibility makes an attack roll, an ability check, or a saving throw, it can expend its fragment to roll an additional {@dice d20} and choose which of the {@dice d20}s to use. Alternatively, when an attack roll is made against the creature, it can expend its fragment to roll a {@dice d20} and choose which of the {@dice d20}s to use, the one it rolled or the one the attacker rolled.","If the original {@dice d20} roll has advantage or disadvantage, the creature rolls its {@dice d20} after advantage or disadvantage has been applied to the original roll."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (874, 2, 'entries', NULL, '{"type":"entries","name":"Soul Snare","entries":["If a follower of the Luxon who has undergone a ritual of consecution dies within 100 miles of a Luxon Beacon, their soul is ensnared by it. This soul will be reincarnated within the body of a random humanoid baby developing within 100 miles of the beacon."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (875, 0, 'string', 'While holding this lyre, you can cast mending as an action. You can also play the lyre as a reaction when an object or a structure you can see within 300 feet of you takes damage, causing it to be immune to that damage and any further damage of the same type until the start of your next turn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (875, 1, 'string', 'In addition, you can play the lyre as an action to cast fabricate, move earth, passwall, or summon construct, and that spell can''t be cast from it again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (876, 0, 'string', 'When you hit a fiend or an undead with this magic weapon, that creature takes an extra 2d6 radiant damage. If the target has 25 hit points or fewer after taking this damage, it must succeed on a 15 Wisdom saving throw or be destroyed. On a successful save, the creature becomes frightened of you until the end of your next turn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (876, 1, 'string', 'While you hold this weapon, it sheds bright light in a 20-foot radius and dim light for an additional 20 feet.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (877, 0, 'string', 'You gain a +1 bonus to attack and damage rolls made with this magic weapon. The bonus increases to +3 when you use the mace to attack a construct.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (877, 1, 'string', 'When you roll a 20 on an attack roll made with this weapon, the target takes an extra 7 bludgeoning damage, or an extra 14 bludgeoning damage if it''s a construct. If a construct has 25 hit points or fewer after taking this damage, it is destroyed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (877, 2, 'string', 'Note: According to the SRD, it is an extra {@damage 2d6 and 4d6 bludgeoning damage, although this is incorrect}.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (878, 0, 'string', 'This magic weapon has 3 charges. While holding it, you can use an action and expend 1 charge to release a wave of terror. Each creature of your choice in a 30-foot radius extending from you must succeed on a 15 Wisdom saving throw or become frightened of you for 1 minute. While it is frightened in this way, a creature must spend its turns trying to move as far away from you as it can, and it can''t willingly move to a space within 30 feet of you. It also can''t take reactions. For its action it can use only the Dash action or try to escape from an effect that prevents it from moving. If it has nowhere it can move, the creature can use the Dodge action. At the end of each of its turns, a creature can repeat the saving throw, ending the effect on itself on a success.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (878, 1, 'string', 'The mace regains 1d3 expended charges daily at dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (880, 0, 'string', 'This mace has a haft of black iron and a ruby head with a fiendish countenance. Carrying the boon of Asmodeus, it is fit for the most powerful servants of the Nine Hells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (880, 1, 'entries', NULL, '{"type":"entries","name":"Sentience","entries":["The Mace of the Black Crown is a sentient lawful evil weapon with an Intelligence of 20, a Wisdom of 12, and a Charisma of 18. It has hearing and {@sense darkvision} out to a range of 120 feet.","The weapon communicates telepathically with its wielder and can speak, read, and understand Common and Infernal."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (880, 2, 'entries', NULL, '{"type":"entries","name":"Personality","entries":["A greedy {@creature erinyes} named Xartaza lives within the Mace of the Black Crown. Xartaza wants to recruit more souls for Asmodeus, so the weapon pushes its wielder toward lawful evil actions by manipulating the wielder''s dreams. A former Blood War general, Xartaza hates demons and relishes crafting sound battle plans in any conflict."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (880, 3, 'entries', NULL, '{"type":"entries","name":"Dormant","entries":["The mace grants the following benefits in its dormant state:",{"type":"list","items":["You can speak, read, and write Infernal.","You gain a +1 bonus to attack and damage rolls made with this magic weapon.","While holding the mace, you can use a bonus action to speak its Infernal command word, causing flames to erupt from the head. These flames shed bright light in a 40-foot radius and dim light for an additional 40 feet. While the mace is ablaze, it deals an extra {@damage 1d6} fire damage to any target it hits. The flames last until you use a bonus action to speak the command word again or until you are no longer holding the mace.","While holding the mace, you can use an action to summon an {@creature imp}. Any devil you summon with this mace is friendly to you and your companions for the duration. The imp obeys any verbal commands that you issue to it and returns to the Nine Hells 10 minutes after you summoned it. This property can''t be used again until the next dawn."]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (880, 4, 'inset', NULL, '{"type":"inset","name":"Betrayer Artifact Properties","entries":["The Arms of the Betrayers advance in power in the same manner as the Vestiges of Divergence. In its dormant state, each of these artifacts has one {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} property and one {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental} property. When the artifact attains an awakened state, it gains an additional {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial} property and an additional {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental} property. When the item reaches its exalted state, it gains a {@table Artifact Properties; Major Beneficial Properties|dmg|major beneficial} property. See \"Artifact Properties\" in chapter 7 of the Dungeon Master''s Guide for more information."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (882, 0, 'string', 'You have a +1 bonus to attack and damage rolls made with this longsword, which is made of laminated wood, and inset with jagged teeth of obsidian. It deals an extra 2d6 damage to any creature of the plant type.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (883, 0, 'string', 'While you wear this helm and are inside Maddgoth''s castle, on its roof, or in its courtyard, you have immunity to all damage. If the helm is taken from the castle, it turns to dust and is destroyed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (884, 0, 'string', 'An executioner''s blade from the Age of Arcanum, this magic greatsword is one of many similar weapons once used to execute magic-wielding criminals. Though most commonly found in the lands now known as Wildemount and Issylra, a few such blades have found their way to Tal''Dorei.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (884, 1, 'string', 'If you hit a creature with an attack using this weapon, you can cast the dispel magic spell from the sword against the target as part of the attack. Alternatively, you can use an action to cast dispel magic from the sword against a target of your choice. If you need to make a check with your spellcasting ability modifier as part of casting the spell, you make this check with a +3 modifier or your own spellcasting ability modifier, whichever is higher.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (884, 2, 'string', 'Once you cast dispel magic using this weapon, you can''t do so again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (885, 0, 'string', 'This lens allows a closer look at small objects. It is also useful as a substitute for flint and steel when starting fires. Lighting a fire with a magnifying glass requires light as bright as sunlight to focus, tinder to ignite, and about 5 minutes for the fire to ignite. A magnifying glass grants advantage on any ability check made to appraise or inspect an item that is small or highly detailed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (886, 0, 'string', 'An opaque striated light and dark green gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (887, 0, 'string', 'A creature subjected to this poison must succeed on a 15 Constitution saving throw or become poisoned for 1 hour. The poisoned creature is blinded.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (888, 0, 'string', 'These metal restraints can bind a Small or Medium creature. Escaping the manacles requires a successful 20 Dexterity check. Breaking them requires a successful 20 Strength check. Each set of manacles comes with one key. Without the key, a creature proficient with thieves'' tools can pick the manacles'' lock with a successful 15 Dexterity check. Manacles have 15 hit points.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (889, 0, 'string', 'You have advantage on saving throws against spells while you wear this cloak.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (890, 0, 'string', 'This book contains health and diet tips, and its words are charged with magic. If you spend 48 hours over a period of 6 days or fewer studying the book''s contents and practicing its guidelines, your Constitution score increases by 2, as does your maximum for that score. The manual then loses its magic, but regains it in a century.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (891, 0, 'string', 'This tome contains information and incantations necessary to make a particular type of golem. The DM chooses the type or determines it randomly. To decipher and use the manual, you must be a spellcaster with at least two 5th-level spell slots. A creature that can''t use a manual of golems and attempts to read it takes 6d6 psychic damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (891, 1, 'string', 'To create a clay golem, you must spend 30 days, working without interruption with the manual at hand and resting no more than 8 hours per day. You must also pay 65,000 gp to purchase supplies. Once you finish creating the golem, the book is consumed in eldritch flames. The golem becomes animate when the ashes of the manual are sprinkled on it. It is under your control, and it understands and obeys your spoken commands.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (892, 0, 'string', 'This tome contains information and incantations necessary to make a particular type of golem. The DM chooses the type or determines it randomly. To decipher and use the manual, you must be a spellcaster with at least two 5th-level spell slots. A creature that can''t use a manual of golems and attempts to read it takes 6d6 psychic damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (892, 1, 'string', 'To create a flesh golem, you must spend 60 days, working without interruption with the manual at hand and resting no more than 8 hours per day. You must also pay 50,000 gp to purchase supplies. Once you finish creating the golem, the book is consumed in eldritch flames. The golem becomes animate when the ashes of the manual are sprinkled on it. It is under your control, and it understands and obeys your spoken commands.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (893, 0, 'string', 'This book describes fitness exercises, and its words are charged with magic. If you spend 48 hours over a period of 6 days or fewer studying the book''s contents and practicing its guidelines, your Strength score increases by 2, as does your maximum for that score. The manual then loses its magic, but regains it in a century.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (894, 0, 'string', 'This tome contains information and incantations necessary to make a particular type of golem. The DM chooses the type or determines it randomly. To decipher and use the manual, you must be a spellcaster with at least two 5th-level spell slots. A creature that can''t use a manual of golems and attempts to read it takes 6d6 psychic damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (894, 1, 'string', 'To create an iron golem, you must spend 120 days, working without interruption with the manual at hand and resting no more than 8 hours per day. You must also pay 100,000 gp to purchase supplies. Once you finish creating the golem, the book is consumed in eldritch flames. The golem becomes animate when the ashes of the manual are sprinkled on it. It is under your control, and it understands and obeys your spoken commands.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (895, 0, 'string', 'This book contains coordination and balance exercises, and its words are charged with magic. If you spend 48 hours over a period of 6 days or fewer studying the book''s contents and practicing its guidelines, your Dexterity score increases by 2, as does your maximum for that score. The manual then loses its magic, but regains it in a century.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (896, 0, 'string', 'This tome contains information and incantations necessary to make a particular type of golem. The DM chooses the type or determines it randomly. To decipher and use the manual, you must be a spellcaster with at least two 5th-level spell slots. A creature that can''t use a manual of golems and attempts to read it takes 6d6 psychic damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (896, 1, 'string', 'To create a stone golem, you must spend 90 days, working without interruption with the manual at hand and resting no more than 8 hours per day. You must also pay 80,000 gp to purchase supplies. Once you finish creating the golem, the book is consumed in eldritch flames. The golem becomes animate when the ashes of the manual are sprinkled on it. It is under your control, and it understands and obeys your spoken commands.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (897, 0, 'string', 'This cylindrical leather case can hold up to ten rolled-up paper (one sheet) or five rolled-up parchment (one sheet).', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (899, 0, 'string', 'This wooden mask is shaped in the likeness of a beast''s visage and has 3 charges. While wearing the mask you can expend 1 charge and use the mask to cast the animal friendship spell as an action. The mask regains all expended charges at dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (900, 0, 'string', 'Individually, the five dragon masks resemble the dragons they are named for. When two or more of the dragon masks are assembled, however, they transform magically into the Mask of the Dragon Queen. Each mask shrinks to become the modeled head of a chromatic dragon, appearing to roar its devotion to Tiamat where all the masks brought together are arranged crown-like on the wearer''s head. Below the five masks, a new mask shapes itself, granting the wearer a draconic visage that covers the face, neck, and shoulders.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (900, 1, 'string', 'While you are attuned to and wear this mask, you can have any of the properties from any one mask. Additionally, you gain the Damage Absorption from each of the five dragon masks, and you gain five uses of the Legendary Resistance property.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (902, 0, 'string', 'A masque charm is a small silver pin. While wearing this charm, you can use an action to cast the disguise self spell (13 to discern the disguise). Once the spell is cast, it can''t be cast from the charm again until the next sunset. When casting the spell, you can have the spell last for its normal 1 hour duration or for 6 hours. If you choose the 6-hour duration, the charm becomes nonmagical when the spell ends. In either case, the spell ends if the charm is removed from you.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (903, 0, 'string', 'Produced by a special needle, this magic tattoo appears on your body as whatever you desire.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (903, 1, 'entries', NULL, '{"type":"entries","name":"Tattoo Attunement","entries":["To attune to this item, you hold the needle to your skin where you want the tattoo to appear, pressing the needle there throughout the attunement process. When the attunement is complete, the needle turns into the ink that becomes the tattoo, which appears on the skin.","If your attunement to the tattoo ends, the tattoo vanishes, and the needle reappears in your space."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (903, 2, 'entries', NULL, '{"type":"entries","name":"Fluid Ink","entries":["As a bonus action, you can shape the tattoo into any color or pattern and move it to any area of your skin. Whatever form it takes, it is always obviously a tattoo. It can range in size from no smaller than a copper piece to an intricate work of art that covers all your skin."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (903, 3, 'entries', NULL, '{"type":"entries","name":"Disguise Self","entries":["As an action, you can use the tattoo to cast the {@spell disguise self} spell ({@dc 13} to discern the disguise). Once the spell is cast from the tattoo, it can''t be cast from the tattoo again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (904, 0, 'string', 'Every shield guardian has an amulet magically linked to it. A shield guardian can have only one corresponding amulet, and if that amulet is destroyed, the shield guardian is incapacitated until a replacement amulet is created. A shield guardian''s amulet is subject to direct attack if it isn''t being worn or carried. It has AC 10, 10 hit points, and immunity to poison and psychic damage. Crafting an amulet requires 1 week and costs 1,000 gp in components.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (904, 1, 'string', 'A shield guardian''s solitary focus is to protect the amulet''s wearer. The amulet''s wearer can command the guardian to attack its enemies or to guard the wielder against attack. If an attack threatens to injure the wearer, the construct can magically absorb the blow into its own body, even at a distance.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (904, 2, 'string', 'A spellcaster can store a single spell within a shield guardian, which can then cast the spell on command or under specific conditions. Many a wizard has been rendered helpless by enemies, only to surprise those foes when its shield guardian unleashes potent magical power.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (907, 0, 'string', 'Erebos wields Mastix, a whip capable of extending like an impossibly long shadow. In the hands of the god of the dead, the whip snares the reluctant dead and drags them into his realm. When Erebos grants his weapon to a mortal follower, it''s typically to reclaim a powerful soul or to humiliate Heliod.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (907, 1, 'entries', NULL, '{"type":"entries","name":"Whip of the Dead","entries":["Erebos''s whip seethes with the enervating energy of the Underworld. This magic whip grants a +3 bonus to attack and damage rolls made with it. When you hit with an attack using this whip, the target takes an extra {@damage 2d8} necrotic damage and you regain hit points equal to half the amount of necrotic damage dealt.","Additionally, when you make an attack with the whip on your turn, you can increase the range of the attack to 300 feet. This property of the weapon can''t be used again until the next dusk."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (907, 2, 'entries', NULL, '{"type":"entries","name":"Blessing of the Dead","entries":["If you are a worshiper of Erebos, you gain all the following benefits for which you have the required piety:",{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Piety 1+","entry":"The whip has 1 randomly determined {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental} propertya burden Erebos imposes to test his faithful."},{"type":"item","name":"Piety 25+","entry":"The whip has 1 randomly determined {@table Artifact Properties; Major Beneficial Properties|dmg|major beneficial} property."},{"type":"item","name":"Piety 50+","entry":"The whip has 1 additional randomly determined {@table Artifact Properties; Major Beneficial Properties|dmg|major beneficial} property."}]},"If you aren''t a worshiper of Erebos, the whip has 2 randomly determined {@table Artifact Properties; Major Detrimental Properties|dmg|major detrimental} properties.","See \"Artifacts\" in chapter 7 of the Dungeon Master''s Guide for details on randomly determined properties."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (907, 3, 'entries', NULL, '{"type":"entries","name":"Erebos''s Claim","entries":["While carrying the whip, you can use an action to cast either {@spell circle of death} or {@spell dominate monster} (targeting only undead) from the whip. The save DC for these spells is 18. Once you use the whip to cast a spell, that spell can''t be cast from it again until the next dusk."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (907, 4, 'entries', NULL, '{"type":"entries","name":"Destroying the Whip","entries":["To destroy the whip, it must be taken to the heights of Mount Hiastos in Nyx, unraveled by a Returned, and left to bask in continual daylight for one month."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (908, 0, 'string', 'Matalotok, the Frost Father, is an ancient hammer fashioned by Thrym, the god of frost giants. The favored weapon of the demon lord Kostchtchie, Matalotok is frigid to the touch and wreathed in mist.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (908, 1, 'string', 'You are immune to cold damage while holding Matalotok. Whenever it deals damage to a creature, the hammer radiates a burst of intense cold in a 30-foot-radius sphere. Each creature in that area takes 10 (3d6) cold damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (909, 0, 'string', 'A switch made of flint is built into the bowl of this fine wooden smoking pipe. With a few flicks of the switch, the pipe lights itself.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (910, 0, 'string', 'You can squeeze this medal tightly in the palm of your hand as an action. Doing so gives you advantage on Strength checks and Strength saving throws for 1 hour. Once this property has been used, it can''t be used again, and the medal becomes nonmagical.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (911, 0, 'string', 'When you use an action to rub this medal, you gain a swimming speed equal to your walking speed for 1 hour. Once this property has been used, it can''t be used again, and the medal becomes nonmagical.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (912, 0, 'string', 'When you would be hit by an attack, you can use your reaction to increase your AC by 5 until the start of your next turn, including against the triggering attack. You must be wearing the medal and able to see the creature that made the triggering attack to use this property. Once this property has been used, it can''t be used again, and the medal becomes nonmagical.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (913, 0, 'string', 'When you use an action to trace the maze inscribed on this medal, you gain advantage on Wisdom checks and know the quickest route to the end of any nonmagical path or maze for 1 hour. Once this property has been used, it can''t be used again, and the medal becomes nonmagical.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (914, 0, 'string', 'You gain 2d4 + 2 temporary hit points when you use an action to press this medal to your mouth. Once this property has been used, it can''t be used again, and the medal becomes nonmagical.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (914, 1, 'string', 'While magical, this medal is slightly warm to the touch (as if it''s fresh from the oven) and smells faintly of baked pie crust.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (915, 0, 'string', 'When you use an action to trace the edge of this medal, difficult terrain doesn''t cost you extra movement for 1 hour. Once this property has been used, it can''t be used again, and the medal becomes nonmagical.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (916, 0, 'string', 'You can press this medal to your temple as an action. Doing so gives you advantage on Intelligence checks and Intelligence saving throws for 1 hour. Once this property has been used, it can''t be used again, and the medal becomes nonmagical.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (917, 0, 'string', 'The medallion has 3 charges. While wearing it, you can use an action and expend 1 charge to cast the detect thoughts spell (save 13) from it. The medallion regains 1d3 expended charges daily at dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (918, 0, 'string', 'The dried leaves of a menga bush can be ground, dissolved in a liquid, heated, and ingested. A creature that ingests 1 ounce of menga leaves in this fashion regains 1 hit point. A creature that ingests more than 5 ounces of menga leaves in a 24-hour period gains no additional benefit and must succeed on a 11 Constitution saving throw or fall unconscious for 1 hour. The unconscious creature awakens if it takes at least 5 damage on one turn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (918, 1, 'string', 'A healthy menga bush usually has 2d6 ounces of leaves. Once picked, the leaves require 1 day to dry out before they can confer any benefit.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (919, 0, 'string', 'A scale includes a small balance, pans, and a suitable assortment of weights up to 2 pounds. With it, you can measure the exact weight of small objects, such as raw precious metals or trade goods, to help determine their worth.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (920, 0, 'string', 'This tin box contains a cup and simple cutlery. The box clamps together, and one side can be used as a cooking pan and the other as a plate or shallow bowl.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (921, 0, 'string', 'A creature that ingests this poison suffers no effect until the stroke of midnight. If the poison has not been neutralized before then, the creature must succeed on a 17 Constitution saving throw, taking 31 (9d6) poison damage on a failed save, or half as much damage on a successful one.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (922, 0, 'string', 'Named for the warlord who infamously employed it, the Mighty Servant of Leuk-o is a fantastically powerful, 10-foot-tall machine that turns into an animate construct when piloted. Crafted of a gleaming black alloy of unknown origin, the servant is often described as a combination of a disproportioned dwarf and an oversized beetle. The servant contains enough space for 1 ton of cargo and a crew compartment within, from which up to two Medium creatures can control it-and potentially execute a spree of unstoppable destruction.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (922, 1, 'string', 'Tales of the servant''s origins involve more conjecture than fact, often referring to otherworldly beings, the mysterious Barrier Peaks in Oerth, and the supposedly related device known as the Machine of Lum the Mad. The best details on the device''s origins and operation can be found in the Mind of Metal, a tome of artificer''s secrets that connects the device to the traditions of the lost Olman people, and which was written by Lum the Mad''s several times over granddaughter, Lum the Maestro, while she reconstructed the long disassembled Mighty Servant of Leuk-o.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (922, 2, 'entries', NULL, '{"type":"entries","name":"Dangerous Attunement","entries":["Two creatures can be attuned to the servant at a time. If a third creature tries to attune to it, nothing happens.","The servant''s controls are accessed by a hatch in its upper back, which is easily opened while there are no creatures attuned to the artifact.","Attuning to the artifact requires two hours, which can be undertaken as part of a long rest, during which time you must be inside the servant, interacting with its controls. While crew members are attuning themselves, any creature or structure outside and within 50 feet of the servant has a {@chance 25} chance of being accidentally targeted by one of its Destructive Fist attacks once during the attunement. This process must be undergone every time a creature attunes itself to the artifact."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (922, 3, 'entries', NULL, '{"type":"entries","name":"Controlling the Servant","entries":["While any creatures are attuned to the artifact, attuned creatures can open the hatch as easily as any other door. Other creatures can open the hatch as an action with a successful {@dc 25} Dexterity check using {@item thieves'' tools|PHB}. A {@spell knock} spell cast on the hatch also opens it until the start of the caster''s next turn.","A creature can enter or exit through the hatch by spending 10 feet of movement. Those inside the servant have {@quickref Cover||3||total cover} from effects originating outside it. The controls within it allow creatures to see outside without obstruction.","While you are inside the servant, you can command it by using the controls. During your turn (for either attuned creature), you can command it in the following ways:",{"type":"list","items":["Open or close the hatch (no action required, once per turn)","Move the servant up to its speed (no action required)","As an action, you can command the servant to take one of the actions in its stat block or some other action.","When a creature provokes an opportunity attack from the servant, you can use your reaction to command the servant to make one Destructive Fist attack against that creature."]},"While there are no attuned creatures inside the servant, it is an inert object."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (922, 4, 'entries', NULL, '{"type":"entries","name":"Ghost in the Machine","entries":["Upon his death, the soul of the mighty warlord Leuk-o was drawn into the artifact and has become its animating force. The servant has been known to attack or move of its own accord, particularly if doing so will cause destruction. Once every 24 hours, the servant, at the DM''s discretion, takes one action while uncrewed.","If the servant loses half of its hit points or more, each creature attuned to it must succeed on a {@dc 20} Wisdom saving throw or be {@condition charmed} for 24 hours. While {@condition charmed} in this way, the creature goes on a destructive spree, seeking to destroy structures and attack any unattuned creatures within sight of the servant, starting with those threatening the artifact-preferably using the servant, if possible."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (922, 5, 'entries', NULL, '{"type":"entries","name":"Self-Destruct","entries":["By inputting a specific series of lever pulls and button presses, the servant''s two crew members can cause it to explode. The self-destruct code is not revealed to crew members when they attune to the artifact. If the code is discovered (the DM determines how), it requires two attuned crew members to be inside the servant and spend their actions on 3 consecutive rounds performing the command. Should the crew members begin the process of entering the code, though, the servant uses its Ghost in the Machine property and turns the crew members against each other.","If the crew members successfully implement the code, at the end of the third round, the servant explodes. Every creature in a 100-foot-radius sphere centered on the servant must make a {@dc 25} Dexterity saving throw. On a failed save, a creature takes 87 ({@damage 25d6}) force damage, 87 ({@damage 25d6}) lightning damage, and 87 ({@damage 25d6}) thunder damage. On a successful save, a creature takes half as much damage. Objects and structures in the area take triple damage. Creatures inside the servant are slain instantly and leave behind no remains.","This does not destroy the servant permanently. Rather, {@dice 2d6} days later, its partsleft arm, left leg, right arm, right leg, lower torso, and upper torsodrop from the sky in random places within 1,000 miles of the explosion. If brought within 5 feet of one another, the pieces reconnect and reform the servant."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (922, 6, 'entries', NULL, '{"type":"entries","name":"Destroying the Servant","entries":["The servant can be destroyed in two ways. After it has self-destructed, its disconnected pieces can be melted down in one of the forge-temples of its ancient Olman creators. Alternatively, if the servant strikes the Machine of Lum the Mad, both artifacts explode in an eruption that is three times the size and three times the damage as the servant''s self-destruct property."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (923, 0, 'string', 'A military saddle braces the rider, helping you keep your seat on an active mount in battle. It gives you advantage on any check you make to remain mounted.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (924, 0, 'string', 'This skull-shaped device is filled with knowledge. The device weighs 5 pounds and is covered with subtle etchings of planar sigils.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (924, 1, 'string', 'As a bonus action, you can toss the device into the air, whereupon it floats at a distance of 1d3 feet from you and you can access its properties. While the mimir is floating, a creature other than you can use an action to grasp or net the device, either by making a successful unarmed strike against AC 22 or a successful 22 Dexterity (Acrobatics) check. You can use a bonus action to seize and stow the device.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (924, 2, 'string', 'The device has AC 22, 25 hit points, immunity to poison and psychic damage, and resistance to all other damage. It is considered to be an object that is being worn while it is floating near you.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (924, 3, 'entries', NULL, '{"type":"entries","name":"Esoteric Knowledge","entries":["While the device is floating, you can use an action to cast {@spell legend lore} from the device. The device speaks the revealed lore aloud. Once this property has been used, it can''t be used again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (924, 4, 'entries', NULL, '{"type":"entries","name":"Planar Knowledge","entries":["The device knows basic, useful information about the planes of existence. While the device is floating, it verbally answers questions you or anyone you designate poses to it about that topic. It knows the information about the planes in the Dungeon Master''s Guide, as well as basic information about the gate-towns of the Outlands (presented in chapter 3 of Sigil and the Outlands)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (925, 0, 'string', 'This gemstone contains a crystallized bit of spellcasting magic.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (925, 1, 'string', 'When you cast a spell that has a casting time of 1 action while holding the mind crystal, you can modify the spell in the way described below. You can use only a single mind crystal to modify the spell, and you can''t use a mind crystal and a Metamagic option on the same spell. Once you use the mind crystal, it becomes a nonmagical gem worth 50 gp.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (925, 2, 'entries', NULL, '{"type":"entries","name":"Modify Spell","entries":["Choose up to three creatures affected by the spell. The chosen creatures automatically succeed on their saving throws against the spell."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (926, 0, 'string', 'This gemstone contains a crystallized bit of spellcasting magic.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (926, 1, 'string', 'When you cast a spell that has a casting time of 1 action while holding the mind crystal, you can modify the spell in the way described below. You can use only a single mind crystal to modify the spell, and you can''t use a mind crystal and a Metamagic option on the same spell. Once you use the mind crystal, it becomes a nonmagical gem worth 50 gp.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (926, 2, 'entries', NULL, '{"type":"entries","name":"Modify Spell","entries":["If the spell has a range of 5 feet or more and doesn''t have a range of self, the spell''s range increases by 100 feet. If the spell has a range of touch, its range becomes 30 feet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (927, 0, 'string', 'This gemstone contains a crystallized bit of spellcasting magic.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (927, 1, 'string', 'When you cast a spell that has a casting time of 1 action while holding the mind crystal, you can modify the spell in the way described below. You can use only a single mind crystal to modify the spell, and you can''t use a mind crystal and a Metamagic option on the same spell. Once you use the mind crystal, it becomes a nonmagical gem worth 50 gp.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (927, 2, 'entries', NULL, '{"type":"entries","name":"Modify Spell","entries":["When you roll damage for the spell, you can reroll up to three damage dice. You must use the new rolls."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (928, 0, 'string', 'This gemstone contains a crystallized bit of spellcasting magic.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (928, 1, 'string', 'When you cast a spell that has a casting time of 1 action while holding the mind crystal, you can modify the spell in the way described below. You can use only a single mind crystal to modify the spell, and you can''t use a mind crystal and a Metamagic option on the same spell. Once you use the mind crystal, it becomes a nonmagical gem worth 50 gp.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (928, 2, 'entries', NULL, '{"type":"entries","name":"Modify Spell","entries":["If the spell has a duration of 1 minute or longer, double the spell''s duration, to a maximum duration of 24 hours."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (929, 0, 'string', 'This gemstone contains a crystallized bit of spellcasting magic.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (929, 1, 'string', 'When you cast a spell that has a casting time of 1 action while holding the mind crystal, you can modify the spell in the way described below. You can use only a single mind crystal to modify the spell, and you can''t use a mind crystal and a Metamagic option on the same spell. Once you use the mind crystal, it becomes a nonmagical gem worth 50 gp.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (929, 2, 'entries', NULL, '{"type":"entries","name":"Modify Spell","entries":["Choose one creature affected by the spell. That creature has disadvantage on the first saving throw it makes against the spell."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (930, 0, 'string', 'This gemstone contains a crystallized bit of spellcasting magic.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (930, 1, 'string', 'When you cast a spell that has a casting time of 1 action while holding the mind crystal, you can modify the spell in the way described below. You can use only a single mind crystal to modify the spell, and you can''t use a mind crystal and a Metamagic option on the same spell. Once you use the mind crystal, it becomes a nonmagical gem worth 50 gp.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (930, 2, 'entries', NULL, '{"type":"entries","name":"Modify Spell","entries":["You change the spell''s casting time to 1 bonus action for this casting."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (931, 0, 'string', 'This gemstone contains a crystallized bit of spellcasting magic.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (931, 1, 'string', 'When you cast a spell that has a casting time of 1 action while holding the mind crystal, you can modify the spell in the way described below. You can use only a single mind crystal to modify the spell, and you can''t use a mind crystal and a Metamagic option on the same spell. Once you use the mind crystal, it becomes a nonmagical gem worth 50 gp.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (931, 2, 'entries', NULL, '{"type":"entries","name":"Modify Spell","entries":["You cast the spell without any somatic or verbal components for this casting."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (932, 0, 'string', 'While you have the skull in your possession, you are invisible to mind flayers, as is anything you are wearing or carrying.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (933, 0, 'string', 'In the hands of any creature other than a mind flayer, a mind lash functions as a normal whip. In the hands of an illithid, this magic weapon strips away a creature''s will to survive as it also strips away flesh, dealing an extra 2d4 psychic damage to any target it hits. Any creature that takes psychic damage from the mind lash must also succeed on a 15 Wisdom saving throw or have disadvantage on Intelligence, Wisdom, and Charisma saving throws for 1 minute. The creature can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (934, 0, 'string', 'This soft, violet cap bears stitching in the pattern of folds on a brain.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (934, 1, 'string', 'As a bonus action while wearing the cap, you can project psychic energy in a 60-foot cone. Each creature in that area must make a 15 Intelligence saving throw. On a failed save, a creature takes 5d8 psychic damage and has the stunned condition for 1 minute. On a successful save, the creature takes half as much damage only. At the end of each of its turns, a stunned creature can repeat the saving throw, ending the stunned condition on itself on a success.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (934, 2, 'string', 'Once this bonus action is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (935, 0, 'string', 'While you wear this adamantine crown, you have advantage on Intelligence, Wisdom, and Charisma saving throws, and you have resistance to psychic damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (937, 0, 'string', 'These silver hand mirrors always come in pairs. When activated as an action, a mirror establishes a visual link with its paired mirror for 10 minutes, with the two mirrors acting as opposite sides of the same open window. The mirrors create this connection even if both are on different planes.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (937, 1, 'string', 'When a single mirror of infinite transpondence is found, the GM decides where its paired mirror is and who possesses it. A random possessor and location can be determined by rolling on the table below. Named bearers are described in chapter 2.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (937, 2, 'string', 'Once the connection between the two mirrors has been activated three times from either end, it can''t be activated again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (937, 3, 'table', NULL, '{"type":"table","caption":"Mirror Locations","colLabels":["d8","Location","Possesor"],"colStyles":["col-1 text-center","col-5","col-6"],"rows":[["1","{@book Emon|TDCSR|3|Emon, the City of Fellowship}","{@book Seeker Odessa Tal''Dorei|TDCSR|2|Seeker Odessa Tal''Dorei}"],["2","{@book Whitestone|TDCSR|3|Whitestone}","None; the mirror is in an empty dungeon"],["3","{@book Kymal|TDCSR|3|Kymal}","{@book Sylker \"The Millionaire\" Uttolot|TDCSR|2|Sylker \"The Millionaire\" Uttolot}"],["4","{@book Westruun|TDCSR|3|Westruun}","{@book Realmseer Eskil Ryndarien|TDCSR|2|Realmseer Eskil Ryndarien}"],["5","{@book Syngorn|TDCSR|3|Syngorn}","{@book Ouestra, the Voice of Memory|TDCSR|2|Ouestra, the Voice of Memory}"],["6","The Elemental Plane of Water","{@deity Khedive Xundi|Exandria|TDCSR}, Lord of Silver Silt"],["7","The Plane of Shadow","A {@creature Remnant Chosen|TDCSR}"],["8","An untraceable location","{@book The Wonderworker|TDCSR|2|The Wonderworker}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (938, 0, 'string', 'When this 4-foot-tall mirror is viewed indirectly, its surface shows faint images of creatures. The mirror weighs 50 pounds, and it has AC 11, 10 hit points, and vulnerability to bludgeoning damage. It shatters and is destroyed when reduced to 0 hit points.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (938, 1, 'string', 'If the mirror is hanging on a vertical surface and you are within 5 feet of it, you can use an action to speak its command word and activate it. It remains activated until you use an action to speak the command word again.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (938, 2, 'string', 'Any creature other than you that sees its reflection in the activated mirror while within 30 feet of it must succeed on a 15 Charisma saving throw or be trapped, along with anything it is wearing or carrying, in one of the mirror''s twelve extradimensional cells. This saving throw is made with advantage if the creature knows the mirror''s nature, and constructs succeed on the saving throw automatically.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (938, 3, 'string', 'An extradimensional cell is an infinite expanse filled with thick fog that reduces visibility to 10 feet. Creatures trapped in the mirror''s cells don''t age, and they don''t need to eat, drink, or sleep. A creature trapped within a cell can escape using magic that permits planar travel. Otherwise, the creature is confined to the cell until freed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (938, 4, 'string', 'If the mirror traps a creature but its twelve extradimensional cells are already occupied, the mirror frees one trapped creature at random to accommodate the new prisoner. A freed creature appears in an unoccupied space within sight of the mirror but facing away from it. If the mirror is shattered, all creatures it contains are freed and appear in unoccupied spaces near it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (938, 5, 'string', 'While within 5 feet of the mirror, you can use an action to speak the name of one creature trapped in it or call out a particular cell by number. The creature named or contained in the named cell appears as an image on the mirror''s surface. You and the creature can then communicate normally.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (938, 6, 'string', 'In a similar way, you can use an action to speak a second command word and free one creature trapped in the mirror. The freed creature appears, along with its possessions, in the unoccupied space nearest to the mirror and facing away from it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (939, 0, 'string', 'This mirror of elven design allows those who stare into it to reflect on positive memories. The 3-foot-tall mirror weighs 25 pounds, and it has AC 11, 10 hit points, and vulnerability to bludgeoning damage. It shatters and is destroyed if reduced to 0 hit points.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (939, 1, 'string', 'While holding the mirror upright, you can use an action to speak its command word and activate it. While activated, the mirror hovers in the air, and it can be destroyed but not moved. It remains activated until you use an action to speak the command word again or your attunement to the mirror ends, at which point the mirror harmlessly floats to the ground. Once the mirror has been deactivated, it can''t be activated again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (939, 2, 'string', 'If a non-Construct creature other than you sees its reflection in the activated mirror while within 30 feet of it, that creature must succeed on a 15 Wisdom saving throw or become paralyzed until the mirror is deactivated or until that creature can no longer see the mirror. A creature paralyzed by the mirror can repeat the saving throw at the end of each of its turns, ending the effect on a success. If a creature''s saving throw is successful or the effect ends for it, the creature is immune to this mirror''s effect for the next 24 hours.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (939, 3, 'string', 'While paralyzed by the mirror, the creature sees events from their past reflected in the mirror''s glass. These memories aren''t real, but rather idealized versions of those occurrences. Nearby observers can glimpse flashes of these memories if looking indirectly at the mirror.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (940, 0, 'string', 'The holder of this platinum hand mirror can learn something about the history of a specific object or creature by taking an action to gaze into the mirror and think of the target. Instead of the holder''s reflection, the mirror presents scenes from the target''s past. Information conveyed is accurate, but it is random and cryptic, and presented in no particular order. Once it is activated, the mirror gives its information for 1 minute or less, then returns to normal. It can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (941, 0, 'string', 'This thick, fur-lined cloak has the frost rune stitched on the hem in silvery blue thread. Frigid wind swirls around the cloak, regardless of the weather.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (941, 1, 'string', 'While wearing this cloak, you have resistance to cold damage. Additionally, when you move within 5 feet of a creature, you can cause the cloak''s cold wind to buffet the creature. The creature must succeed on a 14 Dexterity saving throw or take 1d6 cold damage and have the prone condition. A creature can be affected by the mantle only once during a turn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (941, 2, 'entries', NULL, '{"type":"entries","name":"Invoking the Rune","entries":["As an action, you can invoke the mantle''s rune to cast the {@spell sleet storm} spell (save {@dc 14}) with it. When you use the mantle to cast the spell, the area of the spell isn''t {@quickref difficult terrain||3} for you, and you can see through the storm, ignoring the normal penalties of a {@quickref Vision and Light||2||heavily obscured} area.","Once the rune has been invoked, it can''t be invoked again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (942, 0, 'string', 'Innovation is a dangerous pursuit, at least the way the mages of the Izzet League engage in it. As protection against the risk of an experiment going awry, they have developed a device to help channel and control their magic. This apparatus is a collection of leather straps, flexible tubing, glass cylinders, and plates, bracers, and fittings made from a magic-infused metal alloy called mizzium, all assembled into a harness. The item weighs 8 pounds.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (942, 1, 'string', 'While you are wearing the mizzium apparatus, you can use it as an arcane focus. In addition, you can attempt to cast a spell that you do not know or have prepared. The spell you choose must be on your class''s spell list and of a level for which you have a spell slot, and you must provide the spell''s components.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (942, 2, 'string', 'You expend a spell slot to cast the spell as normal, but before resolving it you must make an Intelligence (Arcana) check. The DC is 10 + twice the level of the spell slot you expend to cast the spell.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (942, 3, 'string', 'On a successful check, you cast the spell as normal, using your spell save DC and spellcasting ability modifier. On a failed check, you cast a different spell from the one you intended. Randomly determine the spell you cast by rolling on the table for the level of the spell slot you expended. If the slot is 6th level or higher, roll on the table for 5th-level spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (942, 4, 'string', 'If you try to cast a cantrip you don''t know, the DC for the Intelligence (Arcana) check is 10, and on a failed check, there is no effect.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (942, 5, 'table', NULL, '{"type":"table","caption":"1st-Level Spells","colLabels":["d6","spell"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","{@spell burning hands}"],["2","{@spell chaos bolt|XGE}"],["3","{@spell color spray}"],["4","{@spell faerie fire}"],["5","{@spell fog cloud}"],["6","{@spell thunderwave}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (942, 6, 'table', NULL, '{"type":"table","caption":"2nd-Level Spells","colLabels":["d6","spell"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","{@spell blur}"],["2","{@spell gust of wind}"],["3","{@spell heat metal}"],["4","{@spell Melf''s acid arrow}"],["5","{@spell scorching ray}"],["6","{@spell shatter}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (942, 7, 'table', NULL, '{"type":"table","caption":"3rd-Level Spells","colLabels":["d6","spell"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","{@spell fear}"],["2","{@spell feign death}"],["3","{@spell fireball}"],["4","{@spell gaseous form}"],["5","{@spell sleet storm}"],["6","{@spell stinking cloud}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (942, 8, 'table', NULL, '{"type":"table","caption":"4th-Level Spells","colLabels":["d4","spell"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","{@spell confusion}"],["2","{@spell conjure minor elementals}"],["3","{@spell Evard''s black tentacles}"],["4","{@spell ice storm}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (942, 9, 'table', NULL, '{"type":"table","caption":"5th-Level Spells","colLabels":["d4","spell"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","{@spell animate objects}"],["2","{@spell cloudkill}"],["3","{@spell cone of cold}"],["4","{@spell flame strike}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (943, 0, 'string', 'This short tube, about 2 feet long and 6 inches in diameter, is made from mizzium, a magically enhanced metal alloy forged by the Izzet League. The end that''s pointed toward a target is open, and a glowing ball of molten metal can be seen at the other end as long as the mortar has at least 1 charge remaining.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (943, 1, 'string', 'The mortar has 4 charges for the following properties. It regains 1d4 expended charges daily at dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (943, 2, 'entries', NULL, '{"type":"entries","name":"Molten Spray","entries":["You can expend 1 charge as an action to loose a 30-foot cone of molten mizzium. Each creature in the area must make a {@dc 15} Dexterity saving throw, taking {@damage 5d4} fire damage on a failed save, or half as much damage on a successful one."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (943, 3, 'entries', NULL, '{"type":"entries","name":"Mizzium Bombard","entries":["You can expend 3 charges as an action to launch a hail of molten projectiles in a 20-foot-radius, 40-foot-high cylinder centered on a point you can see within 60 feet of you. Each creature in the area must make a {@dc 15} Dexterity saving throw. A creature takes {@damage 5d8} fire damage on a failed save, or half as much damage on a successful one."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (944, 0, 'string', 'Includes:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (944, 1, 'list', NULL, '{"type":"list","items":["a {@item chest|phb}","a {@item crowbar|phb}","a {@item hammer|phb}","three wooden stakes","a {@item holy symbol|phb}","a {@item holy water (flask)|phb|flask of holy water}","a set of {@item manacles|phb}","a {@item steel mirror|phb}","a {@item oil (flask)|phb|flask of oil}","a {@item tinderbox|phb}","3 {@item torch|phb|torches}"]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (945, 0, 'string', 'This thick, black paint is stored in a small jar, containing enough paint to apply moodmarks to one creature. The paint is dabbed on the face in spots or markings that often resemble the eyes of insects or spiders. Applying the paint in this way takes 1 minute.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (945, 1, 'string', 'For the next 8 hours, the marks change to reflect your mental state. A creature that can see you and makes a successful 10 Wisdom (Insight) check can discern whether you are happy, sad, angry, disgusted, surprised, or afraid, as well as the main source of that emotion. For example, you might communicate fear caused by a monster you just saw around the corner, grief at the loss of a friend, or happiness derived from pride in your performance in combat. A dark elf has advantage on this check.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (946, 0, 'string', 'As an action, you can speak a command word while touching this card and transform into a wolflike biped for 1 minute. For that duration, you have resistance to bludgeoning, piercing, and slashing damage, and when you hit a target with a weapon or an unarmed strike, the target takes an extra 1d6 force damage. Once this property is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (947, 0, 'string', 'Of all the magic items created by the elves, one of the most prized and jealously guarded is a moon blade. In ancient times, nearly all elven noble houses claimed one such blade. Over the centuries, some blades have faded from the world, their magic lost as family lines have become extinct. Other blades have vanished with their bearers during great quests. Thus, only a few of these weapons remain.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (947, 1, 'string', 'A moonblade passes down from parent to child. The sword chooses its bearer and remains bonded to that person for life. If the bearer dies, another heir can claim the blade. If no worthy heir exists, the sword lies dormant. It functions like a normal longsword until a worthy soul finds it and lays claim to its power.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (947, 2, 'string', 'A moonblade serves only one master at a time. The attunement process requires a special ritual in the throne room of an elven regent or in a temple dedicated to the elven gods.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (947, 3, 'string', 'A moonblade won''t serve anyone it regards as craven, erratic, corrupt, or at odds with preserving and protecting elvenkind. If the blade rejects you, you make ability checks, attack rolls, and saving throws with disadvantage for 24 hours. If the blade accepts you, you become attuned to it and a new rune appears on the blade. You remain attuned to the weapon until you die or the weapon is destroyed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (947, 4, 'string', 'A moonblade has one rune on its blade for each master it has served (typically 1d6 + 1). The first rune always grants a +1 bonus to attack and damage rolls made with this magic weapon. Each rune beyond the first grants the moon blade an additional property. The DM chooses each property or determines it randomly on the Moon Blade Properties table.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (947, 5, 'table', NULL, '{"type":"table","caption":"Moonblade Properties","colLabels":["{@dice d100}","Property"],"colStyles":["col-1 text-center","col-11"],"rows":[["01-40","Increase the bonus to attack and damage rolls by 1, to a maximum of +3. Reroll if the moonblade already has a +3 bonus."],["41-80","The moonblade gains a randomly determined {@table Special Features; What Minor Property Does It Have|dmg|minor property} (see \"Special Features\" DMG p143)."],["81-82","The moonblade gains the finesse property."],["83-84","The moonblade gains the thrown property (range 20/60 feet)."],["85-86","The moonblade functions as a {@item defender}."],["87-90","The moon blade scores a critical hit on a roll of 19 or 20."],["91-92","When you hit with an attack using the moon blade, the attack deals an extra {@damage 1d6} slashing damage."],["93-94","When you hit a creature of a specific type (such as dragon, fiend, or undead) with the moonblade, the target takes an extra {@damage 1d6} damage of one of these types: acid, cold, fire, lightning, or thunder."],["95-96","You can use a bonus action to cause the moonblade to flash brightly. Each creature that can see you and is within 30 feet of you must succeed on a {@dc 15} Constitution saving throw or become {@condition blinded} for 1 minute. A creature can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success. This property can''t be used again until you take a short rest while attuned to the weapon."],["97-98","The moonblade functions as a {@item ring of spell storing}."],["99","You can use an action to call forth an elfshadow, provided that you don''t already have one serving you. The elfshadow appears in an unoccupied space within 120 feet of you. It uses the statistics for a {@creature shadow}, except it is neutral, immune to effects that turn undead, and doesn''t create new shadows. You control this creature, deciding how it acts and moves. It remains until it drops to 0 hit points or you dismiss it as an action."],["00","The moonblade functions as a {@item vorpal sword}."]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (947, 6, 'entries', NULL, '{"name":"Sentience","type":"entries","entries":["A moon blade is a sentient neutral good weapon with an Intelligence of 12, a Wisdom of 10, and a Charisma of 12. It has hearing and {@sense darkvision} out to a range of 120 feet.","The weapon communicates by transmitting emotions, sending a tingling sensation through the wielder''s hand when it wants to communicate something it has sensed. It can communicate more explicitly, through visions or dreams, when the wielder is either in a trance or asleep."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (947, 7, 'entries', NULL, '{"name":"Personality","type":"entries","entries":["Every moonblade seeks the advancement of elvenkind and elven ideals. Courage, loyalty, beauty, music, and life are all part of this purpose.","The weapon is bonded to the family line it is meant to serve. Once it has bonded with an owner who shares its ideals, its loyalty is absolute.","If a moon blade has a flaw, it is overconfidence. Once it has decided on an owner, it believes that only that person should wield it, even if the owner falls short of elven ideals."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (948, 0, 'string', 'A translucent white with pale blue glow gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (949, 0, 'string', 'Beasts of burden are common throughout Xhorhas, and often horses and other riding animals are the best or only option. For those who require speed and have a little more coin to spend, the best option is often a moorbounder. However, moorbounders that haven''t undergone proper training or established bonds of trust with their masters tend to attack and even eat their riders.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (949, 1, 'string', 'Moorbounders can cost anywhere from 300 to 500 gp each. They''re often cheaper and more abundant in towns on the wastes than in cities like Rosohna.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (950, 0, 'string', 'A translucent pink or yellow-white with mossy gray or green markings gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (951, 0, 'table', NULL, '{"caption":"Mournland Trinkets","colLabels":["d10","Trinket"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","The favorite childhood toy of one of the adventurers, floating in midair"],["2","A tin whistle that makes beautiful colors and patterns magically appear when it''s played"],["3","A toy lightning rail car"],["4","A glass eye"],["5","A silver lapel pin of a finely detailed gorgon''s head with ruby eyes"],["6","A monocle that can be used as a telescope"],["7","A six-inch-tall mechanical marmoset in need of minor repairs"],["8","A small medallion made of silver, depicting a tower with an eye at the top of it and other smaller eyes embedded in the sides"],["9","A cloak pin made of iron in the shape of two hammers joined by a semicircular haft"],["10","A wooden dinosaur toy that has movable legs"]],"type":"table"}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (952, 0, 'string', 'You can use an action to place this 1-inch-diameter granite sphere on the ground and speak its command word, which is "petrification" in Terran. The sphere rapidly grows into a stout tower that remains until you use an action to touch the tower and speak the command word again, whereupon the tower shrinks back to a 1-inch-diameter granite sphere. The tower must be empty to shrink in this way. The tower bristles with muddy knobs that constantly extrude and retract across its surface, as though the tower were breathing through a coating of thick mud.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (952, 1, 'string', 'Each creature in the area where the tower appears must make a 15 Dexterity saving throw, taking 10d10 bludgeoning damage on a failed save, or half as much damage on a successful one. In either case, the creature is pushed to an unoccupied space outside but next to the tower. Objects in the area that aren''t being worn or carried take this damage and are pushed automatically.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (952, 2, 'string', 'Whenever it expands, the mudslick tower merges with any natural stone it touches, awkwardly tipping and wedging itself to touch as much natural stone as it can.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (952, 3, 'string', 'The tower is 20 feet on a side and 30 feet high, with arrow slits on all sides and a battlement atop it. Its interior is divided into two floors, with a ladder running along one wall to connect them. The ladder ends at a trapdoor leading to the roof. When activated, the tower has a small door on the side facing you. The door opens only at your command, which you can speak as a bonus action. It is immune to the knock spell and similar magic, such as that of a chime of opening.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (952, 4, 'string', 'Although it looks like stone, the tower is made of adamantine, and its magic prevents creatures from tipping it over. The roof, the door, and the walls each have 100 hit points, immunity to damage from nonmagical weapons excluding siege weapons, and resistance to all other damage. While merged with natural stone, the mudslick tower has immunity to all damage. Only a wish spell can repair the tower (this use of the spell counts as replicating a spell of 8th level or lower). Each casting of wish causes the roof, the door, or one wall to regain 50 hit points.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (954, 0, 'string', 'White dust swirls constantly within this pale gray potion. When you drink the potion, it cures you of mummy rot.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (955, 0, 'string', 'Whoever drinks this concoction gains advantage on death saving throws for 24 hours.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (956, 0, 'string', 'Roiling green mist fills this glass orb, which the exiled Strixhaven mage Murgaxor once used in foul magical experiments. Murgaxor''s spirit has infused the orb, which he uses to spread a terrible curse among Strixhaven''s students.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (956, 1, 'entries', NULL, '{"type":"entries","name":"Sentience","entries":["Murgaxor''s orb is a sentient, chaotic evil magic item with the following properties:",{"type":"list","items":["The orb has an Intelligence of 20, a Wisdom of 16, and a Charisma of 16, as well as hearing and {@sense darkvision} out to a range of 30 feet.","The orb can speak, read, and understand Common, and it can communicate telepathically with any creature touching it.","At any time during your turn, the orb can cast the {@spell suggestion} spell (save {@dc 17}), targeting you or one other creature that touched the orb within the last 24 hours. This isn''t a power of the orb that you control."]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (956, 2, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["Any Humanoid you touch while holding the orb must succeed on a {@dc 10} Wisdom saving throw or become cursed. Each creature cursed by the orb bears an echo of Murgaxor''s hateful thoughts, and that creature suffers from headaches that are persistent but not debilitating until the curse ends. On your turn, the orb can use an action to produce one of the following effects, targeting one or more creatures it has cursed:"]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (956, 3, 'entries', NULL, '{"type":"entries","name":"Unconsciousness","entries":["The cursed creature falls {@condition unconscious} for 1 hour. The creature is roused if it takes damage or someone uses an action to shake or slap it awake."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (956, 4, 'entries', NULL, '{"type":"entries","name":"Visions of Terror","entries":["The cursed creature sees terrifying visions, causing it to view all creatures that aren''t also cursed as dangerous monsters for 10 minutes. The cursed creature must use its action each round to make one attack against the nearest non-cursed creature. If the cursed creature has multiple possible targets, it attacks one at random. This effect ends if the cursed creature is {@condition incapacitated}.","After either of these effects ends, the affected creature is no longer cursed. The curse can also be removed from a creature with a {@spell remove curse} spell or similar magic. All cases of the curse end if Murgaxor''s orb is destroyed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (956, 5, 'entries', NULL, '{"type":"entries","name":"Magical Signature","entries":["As a side effect of the orb''s curse, the spell {@spell detect magic} reveals an aura of enchantment surrounding creatures bearing the curse. This aura is distinctive, but in a way {@spell detect magic} offers no further details about."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (956, 6, 'entries', NULL, '{"type":"entries","name":"Destroying the Orb","entries":["Murgaxor''s orb has AC 18; 20 hit points; immunity to necrotic, poison, and psychic damage; and resistance to all other types of damage. If reduced to 0 hit points, the orb shatters."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (957, 0, 'string', 'This paste made from the muroosa bush is known to help prevent sunburn, but it is also a fire retardant. After spending 1 minute applying a quarter pint of muroosa balm to your skin, you gain resistance against fire damage for 1 hour.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (957, 1, 'string', 'A dose of muroosa balm sufficient for treating sunburn costs 1 gp.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (958, 0, 'string', 'A question mark is worked into the head of this key. The key has a 5 chance of unlocking any lock into which it''s inserted. Once it unlocks something, the key disappears.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (960, 0, 'string', 'This incredible silvered blade once belonged to the legendary White Duke, a founding member of the Golden Grin, and many other master bards before him. It resonates with nearby musical tones. In the modern era, it is best known as the personal weapon of famed bard Scanlan Shorthalt.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (960, 1, 'entries', NULL, '{"type":"entries","name":"Dormant","entries":["While Mythcarver is in a dormant state, you gain a +1 bonus to attack and damage rolls made with this magic weapon. This longsword''s enchantments and light construction also grant it the Finesse property, which allows you to use either your Strength or Dexterity modifier for the attack and damage rolls when making an attack with this weapon.","Additionally, when you target an ally with your Bardic Inspiration feature, you gain {@quickref Advantage and Disadvantage|PHB|2|0|advantage} on attacks with this weapon until the end of your turn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (962, 0, 'string', 'This backpack-sized device holds a balloon-based parachute. If you fall while wearing this device, you can use your reaction to deploy the parachute. Once deployed, the parachute rapidly inflates, and you descend 60 feet per round and take no damage from falling. When you are 10 feet away from the ground, roll a d20. If you roll a 5 or less, the parachute gives out, and you begin to fall normally.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (963, 0, 'string', 'This cloak shifts color and texture to blend with the terrain surrounding you. While wearing the cloak, you can use it as a spellcasting focus for your druid and ranger spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (963, 1, 'string', 'While you are in an area that is lightly obscured, you can Hide as a bonus action even if you are being directly observed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (964, 0, 'string', 'Built and used by mind flayers, nautiloids are designed exclusively for space travel. They can''t float on water, nor can they land safely on the ground.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (964, 1, 'string', 'As an action, a creature attuned to a nautiloid''s spelljamming helm and in physical contact with the ship can transport the nautiloid and all creatures and objects aboard it to a different plane of existence, at or near a destination envisioned by the spelljammer (or to a random location on the plane if no destination is envisioned). This property is a feature of the ship, not the spelljamming helm. Each time this property is used, roll a d6. On a 56, the property recharges after 1 minute; otherwise, it can''t be used again for 24 hours.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (965, 0, 'string', 'A navigation orb is a hollow, 7-foot-diameter sphere of thin, polished mithral with a large skye (cloud) rune embossed on its outer surface. The orb levitates 10 feet above the ground and is keyed to a particular cloud castle, allowing you to control that castle''s altitude and movement while the orb is inside the castle. If the orb is destroyed or removed from its castle, the castle''s altitude and location remain fixed until the orb is returned or replaced.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (965, 1, 'string', 'As an action, you can cause one of the following effects to occur if you are touching the orb:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (965, 2, 'string', 'The castle moves at a speed of 1 mph in a straight line, in a direction of your choice, until the castle stops or is made to stop, or until another action is used to change its direction. If this movement brings the castle into contact with the ground, the castle lands gently.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (965, 3, 'string', 'The castle, if it is moving, comes to a gradual stop.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (965, 4, 'string', 'The castle makes a slow, 90-degree turn clockwise or counterclockwise (turning a northerly view into a westerly view, for example). The castle can turn while it is moving in a straight line.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (965, 5, 'string', 'Any creature touching the orb knows the altitude of the base of the castle above the ground or water below it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (966, 0, 'string', 'This set of instruments is used for navigation at sea. Proficiency with navigator''s tools lets you chart a ship''s course and follow navigation charts. In addition, these tools allow you to add your proficiency bonus to any ability check you make to avoid getting lost at sea.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (967, 0, 'string', 'While wearing this necklace, you can breathe normally in any environment, and you have advantage on saving throws made against harmful gases and vapors (such as cloudkill and stinking cloud effects, inhaled poisons, and the breath weapons of some dragons).', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (969, 0, 'string', 'This necklace has 1d6 + 3 beads hanging from it. You can use an action to detach a bead and throw it up to 60 feet away. When it reaches the end of its trajectory, the bead detonates as a 3rd-level fireball spell (save 15).', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (969, 1, 'string', 'You can hurl multiple beads, or even the whole necklace, as one action. When you do so, increase the level of the fireball by 1 for each bead beyond the first.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (971, 0, 'string', 'This necklace has 1d4 + 2 magic beads made from aquamarine, black pearl, or topaz. It also has many nonmagical beads made from stones such as amber, bloodstone, citrine, coral, jade, pearl, or quartz. If a magic bead is removed from the necklace, that bead loses its magic.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (971, 1, 'string', 'Six types of magic beads exist. The DM decides the type of each bead on the necklace or determines it randomly. A necklace can have more than one bead of the same type. To use one, you must be wearing the necklace. Each bead contains a spell that you can cast from it as a bonus action (using your spell save DC if a save is necessary). Once a magic bead''s spell is cast, that bead can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (971, 2, 'table', NULL, '{"type":"table","colLabels":["{@dice d20}","Bead of...","Spell"],"colStyles":["col-1 text-center","col-2","col-9"],"rows":[["1-6","Blessing","{@spell Bless}"],["7-12","Curing","{@spell Cure wounds} (2nd level) or {@spell lesser restoration}"],["13-16","Favor","{@spell Greater restoration}"],["17-18","Smiting","{@spell Branding smite}"],["19","Summons","{@spell Planar ally}"],["20","Wind walking","{@spell Wind walk}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (973, 0, 'string', '{#itemEntry Absorbing Tattoo|TCE}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (974, 0, 'string', 'This weapon is a magic dagger disguised as a sewing needle. When you hold it and use a bonus action to speak its command word, it transforms into a dagger or back into a needle.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (974, 1, 'string', 'You gain a +1 bonus to attack and damage rolls made with the dagger. While holding it, you can use an action to cast the mending cantrip from it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (975, 0, 'string', 'This strange pistol resembles a flask with a honeycomb of tubes sticking out its front. The weapon is powered by an energy cell stored at the base of the flask. Placing a full energy cell in the pistol gives the pistol 10 charges.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (975, 1, 'string', 'As an action while holding this pistol, you can expend 1 of its charges to fire a burst of glowing, needlelike darts from the pistol in a 15-foot cone. Each creature in that area must make a 15 Dexterity saving throw, taking 8d4 piercing damage on a failed save or half as much damage on a successful one.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (975, 2, 'entries', NULL, '{"name":"Replacing the Energy Cell","type":"entries","entries":["While the pistol has charges remaining, its {@item energy cell} can''t be removed. Once the pistol has 0 charges, you can replace the {@item energy cell} with a new cell by using an action or a bonus action."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (976, 0, 'string', 'You gain a +3 bonus to attack and damage rolls made with this magic weapon. When you hit a fiend or an undead with it, that creature takes an extra 2d10 radiant damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (976, 1, 'string', 'While you hold the drawn sword, it creates an aura in a 10-foot radius around you. You and all creatures friendly to you in the aura have advantage on saving throws against spells and other magical effects. If you have 17 or more levels in the paladin class, the radius of the aura increases to 30 feet.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (976, 2, 'entries', NULL, '{"type":"entries","name":"Sentience","entries":["Nepenthe is a sentient, neutral evil weapon with an Intelligence of 10, a Wisdom of 8, and a Charisma of 18. It has hearing and {@sense darkvision} out to a range of 60 feet. It can read and understand Elvish. It can also speak Elvish, but only through the voice of its wielder, with whom the sword can communicate telepathically."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (976, 3, 'entries', NULL, '{"type":"entries","name":"Personality","entries":["In its lifetime, the sword has beheaded thousands of criminals, not all of whom were guilty of the crimes for which they were convicted. The sword cannot distinguish the guilty from the innocent. With each beheading, it hungers for more justice and blood. The sword is corrupt and irredeemable."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (977, 0, 'string', 'Unlike most scrolls, a Nether Scroll of Azumar is not a consumable magic item. It takes 30 days of concentrated studyat least 8 hours per dayto attempt to understand this scroll. After completing this study, you must make a 25 Intelligence (Arcana) check. If this check fails, you take 16d10 psychic damage, and you can attempt the check again after another 30 days of concentrated study.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (977, 1, 'string', 'When you succeed on the check, you gain the following benefits:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (977, 2, 'list', NULL, '{"type":"list","items":["Your Intelligence score increases by 2, to a maximum of 22. Once you gain this benefit, you can''t use this scroll to increase your Intelligence again.","You gain advantage on saving throws against spells and other magical effects."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (977, 3, 'string', 'When you gain the scroll''s benefits, a stone golem magically appears in an unoccupied space within 60 feet of you and acts as your ally. If you die, the golem turns to dust.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (978, 0, 'string', 'You gain a +1 bonus to AC and saving throws while wearing this ring.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (978, 1, 'string', 'The ring is made from a single piece of mystical green glass that''s stronger than steel. It has the added property of making the wearer less likely to drop or lose anything held in the hand the ring is on. The wearer receives a +4 bonus to saving throws or checks made to avoid dropping or being disarmed of any item held in that hand.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (979, 0, 'string', 'Copper coin about the size of a thumbnail (1 nib = 1 common copper coin)', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (980, 0, 'string', 'This whistle is carved from transparent crystal, and it resembles a tiny dragon curled up like a snail. The name Night Caller is etched on the whistle in Dwarvish runes. If a character succeeds on a 20 Intelligence (Arcana or History) check, the character recalls lore that says the duergar made several such whistles for various groups in an age past.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (980, 1, 'string', 'If you blow the whistle in darkness or under the night sky, it allows you to cast the animate dead spell. The target can be affected through up to 10 feet of soft earth or similar material, and if it is, it takes 1 minute to claw its way to the surface to serve you. Once the whistle has animated an undead creature, it can''t do so again until 7 days have passed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (980, 2, 'string', 'Once every 24 hours, you can blow the whistle to reassert control over one or two creatures you animated with it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (981, 0, 'string', 'This heavy mace has a dark oaken handle and a head of blackened steel trimmed with gold. In combat, the mace''s head is wreathed in inky black energy.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (981, 1, 'string', 'You gain a +3 bonus to attack and damage rolls made with this weapon. When you hit with an attack roll using it, the target takes an additional 4d4 radiant damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (981, 2, 'string', 'If you have an evil alignment, you also gain the following benefits when attuned to Nightbringer:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (981, 3, 'entries', NULL, '{"type":"entries","name":"Blessing of the Dragon Queen","entries":["You are immune to the {@condition charmed} and {@condition frightened} conditions, and you gain immunity to one of the following damage types (choose when you attune to the weapon): acid, cold, fire, lightning, or poison."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (981, 4, 'entries', NULL, '{"type":"entries","name":"Eyes of Midnight","entries":["You have {@sense darkvision} with a range of 60 feet. If you already have {@sense darkvision}, its range increases by 60 feet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (981, 5, 'entries', NULL, '{"type":"entries","name":"Midnight Shroud","entries":["The weapon has 6 charges. When you hit a creature with an attack using this weapon, you can expend one charge to force the creature to make a {@dc 20} Constitution saving throw. On a failed save, the creature is {@condition blinded} until the start of your next turn. The weapon regains {@dice 1d6} expended charges daily at dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (982, 0, 'string', 'Used to summon night, this 6-inch-diameter, jet-black orb is cold to the touch. You can spend 10 minutes to activate it, causing the area within 10 miles of it at the moment of activation to become night even if it is daytime. This night lasts for 24 hours, until you cancel it as an action, or until your attunement to the pearl ends. Once used, the pearl can''t be used again for 24 hours.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (983, 0, 'string', 'Neogi terrorize Wildspace in ships of their own construction, which others call nightspiders. These vessels often wait in ambush for other ships. Once a target ship is identified, the neogi try to steal its spelljamming helm and capture its crew.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (983, 1, 'string', 'A nightspider''s crew usually consists of nineteen neogi and up to a half-dozen umber hulks, which the neogi use as shock troops.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (983, 2, 'string', 'Nightspiders are designed for space travel alone and can''t float on water or land safely on the ground (the weight of the ship would snap its spindly legs, destroying its weblike rigging in the process). Standard weaponry on a nightspider includes four ballistae (two forward-facing and two aft-facing) and a forward-facing mangonel.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (984, 0, 'string', 'To activate the nimblewright detector, a character must hold down its trigger. When the activated device comes within 500 feet of a nimblewright other than Nim, the umbrella begins to spin, whir, and click. The spinning, whirring, and clicking accelerates as the distance to the target lessens, reaching maximum velocity and volume when a nimblewright other than Nim is within 30 feet of the device.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (985, 0, 'string', 'The design of this bronze circlet resembles swirling clouds. At its center is set a deep-blue stone, upon which is inscribed the cloud rune.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (985, 1, 'string', 'While wearing this circlet, you take no damage from falling. Additionally, as a bonus action, you and everything you are wearing or carrying can teleport to an unoccupied space you can see within 15 feet of yourself, reappearing in a puff of shimmering clouds.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (985, 2, 'entries', NULL, '{"type":"entries","name":"Invoking the Rune","entries":["As an action, you can invoke the circlet''s rune to assume a cloudlike form. The form lasts for 1 minute, until you are incapacitated, or until you dismiss it (no action required).","While in cloud form, you have a flying speed of 60 feet and resistance to bludgeoning, piercing, and slashing damage.","Once the rune has been invoked, it can''t be invoked again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (986, 0, 'string', 'Typically found in 1d4 pots inside a fine wooden box with a brush (weighing 1 pound in total), these pigments allow you to create three-dimensional objects by painting them in two dimensions. The paint flows from the brush to form the desired object as you concentrate on its image.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (986, 1, 'string', 'Each pot of paint is sufficient to cover 1,000 square feet of a surface, which lets you create inanimate objects or terrain featuressuch as a door, a pit, flowers, trees, cells, rooms, or weaponsthat are up to 10,000 cubic feet. It takes 10 minutes to cover 100 square feet.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (986, 2, 'string', 'When you complete the painting, the object or terrain feature depicted becomes a real, nonmagical object. Thus, painting a door on a wall creates an actual door that can be opened to whatever is beyond. Painting a pit on a floor creates a real pit, and its depth counts against the total area of objects you create.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (986, 3, 'string', 'Nothing created by the pigments can have a value greater than 25 gp. If you paint an object of greater value (such as a diamond or a pile of gold), the object looks authentic, but close inspection reveals it is made from paste, bone, or some other worthless material.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (986, 4, 'string', 'If you paint a form of energy such as fire or lightning, the energy appears but dissipates as soon as you complete the painting, doing no harm to anything.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (987, 0, 'string', 'When you nock an arrow on this bow, it whispers in Elvish, "Swift defeat to my enemies." When you use this weapon to make a ranged attack, you can, as a command phrase, say, "Swift death to you who have wronged me." The target of your attack becomes your sworn enemy until it dies or until dawn seven days later. You can have only one such sworn enemy at a time. When your sworn enemy dies, you can choose a new one after the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (987, 1, 'string', 'When you make a ranged attack roll with this weapon against your sworn enemy, you have advantage on the roll. In addition, your target gains no benefit from cover, other than Cover, and you suffer no disadvantage due to long range. If the attack hits, your sworn enemy takes an extra 3d6 piercing damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (987, 2, 'string', 'While your sworn enemy lives, you have disadvantage on attack rolls with all other weapons.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (988, 0, 'string', 'An opaque black gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (989, 0, 'string', 'You gain a +2 bonus to AC and resistance to poison damage while you wear this armor. In addition, you gain advantage on ability checks and saving throws made to avoid or end the grappled condition on yourself.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (991, 0, 'string', 'Also at rank 2, you gain the use of an uncommon magic item taking the form of a pair of spectacles, a spyglass, a monocle, or any other device with one or two lenses. When you look through the obviator''s lenses, you use a bonus action to make them function as eyes of minute seeing or eyes of the eagle. Once you make this choice, it cannot be changed until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (991, 1, 'entries', NULL, '{"type":"entries","name":"Enhanced Lenses","entries":["At rank 4, your obviator''s lenses gain additional power and become a rare magic item. The lenses now function as both {@item eyes of the eagle} and {@item eyes of minute seeing}.","Additionally, you can focus the power of the lenses to gain accuracy in combat, gaining advantage on a weapon attack roll (no action required). If that attack hits, roll one additional weapon damage die. This property of the lenses can''t be used again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (992, 0, 'string', 'Head office grants you the use of a unique item known as an occultant abacus (sometimes just referred to as an occultant), whose beads resemble tiny skulls.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (992, 1, 'entries', NULL, '{"type":"entries","name":"Read the Kill","entries":["Also at rank 1, your occultant abacus not only tracks your franchise''s kills, it helps you determine the impact of those kills on the franchise''s fate. Over a period of 1 minute, you can study a creature killed by someone in your franchise within the last 24 hours, then grant the character who slew it a {@dice d10}. Once within the next hour, that character can add the {@dice d10} to an attack roll, ability check, or saving throw. If it''s not clear who administered the killing blow, you grant this benefit to a random creature involved in the fight. Once you use this feature, you can''t use it again until you finish a long rest.",{"type":"table","colLabels":["d6","Reading"],"colStyles":["col-2 text-center","col-10"],"rows":[["1","Using a tiny bellows to pump one last breath into a corpse."],["2","Reading the entrails with special reading-the-entrails glasses."],["3","Burning a small piece of the body and looking for shapes in the smoke."],["4","A cautious, reasoned, professional guesstimate."],["5","Careful measurements. The distance between nostrils. The difference between the index finger and the middle finger. The elasticity of the ear lobe."],["6","You take a long, careful look, and then decide based on which fellow franchisee has been nice to you lately."]]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (992, 2, 'entries', NULL, '{"type":"entries","name":"Eldritch Occultant","entries":["Starting at rank 2, your occultant abacus becomes an uncommon magic item that can track lives both eliminated and saved. While holding your occultant abacus within 5 feet of a creature killed within the past 24 hours, you can cast the {@spell augury} spell. The course of action you inquire about with the spell does not need to have any connection to the dead creature. This property of the occultant abacus can''t be used again until the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (992, 3, 'entries', NULL, '{"type":"entries","name":"Bring Out Your Dead","entries":["Also at rank 2, you regain the use of your Read the Kill feature after you finish a short or long rest."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (992, 4, 'entries', NULL, '{"type":"entries","name":"Bead of Instant Karma","entries":["At rank 3, one of the beads on your occultant abacus channels the power of instant karma, turning the occultant abacus into a rare magic item. As a reaction, you can target one creature you can see that is about to attempt an ability check, attack roll, or saving throw, either granting advantage or imposing disadvantage on the roll. The bead crumbles to dust when used, reappearing on your occultant abacus at the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (992, 5, 'entries', NULL, '{"type":"entries","name":"Bead of Diverted Karma","entries":["At rank 4, one of the beads on your occultant abacus allows you to divert karma to where it''s needed, turning the occultant abacus into a very rare magic item. While the bead is unused, you know automatically when any creature you can see is about to make an ability check, attack roll, or saving throw with disadvantage. When such a roll is made, you can use a reaction to grant a {@dice d10} to a different creature you can see. That creature can add the {@dice d10} to any ability check, attack roll, or saving throw it makes within the next minute. The bead crumbles to dust when used, reappearing on your occultant abacus at the next dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (992, 6, 'entries', NULL, '{"type":"entries","name":"Correct the Balance","entries":["As a rank 4 occultant, you learn that sometimes the death of a creature has unintended consequences. Within seven days of a creature''s death, you can use your occultant abacus to divine ways to reverse or mitigate events resulting from that death. As an action, make a {@dc 15} Intelligence ({@skill Religion}) check. On a success, you learn the relevant information based on the nature of the creature and its place in the world.","If you fail the check, this property of the occultant abacus can''t be used again until the next dawn. If you succeed on the check, this property can''t be used again until dawn seven days later."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (993, 0, 'string', 'Oil usually comes in a clay flask that holds 1 pint. As an action, you can splash the oil in this flask onto a creature within 5 feet of you or throw it up to 20 feet, shattering it on impact. Make a ranged attack against a target creature or object, treating the oil as an improvised weapon. On a hit, the target is covered in oil. If the target takes any fire damage before the oil dries (after 1 minute), the target takes an additional 5 fire damage from the burning oil. You can also pour a flask of oil on the ground to cover a 5-foot-square area, provided that the surface is level. If lit, the oil burns for 2 rounds and deals 5 fire damage to any creature that enters the area or ends its turn in the area. A creature can take this damage only once per turn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (994, 0, 'string', 'Beads of this cloudy gray oil form on the outside of its container and quickly evaporate. The oil can cover a Medium or smaller creature, along with the equipment it''s wearing and carrying (one additional vial is required for each size category above Medium). Applying the oil takes 10 minutes. The affected creature then gains the effect of the etherealness spell for 1 hour.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (995, 0, 'string', 'This clear, gelatinous oil sparkles with tiny, ultrathin silver shards. The oil can coat one slashing or piercing weapon or up to 5 pieces of slashing or piercing ammunition. Applying the oil takes 1 minute. For 1 hour, the coated item is magical and has a +3 bonus to attack and damage rolls.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (996, 0, 'string', 'This sticky black unguent is thick and heavy in the container, but it flows quickly when poured. The oil can cover a Medium or smaller creature, along with the equipment it''s wearing and carrying (one additional vial is required for each size category above Medium). Applying the oil takes 10 minutes. The affected creature then gains the effect of a freedom of movement spell for 8 hours.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (996, 1, 'string', 'Alternatively, the oil can be poured on the ground as an action, where it covers a 10-foot square, duplicating the effect of the grease spell in that area for 8 hours.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (997, 0, 'string', 'A creature subjected to this poison must succeed on a 13 Constitution saving throw or become poisoned for 24 hours. The poisoned creature is unconscious. The creature wakes up if it takes damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (999, 0, 'string', 'These dried leaves of the Olisuba tree, when steeped to make a tea, can help a body recover from strenuous activity. If you drink a dose of Olisuba tea during a long rest, your exhaustion level is reduced by 2 instead of 1 at the end of that long rest.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1000, 0, 'string', 'This root is typically Oloore Root Teabag for 100 gp per dose. It was originally a ritualistic tool used by the followers of many gods, but now sees more recreational use. Oracles used it to enhance their divinations, for it helped them leave their physical bodies behind and become closer to gods and spirits. Drinking tea brewed from the root causes intense hallucinations, which feel like they have granted perfect clarity of mind to the imbiber.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1000, 1, 'string', 'After drinking Oloore Root Teabag as an action, you must succeed on a 19 Constitution saving throws or experience vivid hallucinations for 1d4 hours. During this time, you have Advantage and Disadvantage on attack rolls and ability checks. If you fail the saving throws by 10 or more, your hallucinations become horrific visions and you gain a level of exhaustion. On a success, you gain preternatural clarity, allowing you to cast the scrying spell once within the next 1d4 hours.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1001, 0, 'string', 'An opaque black and white banded, or pure black or white gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1002, 0, 'string', 'A translucent pale blue with green and golden mottling gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1003, 0, 'string', 'This triangular fire opal measures about three inches on each side and is half an inch thick. The ild (fire) rune shimmers within its core, causing it to be slightly warm to the touch. The opal has the following properties, which work only while it''s on your person.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1003, 1, 'entries', NULL, '{"name":"Ignite","type":"entries","entries":["As an action, you can ignite an object within 10 feet of you. The object must be flammable, and the fire starts in a circle no larger than 1 foot in diameter."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1003, 2, 'entries', NULL, '{"name":"Fires Friend","type":"entries","entries":["You have resistance to cold damage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1003, 3, 'entries', NULL, '{"name":"Fire Tamer","type":"entries","entries":["As an action, you can extinguish any open flame within 10 feet of you. You choose how much fire to extinguish in that radius."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1003, 4, 'entries', NULL, '{"name":"Gift of Flame","type":"entries","entries":["You can transfer the opal''s magic to a nonmagical itema weapon or a suit of armorby tracing the ild rune there with your finger. The transfer takes 8 hours of work that requires the two items to be within 5 feet of each other. At the end, the opal is destroyed, and the rune appears in red on the chosen item, which gains a benefit based on its form:",{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Weapon","entry":"The weapon is now an uncommon magic weapon. It deals an extra {@damage 1d6} fire damage to any target it hits."},{"type":"item","name":"Armor","entry":"The armor is now a rare magic item that requires attunement. You have resistance to cold damage while wearing the armor."}]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1004, 0, 'string', 'While holding this orb, you can use an action to determine which way is north. This property functions only on the Material Plane.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1005, 0, 'string', 'Ages past, on the world of Krynn, elves and humans waged a terrible war against evil dragons. When the world seemed doomed, the wizards of the Towers of High Sorcery came together and worked their greatest magic, forging five Orbs of Dragonkind (or Dragon Orbs) to help them defeat the dragons. One orb was taken to each of the five towers, and there they were used to speed the war toward a victorious end. The wizards used the orbs to lure dragons to them, then destroyed the dragons with powerful magic.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1005, 1, 'string', 'As the Towers of High Sorcery fell in later ages, the orbs were destroyed or faded into legend, and only three are thought to survive. Their magic has been warped and twisted over the centuries, so although their primary purpose of calling dragons still functions, they also allow some measure of control over dragons.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1005, 2, 'string', 'Each orb contains the essence of an evil dragon, a presence that resents any attempt to coax magic from it. Those lacking in force of personality might find themselves enslaved to an orb.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1005, 3, 'string', 'An orb is an etched crystal globe about 10 inches in diameter. When used, it grows to about 20 inches in diameter, and mist swirls inside it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1005, 4, 'string', 'While attuned to an orb, you can use an action to peer into the orb''s depths and speak its command word. You must then make a 15 Charisma check. On a successful check, you control the orb for as long as you remain attuned to it. On a failed check, you become charmed by the orb for as long as you remain attuned to it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1005, 5, 'string', 'While you are charmed by the orb, you can''t voluntarily end your attunement to it, and the orb casts suggestion on you at will (save 18), urging you to work toward the evil ends it desires. The dragon essence within the orb might want many things: the annihilation of a particular people, freedom from the orb, to spread suffering in the world, to advance the worship of Takhisis (Tiamat name on Krynn), or something else the DM decides.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1005, 6, 'entries', NULL, '{"type":"entries","name":"Random Properties","entries":["An Orb of Dragonkind has the following random properties:",{"type":"list","items":["2 {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial properties}","1 {@table Artifact Properties; Minor Detrimental Properties|dmg|minor detrimental property}","1 {@table Artifact Properties; Major Detrimental Properties|dmg|major detrimental property}"]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1005, 7, 'entries', NULL, '{"type":"entries","name":"Spells","entries":["The orb has 7 charges and regains {@dice 1d4 + 3} expended charges daily at dawn. If you control the orb, you can use an action and expend 1 or more charges to cast one of the following spells (save {@dc 18}) from it: {@spell cure wounds} (5th-level version, 3 charges), {@spell daylight} (1 charge), {@spell death ward} (2 charges), or {@spell scrying} (3 charges). You can also use an action to cast the {@spell detect magic} spell from the orb without using any charges."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1005, 8, 'entries', NULL, '{"type":"entries","name":"Call Dragons","entries":["While you control the orb, you can use an action to cause the artifact to issue a telepathic call that extends in all directions for 40 miles. Evil dragons in range feel compelled to come to the orb as soon as possible by the most direct route. Dragon deities such as {@creature Tiamat|RoT} are unaffected by this call. Dragons drawn to the orb might be hostile toward you for compelling them against their will. Once you have used this property, it can''t be used again for 1 hour."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1005, 9, 'entries', NULL, '{"type":"entries","name":"Destroying an Orb","entries":["An Orb of Dragonkind appears fragile but is impervious to most damage, including the attacks and breath weapons of dragons. A {@spell disintegrate} spell or one good hit from a +3 magic weapon is sufficient to destroy an orb, however."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1006, 0, 'string', 'This common wondrous item is a hollow, 5-inch-diameter orb that weighs 5 pounds. Its outer shell is composed of notched bronze rings, which can be turned so that the notches line up. Aligning the notches requires an action, and doing so causes the orb to gong loudly until the notches are no longer aligned. The sounds are spaced 6 seconds apart and can be heard out to a range of 600 feet.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1007, 0, 'string', 'Said to be infused with the wisdom and power of the god Skoraeus, this polished stone orb is veined with iridescent crystal that seems to glow from within. The orb is 8 inches in diameter and weighs 8 pounds, making it a palm-sized trinket for a stone giant but a more unwieldy item for a Medium creature to use.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1007, 1, 'string', 'While holding this orb, you can use it as a spellcasting focus for your spells. You also gain the following benefits:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1007, 2, 'entries', NULL, '{"type":"entries","name":"Abundant Components","entries":["The orb has 3 charges and regains all expended charges at dawn. When you cast a spell while holding this orb, you can expend up to 3 charges to ignore the spell''s material components with a gold piece cost, up to 300 gp per charge expended."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1007, 3, 'entries', NULL, '{"type":"entries","name":"Astute Mind","entries":["You gain a +2 bonus to any Constitution saving throw you make to maintain your {@status concentration} on a spell."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1007, 4, 'entries', NULL, '{"type":"entries","name":"Divine Sight","entries":["You can see normally in darkness, both magical and nonmagical, to a distance of 120 feet."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1008, 0, 'string', 'This orb of granite is about the size of an adult human''s fist. The stein (stone) rune appears on it in the form of crystalline veins that run across the surface. The orb has the following properties, which work only while it''s on your person.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1008, 1, 'entries', NULL, '{"type":"entries","name":"Indomitable Stand","entries":["As an action, you can channel the orb''s magic to hold your ground. For the next minute or until you move any distance, you have advantage on all checks and saving throws to resist effects that force you to move. In addition, any enemy that moves to a space within 10 feet of you must succeed on a {@dc 12} Strength saving throw or be unable to move any farther this turn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1008, 2, 'entries', NULL, '{"type":"entries","name":"Stone Soul","entries":["You can''t be {@condition petrified}."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1008, 3, 'entries', NULL, '{"type":"entries","name":"Earthen Step","entries":["You can cast {@spell meld into stone} as a bonus action. Once you use this property, you can''t use it again until you finish a short or long rest."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1008, 4, 'entries', NULL, '{"type":"entries","name":"Gift of Stone","entries":["You can transfer the orb''s magic to a nonmagical itema shield or a pair of bootsby tracing the stein rune there with your finger. The transfer takes 8 hours of work that requires the two items to be within 5 feet of each other. At the end, the orb is destroyed, and the rune appears in silver on the chosen item, which gains a benefit based on its form:",{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Shield","entry":"The shield is now a rare magic item that requires attunement. While you wield it, you have resistance to all damage dealt by ranged weapon attacks."},{"type":"item","name":"Boots","entry":"The pair of boots is now an uncommon magic item that requires attunement. While you wear the boots, you have advantage on Strength saving throws, and you can use your reaction to avoid being knocked {@condition prone}."}]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1009, 0, 'string', 'This onyx sphere bears deep, spiraling grooves and dangles from an iron chain. While the orb is on your person, you gain the following benefits:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1009, 1, 'list', NULL, '{"type":"list","items":["Your Wisdom score increases by 2, as does your maximum for that score.","You gain {@sense darkvision} out to a range of 60 feet. If you already have {@sense darkvision}, the orb increases its range by 60 feet.","You have advantage on Wisdom checks to find hidden doors and paths."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1009, 2, 'entries', NULL, '{"type":"entries","name":"Curse","entries":["The orb is cursed, and becoming attuned to it extends the curse to you. As long as you remain cursed, you are unwilling to part with the orb, keeping it on your person at all times. All nonmagical flames within 30 feet of you automatically extinguish, and fire damage dealt by you is halved."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1010, 0, 'string', 'While holding this orb, you can use an action to determine whether it is morning, afternoon, evening, or nighttime outside. This property functions only on the Material Plane.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1011, 0, 'string', 'I saved the life of an orc, who gave me a stone with the symbol of the Many-Arrows tribe (a humanoid skull pierced by three arrows) carved into it. The stone is an uncommon magic item that requires attunement, and only I can attune to it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1011, 1, 'string', 'As an action, I can use the stone to summon the spirit of an orc warrior, which appears within 30 feet of me. The spirit uses the orc war chief stat block in the Monster Manual and disappears after 10 minutes or when reduced to 0 hit points. The spirit understands any language I speak and obeys my commands.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1011, 2, 'string', 'After the stone is used three times, it turns to dust. (See Appendix B of Icewind Dale: Rime of the Frostmaiden)', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1012, 0, 'string', 'A mighty axe wielded long ago by the dwarf king Torhild Flametongue, Orcsplitter is a battered weapon that appears unremarkable at first glance. Its head is graven with the Dwarvish runes for "orc," but the runes are depicted with a gap or slash through the markings; the word "orc" is literally split in two.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1012, 1, 'string', 'You gain the following benefits while holding this magic weapon:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1012, 2, 'list', NULL, '{"type":"list","items":["You gain a +2 bonus to attack and damage rolls made with it.","When you roll a 20 on an attack roll with this weapon against an {@creature orc}, that {@creature orc} must succeed on a {@dc 17} Constitution saving throw or drop to 0 hit points.","You can''t be {@status surprised} by {@creature orc||orcs} while you''re not {@condition incapacitated}. You are also aware when {@creature orc||orcs} are within 120 feet of you and aren''t behind {@quickref Cover||3||total cover}, although you don''t know their location.","You and any of your friends within 30 feet of you can''t be {@condition frightened} while you''re not {@condition incapacitated}."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1012, 3, 'entries', NULL, '{"name":"Sentience","type":"entries","entries":["Orcsplitter is a sentient, lawful good weapon with an Intelligence of 6, a Wisdom of 15, and a Charisma of 10. It can see and hear out to 120 feet and has {@sense darkvision}. It communicates by transmitting emotions to its wielder, although on rare occasions it uses a limited form of telepathy to bring to the wielder''s mind a couplet or stanza of ancient Dwarvish verse."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1012, 4, 'entries', NULL, '{"name":"Personality","type":"entries","entries":["Orcsplitter is grim, taciturn, and inflexible. It knows little more than the desire to face {@creature orc||orcs} in battle and serve a courageous, just wielder. It disdains cowards and any form of duplicity, deception, or disloyalty. The weapon''s purpose is to defend dwarves and to serve as a symbol of dwarven resolve. It hates the traditional foes of dwarvesgiants, goblins, and, most of all, {@creature orc||orcs}and silently urges its possessor to meet such creatures in battle."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1013, 0, 'string', 'Carved from an ogre''s petrified heart, the gray figurine depicts the Demon Prince of Undeath in ghastly detail, clutching his skull-topped wand in one hand and three severed heads by the hair in the other. The figurine smells like decaying flesh, and this scent is detectable out to a range of 5 feet.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1013, 1, 'string', 'The figurine is a Tiny object with AC 17, 3 hit points, and immunity to all types of damage except radiant damage. A detect evil and good spell or similar magic reveals that the figurine has been desecrated. As long as it has at least 1 hit point, the figurine has the following magical properties:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1013, 2, 'list', NULL, '{"type":"list","items":["Undead within 30 feet of the figurine can''t be turned.","Dead creatures within 30 feet of the figurine can''t be brought back to life.","A creature that holds the figurine while praying to Orcus for at least 1 hour has a {@chance 10} chance of summoning a smoky avatar of the demon lord. Once this avatar is summoned, it can''t be summoned again for 30 days. Orcus''s avatar has the statistics of a {@creature wraith} except that it''s chaotic evil. It attacks all non-undead creatures it encounters, and it disappears after 1 hour or when reduced to 0 hit points."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1014, 0, 'string', 'You can use this contraption to fly, provided your weight (including whatever you are wearing or carrying) doesn''t exceed 300 pounds. The ornithopter has a flying speed of 30 feet, and it moves according to your spoken directions while you are riding it. It can''t hover. If the ornithopter loses its rider while airborne, it falls and can''t fly again for 1d6 + 4 days.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1014, 1, 'string', 'The ornithopter is 8 feet long, has a 14-foot wingspan, and weighs 25 pounds.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1015, 0, 'string', 'This delicate and exquisitely crafted clockwork orrery features multiple geared components whose sweeping hands and dials represent the complex interplay of planar and magical realms. Standing two feet high, the orrery housing is a wondrous device imbued with magic of its own, but the power of its six clockwork components makes the artifact even more potent.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1015, 1, 'string', 'The Orrery of the Wanderer was created by a renowned clockwork mage known only as Lottie. She crafted the relic as a means of tapping into the power of the planes, and to channel the divination and foretelling powers of the stars. But Lottie soon realized that her creation was far more powerful than she had intended. As it tapped into planar magic, the orrery began to manifest the darkness and malevolence inherent to some of the planes. Eventually, it would impress upon its owner a desire to open portals into the most cursed and dangerous worlds, including the Far Realm.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1015, 2, 'string', 'To prevent the orrery from ever being so used, Lottie scattered the components that powered it, secreting them across the world. But the orrery''s instinct for survival is strong, and its components have a way of inexorably coming together over decades or centuries, found by treasure hunters, stolen by monsters, found and stolen againand moving closer to each other all the time.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1015, 3, 'string', 'Random Properties. The orrery has the following randomly determined properties:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1015, 4, 'list', NULL, '{"type":"list","items":["2 {@table Artifact Properties; Minor Beneficial Properties|dmg|minor beneficial properties}","1 {@table Artifact Properties; Major Beneficial Properties|dmg|major beneficial property}","1 {@table Artifact Properties; Major Detrimental Properties|dmg|major detrimental properties}"]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1015, 5, 'string', 'The random properties of the orrery might function only when all its components are installed, or might function sporadically if any components are missing.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1015, 6, 'entries', NULL, '{"type":"entries","name":"The Sum of Its Parts","entries":["Each of the six components that powers the Orrery of the Wanderer is a powerful magic relic in its own right:",{"type":"list","style":"list-no-bullets","items":["The {@item chronolometer|AI}","The {@item dimensional loop|AI}","The {@item far gear|AI}","The {@item rotor of return|AI}","The {@item timepiece of travel|AI}","The {@item wheel of stars|AI}"]},"Creatures can attune to the orrery''s components individually. If attuned to an individual component, a creature must hold the component to make use of its features. A creature can also attune to the orrery and all the components installed in it. Attuning to an installed component doesn''t count against the number of magic items you can normally attune to.","While a component is not installed in the orrery, its magic might function sporadically or with unpredictable side effects, as determined by the DM."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1015, 7, 'entries', NULL, '{"type":"entries","name":"Future Echoes","entries":["With or without its components, the orrery lets you catch momentary glimpses of the future that warn you of danger. As long as the orrery is within 30 feet of you, you gain a +1 bonus to AC."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1015, 8, 'entries', NULL, '{"type":"entries","name":"Into the Void","entries":["With or without its components, the attuned orrery infuses your spirit with the tumultuous energy of the planes. Over time, your alignment changes to chaotic neutral, and you are compelled to engage with and explore the most dangerous planar realms."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1015, 9, 'entries', NULL, '{"type":"entries","name":"Master of Travel","entries":["While all six components of the Orrery of the Wanderer are installed, the artifact has 7 charges. While touching the orrery, you can use an action and expend 1 or more charges to cast one of the following spells (save {@dc 17}): {@spell contact other plane} (3 charges), {@spell demiplane} (6 charges), {@spell gate} (7 charges), {@spell plane shift} (5 charges), or {@spell teleportation circle} (3 charges). The orrery regains {@dice 1d4 + 4} expended charges daily at dawn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1016, 0, 'string', 'This ring, adorned with the symbol of Orzhov, allows you to cast command. A guild signet is sometimes awarded to a guild member whose renown score in that guild is 5 or higher, as a reward for performing special services for the guild. Aside from its magical properties, the ring is also an indicator of Orzhov''s recognition and favor.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1016, 1, 'string', 'A signet has 3 charges, and it regains 1d3 expended charges daily at dawn. While wearing it, you can expend 1 charge to cast the associated spell (save 13).', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1017, 0, 'string', 'This keyrune is carved from white marble with veins of black. The end is shaped like a thrull''s head, with a gold faceplate affixed. On command, the keyrune transforms into a winged thrull for up to 2 hours. If you don''t come from an Orzhov oligarch family, it serves you grudgingly, clownishly aping your movements and mannerisms while carrying out your orders.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1017, 1, 'string', 'When you use an action to speak the item''s command word and place the keyrune on the ground in an unoccupied space within 5 feet of you, the keyrune transforms into a winged thrull. If there isn''t enough space for the creature, the keyrune doesn''t transform.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1017, 2, 'string', 'The creature is friendly to you, your companions, and other members of your guild (unless those guild members are hostile to you). It understands your languages and obeys your spoken commands. If you issue no commands, the creature takes the Dodge action and moves to avoid danger.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1017, 3, 'string', 'At the end of the duration, the creature reverts to its keyrune form. It reverts early if it drops to 0 hit points or if you use an action to speak the command word again while touching it. When the creature reverts to its keyrune form, it can''t transform again until 36 hours have passed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1018, 0, 'string', 'This flickering crystal holds the essence of an Outer Plane. As an action, you can attach the shard to a Tiny object (such as a weapon or a piece of jewelry) or detach it. It falls off if your attunement to it ends. You can use the shard as a spellcasting focus while you hold or wear it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1018, 1, 'string', 'Roll a d4 and consult the Outer Essence Shards table to determine the shard''s essence and property. When you use a Metamagic option on a spell while you are holding or wearing the shard, you can use that property.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1018, 2, 'table', NULL, '{"type":"table","caption":"Outer Essence Shards","colLabels":["d4","Property","Shard"],"colStyles":["col-2 text-center","col-2 text-center","col-8"],"rows":[["1","Lawful","{@item Outer Essence Shard (Lawful)|TCE}"],["2","Chaotic","{@item Outer Essence Shard (Chaotic)|TCE}"],["3","Good","{@item Outer Essence Shard (Good)|TCE}"],["4","Evil","{@item Outer Essence Shard (Evil)|TCE}"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1019, 0, 'string', 'This flickering crystal holds the essence of an Outer Plane. As an action, you can attach the shard to a Tiny object (such as a weapon or a piece of jewelry) or detach it. It falls off if your attunement to it ends. You can use the shard as a spellcasting focus while you hold or wear it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1019, 1, 'string', 'When you use a Metamagic option on a spell while you are holding or wearing the shard, you can use that property.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1019, 2, 'entries', NULL, '{"type":"entries","name":"Property","entries":["Choose one creature who takes damage from the spell. That target has disadvantage on attack rolls and ability checks made before the start of your next turn."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1020, 0, 'string', 'This flickering crystal holds the essence of an Outer Plane. As an action, you can attach the shard to a Tiny object (such as a weapon or a piece of jewelry) or detach it. It falls off if your attunement to it ends. You can use the shard as a spellcasting focus while you hold or wear it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1020, 1, 'string', 'When you use a Metamagic option on a spell while you are holding or wearing the shard, you can use that property.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1020, 2, 'entries', NULL, '{"type":"entries","name":"Property","entries":["Choose one creature who takes damage from the spell. That target takes an extra {@damage 3d6} necrotic damage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1021, 0, 'string', 'This flickering crystal holds the essence of an Outer Plane. As an action, you can attach the shard to a Tiny object (such as a weapon or a piece of jewelry) or detach it. It falls off if your attunement to it ends. You can use the shard as a spellcasting focus while you hold or wear it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1021, 1, 'string', 'When you use a Metamagic option on a spell while you are holding or wearing the shard, you can use that property.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1021, 2, 'entries', NULL, '{"type":"entries","name":"Property","entries":["You or one creature of your choice that you can see within 30 feet of you gains {@dice 3d6} temporary hit points."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1022, 0, 'string', 'This flickering crystal holds the essence of an Outer Plane. As an action, you can attach the shard to a Tiny object (such as a weapon or a piece of jewelry) or detach it. It falls off if your attunement to it ends. You can use the shard as a spellcasting focus while you hold or wear it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1022, 1, 'string', 'When you use a Metamagic option on a spell while you are holding or wearing the shard, you can use that property.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1022, 2, 'entries', NULL, '{"type":"entries","name":"Property","entries":["You can end one of the following conditions affecting yourself or one creature you can see within 30 feet of you: {@condition charmed}, {@condition blinded}, {@condition deafened}, {@condition frightened}, {@condition poisoned}, or {@condition stunned}."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1023, 0, 'string', 'This unique weapon can be used only by a Medium or larger creature that has a Strength of 18 or higher. The bow shoots oversized arrows that deal piercing damage equal to 2d6 + the wielder''s Strength modifier.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1031, 0, 'string', 'A creature subjected to this poison must succeed on a 16 Constitution saving throw or take 3 (1d6) poison damage and become poisoned. The poisoned creature must repeat the saving throw every 24 hours, taking 3 (1d6) poison damage on a failed save. Until this poison ends, the damage the poison deals can''t be healed by any means. After seven successful saving throws, the effect ends and the creature can heal normally.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1033, 0, 'string', 'After you write a message of fifty words or fewer on this magic sheet of parchment and speak a creature''s name, the parchment magically folds into a Tiny paper bird and flies to the recipient whose name you uttered. The recipient must be on the same plane of existence as you, otherwise the bird turns into ash as it takes flight.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1033, 1, 'string', 'The bird is an object that has 1 hit point, an Armor Class of 13, a flying speed of 60 feet, a Dexterity of 16 (+3), and a score of 1 (5) in all other abilities, and it is immune to poison and psychic damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1033, 2, 'string', 'It travels to within 5 feet of its intended recipient by the most direct route, whereupon it turns into a nonmagical and inanimate sheet of parchment that can be unfolded only by the intended recipient. If the bird''s hit points or speed is reduced to 0 or if it is otherwise immobilized, it turns into ash.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1033, 3, 'string', 'Paper birds usually come in small, flat boxes containing 1d6 + 3 sheets of the parchment.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1034, 0, 'string', 'This curious-looking pistol is shaped like a large, glass bulb with a handle on the bottom and brass prongs protruding from its front. The weapon is powered by an energy cell stored in its grip. Placing a full energy cell in the pistol gives the pistol 6 charges.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1034, 1, 'string', 'As an action while holding this pistol, you can expend 1 of its charges to fire a ray of crackling energy at a creature you can see within 60 feet of yourself. The target must succeed on a 15 Constitution saving throw or have the paralyzed condition for 1 minute. At the end of each of the target''s turns, it can repeat the saving throw, ending the effect on itself on a success.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1034, 2, 'entries', NULL, '{"name":"Replacing the Energy Cell","type":"entries","entries":["While the pistol has charges remaining, its {@item energy cell} can''t be removed. Once the pistol has 0 charges, you can replace the {@item energy cell} with a new cell by using an action or a bonus action."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1036, 0, 'string', 'Soldiers of the Boros Legion consider it an honor to bear this shield, even knowing that it might be the last honor they receive. The front of the shield is sculpted to depict a grieving human face.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1036, 1, 'string', 'You gain a +1 bonus to AC for every two allies within 5 feet of you (up to a maximum of +3) while you wield this shield. This bonus is in addition to the shield''s normal bonus to AC.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1036, 2, 'string', 'When a creature you can see within 5 feet of you takes damage, you can use your reaction to take that damage, instead of the creature taking it. When you do so, the damage type changes to force.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1037, 0, 'string', 'When you wield this +1 greataxe, you always know the way to the nearest passage leading from underground toward the surface, as well as the approximate depth the axe is underground.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1038, 0, 'string', 'An opaque lustrous white, yellow, or pink gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1039, 0, 'string', 'While this pearl is on your person, you can use an action to speak its command word and regain one expended spell slot. If the expended slot was of 4th level or higher, the new slot is 3rd level. Once you have used the pearl, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1040, 0, 'string', 'This black pearl glows faintly when undead are within 120 feet of it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1041, 0, 'string', 'This blue pennant is crafted from silk and is five feet long and whips about as if buffeted by a wind. The vind (wind) rune appears on its surface, looking almost like a cloud. The pennant has the following properties, which work only while it''s on your person.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1041, 1, 'entries', NULL, '{"type":"entries","name":"Wind Step","entries":["As an action, you fly up to 20 feet. If you don''t land at the end of this flight, you fall unless you have another means of staying aloft."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1041, 2, 'entries', NULL, '{"type":"entries","name":"Comforting Wind","entries":["You can''t suffocate."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1041, 3, 'entries', NULL, '{"type":"entries","name":"Winds Grasp","entries":["As a reaction when you fall, you can cause yourself to take no damage from the fall. Once you use this property, you can''t use it again until you finish a short or long rest."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1041, 4, 'entries', NULL, '{"type":"entries","name":"Wind Walker","entries":["While you are attuned to this rune, you can cast {@spell levitate} as a bonus action. Once you use this property, you can''t use it again until you finish a short or long rest."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1041, 5, 'entries', NULL, '{"type":"entries","name":"Gift of Wind","entries":["You can transfer the pennant''s magic to a nonmagical itema suit of armor, a pair of boots, or a cloakby tracing the vind rune there with your finger. The transfer takes 8 hours of work that requires the two items to be within 5 feet of each other. At the end, the pennant is destroyed, and the rune appears in silver on the chosen item, which gains a benefit based on its form:",{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Armor","entry":"The armor is now an uncommon magic item that requires attunement. You gain a bonus to speed of 5 feet while you wear the armor, and if it normally imposes disadvantage on {@skill Stealth} checks, it no longer does so."},{"type":"item","name":"Boots/Cloak","entry":"The pair of boots or cloak is now a rare magic item that requires attunement. While wearing the item, you can convert up to 20 feet of your movement on each of your turns into flight. If you don''t land at the end of this flight, you fall unless you have another means of staying aloft. You can also cast {@spell feather fall} once from the item, and you regain the ability to do so when you finish a short or long rest."}]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1043, 0, 'string', 'While wearing this winged helm, you have a flying speed of 60 feet. In addition, you have advantage on initiative rolls.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1045, 0, 'string', 'This tiny vial contains magic perfume, enough for one use. You can use an action to apply the perfume to yourself, and its effect lasts 1 hour. For the duration, you have advantage on all Charisma checks directed at humanoids of challenge rating 1 or lower. Those subjected to the perfume''s effect are not aware that they''ve been influenced by magic.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1046, 0, 'string', 'You are immune to contracting any disease while you wear this pendant. If you are already infected with a disease, the effects of the disease are suppressed while you wear the pendant.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1047, 0, 'string', 'This delicate silver chain has a brilliant-cut black gem pendant. While you wear it, poisons have no effect on you. You are immune to the poisoned condition and have immunity to poison damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1048, 0, 'string', 'While you wear this pendant, you stabilize whenever you are dying at the start of your turn. In addition, whenever you roll a Hit Die to regain hit points, double the number of hit points it restores.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1049, 0, 'string', 'A transparent rich olive green gemstone.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1050, 0, 'string', 'While this egg is on your person, you can use an action to speak its command word and regain one expended spell slot. If the expended slot was of 4th level or higher, the new slot is 3rd level. Once you have used the egg, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1050, 1, 'string', 'Additionally, while you are attuned to the egg, you gain resistance to poison damage.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1050, 2, 'string', 'The egg turns to dust and is destroyed if it leaves the Tomb of the Nine Gods.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1052, 0, 'string', 'The next time you see a creature within 10 minutes after drinking this philter, you become charmed by that creature for 1 hour. If the creature is of a species and gender you are normally attracted to, you regard it as your true love while you are charmed. This potion''s rose-hued, effervescent liquid contains one easy-to-miss bubble shaped like a heart.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1053, 0, 'string', 'You have a +1 bonus to attack and damage rolls made with this magic weapon.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1053, 1, 'string', 'A character attuned to the sword regains the maximum possible number of hit points from expended Hit Dice. However, the attuned character must eat twice as much food each day (a minimum of 2 pounds) to avoid exhaustion (see "The Environment" in chapter 8 of the Player''s Handbook.)', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1055, 0, 'string', 'This long, delicate wooden pipe features a bowl made from smooth river stone. When the pipe is lit, smoke exhaled from it does not dissipate, instead lingering around the bearer. After 10 minutes, the smoke forms moving shapes that reenact the bearer''s most impressive and heroic achievements for 5 minutes. When this realistic performance is complete, the smoke dissipates. The pipe can''t be used this way again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1056, 0, 'string', 'While smoking this pipe, you can use an action to exhale a puff of smoke that takes the form of a single creature, such as a dragon, a flumph, or a froghemoth. The form must be small enough to fit in a 1-foot cube and loses its shape after a few seconds, becoming an ordinary puff of smoke.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1057, 0, 'string', 'You must be proficient with wind instruments to use these pipes. They have 3 charges. You can use an action to play them and expend 1 charge to create an eerie, spellbinding tune. Each creature within 30 feet of you that hears you play must succeed on a 15 Wisdom saving throw or become frightened of you for 1 minute. If you wish, all creatures in the area that aren''t hostile toward you automatically succeed on the saving throw. A creature that fails the saving throw can repeat it at the end of each of its turns, ending the effect on itself on a success. A creature that succeeds on its saving throw is immune to the effect of these pipes for 24 hours. The pipes regain 1d3 expended charges daily at dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1058, 0, 'string', 'You must be proficient with wind instruments to use these pipes. While you are attuned to the pipes, ordinary rats and giant rats are indifferent toward you and will not attack you unless you threaten or harm them.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1058, 1, 'string', 'The pipes have 3 charges. If you play the pipes as an action, you can use a bonus action to expend 1 to 3 charges, calling forth one swarm of rats with each expended charge, provided that enough rats are within half a mile of you to be called in this fashion (as determined by the DM). If there aren''t enough rats to form a swarm, the charge is wasted. Called swarms move toward the music by the shortest available route but aren''t under your control otherwise. The pipes regain 1d3 expended charges daily at dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1058, 2, 'string', 'Whenever a swarm of rats that isn''t under another creature''s control comes within 30 feet of you while you are playing the pipes, you can make a Charisma check contested by the swarm''s Wisdom check. If you lose the contest, the swarm behaves as it normally would and can''t be swayed by the pipes'' music for the next 24 hours. If you win the contest, the swarm is swayed by the pipes'' music and becomes friendly to you and your companions for as long as you continue to play the pipes each round as an action. A friendly swarm obeys your commands. If you issue no commands to a friendly swarm, it defends itself but otherwise takes no actions. If a friendly swarm starts its turn and can''t hear the pipes'' music, your control over that swarm ends, and the swarm behaves as it normally would and can''t be swayed by the pipes'' music for the next 24 hours.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1059, 0, 'string', 'This +1 shortsword is a terrifying weapon with a serrated edge. It grants its wielder advantage on Charisma (Intimidation) checks when brandished.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1060, 0, 'string', 'A pitcher holds 1 gallon of liquid.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1062, 0, 'string', 'This dark spider silk cloak is made by drow. It is a cloak of elvenkind. It loses its magic if exposed to sunlight for 1 hour without interruption.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1062, 1, 'string', 'While you wear this cloak with its hood up, Wisdom (Perception) checks made to see you have disadvantage. and you have advantage on Dexterity (Stealth) checks made to hide, as the cloak''s color shifts to camouflage you. Pulling the hood up or down requires an action.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1063, 0, 'string', 'This dark spider silk cloak is made by drow. It is a cloak of elvenkind. It also grants resistance to fire damage while you wear it. It loses its magic if exposed to sunlight for 1 hour without interruption.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1063, 1, 'string', 'While you wear this cloak with its hood up, Wisdom (Perception) checks made to see you have disadvantage. and you have advantage on Dexterity (Stealth) checks made to hide, as the cloak''s color shifts to camouflage you. Pulling the hood up or down requires an action.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1064, 0, 'string', 'As an action, you can sprinkle this dust on yourself or another creature you can see within 5 feet of you. The recipient gains a flying speed of 30 feet and the ability to hover for 1 minute. If the creature is airborne when this effect ends, it falls safely to the ground, taking no damage and landing on its feet.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1064, 1, 'string', 'A small packet holds enough pixie dust for one use.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1065, 0, 'string', 'The hulking plainscow is an exceptionally sturdy beast of burden native to the Dividing Plains and used by the people of central Tal''Dorei for centuries. Though many wild herds still roam the plains, domesticated plainscows are more common, used as Mounts and Vehicles, pack animals, and livestock. Plainscows are unusually empathetic for beasts, and often bond with a rider for life. They stand 6 feet tall, weigh about two tons, and live for 70 years.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1065, 1, 'entries', NULL, '{"type":"entries","entries":[{"type":"entries","entries":[{"type":"entries","name":"Market Value","page":248,"entries":["Plainscows can be bought in {@book Westruun|TDCSR|3|Westruun} for 200 gp. A plainscow has a carrying capacity of 1,500 pounds."]}]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1066, 0, 'string', 'This key is etched with sigils.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1066, 1, 'string', 'A planar key begins existence with 1d4 + 1 charges and cannot regain charges. When the key''s last charge is expended, the key''s sigils and magic fade away, but the key remains.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1066, 2, 'string', 'While touching the key, you can use your action to expend 1 charge and have the key cast plane shift limited to the key''s predefined destination only. Each planar key links to a single destination defined when the key is made.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1067, 0, 'table', NULL, '{"type":"table","colStyles":["col-2 text-center","col-10"],"colLabels":["d6","Trinket"],"rows":[["1","Locket with a picture of my mentor and an inscription I can''t read"],["2","Bleached cranium rat skull with colored glass beads in its eye sockets"],["3","Torn parchment with half a rebus painted on it"],["4","Bracelet of twisted razorvine stems"],["5","Fragment of a bronze blade covered in verdigris"],["6","Broken holy symbol of a forgotten god"]]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1068, 0, 'string', 'The pages of this book are bound in fiend hide, and its cover is embossed with a diagram of the Great Wheel of the multiverse. When found, the book contains the following spells: banishment, find familiar, gate, magic circle, planar binding, and summon elemental. It functions as a spellbook for you.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1068, 1, 'string', 'While you are holding the book, you can use it as a spellcasting focus for your wizard spells.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1068, 2, 'string', 'The book has 3 charges, and it regains 1d3 expended charges daily at dawn. You can use the charges in the following ways while holding it:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1068, 3, 'list', NULL, '{"type":"list","items":["If you spend 1 minute studying the book, you can expend 1 charge to replace one of your prepared wizard spells with a different spell in the book. The new spell must be of the conjuration school.","When you cast a conjuration spell that summons or creates one creature, you can expend 1 charge to grant that creature advantage on attack rolls for 1 minute."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1069, 0, 'string', 'While you''re wearing this armor, you can speak its command word as an action to gain the effect of the etherealness spell, which lasts for 10 minutes or until you remove the armor or use an action to speak the command word again. This property of the armor can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1070, 0, 'string', 'This gleaming set of silver-and-gold plate armor never tarnishes.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1070, 1, 'string', 'While wearing this armor, you can use a bonus action to summon the spirit of a warrior to your aid. The spirit''s corporeal form manifests in an unoccupied space of your choice within 30 feet of you, and it uses the knight stat block. The spirit disappears when it drops to 0 hit points or after 1 minute, whichever comes first.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1070, 2, 'string', 'The spirit is an ally to you and your companions. In combat, the spirit shares your initiative count but takes its turn immediately after yours. The spirit obeys your commands (no action required by you); if you don''t issue any commands, the spirit takes the Dodge action and uses its movement to avoid danger.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1070, 3, 'string', 'Once this bonus action is used, it can''t be used again until the next dawn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1070, 4, 'string', 'Plate consists of shaped, interlocking metal plates to cover the entire body. A suit of plate includes gauntlets, heavy leather boots, a visored helmet, and thick layers of padding underneath the armor. Buckles and straps distribute the weight over the body.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1072, 0, 'string', 'Beautiful silver scrollwork and priceless cut rubies adorn this set of brass-and-gold armor. Legend holds that it was worn by Duana, High Priest of the The Dawnfather, when she was martyred in the Battle of Ghor Dranas in the final days of the Calamity. In more recent times, it was worn by Pike Trickfoot, a legendary cleric of the The Everlight, while fighting against the Chroma Conclave and The Whispered One.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1072, 1, 'entries', NULL, '{"type":"entries","name":"Dormant","entries":["While the Plate of the Dawnmartyr is in a dormant state, you gain a +1 bonus to AC while wearing it. When a creature within 5 feet of you hits you with a melee attack, you can use your reaction to rebuke that creature, dealing it {@damage 1d6} fire damage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1075, 0, 'string', 'Common coins come in several different denominations based on the relative worth of the metal from which they are made. The three most common coins are the gold piece (gp), the silver piece (sp), and the copper piece (cp).', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1075, 1, 'string', 'With one gold piece, a character can buy a belt pouch, 50 feet of good rope, or a goat. A skilled (but not exceptional) artisan can earn one gold piece a day. The gold piece is the standard unit of measure for wealth, even if the coin itself is not commonly used. When merchants discuss deals that involve goods or services worth hundreds or thousands of gold pieces, the transactions don''t usually involve the exchange of individual coins. Rather, the gold piece is a standard measure of value, and the actual exchange is in gold bars, letters of credit, or valuable goods.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1075, 2, 'string', 'One gold piece is worth ten silver pieces, the most prevalent coin among commoners. A silver piece buys a laborer''s work for a day, a flask of lamp oil, or a night''s rest in a poor inn.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1075, 3, 'string', 'One silver piece is worth ten copper pieces, which are common among laborers and beggars. A single copper piece buys a candle, a torch, or a piece of chalk.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1075, 4, 'string', 'In addition, unusual coins made of other precious metals sometimes appear in treasure hoards. The electrum piece (ep) and the platinum piece (pp) originate from fallen empires and lost kingdoms, and they sometimes arouse suspicion and skepticism when used in transactions. An electrum piece is worth five silver pieces, and a platinum piece is worth ten gold pieces.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1075, 5, 'string', 'A standard coin weighs about a third of an ounce, so fifty coins weigh a pound.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1081, 0, 'string', 'This scarf is made of sturdy cloth and covered in platinum-colored scales.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1081, 1, 'string', 'As an action, you can pull a scale from the scarf and speak a command word. When you do so, choose one of the following effects:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1081, 2, 'entries', NULL, '{"type":"entries","name":"Breath of Life","entries":["The scale disappears, and you or a creature you touch regains {@dice 10d4} hit points."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1081, 3, 'entries', NULL, '{"type":"entries","name":"Platinum Shield","entries":["For 1 hour or until you dismiss it (no action required), the scale becomes a {@item +1 shield}, which you or another creature can use. A creature wielding the shield has immunity to radiant damage."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1081, 4, 'entries', NULL, '{"type":"entries","name":"Radiant Hammer","entries":["For 1 hour or until you dismiss it (no action required), the scale becomes a magic {@item light hammer|phb}, which you or another creature can use. The weapon deals {@damage 2d4} radiant damage, instead of the bludgeoning damage normal for a light hammer. It deals an extra {@damage 2d4} radiant damage to chromatic dragons.","Once three scales have been pulled from the scarf, no more scales can be removed until the next dawn, when all the missing scales grow back. If you pull off a scale but don''t speak a command word, it disappears after 1 minute."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1084, 0, 'string', '{#itemEntry Absorbing Tattoo|TCE}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1085, 0, 'string', 'A poisoner''s kit includes the vials, chemicals, and other equipment necessary for the creation of poisons. Proficiency with this kit lets you add your proficiency bonus to any ability checks you make to craft or use poisons.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1085, 1, 'string', 'Additionally, the Crafting and Harvesting Poison rules require the use of a poisoner''s kit.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1087, 0, 'string', 'While holding this 10-foot pole, you can speak a command word and transform it into a fishing pole with a hook, a line, and a reel. Speaking the command word again changes the fishing pole back into a normal 10-foot pole.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1088, 0, 'string', 'While holding this 10-foot pole, you can use an action to speak a command word and cause it to collapse into a 1-foot-long rod, for ease of storage. The pole''s weight doesn''t change. You can use an action to speak a different command word and cause the rod to revert to a pole; however, the rod will elongate only as far as the surrounding space allows.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1090, 0, 'string', 'This fine black cloth, soft as silk, is folded up to the dimensions of a handkerchief. It unfolds into a circular sheet 6 feet in diameter.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1090, 1, 'string', 'You can use an action to unfold a portable hole and place it on or against a solid surface, whereupon the portable hole creates an extradimensional hole 10 feet deep. The cylindrical space within the hole exists on a different plane, so it can''t be used to create open passages. Any creature inside an open portable hole can exit the hole by climbing out of it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1090, 2, 'string', 'You can use an action to close a portable hole by taking hold of the edges of the cloth and folding it up. Folding the cloth closes the hole, and any creatures or objects within remain in the extradimensional space. No matter what''s in it, the hole weighs next to nothing.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1090, 3, 'string', 'If the hole is folded up, a creature within the hole''s extradimensional space can use an action to make a 10 Strength check. On a successful check, the creature forces its way out and appears within 5 feet of the portable hole or the creature carrying it. A breathing creature within a closed portable hole can survive for up to 10 minutes, after which time it begins to suffocate.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1090, 4, 'string', 'Placing a portable hole inside an extradimensional space created by a bag of holding, Heward''s handy haversack, or similar item instantly destroys both items and opens a gate to the Astral Plane. The gate originates where the one item was placed inside the other. Any creature within 10 feet of the gate is sucked through it and deposited in a random location on the Astral Plane. The gate then closes. The gate is one-way only and can''t be reopened.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1091, 0, 'string', 'You can use a portable ram to break down doors. When doing so, you gain a +4 bonus on the Strength check. One other character can help you use the ram, giving you advantage on this check.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1092, 0, 'string', 'This portable arcane instrument points in the direction of the last portal it passed through while it and the portal are on the same plane of existence. If that portal no longer exists, the needle becomes static until the compass passes through a new portal.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1093, 0, 'string', 'At rank 2, you gain the use of a weathered leather pouch that is an uncommon magic item. The portfolio keeper holds and organizes notes, brochures, and business cards bearing your contact information. It also has an inexhaustible supply of brochures related to your franchise''s current branding scheme.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1093, 1, 'string', 'When you meet someone for the first time, their details and a rough sketch are magically stored on a small parchment card in the portfolio keeper. You can access the details of any such stored card as a bonus action.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1093, 2, 'entries', NULL, '{"type":"entries","name":"Cards of Sending","entries":["At rank 3, the power of your portfolio keeper becomes more potent, making it a rare magic item. When you give out a business card stored in your portfolio keeper, you can choose to have the card allow its recipient to contact you through your {@item sending stone|AI} as if they had cast a {@spell sending} spell. A business card loses this power seven days after it has been given out, and no more than five cards can have this power at one time.","You can also use your {@item sending stone|AI} to cast {@spell sending} and contact anyone who has one of your business cards. Once you use this feature of the stone, you cannot use it again until dawn seven days later."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1093, 3, 'entries', NULL, '{"type":"entries","name":"Charming Introduction","entries":["Also at rank 4, you are able to make a focused effort to keep a potential customer''s attention. When you provide a humanoid with a business card from your portfolio keeper, you can cast {@spell charm person} on them (save {@dc 15}). The spell ends prematurely if the character loses the business card."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1094, 0, 'string', 'If you plant an ordinary shrub in this 10-pound clay pot and let it grow for 30 days, the shrub magically transforms into an awakened shrub at the end of that time. When the shrub awakens, its roots break the pot, destroying it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1094, 1, 'string', 'The awakened shrub is friendly toward you. Absent commands from you, it does nothing.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1095, 0, 'string', '{#itemEntry Potion of Resistance}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1096, 0, 'string', 'When you drink this potion, you gain advantage on one ability check, attack roll, or saving throw of your choice that you make within the next hour.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1096, 1, 'string', 'This potion takes the form of a sparkling, golden mist that moves and pours like water.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1097, 0, 'string', 'When you drink this potion, you can cast the animal friendship spell (save 13) for 1 hour at will. Agitating this muddy liquid brings little bits into view: a fish scale, a hummingbird tongue, a cat claw, or a squirrel hair.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1098, 0, 'string', 'When you drink this potion, you transform into a pool of water. You return to your true form after 10 minutes or if you are incapacitated or die.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1098, 1, 'string', 'You''re under the following effects while in this form:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1098, 2, 'list', NULL, '{"type":"list","style":"list-hang-notitle","items":[{"type":"item","name":"Liquid Movement","entry":"You have a swimming speed of 30 feet. You can move over or through other liquids. You can enter and occupy the space of another creature. You can rise up to your normal height, and you can pass through even Tiny openings. You extinguish nonmagical flames in any space you enter."},{"type":"item","name":"Watery Resilience","entry":"You have resistance to nonmagical damage. You also have advantage on Strength, Dexterity, and Constitution saving throws."},{"type":"item","name":"Limitations","entry":"You can''t talk, attack, cast spells, or activate magic items. Any objects you were carrying or wearing meld into your new form and are inaccessible, though you continue to be affected by anything you''re wearing, such as armor."}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1099, 0, 'string', 'When you drink this potion, you gain the effect of the clairvoyance spell. An eyeball bobs in this yellowish liquid but vanishes when the potion is opened.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1100, 0, 'string', 'When you drink this potion, you gain a climbing speed equal to your walking speed for 1 hour. During this time, you have advantage on Strength (Athletics) checks you make to climb. The potion is separated into brown, silver, and gray layers resembling bands of stone. Shaking the bottle fails to mix the colors.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1101, 0, 'string', 'When you drink this potion, your Strength score changes to 27 for 1 hour. The potion has no effect on you if your Strength is equal to or greater than that score.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1101, 1, 'string', 'This potion''s transparent liquid has floating in it a sliver of fingernail from a cloud giant.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1102, 0, 'string', '{#itemEntry Potion of Resistance}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1103, 0, 'string', 'When you drink this potion, you gain the effect of a comprehend languages spell for 1 hour. This liquid is a clear concoction with bits of salt and soot swirling in it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1104, 0, 'string', 'When you drink this potion, you gain the "reduce" effect of the enlarge/reduce spell for 1d4 hours (no concentration required). The red in the potion''s liquid continuously contracts to a tiny bead and then expands to color the clear liquid around it. Shaking the bottle fails to interrupt this process.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1105, 0, 'string', 'This potion looks like liquid gold, with a single scale from a chromatic, gem, or metallic dragon suspended in it. When you drink this potion, you transform into an adult dragon of the same kind as the dragon the scale came from. The transformation lasts for 1 hour. Any equipment you are wearing or carrying melds into your new form or falls to the ground (your choice). For the duration, you use the game statistics of the adult dragon instead of your own, but you retain your languages, personality, and memories. You can''t use a dragon''s Change Shape or its legendary or lair actions.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1106, 0, 'string', 'After drinking this potion, you can use a bonus action to exhale fire at a target within 30 feet of you. The target must make a 13 Dexterity saving throw, taking 4d6 fire damage on a failed save, or half as much damage on a successful one. The effect ends after you exhale the fire three times or when 1 hour has passed. This potion''s orange liquid flickers, and smoke fills the top of the container and wafts out whenever it is opened.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1107, 0, 'string', 'When you drink this potion, your Strength score changes to 25 for 1 hour. The potion has no effect on you if your Strength is equal to or greater than that score.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1107, 1, 'string', 'This potion''s transparent liquid has floating in it a sliver of fingernail from a fire giant.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1108, 0, 'string', '{#itemEntry Potion of Resistance}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1109, 0, 'string', 'When you drink this potion, you gain a flying speed equal to your walking speed for 1 hour and can hover. If you''re in the air when the potion wears off, you fall unless you have some other means of staying aloft. This potion''s clear liquid floats at the top of its container and has cloudy white impurities drifting in it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1110, 0, 'string', '{#itemEntry Potion of Resistance}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1111, 0, 'string', 'When you drink this potion, your Strength score changes to 23 for 1 hour. The potion has no effect on you if your Strength is equal to or greater than that score.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1111, 1, 'string', 'This potion''s transparent liquid has floating in it a sliver of fingernail from a frost giant.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1112, 0, 'string', 'When you drink this potion, you gain the effect of the gaseous form spell for 1 hour (no concentration required) or until you end the effect as a bonus action. This potion''s container seems to hold fog that moves and pours like water.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1113, 0, 'string', 'When you drink this potion, you become Huge for 24 hours if you are Medium or smaller, otherwise the potion does nothing. For that duration, your Strength becomes 25, if it isn''t already higher, and your hit point maximum is doubled (your current hit points are doubled when you drink the potion). In addition, the reach of your melee attacks increases by 5 feet.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1113, 1, 'string', 'Everything you are carrying and wearing also increases in size for the duration. When rolling damage for weapons enlarged in this manner, roll three times the normal number of dice; for example, an enlarged longsword would deal 3d8 slashing damage (instead of 1d8), or 3d10 slashing damage (instead of 1d10) when used with two hands.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1113, 2, 'string', 'When the effect ends, any hit points you have above your hit point maximum become temporary hit points. This potion is a pale white liquid made from the tongue of a giant clam, with a pungent aroma akin to that of rotting algae. It tastes sweet, however, when consumed.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1114, 0, 'string', 'You regain 4d4 + 4 hit points when you drink this potion. The potion''s red liquid glimmers when agitated.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1115, 0, 'string', 'When you drink this potion, you gain the "enlarge" effect of the enlarge/reduce spell for 1d4 hours (no concentration required). The red in the potion''s liquid continuously expands from a tiny bead to color the clear liquid around it and then contracts. Shaking the bottle fails to interrupt this process.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1116, 0, 'string', 'You regain 2d4 + 2 hit points when you drink this potion. The potion''s red liquid glimmers when agitated.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1117, 0, 'string', 'For 1 hour after drinking it, you gain 10 temporary hit points that last for 1 hour. For the same duration, you are under the effect of the bless spell (no concentration required). This blue potion bubbles and steams as if boiling.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1118, 0, 'string', 'When you drink this potion, your Strength score changes to 21 for 1 hour. The potion has no effect on you if your Strength is equal to or greater than that score.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1118, 1, 'string', 'This potion''s transparent liquid has floating in it a sliver of fingernail from a hill giant.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1119, 0, 'string', 'This potion''s container looks empty but feels as though it holds liquid. When you drink it, you become invisible for 1 hour. Anything you wear or carry is invisible with you. The effect ends early if you attack or cast a spell.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1120, 0, 'string', 'For 1 minute after you drink this potion, you have resistance to all damage. The potion''s syrupy liquid looks like liquefied iron.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1121, 0, 'string', '{#itemEntry Potion of Resistance}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1122, 0, 'string', 'When you drink this potion, your physical age is reduced by 1d6 + 6 years, to a minimum of 13 years. Each time you subsequently drink a potion of longevity, there is a 10 cumulative chance that you instead age by 1d6 + 6 years. Suspended in this amber liquid are a scorpion''s tail, an adder''s fang, a dead spider, and a tiny heart that, against all reason, is still beating. These ingredients vanish when the potion is opened.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1123, 0, 'string', 'The first time you cast a damage-dealing spell of 4th level or lower within 1 minute after drinking the potion, instead of rolling dice to determine the damage dealt, you can instead use the highest number possible for each die.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1123, 1, 'string', 'This glowing purple liquid smells of sugar and plum, but it has a muddy taste.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1124, 0, 'string', 'When you drink a potion of mind control, you can cast a dominate spell (save 15) on a specific creature if you do so before the end of your next turn. If you don''t, the potion is wasted.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1124, 1, 'string', 'A potion of mind control produces the effect of a dominate beast spell. If the target''s initial saving throw fails, the effect lasts for 1 hour, with no concentration required on your part. The charmed creature has disadvantage on new saving throws to break the effect during this time.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1125, 0, 'string', 'When you drink a potion of mind control, you can cast a dominate spell (save 15) on a specific creature if you do so before the end of your next turn. If you don''t, the potion is wasted.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1125, 1, 'string', 'A potion of mind control produces the effect of a dominate person spell. If the target''s initial saving throw fails, the effect lasts for 1 hour, with no concentration required on your part. The charmed creature has disadvantage on new saving throws to break the effect during this time.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1126, 0, 'string', 'When you drink a potion of mind control, you can cast a dominate spell (save 15) on a specific creature if you do so before the end of your next turn. If you don''t, the potion is wasted.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1126, 1, 'string', 'A potion of mind control produces the effect of a dominate monster spell. If the target''s initial saving throw fails, the effect lasts for 1 hour, with no concentration required on your part. The charmed creature has disadvantage on new saving throws to break the effect during this time.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1127, 0, 'string', 'When you drink this potion, you gain the effect of the detect thoughts spell (save 13). The potion''s dense, purple liquid has an ovoid cloud of pink floating in it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1128, 0, 'string', '{#itemEntry Potion of Resistance}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1129, 0, 'string', 'This concoction looks, smells, and tastes like a potion of healing or other beneficial potion. However, it is actually poison masked by illusion magic. An identify spell reveals its true nature.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1129, 1, 'string', 'If you drink it, you take 3d6 poison damage, and you must succeed on a 13 Constitution saving throw or be poisoned. At the start of each of your turns while you are poisoned in this way, you take 3d6 poison damage. At the end of each of your turns, you can repeat the saving throw. On a successful save, the poison damage you take on your subsequent turns decreases by 1d6. The poison ends when the damage decreases to 0.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1130, 0, 'string', '{#itemEntry Potion of Resistance}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1131, 0, 'string', 'When you drink this potion, you and everything you are wearing or carrying take on a rainbow-hued appearance for 1 hour. During that time, you can use a bonus action to turn any color or combination of colors you choose. If you mimic the colors of your surroundings, your hues continually shift to match your surroundings, and you have advantage on Dexterity (Stealth) checks until you change your colors again or the potion wears off.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1131, 1, 'string', 'The potion is separated into seven brightly colored bands of immiscible liquids and has a syrupy taste.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1132, 0, 'string', 'When you drink this clear potion, you gain two Fragments of Possibility, each of which looks like a Tiny, grayish bead of energy that follows you around, staying within 1 foot of you at all times. Each fragment lasts for 8 hours or until used.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1132, 1, 'string', 'When you make an attack roll, an ability check, or a saving throw, you can expend your fragment to roll an additional d20 and choose which of the d20s to use. Alternatively, when an attack roll is made against you, you can expend your fragment to roll a d20 and choose which of the d20s to use, the one you rolled or the one the attacker rolled.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1132, 2, 'string', 'If the original d20 roll has advantage or disadvantage, you roll your d20 after advantage or disadvantage has been applied to the original roll.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1132, 3, 'string', 'While you have one or more Fragments of Possibility from this potion, you can''t gain another Fragment of Possibility from any source.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1133, 0, 'string', 'When you drink this potion, you have advantage for 1 hour on saving throws you make to avoid or end the charmed or stunned condition on yourself.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1133, 1, 'string', 'This black potion swirls with shimmering flecks of pink and purple.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1134, 0, 'string', '{#itemEntry Potion of Resistance}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1135, 0, 'string', '{#itemEntry Potion of Resistance}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1136, 0, 'string', 'When you drink this potion, you gain the effect of the haste spell for 1 minute (no concentration required). The potion''s yellow fluid is streaked with black and swirls on its own.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1137, 0, 'string', 'When you drink this potion, your Strength score changes to 23 for 1 hour. The potion has no effect on you if your Strength is equal to or greater than that score.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1137, 1, 'string', 'This potion''s transparent liquid has floating in it a sliver of fingernail from a stone giant.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1138, 0, 'string', 'When you drink this potion, your Strength score changes to 29 for 1 hour. The potion has no effect on you if your Strength is equal to or greater than that score.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1138, 1, 'string', 'This potion''s transparent liquid has floating in it a sliver of fingernail from a storm giant.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1139, 0, 'string', 'You regain 8d4 + 8 hit points when you drink this potion. The potion''s red liquid glimmers when agitated.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1140, 0, 'string', 'You regain 10d4 + 20 hit points when you drink this potion. The potion''s red liquid glimmers when agitated.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1141, 0, 'string', '{#itemEntry Potion of Resistance}', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1142, 0, 'string', 'When you drink this potion, it removes any exhaustion you are suffering and cures any disease or poison affecting you. For the next 24 hours, you regain the maximum number of hit points for any Hit Die you spend. The potion''s crimson liquid regularly pulses with dull light, calling to mind a heartbeat.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1143, 0, 'string', 'When you drink this potion, you gain the following benefits for the next 8 hours: magic can''t put you to sleep, and you can remain awake during a long rest and still gain its benefits. This sweet, amber-colored brew has no effect on creatures that don''t require sleep, such as elves.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1144, 0, 'string', 'You can breathe underwater for 1 hour after drinking this potion. Its cloudy green fluid smells of the sea and has a jellyfish-like bubble floating in it.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1146, 0, 'string', 'A cloth or leather pouch can hold up to 20 sling bullet or 50 blowgun needle, among other things. A compartmentalized pouch for holding spell components is called a component pouch. A pouch can hold up to  cubic foot or 6 pounds of gear.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1147, 0, 'string', 'Powered armor resembles a suit of unusual plate armor, with finely articulated joints connected by an oily, black, leather-like material. The armor has been worked to create the appearance of a heavily muscled warrior, and its great helm is unusual in that it has no openingsonly a broad glass plate in the front with a second piece of glass above it. Strange plates, tubing, and large metal bosses adorn the armor in seemingly random fashion. On the back of the armor''s left gauntlet is a rectangular metal box, from which projects a short rod tipped with a cone-shaped red crystal.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1147, 1, 'string', 'While wearing this armor, you gain the following benefits:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1147, 2, 'list', NULL, '{"type":"list","items":["You have a +1 bonus to AC.","Your Strength score is 18 (this has no effect if your Strength is already 18 or higher).","You have advantage on death saving throws."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1147, 3, 'string', 'The armor has further capabilities that can be powered either by energy cell or by your own life energy. You can use a bonus action to draw power from an energy cell or sacrifice hit points to gain one of the following benefits:', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1147, 4, 'list', NULL, '{"type":"list","items":["Emit a force field to gain {@dice 2d6 + 5} temporary hit points (1 charge or 5 hit points).","Activate boosters to gain a flying speed of 15 feet for 1 minute (1 charge or 5 hit points).","Fire arm-mounted laser: {@atk rw} {@hit 8} to hit, range 120 feet, one target. {@h}{@damage 2d6} radiant damage (1 charge or 5 hit points).","Translate any writing you can see in any nonmagical language, to a total of one thousand words over 1 minute (1 charge or 5 hit points).","Fill the armor with air, allowing you to breathe normally in any environment for up to 1 hour (1 charge or 5 hit points).","Gain {@sense darkvision} to a range of 60 feet for up to 1 hour (1 charge or 5 hit points)."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1147, 5, 'string', 'The armor can accept only one energy cell at a time. It is found with one energy cell attached, containing 2d10 charges.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1147, 6, 'inset', NULL, '{"type":"inset","name":"Powered Armor Options","entries":["Depending on where and how it appears in the adventure, you might wish to modify the features of Kwalish''s legendary powered armor.",{"type":"entries","name":"Automatic Defenses","entries":["Unless Kwalish deactivates the suit''s automatic defenses, no one can approach the armor without setting those defenses off. Treat the armor as a {@creature shield guardian} that has stored a {@spell magic missile} spell cast using a 4th-level spell slot. When the armor is reduced to 0 hit points, its defenses are rendered inert and it can be safely approached."]},{"type":"entries","name":"Battle of Wills","entries":["When donned by a new user, the armor deems itself superior and attempts to take possession of that user. The user must succeed on a {@dc 13} Charisma saving throw or be possessed by the armor. While possessed, the user is {@condition incapacitated} and loses control of its body but retains its awareness. The armor uses the possessed user''s statistics (as adjusted by the armor), but doesn''t gain access to the user''s knowledge, features, or proficiencies.","Freeing a creature trapped inside the armor first requires defeating the armor''s automatic defenses (as above). The trapped creature can also attempt a {@dc 20} Charisma saving throw each day at dawn. On a successful save, the armor no longer controls the creature and can be safely donned by that creature at any time."]},{"type":"entries","name":"Stasis","entries":["Whenever a creature wearing the armor drops to 0 hit points, the armor places that creature into a state of stasis. While in this state, the creature is stable and does not make death saving throws, but the armor takes control of the creature (as above). Additionally, the armor attempts to assume the identity of the user, assuring their allies that nothing is amiss. Freeing the user first requires defeating the armor''s automatic defenses (as above). A creature in stasis does not make Charisma saving throws to break the armor''s control."]},{"type":"entries","name":"Alternative Power","entries":["Powered armor originally required energy cells to fuel it, but was adapted by Kwalish to be fueled by the life energy of the creature wearing it. You might decide that the armor can also draw power from additional sources, or that energy cells can be recharged with the aid of a tinker, inventor, or artificer. It might also be possible for allies to connect to the armor through the use of magic that generates a conduit something like an astral silver cord. While so connected, a willing ally can give up hit points as a reaction to fuel the armor''s abilities."]}]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1148, 0, 'string', 'This suit of technologically advanced plate armor includes an under-suit that can fully seal, a helmet with a full face mask and crystal lenses in the eyeholes, and a set of gauntlets. The armor is powered by an energy cell stored in a compartment on the thigh plate.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1148, 1, 'string', 'Placing a full energy cell in the armor gives the armor 24 charges. A suit of powered armor functions as a suit of normal plate armor, even when it has 0 charges remaining.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1148, 2, 'entries', NULL, '{"type":"entries","name":"Activating the Armor","entries":["As an action, you can expend any number of the armor''s charges to activate it; the armor remains active for 1 hour per charge expended. You can use a bonus action to deactivate the armor early, but doing so doesn''t recover any expended charges.","While the armor is active, you gain the following benefits:"]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1148, 3, 'entries', NULL, '{"type":"entries","name":"Augmented Physicality","entries":["You have advantage on Strength checks, and your carrying capacity is doubled."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1148, 4, 'entries', NULL, '{"type":"entries","name":"Environmental Adaptation","entries":["The armor seals airtight and provides its own atmosphere. You can breathe normally in any environment and withstand extreme temperatures, and you''re unaffected by harmful gases, as well as contact and inhaled poisons."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1148, 5, 'entries', NULL, '{"type":"entries","name":"Force Field","entries":["When you would take damage, you can use your reaction to expend 1 of the armor''s charges to deploy a defensive force field. Roll {@dice 3d10} and reduce the damage taken by the total rolled."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1148, 6, 'entries', NULL, '{"type":"entries","name":"Propulsion","entries":["As a bonus action, you can expend 1 of the armor''s charges to gain a flying speed equal to your walking speed for 1 minute. If you''re airborne when this duration ends, you fall."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1148, 7, 'entries', NULL, '{"type":"entries","name":"Replacing the Energy Cell","entries":["While the armor has charges remaining, its {@item energy cell} can''t be removed. Once the armor has 0 charges, you can replace the {@item energy cell} with a new cell by using an action or a bonus action."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1149, 0, 'string', 'Larger and more roughly hewn than typical figurines of wondrous power, these statuettes depict dinosaurs and related creatures from the earliest days of the world.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1149, 1, 'string', 'As an action, you can throw a prehistoric figurine of wondrous power to a point on the ground within 60 feet of yourself while speaking a command word, whereupon the figurine magically transforms into a living creature. If the space where the creature would appear is occupied by other creatures or objects, or if there isn''t enough space for the creature, the figurine doesn''t become a creature.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1149, 2, 'string', 'The creature is friendly to you and your companions. It understands your languages and obeys your spoken commands. If you issue no commands, the creature defends itself but takes no other actions. See the Monster Manual for the creature''s statistics.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1149, 3, 'string', 'The creature exists for a duration specific to each figurine. At the end of the duration, the creature reverts to its statuette form. It reverts to a figurine early if it drops to 0 hit points or if you use an action to speak the command word again while touching it. When the creature becomes a figurine again, its property can''t be used again until a certain amount of time has passed, as specified in the figurine''s description.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1149, 4, 'entries', NULL, '{"type":"entries","name":"{@creature triceratops||Carnelian Triceratops}","entries":["This figurine becomes a {@creature triceratops} for up to 6 hours and can be ridden as a mount. Once it has been used, it can''t be used again until 10 days have passed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1150, 0, 'string', 'Larger and more roughly hewn than typical figurines of wondrous power, these statuettes depict dinosaurs and related creatures from the earliest days of the world.', NULL);
