INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1150, 4, 'entries', NULL, '{"type":"entries","name":"{@creature tyrannosaurus rex||Jasper Tyrannosaurus Rex}","entries":["This figurine, crafted from rare green jasper, becomes a {@creature tyrannosaurus rex} for up to 1 hour. Once it has been used, it can''t be used again until 14 days have passed.","Whenever you command the figurine while it''s in tyrannosaurus rex form (including commanding it to revert to figurine form), you must roll a {@dice d20}. On a 1, you lose control of the figurine, and it becomes hostile to you and your companions until it is reduced to 0 hit points, at which point it reverts to figurine form."]}');
