INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1151, 4, 'entries', NULL, '{"type":"entries","name":"{@creature pteranodon||Kyanite Pteranodon}","entries":["This figurine becomes a {@creature pteranodon} for up to 8 hours. If your size is Medium or smaller, you can ride the pteranodon as a mount. Once it has been used, it can''t be used again until 7 days have passed."]}');
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1152, 0, 'string', 'Larger and more roughly hewn than typical figurines of wondrous power, these statuettes depict dinosaurs and related creatures from the earliest days of the world.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1152, 1, 'string', 'As an action, you can throw a prehistoric figurine of wondrous power to a point on the ground within 60 feet of yourself while speaking a command word, whereupon the figurine magically transforms into a living creature. If the space where the creature would appear is occupied by other creatures or objects, or if there isn''t enough space for the creature, the figurine doesn''t become a creature.', NULL);
