INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1444, 2, 'string', 'A quartz spell gem can store up to 2nd level spells. Spells cast from the spell gem have a save DC of 13 and an attack bonus of 5.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1444, 3, 'string', 'You can imbue the gem with a spell if you''re attuned to it and it''s empty. To do so, you cast the spell while holding the gem. The spell is stored in the gem instead of having any effect. Casting the spell must require either 1 action or 1 minute or longer, and the spell''s level must be no higher than the gem''s maximum. If the spell belongs to the school of abjuration and requires material components that are consumed, you must provide them, but they can be worth half as much as normal.', NULL);
