INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1550, 0, 'string', 'In the world of Greyhawk, only nine of these blades are known to exist. Each is patterned after the legendary sword Fragarach, which is variously translated as "Final Word." Each of the nine swords has its own name and alignment, and each bears a different gem in its pommel.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1550, 1, 'string', 'Answerer, the Chaotic Good sword, has an emerald set in its pommel.', NULL);
INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1550, 2, 'string', 'You gain a +3 bonus to attack and damage rolls made with this sword. In addition, while you hold the sword, you can use your reaction to make one melee attack with it against any creature in your reach that deals damage to you. You have advantage on the attack roll, and any damage dealt with this special attack ignores any damage immunity or resistance the target has.', NULL);
