INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1555, 0, 'string', 'In the world of Greyhawk, only nine of these blades are known to exist. Each is patterned after the legendary sword Fragarach, which is variously translated as "Final Word." Each of the nine swords has its own name and alignment, and each bears a different gem in its pommel.', NULL);
