INSERT INTO item_entries (item_id, entry_order, type, content, data) VALUES (1624, 0, 'string', 'When you make your character, you can roll once on the Trinkets table to gain a trinket, a simple item lightly touched by mystery. The DM might also use this table. It can help stock a room in a dungeon or fill a creatures pockets.', NULL);
