INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (1, 0, 'string', NULL, 'When you cast eldritch blast, add your Charisma modifier to the damage it deals on a hit.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (2, 0, 'string', NULL, 'When you make a Dexterity (Stealth) check or an initiative roll, you can expend one superiority die and add the die to the roll, provided you aren''t incapacitated.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (3, 0, 'string', NULL, 'The wearer of this armor gains these benefits:', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (3, 1, 'list', NULL, NULL, '["The wearer''s walking speed increases by 5 feet.","The armor includes gauntlets, each of which is a magic melee weapon that can be wielded only when the hand is holding nothing. The wearer is proficient with the gauntlets, and each one deals {@damage 1d8} force damage on a hit and has the thrown property, with a normal range of 20 feet and a long range of 60 feet. When thrown, the gauntlet detaches and flies at the attack''s target, then immediately returns to the wearer and reattaches.","The armor can''t be removed against the wearer''s will.","If the wearer is missing any limbs, the armor replaces those limbshands, arms, feet, legs, or similar appendages. The replacements function identically to the body parts they replace."]', NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (4, 0, 'string', NULL, 'You gain a +2 bonus to attack rolls you make with ranged weapons.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (5, 0, 'string', NULL, 'This armor has 6 charges. The wearer can expend the armor''s charges in the following ways:', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (5, 1, 'list', NULL, NULL, '["When the wearer makes a Strength check or a Strength saving throw, it can expend 1 charge to add a bonus to the roll equal to its Intelligence modifier.","If the creature would be knocked {@condition prone}, it can use its reaction to expend 1 charge to avoid being knocked prone."]', NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (5, 2, 'string', NULL, 'The armor regains 1d6 expended charges daily at dawn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (6, 0, 'string', NULL, 'You can cast mage armor on yourself at will, without expending a spell slot or material components.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (7, 0, 'string', NULL, 'You can cast levitate on yourself at will, without expending a spell slot or material components.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (8, 0, 'string', NULL, 'You no longer need to sleep and can''t be forced to sleep by any means. To gain the benefits of a long rest, you can spend all 8 hours doing light activity, such as reading your Book of Shadows and keeping watch.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (9, 0, 'string', NULL, 'When you''re within 5 feet of a creature on your turn, you can expend one superiority die and switch places with that creature, provided you spend at least 5 feet of movement and the creature is willing and isn''t incapacitated. This movement doesn''t provoke opportunity attacks.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (9, 1, 'string', NULL, 'Roll the superiority die. Until the start of your next turn, you or the other creature (your choice) gains a bonus to AC equal to the number rolled.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (10, 0, 'string', NULL, 'You use abjuration magic to try to temporarily banish your target to a harmless location in the Feywild. The creature hit by the arrow must also succeed on a Charisma saving throw or be banished. While banished in this way, the target''s speed is 0, and it is incapacitated. At the end of its next turn, the target reappears in the space it vacated or in the nearest unoccupied space if that space is occupied.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (10, 1, 'string', NULL, 'After you reach 18th level in this class, a target also takes 2d6 force damage when the arrow hits it.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (11, 0, 'string', NULL, 'You can cast speak with animals at will, without expending a spell slot.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (12, 0, 'string', NULL, 'Your enchantment magic causes this arrow to temporarily beguile its target. The creature hit by the arrow takes an extra 2d6 psychic damage, and choose one of your allies within 30 feet of the target. The target must succeed on a Wisdom saving throw, or it is charmed by the chosen ally until the start of your next turn. This effect ends early if the chosen ally attacks the charmed target, deals damage to it, or forces it to make a saving throw.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (12, 1, 'string', NULL, 'The psychic damage increases to 4d6 when you reach 18th level in this class.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (13, 0, 'string', NULL, 'You gain proficiency in the Deception and Persuasion skills.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (14, 0, 'string', NULL, 'You can cast compulsion once using a warlock spell slot. You can''t do so again until you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (15, 0, 'string', NULL, 'You learn two cantrips of your choice from the cleric spell list. They count as paladin spells for you, and Charisma is your spellcasting ability for them. Whenever you gain a level in this class, you can replace one of these cantrips with another cantrip from the cleric spell list.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (16, 0, 'string', NULL, 'You have blindsight with a range of 10 feet. Within that range, you can effectively see anything that isn''t behind Cover, even if you''re blinded or in darkness. Moreover, you can see an invisible creature within that range, unless the creature successfully hides from you.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (17, 0, 'string', NULL, 'While someone else is wearing your talisman, you can use your action to teleport to the unoccupied space closest to them, provided the two of you are on the same plane of existence. The wearer of your talisman can do the same thing, using their action to teleport to you. The teleportation can be used a number of times equal to your proficiency bonus, and all expended uses are restored when you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (18, 0, 'string', NULL, 'You can now inscribe magical rituals in your Book of Shadows. Choose two 1st-level spells that have the ritual tag from any class''s spell list. The spells needn''t be from the same spell list. The spells appear in the book and don''t count against the number of spells you know. With your Book of Shadows in hand, you can cast the chosen spells as rituals. You can''t cast the spells except as rituals, unless you''ve learned them by some other means. You can also cast a warlock spell you know as a ritual if it has the ritual tag.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (18, 1, 'string', NULL, 'On your adventures, you can add other ritual spells to your Book of Shadows. When you find such a spell, you can add it to the book if the spell''s level is equal to or less than half your warlock level (rounded up) and if you can spare the time to transcribe the spell. For each level of the spell, the transcription process takes 2 hours and costs 50 gp for the rare inks needed to inscribe it.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (19, 0, 'string', NULL, 'While wearing these boots, a creature can teleport up to 15 feet as a bonus action to an unoccupied space the creature can see. The creature must have occupied that space at some point during the current turn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (20, 0, 'string', NULL, 'When a creature you can see moves into the reach you have with the melee weapon you''re wielding, you can use your reaction to expend one superiority die and make one attack against the creature, using that weapon. If the attack hits, add the superiority die to the weapon''s damage roll.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (21, 0, 'string', NULL, 'You can spend 6 ki points to cast cone of cold.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (22, 0, 'string', NULL, 'You imbue your arrow with force energy drawn from the school of evocation. The energy detonates after your attack. Immediately after the arrow hits the creature, the target and all other creatures within 10 feet of it take 2d6 force damage each.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (22, 1, 'string', NULL, 'The force damage increases to 4d6 when you reach 18th level in this class.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (23, 0, 'string', NULL, 'When you cast a spell that forces other creatures to make a saving throw, you can protect some of those creatures from the spell''s full force. To do so, you spend 1 sorcery point and choose a number of those creatures up to your Charisma modifier (minimum of one creature). A chosen creature automatically succeeds on its saving throw against the spell.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (24, 0, 'string', NULL, 'You can cast hold monster at willtargeting a celestial, fiend, or elementalwithout expending a spell slot or material components. You must finish a long rest before you can use this invocation on the same creature again.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (25, 0, 'string', NULL, 'You can spend 3 ki points to cast hold person.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (26, 0, 'string', NULL, 'As a bonus action, you can surround yourself with a magical aura that looks like buzzing flies. The aura extends 5 feet from you in every direction, but not through Cover. It lasts until you''re incapacitated or you dismiss it as a bonus action.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (26, 1, 'string', NULL, 'The aura grants you advantage on Charisma (Intimidation) checks but disadvantage on all other Charisma checks. Any other creature that starts its turn in the aura takes poison damage equal to your Charisma modifier (minimum of 0 damage).', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (26, 2, 'string', NULL, 'Once you use this invocation, you can''t use it again until you finish a short or long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (27, 0, 'string', NULL, 'This rune emulates the deceptive magic used by some cloud giants. While wearing or carrying an object inscribed with this rune, you have advantage on Dexterity (Sleight of Hand) checks and Charisma (Deception) checks.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (27, 1, 'string', NULL, 'In addition, when you or a creature you can see within 30 feet of you is hit by an attack roll, you can use your reaction to invoke the rune and choose a different creature within 30 feet of you, other than the attacker. The chosen creature becomes the target of the attack, using the same roll. This magic can transfer the attack''s effects regardless of the attack''s range. Once you invoke this rune, you can''t do so again until you finish a short or long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (28, 0, 'string', NULL, 'When you take the Attack action on your turn, you can forgo one of your attacks and use a bonus action to direct one of your companions to strike. When you do so, choose a friendly creature who can see or hear you and expend one superiority die. That creature can immediately use its reaction to make one weapon attack, adding the superiority die to the attack''s damage roll.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (29, 0, 'string', NULL, 'When you make a Charisma (Intimidation), a Charisma (Performance), or a Charisma (Persuasion) check, you can expend one superiority die and add the superiority die to the ability check.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (30, 0, 'string', NULL, 'While you are wearing armor, you gain a +1 bonus to AC.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (31, 0, 'string', NULL, 'You can see normally in darkness, both magical and nonmagical, to a distance of 120 feet.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (32, 0, 'string', NULL, 'When you hit a creature with a weapon attack, you can expend one superiority die to attempt to disarm the target, forcing it to drop one item of your choice that it''s holding. You add the superiority die to the attack''s damage roll, and the target must make a Strength saving throw. On a failed save, it drops the object you choose. The object lands at its feet.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (33, 0, 'string', NULL, 'When you cast a spell that has a range of 5 feet or greater, you can spend 1 sorcery point to double the range of the spell.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (33, 1, 'string', NULL, 'When you cast a spell that has a range of touch, you can spend 1 sorcery point to make the range of the spell 30 feet.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (34, 0, 'string', NULL, 'When you hit a creature with a weapon attack, you can expend one superiority die to distract the creature, giving your allies an opening. You add the superiority die to the attack''s damage roll. The next attack roll against the target by an attacker other than you has advantage if the attack is made before the start of your next turn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (35, 0, 'string', NULL, 'You can cast confusion once using a warlock spell slot. You can''t do so again until you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (36, 0, 'string', NULL, 'You learn two cantrips of your choice from the druid spell list. They count as ranger spells for you, and Wisdom is your spellcasting ability for them. Whenever you gain a level in this class, you can replace one of these cantrips with another cantrip from the druid spell list.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (37, 0, 'string', NULL, 'When you are wielding a melee weapon in one hand and no other weapons, you gain a +2 bonus to damage rolls with that weapon.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (38, 0, 'string', NULL, 'You have advantage on Constitution saving throws that you make to maintain your concentration on a spell.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (39, 0, 'string', NULL, 'You can cast detect magic at will, without expending a spell slot.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (40, 0, 'string', NULL, 'Once per turn when you hit a creature with your pact weapon, you can expend a warlock spell slot to deal an extra 1d8 force damage to the target, plus another 1d8 per level of the spell slot, and you can knock the target prone if it is Huge or smaller.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (41, 0, 'string', NULL, 'When you cast eldritch blast, its range is 300 feet.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (42, 0, 'string', NULL, 'You can use your action to briefly control elemental forces within 30 feet of you, causing one of the following effects of your choice:', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (42, 1, 'list', NULL, NULL, '["Create a harmless, instantaneous sensory effect related to air, earth, fire, or water, such as a shower of sparks, a puff of wind, a spray of light mist, or a gentle rumbling of stone.","Instantaneously light or snuff out a candle, a torch, or a small campfire.","Chill or warm up to 1 pound of nonliving material for up to 1 hour.","Cause earth, fire, water, or mist that can fit within a 1-foot cube to shape itself into a crude form you designate for 1 minute."]', NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (43, 0, 'string', NULL, 'When you roll damage for a spell, you can spend 1 sorcery point to reroll a number of the damage dice up to your Charisma modifier (minimum of one). You must use the new rolls.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (43, 1, 'string', NULL, 'You can use Empowered Spell even if you have already used a different Metamagic option during the casting of the spell.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (44, 0, 'string', NULL, 'You weave necromantic magic into your arrow. The creature hit by the arrow takes an extra 2d6 necrotic damage. The target must also succeed on a Constitution saving throw, or the damage dealt by its weapon attacks is halved until the start of your next turn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (44, 1, 'string', NULL, 'The necrotic damage increases to 4d6 when you reach 18th level in this class.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (45, 0, 'string', NULL, 'While holding this item, a creature gains a +1 bonus to spell attack rolls. In addition, the creature ignores Cover when making a spell attack.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (45, 1, 'string', NULL, 'The bonus increases to +2 when you reach 10th level in this class.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (46, 0, 'string', NULL, 'A creature gains a +1 bonus to Armor Class while wearing (armor) or wielding (shield) the infused item.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (46, 1, 'string', NULL, 'The bonus increases to +2 when you reach 10th level in this class.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (47, 0, 'string', NULL, 'This magic weapon grants a +1 bonus to attack and damage rolls made with it.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (47, 1, 'string', NULL, 'The bonus increases to +2 when you reach 10th level in this class.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (48, 0, 'string', NULL, 'You can spend 5 ki points to cast stoneskin, targeting yourself.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (49, 0, 'string', NULL, 'When you move, you can expend one superiority die, rolling the die and adding the number rolled to your AC until you stop moving.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (50, 0, 'string', NULL, 'When you cast a spell that has a duration of 1 minute or longer, you can spend 1 sorcery point to double its duration, to a maximum duration of 24 hours.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (51, 0, 'string', NULL, 'You can read all writing.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (52, 0, 'string', NULL, 'When you use the Attack action on your turn, you can spend 1 ki point to cause tendrils of flame to stretch out from your fists and feet. Your reach with your unarmed strikes increases by 10 feet for that action, as well as the rest of the turn. A hit with such an attack deals fire damage instead of bludgeoning damage, and if you spend 1 ki point when the attack hits, it also deals an extra 1d10 fire damage.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (53, 0, 'string', NULL, 'A new page appears in your Book of Shadows. With your permission, a creature can use its action to write its name on that page, which can contain a number of names equal to your proficiency bonus.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (53, 1, 'string', NULL, 'You can cast the sending spell, targeting a creature whose name is on the page, without using a spell slot and without using material components. To do so, you must write the message on the page. The target hears the message in their mind, and if the target replies, their message appears on the page, rather than in your mind. The writing disappears after 1 minute.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (53, 2, 'string', NULL, 'As an action, you can magically erase a name on the page by touching it.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (54, 0, 'string', NULL, 'You can expend one superiority die and use a bonus action on your turn to feint, choosing one creature within 5 feet of you as your target. You have advantage on your next attack roll against that creature this turn. If that attack hits, add the superiority die to the attack''s damage roll.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (55, 0, 'string', NULL, 'You can cast false life on yourself at will as a 1st-level spell, without expending a spell slot or material components.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (56, 0, 'string', NULL, 'This rune''s magic channels the masterful craftsmanship of great smiths. While wearing or carrying an object inscribed with this rune, your proficiency bonus is doubled for any ability check you make that uses your proficiency with a tool.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (56, 1, 'string', NULL, 'In addition, when you hit a creature with an attack using a weapon, you can invoke the rune to summon fiery shackles: the target takes an extra 2d6 fire damage, and it must succeed on a Strength saving throw or be restrained for 1 minute. While restrained by the shackles, the target takes 2d6 fire damage at the start of each of its turns. The target can repeat the saving throw at the end of each of its turns, banishing the shackles on a success. Once you invoke this rune, you can''t do so again until you finish a short or long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (57, 0, 'string', NULL, 'You can spend 2 ki points to cast thunderwave.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (58, 0, 'string', NULL, 'You can spend 4 ki points to cast fireball.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (59, 0, 'string', NULL, 'This rune''s magic evokes the might of those who survive in the wintry wilderness, such as frost giants. While wearing or carrying an object inscribed with this rune, you have advantage on Wisdom (Animal Handling) checks and Charisma (Intimidation) checks.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (59, 1, 'string', NULL, 'In addition, you can invoke the rune as a bonus action to increase your sturdiness. For 10 minutes, you gain a +2 bonus to all ability checks and saving throws that use Strength or Constitution. Once you invoke this rune, you can''t do so again until you finish a short or long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (60, 0, 'string', NULL, 'You can use your action to touch a willing humanoid and perceive through its senses until the end of your next turn. As long as the creature is on the same plane of existence as you, you can use your action on subsequent turns to maintain this connection, extending the duration until the end of your next turn. While perceiving through the other creature''s senses, you benefit from any special senses possessed by that creature, and you are blinded and deafened to your own surroundings.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (61, 0, 'string', NULL, 'As an action, you gain the ability to see through solid objects to a range of 30 feet. Within that range, you have darkvision if you don''t already have it. This special sight lasts for 1 minute or until your concentration ends (as if you were concentration on a spell). During that time, you perceive objects as ghostly, transparent images.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (61, 1, 'string', NULL, 'Once you use this invocation, you can''t use it again until you finish a short or long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (62, 0, 'string', NULL, 'You can breathe underwater, and you gain a swimming speed equal to your walking speed.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (62, 1, 'string', NULL, 'You can also cast water breathing once without expending a spell slot. You regain the ability to do so when you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (63, 0, 'string', NULL, 'Whenever you regain hit points while your familiar is within 100 feet of you, treat any dice rolled to determine the hit points you regain as having rolled their maximum value for you.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (64, 0, 'string', NULL, 'A new page appears in your Book of Shadows. With your permission, a creature can use its action to write its name on that page, which can contain a number of names equal to your proficiency bonus.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (64, 1, 'string', NULL, 'When any creature whose name is on the page is reduced to 0 hit points but not killed outright, the creature magically drops to 1 hit point instead. Once this magic is triggered, no creature can benefit from it until you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (64, 2, 'string', NULL, 'As an action, you can magically erase a name on the page by touching it.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (65, 0, 'string', NULL, 'When you hit a creature with a weapon attack, you can expend one superiority die to attempt to goad the target into attacking you. You add the superiority die to the attack''s damage roll, and the target must make a Wisdom saving throw. On a failed save, the target has disadvantage on all attack rolls against targets other than you until the end of your next turn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (66, 0, 'string', NULL, 'You can spend 3 ki points to cast shatter.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (67, 0, 'string', NULL, 'Immediately after you hit a creature with a melee attack on your turn, you can expend one superiority die and then try to grapple the target as a bonus action (see the Player''s Handbook for rules on grappling). Add the superiority die to your Strength (Athletics) check.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (68, 0, 'string', NULL, 'Once on each of your turns when you hit a creature with your eldritch blast, you can move that creature in a straight line 10 feet closer to you.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (69, 0, 'string', NULL, 'When this arrow strikes its target, conjuration magic creates grasping, poisonous brambles, which wrap around the target. The creature hit by the arrow takes an extra 2d6 poison damage, its speed is reduced by 10 feet, and it takes 2d6 slashing damage the first time on each turn it moves 1 foot or more without teleporting. The target or any creature that can reach it can use its action to remove the brambles with a successful Strength (Athletics) check against your Arcane Shot save DC. Otherwise, the brambles last for 1 minute or until you use this option again.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (69, 1, 'string', NULL, 'The poison damage and slashing damage both increase to 4d6 when you reach 18th level in this class.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (70, 0, 'string', NULL, 'When you roll a 1 or 2 on a damage die for an attack you make with a melee weapon that you are wielding with two hands, you can reroll the die and must use the new roll, even if the new roll is a 1 or a 2. The weapon must have the two-handed or versatile property for you to gain this benefit.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (71, 0, 'string', NULL, 'When you cast a spell that forces a creature to make a saving throw to resist its effects, you can spend 3 sorcery points to give one target of the spell disadvantage on its first saving throw made against the spell.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (72, 0, 'string', NULL, 'While wearing this helmet, a creature has advantage on initiative rolls. In addition, the wearer can''t be surprised, provided it isn''t incapacitated.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (73, 0, 'string', NULL, 'This rune''s magic bestows a resilience reminiscent of a hill giant. While wearing or carrying an object that bears this rune, you have advantage on saving throws against being poisoned, and you have resistance against poison damage.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (73, 1, 'string', NULL, 'In addition, you can invoke the rune as a bonus action, gaining resistance to bludgeoning, piercing, and slashing damage for 1 minute. Once you invoke this rune, you can''t do so again until you finish a short or long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (74, 0, 'string', NULL, 'You learn intricate methods for magically creating a special Homunculus Servant that serves you. The item you infuse serves as the creature''s heart, around which the creature''s body instantly forms.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (74, 1, 'string', NULL, 'You determine the homunculus''s appearance. Some artificers prefer mechanical-looking birds, whereas some like winged vials or miniature, animate cauldrons.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (74, 2, 'string', NULL, 'The homunculus is friendly to you and your companions, and it obeys your commands. See this creature''s game statistics in the Homunculus Servant stat block, which uses your proficiency bonus (PB) in several places.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (74, 3, 'string', NULL, 'In combat, the homunculus shares your initiative count, but it takes its turn immediately after yours. It can move and use its reaction on its own, but the only action it takes on its turn is the Dodge action, unless you take a bonus action on your turn to command it to take another action. That action can be one in its stat block or some other action. If you are incapacitated, the homunculus can take any action of its choice, not just Dodge.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (74, 4, 'string', NULL, 'The homunculus regains 2d6 hit points if the mending spell is cast on it. If you or the homunculus dies, it vanishes, leaving its heart in its space.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (75, 0, 'string', NULL, 'You can use any weapon you summon with your Pact of the Blade feature as a spellcasting focus for your warlock spells.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (75, 1, 'string', NULL, 'In addition, the weapon gains a +1 bonus to its attack and damage rolls, unless it is a magic weapon that already has a bonus to those rolls.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (75, 2, 'string', NULL, 'Finally, the weapon you conjure can be a shortbow, longbow, light crossbow, or heavy crossbow.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (76, 0, 'string', NULL, 'When a creature you can see hits a target, other than you, within 5 feet of you with an attack, you can use your reaction to reduce the damage the target takes by 1d10 + your proficiency bonus (to a minimum of 0 damage). You must be wielding a shield or a simple or martial weapon to use this reaction.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (77, 0, 'string', NULL, 'When you cast find familiar, you infuse the summoned familiar with a measure of your eldritch power, granting the creature the following benefits:', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (77, 1, 'list', NULL, NULL, '["The familiar gains either a flying speed or a swimming speed (your choice) of 40 feet.","As a bonus action, you can command the familiar to take the {@action Attack} action.","The familiar''s weapon attacks are considered magical for the purpose of overcoming immunity and resistance to nonmagical attacks.","If the familiar forces a creature to make a saving throw, it uses your spell save DC.","When the familiar takes damage, you can use your reaction to grant it resistance against that damage."]', NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (78, 0, 'string', NULL, 'Once on each of your turns when you hit a creature with your eldritch blast, you can reduce that creature''s speed by 10 feet until the end of your next turn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (79, 0, 'string', NULL, 'When you hit a creature with your pact weapon, the creature takes extra necrotic damage equal to your Charisma modifier (minimum of 1).', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (80, 0, 'string', NULL, 'When you make a melee weapon attack on your turn, you can expend one superiority die to increase your reach for that attack by 5 feet. If you hit, you add the superiority die to the attack''s damage roll.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (81, 0, 'string', NULL, 'As a bonus action, you cause a psychic disturbance around the target cursed by your hex spell or by a warlock feature of yours, such as Hexblade''s Curse or Sign of Ill Omen. When you do so, you deal psychic damage to the cursed target and each creature of your choice that you can see within 5 feet of it. The psychic damage equals your Charisma modifier (minimum of 1 damage). To use this invocation, you must be able to see the cursed target, and it must be within 30 feet of you.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (82, 0, 'string', NULL, 'When you hit a creature with a weapon attack, you can expend one superiority die to maneuver one of your comrades into a more advantageous position. You add the superiority die to the attack''s damage roll, and you choose a friendly creature who can see or hear you. That creature can use its reaction to move up to half its speed without provoking opportunity attacks from the target of your attack.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (83, 0, 'string', NULL, 'You can cast disguise self at will, without expending a spell slot.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (84, 0, 'string', NULL, 'You can cast alter self at will, without expending a spell slot.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (85, 0, 'string', NULL, 'When you hit a creature with a weapon attack, you can expend one superiority die to attempt to frighten the target. You add the superiority die to the attack''s damage roll, and the target must make a Wisdom saving throw. On a failed save, it is frightened of you until the end of your next turn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (86, 0, 'string', NULL, 'The infused item can send a jolt to the wearer to refocus their mind. The item has 4 charges. When the wearer fails a Constitution saving throw to maintain concentration on a spell, the wearer can use its reaction to expend 1 of the item''s charges to succeed instead. The item regains 1d4 expended charges daily at dawn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (87, 0, 'string', NULL, 'You can cast conjure elemental once using a warlock spell slot. You can''t do so again until you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (88, 0, 'string', NULL, 'You can cast slow once using a warlock spell slot. You can''t do so again until you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (89, 0, 'string', NULL, 'You can spend 4 ki points to cast gaseous form, targeting yourself.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (90, 0, 'string', NULL, 'You can cast silent image at will, without expending a spell slot or material components.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (91, 0, 'string', NULL, 'When you are in an area of dim light or darkness, you can use your action to become invisible until you move or take an action or a reaction.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (92, 0, 'string', NULL, 'You can cast jump on yourself at will, without expending a spell slot or material components.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (93, 0, 'string', NULL, 'You can use your action to create a pact weapon in your empty hand. You can choose the form that this melee weapon takes each time you create it (see chapter 5 for weapon options). You are proficient with it while you wield it. This weapon counts as magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (93, 1, 'string', NULL, 'Your pact weapon disappears if it is more than 5 feet away from you for 1 minute or more. It also disappears if you use this feature again, if you dismiss the weapon (no action required), or if you die.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (93, 2, 'string', NULL, 'You can transform one magic weapon into your pact weapon by performing a special ritual while you hold the weapon. You perform the ritual over the course of 1 hour, which can be done during a short rest. You can then dismiss the weapon, shunting it into an extradimensional space, and it appears whenever you create your pact weapon thereafter. You can''t affect an artifact or a sentient weapon in this way. The weapon ceases being your pact weapon if you die, if you perform the 1-hour ritual on a different weapon, or if you use a 1-hour ritual to break your bond to it. The weapon appears at your feet if it is in the extradimensional space when the bond breaks.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (94, 0, 'string', NULL, 'You learn the find familiar spell and can cast it as a ritual. The spell doesn''t count against your number of spells known.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (94, 1, 'string', NULL, 'When you cast the spell, you can choose one of the normal forms for your familiar or one of the following special forms: imp, pseudodragon, quasit, or sprite.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (94, 2, 'string', NULL, 'Additionally, when you take the Attack action, you can forgo one of your own attacks to allow your familiar to use its reaction to make one attack of its own.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (95, 0, 'string', NULL, 'Your patron gives you an amulet, a talisman that can aid the wearer when the need is great. When the wearer fails an ability check, they can add a d4 to the roll, potentially turning the roll into a success. This benefit can be used a number of times equal to your proficiency bonus, and all expended uses are restored when you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (95, 1, 'string', NULL, 'If you lose the talisman, you can perform a 1-hour ceremony to receive a replacement from your patron. This ceremony can be performed during a short or long rest, and it destroys the previous amulet. The talisman turns to ash when you die.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (96, 0, 'string', NULL, 'Your patron gives you a grimoire called a Book of Shadows. When you gain this feature, choose three cantrips from any class''s spell list. The cantrips do not need to be from the same spell list. While the book is on your person, you can cast those cantrips at will. They don''t count against your number of cantrips known. Any cantrip you cast with this feature is considered a warlock cantrip for you. If you lose your Book of Shadows, you can perform a 1-hour ceremony to receive a replacement from your patron. This ceremony can be performed during a short or long rest, and it destroys the previous book. The book turns to ash when you die.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (97, 0, 'string', NULL, 'When another creature damages you with a melee attack, you can use your reaction and expend one superiority die to reduce the damage by the number you roll on your superiority die + your Dexterity modifier.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (98, 0, 'string', NULL, 'You use transmutation magic to give your arrow an ethereal quality. When you use this option, you don''t make an attack roll for the attack. Instead, the arrow shoots forward in a line, which is 1 foot wide and 30 feet long, before disappearing. The arrow passes harmlessly through objects, ignoring cover. Each creature in that line must make a Dexterity saving throw. On a failed save, a creature takes damage as if it were hit by the arrow, plus an extra 1d6 piercing damage. On a successful save, a target takes half as much damage.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (98, 1, 'string', NULL, 'The piercing damage increases to 2d6 when you reach 18th level in this class.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (99, 0, 'string', NULL, 'When you make a weapon attack roll against a creature, you can expend one superiority die to add it to the roll. You can use this maneuver before or after making the attack roll, but before any effects of the attack are applied.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (100, 0, 'string', NULL, 'When a creature you can see attacks a target other than you that is within 5 feet of you, you can use your reaction to impose disadvantage on the attack roll. You must be wielding a shield.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (101, 0, 'string', NULL, 'When the wearer of your talisman fails a saving throw, they can add a d4 to the roll, potentially turning the save into a success. This benefit can be used a number of times equal to your proficiency bonus, and all expended uses are restored when you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (102, 0, 'string', NULL, 'When you hit a creature with a weapon attack, you can expend one superiority die to attempt to drive the target back. You add the superiority die to the attack''s damage roll, and if the target is Large or smaller, it must make a Strength saving throw. On a failed save, you push the target up to 15 feet away from you.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (103, 0, 'string', NULL, 'As a bonus action, you can expend one superiority die and make a ranged attack with a weapon that has the thrown property. You can draw the weapon as part of making this attack. If you hit, add the superiority die to the weapon''s damage roll.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (104, 0, 'string', NULL, 'When you cast a spell that has a casting time of 1 action, you can spend 2 sorcery points to change the casting time to 1 bonus action for this casting.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (105, 0, 'string', NULL, 'This magic weapon grants a +1 bonus to attack and damage rolls made with it. While holding it, the wielder can take a bonus action to cause it to shed bright light in a 30-foot radius and dim light for an additional 30 feet. The wielder can extinguish the light as a bonus action.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (105, 1, 'string', NULL, 'The weapon has 4 charges. As a reaction immediately after being hit by an attack, the wielder can expend 1 charge and cause the attacker to be blinded until the end of the attacker''s next turn, unless the attacker succeeds on a Constitution saving throw against your spell save DC. The weapon regains 1d4 expended charges daily at dawn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (106, 0, 'string', NULL, 'On your turn, you can use a bonus action and expend one superiority die to bolster the resolve of one of your companions. When you do so, choose a friendly creature who can see or hear you. That creature gains temporary hit points equal to the superiority die roll + your Charisma modifier.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (107, 0, 'string', NULL, 'When the wearer of your talisman is hit by an attacker you can see within 30 feet of you, you can use your reaction to deal psychic damage to the attacker equal to your proficiency bonus and push it up to 10 feet away from the talisman''s wearer.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (108, 0, 'string', NULL, 'Your curse creates a temporary bond between you and your target. As a bonus action, you can magically teleport up to 30 feet to an unoccupied space you can see within 5 feet of the target cursed by your hex spell or by a warlock feature of yours, such as Hexblade''s Curse or Sign of Ill Omen. To teleport in this way, you must be able to see the cursed target.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (109, 0, 'string', NULL, 'This magic weapon grants a +1 bonus to attack and damage rolls made with it when it''s used to make a ranged attack, and it ignores the loading property if it has it.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (109, 1, 'string', NULL, 'If the weapon lacks ammunition, it produces its own, automatically creating one piece of magic ammunition when the wielder makes a ranged attack with it. The ammunition created by the weapon vanishes the instant after it hits or misses a target.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (110, 0, 'string', NULL, 'When you hit a creature with eldritch blast, you can push the creature up to 10 feet away from you in a straight line.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (111, 0, 'string', NULL, 'Using this infusion, you replicate a particular magic item. You can learn this infusion multiple times; each time you do so, choose a magic item that you can make with it, picking from the Replicable Items tables below. A table''s title tells you the level you must be in the class to choose an item from the table. Alternatively, you can choose the magic item from among the common magic items in the game, not including potions or scrolls.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (111, 1, 'string', NULL, 'In the tables, an item''s entry tells you whether the item requires attunement. See the item''s description in the Dungeon Master''s Guide for more information about it, including the type of object required for its making.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (111, 2, 'table', NULL, NULL, NULL, 'Replicable Items (2nd-Level Artificer)', '["Magic Item","Attunement"]', '["col-10","col-2 text-center"]', '[["{@item Alchemy jug}","No"],["{@item Bag of holding}","No"],["{@item Cap of water breathing}","No"],["{@item Goggles of night}","No"],["{@item Rope of climbing}","No"],["{@item Sending stones}","No"],["{@item Wand of magic detection}","No"],["{@item Wand of secrets}","No"]]', NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (111, 3, 'table', NULL, NULL, NULL, 'Replicable Items (6th-Level Artificer)', '["Magic Item","Attunement"]', '["col-10","col-2 text-center"]', '[["{@item Boots of elvenkind}","No"],["{@item Cloak of elvenkind}","Yes"],["{@item Cloak of the manta ray}","No"],["{@item Eyes of charming}","Yes"],["{@item Gloves of thievery}","No"],["{@item Lantern of revealing}","No"],["{@item Pipes of haunting}","No"],["{@item Ring of water walking}","No"]]', NULL);
