INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (112, 0, 'string', NULL, 'A creature gains a +1 bonus to Armor Class while wielding this shield.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (112, 1, 'string', NULL, 'The shield has 4 charges. While holding it, the wielder can use a reaction immediately after being hit by a melee attack to expend 1 of the shield''s charges and push the attacker up to 15 feet away. The shield regains 1d4 expended charges daily at dawn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (113, 0, 'string', NULL, 'While wearing this armor, a creature has resistance to one of the following damage types, which you choose when you infuse the item: acid, cold, fire, force, lightning, necrotic, poison, psychic, radiant, or thunder.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (114, 0, 'string', NULL, 'This magic weapon grants a +1 bonus to attack and damage rolls made with it, and it returns to the wielder''s hand immediately after it is used to make a ranged attack.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (115, 0, 'string', NULL, 'You can spend 4 ki points to cast fly, targeting yourself.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (116, 0, 'string', NULL, 'When a creature misses you with a melee attack, you can use your reaction and expend one superiority die to make a melee weapon attack against the creature. If you hit, you add the superiority die to the attack''s damage roll.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (117, 0, 'string', NULL, 'You can spend 5 ki points to cast wall of fire.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (118, 0, 'string', NULL, 'You can spend 2 ki points to cast gust of wind.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (119, 0, 'string', NULL, 'You can cast polymorph once using a warlock spell slot. You can''t do so again until you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (120, 0, 'string', NULL, 'Using divination magic, you grant your arrow the ability to seek out a target. When you use this option, you don''t make an attack roll for the attack. Instead, choose one creature you have seen in the past minute. The arrow flies toward that creature, moving around corners if necessary and ignoring Cover and Cover. If the target is within the weapon''s range and there is a path large enough for the arrow to travel to the target, the target must make a Dexterity saving throw. Otherwise, the arrow disappears after traveling as far as it can. On a failed save, the target takes damage as if it were hit by the arrow, plus an extra 1d6 force damage, and you learn the target''s current location. On a successful save, the target takes half as much damage, and you don''t learn its location.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (120, 1, 'string', NULL, 'The force damage increases to 2d6 when you reach 18th level in this class.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (121, 0, 'string', NULL, 'If you make an attack roll for a spell and miss, you can spend 2 sorcery points to reroll the d20, and you must use the new roll.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (121, 1, 'string', NULL, 'You can use Seeking Spell even if you have already used a different Metamagic option during the casting of the spell.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (122, 0, 'string', NULL, 'You weave illusion magic into your arrow, causing it to occlude your foe''s vision with shadows. The creature hit by the arrow takes an extra 2d6 psychic damage, and it must succeed on a Wisdom saving throw or be unable to see anything farther than 5 feet away until the start of your next turn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (122, 1, 'string', NULL, 'The psychic damage increases to 4d6 when you reach 18th level in this class.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (123, 0, 'string', NULL, 'As an action, you can spend 1 ki point to choose an area of ice or water no larger than 30 feet on a side within 120 feet of you. You can change water to ice within the area and vice versa, and you can reshape ice in the area in any manner you choose. You can raise or lower the ice''s elevation, create or fill in a trench, erect or flatten a wall, or form a pillar. The extent of any such changes can''t exceed half the area''s largest dimension. For example, if you affect a 30-foot square, you can create a pillar up to 15 feet high. raise or lower the square''s elevation by up to 15 feet, dig a trench up to 15 feet deep, and so on. You can''t shape the ice to trap or injure a creature in the area.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (124, 0, 'string', NULL, 'You can cast invisibility at will, without expending a spell slot.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (125, 0, 'string', NULL, 'You can cast bestow curse once using a warlock spell slot. You can''t do so again until you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (126, 0, 'string', NULL, 'While wearing this ring, the creature can recover one expended spell slot as an action. The recovered slot can be of 3rd level or lower. Once used, the ring can''t be used again until the next dawn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (127, 0, 'string', NULL, 'This rune''s magic channels the judiciousness associated with stone giants. While wearing or carrying an object inscribed with this rune, you have advantage on Wisdom (Insight) checks, and you have darkvision out to a range of 120 feet.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (127, 1, 'string', NULL, 'In addition, when a creature you can see ends its turn within 30 feet of you, you can use your reaction to invoke the rune and force the creature to make a Wisdom saving throw. Unless the save succeeds, the creature is charmed by you for 1 minute. While charmed in this way, the creature has a speed of 0 and is incapacitated, descending into a dreamy stupor. The creature repeats the saving throw at the end of each of its turns, ending the effect on a success. Once you invoke this rune, you can''t do so again until you finish a short or long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (128, 0, 'string', NULL, 'Using this rune, you can glimpse the future like a storm giant seer. While wearing or carrying an object inscribed with this rune, you have advantage on Intelligence (Arcana) checks, and you can''t be surprised as long as you aren''t incapacitated.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (128, 1, 'string', NULL, 'In addition, you can invoke the rune as a bonus action to enter a prophetic state for 1 minute or until you''re incapacitated. Until the state ends, when you or another creature you can see within 60 feet of you makes an attack roll, a saving throw, or an ability check, you can use your reaction to cause the roll to have advantage or disadvantage. Once you invoke this rune, you can''t do so again until you finish a short or long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (129, 0, 'string', NULL, 'When you cast a spell, you can spend 1 sorcery point to cast it without any somatic or verbal components.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (130, 0, 'string', NULL, 'You learn one maneuver of your choice from among those available to the fighter archetype. If a maneuver you use requires your target to make a saving throw to resist the maneuver''s effects, the saving throw DC equals 8 + your proficiency bonus + your Strength or Dexterity modifier (your choice).', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (130, 1, 'string', NULL, 'You gain one superiority die, which is a d6 (this die is added to any superiority dice you have from another source). This die is used to fuel your maneuvers. A superiority die is expended when you use it. You regain your expended superiority dice when you finish a short or long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (131, 0, 'string', NULL, 'When you hit a creature with a melee weapon attack, you can expend one superiority die to attempt to damage another creature with the same attack. Choose another creature within 5 feet of the original target and within your reach. If the original attack roll would hit the second creature, it takes damage equal to the number you roll on your superiority die. The damage is of the same type dealt by the original attack.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (132, 0, 'string', NULL, 'You can spend 2 ki points to cast burning hands.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (133, 0, 'string', NULL, 'When you make an Intelligence (Investigation), an Intelligence (History), or a Wisdom (Insight) check, you can expend one superiority die and add the superiority die to the ability check.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (134, 0, 'string', NULL, 'You can cast bane once using a warlock spell slot. You can''t do so again until you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (135, 0, 'string', NULL, 'You can attack with your pact weapon twice, instead of once, whenever you take the Attack action on your turn.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (136, 0, 'string', NULL, 'You can draw a weapon that has the thrown property as part of the attack you make with the weapon.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (136, 1, 'string', NULL, 'In addition, when you hit with a ranged attack using a thrown weapon, you gain a +2 bonus to the damage roll.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (137, 0, 'string', NULL, 'As a reaction when you take damage, you can entomb yourself in ice, which melts away at the end of your next turn. You gain 10 temporary hit points per warlock level, which take as much of the triggering damage as possible. Immediately after you take the damage, you gain vulnerability to fire damage, your speed is reduced to 0, and you are incapacitated. These effects, including any remaining temporary hit points, all end when the ice melts.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (137, 1, 'string', NULL, 'Once you use this invocation, you can''t use it again until you finish a short or long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (138, 0, 'string', NULL, 'When you cast a spell that deals a type of damage from the following list, you can spend 1 sorcery point to change that damage type to one of the other listed types: acid, cold, fire, lightning, poison, thunder.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (139, 0, 'string', NULL, 'You can cast freedom of movement once on yourself without expending a spell slot. You regain the ability to do so when you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (140, 0, 'string', NULL, 'When you hit a creature with a weapon attack, you can expend one superiority die to attempt to knock the target down. You add the superiority die to the attack''s damage roll, and if the target is Large or smaller, it must make a Strength saving throw. On a failed save, you knock the target prone.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (141, 0, 'string', NULL, 'When you cast a spell that targets only one creature and doesn''t have a range of self, you can spend a number of sorcery points equal to the spell''s level to target a second creature in range with the same spell (1 sorcery point if the spell is a cantrip).', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (141, 1, 'string', NULL, 'To be eligible, a spell must be incapable of targeting more than one creature at the spell''s current level. For example, magic missile and scorching ray aren''t eligible, but ray of frost and chromatic orb are.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (142, 0, 'string', NULL, 'When you engage in two-weapon fighting, you can add your ability modifier to the damage of the second attack.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (143, 0, 'string', NULL, 'Your unarmed strikes can deal bludgeoning damage equal to 1d6 + your Strength modifier on a hit. If you aren''t wielding any weapons or a shield when you make the attack roll, the d6 becomes a d8.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (143, 1, 'string', NULL, 'At the start of each of your turns, you can deal 1d4 bludgeoning damage to one creature grappled by you.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (144, 0, 'string', NULL, 'You can create a blast of compressed air that strikes like a mighty fist. As an action, you can spend 2 ki points and choose a creature within 30 feet of you. That creature must make a Strength saving throw. On a failed save, the creature takes 3d10 bludgeoning damage, plus an extra 1d10 bludgeoning damage for each additional ki point you spend, and you can push the creature up to 20 feet away from you and knock it prone. On a successful save, the creature takes half as much damage, and you don''t push it or knock it prone.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (145, 0, 'string', NULL, 'You can cast animate dead without using a spell slot. Once you do so, you can''t cast it in this way again until you finish a long rest.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (146, 0, 'string', NULL, 'You can cast arcane eye at will, without expending a spell slot.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (147, 0, 'string', NULL, 'You can communicate telepathically with your familiar and perceive through your familiar''s senses as long as you are on the same plane of existence. Additionally, while perceiving through your familiar''s senses, you can also speak through your familiar in your own voice, even if your familiar is normally incapable of speech.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (148, 0, 'string', NULL, 'You can spend 2 ki points as an action to create a whip of water that shoves and pulls a creature to unbalance it. A creature that you can see that is within 30 feet of you must make a Dexterity saving throw. On a failed save, the creature takes 3d10 bludgeoning damage, plus an extra 1d10 bludgeoning damage for each additional ki point you spend, and you can either knock it prone or pull it up to 25 feet closer to you. On a successful save, the creature takes half as much damage, and you don''t pull it or knock it prone.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (149, 0, 'string', NULL, 'You can spend 6 ki points to cast wall of stone.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (150, 0, 'string', NULL, 'You can cast speak with dead at will, without expending a spell slot.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO optional_feature_entries (optional_feature_id, entry_order, type, name, content, list_items, table_caption, table_col_labels, table_col_styles, table_rows, raw_json) VALUES (151, 0, 'string', NULL, 'You can see the true form of any shapechanger or creature concealed by illusion or transmutation magic while the creature is within 30 feet of you and within line of sight.', NULL, NULL, NULL, NULL, NULL, NULL);
